(7, 'wfInner', 'Displays sub menu items', 0, 0, 0, '<ul class="children">\n  [[+wf.wrapper]]\n</ul>', 0, 'a:0:{}'),
(9, 'footer', 'footer content', 0, 12, 0, '\n   <section class="content-lower">\n     <div class="wrapper">\n\n      <div class="column">\n              <h4>Navigate</h4>\n              [[!Wayfinder? &startId=`0` &level=`1` &outerTpl=`wfOuter-footer` &RowTpl=`wfRow-footer`]]  \n            </div>\n          \n\n            <div class="column">\n  <h4>Latest News</h4>\n[[!getResources?\n  &parents=`6`\n  &hideContainers=`1`\n  &tpl=`latestNewsFooterTpl`\n  &limit=`3`\n  &sortby=`publishedon`\n]]\n              </div>\n\n          \n          \n   <div class="column">\n                    <h4>Newsletter</h4>\n          \n            <form method="post">\n              <fieldset class="login">\n              <legend>Sign up to the newsletter</legend>\n               <!-- <input type="text" value="First Name" name="FNAME" id="mce-FNAME" class="fname" />\n                <input type="text" value="Last Name" name="LNAME" id="mce-LNAME" class="lname" />-->\n                <input type="text" value="Email Address" name="EMAIL" class="required email" id="mce-EMAIL" />\n                <button type="submit" name="subscribe" class="send"><span>SUBSCRIBE</span></button><br/>\n   <!-- <a href="#">CHECK OUT THE ARCHIVE</a> -->\n    </fieldset>\n  </form>\n          \n\n      </div>\n      \n          \n          \n          \n  <div class="column">\n         <h4>Connect with us</h4>\n                <ul class="social-nav">\n                  <li><a href="#" class="twit"><span>Twitter</span></a></li>\n                  <li><a href="#" class="face"><span>Facebook</span></a></li>\n                  <li><a href="#" class="rss"><span>rss</span></a></li>\n    \n                </ul>\n      </div>\n    \n\n      \n    </div><!--.wrapper-->\n    \n        \n  </section><!--Lower section-->\n\n\n\n <footer>\n         <div class="wrapper">\n             <p>Copyright 2011 © <strong>Wedding Websites UK</strong>. All Rights Reserved.</p>\n           <a href="http://www.9thwave.co.uk">Design by: 9thwave.co.uk</a>\n            </div><!--.wrapper-->\n        </footer>\n       <script src=''assets/templates/wedding/js/superfish/superfish.js?v=1''></script>\n<script src=''assets/templates/wedding/js/prosto.js''></script>\n       <script type="text/javascript" src="assets/templates/wedding/js/jquery-ui-1.8.11.custom.min.js"></script>\n\n\n <script>\n  $(function() {\n    $( "#datepicker" ).datepicker({\n      showOn: "both",\n      buttonImage: "images/calendar.gif",\n      buttonImageOnly: true,\n      minDate: -2, maxDate: "+48M +28D",\n      changeYear: true,\n      changeMonth: true\n    });\n  });\n</script>     \n\n</body>\n</html>\n\n', 0, 'a:0:{}'),
(52, 'wfOuter-side', '', 0, 12, 0, '<ul id="left-column-menu">\n [[+wf.wrapper]]\n</ul>', 0, 'a:0:{}'),
(53, 'userGuestsList', '', 0, 26, 0, '<tr>\n    <td>[[+userGuestId]]</td>\n    <td class="guestEditable">[[+userGuestName]]</td>\n    [[+userGuestEvents]]\n    <td class="guestEditable">[[+userGuestGuestOf]]</td>\n    <td class="guestEditableYN">[[+userGuestRSVP]]</td>\n    <td class="guestEditableYN">[[+userGuestActive]]</td>\n    <td><a href="[[+userGuestEditLink]]" title="Edit" class="edit-small"><span>Edit</span></a>\n    <td><a href="[[+userGuestDeleteLink]]" title="Delete" class="off deleteLink"><span>Delete</span></a>    \n    </td>\n</tr>', 0, 'a:0:{}'),
