(58, 'userGuestsAddEditHeaderUpdate', '', 0, 26, 0, '<p>Here you can edit the the details for each guest. Choose which events the guest has accepted by choosing either Yes or No.\n                        You can change the events names and details by going to the Events page.</p>', 0, NULL),
(19, 'frontpageEdit', 'Front Page edit resource chunk', 0, 10, 0, '<div id="editForm" >\n <form action="[[~[[*id]]]]" method="post" name="frontpage" id="frontpage">\n    <input type="hidden" name="source" id="source" value="[[+fp.source]]" />\n    <input type="hidden" name="frontpage" id="frontpage" value="1" />\n    <p class="label_field_pair">\n    <label for="title">[[+fp.titlelabel]]</label>\n    <input type="text" name="title" id="title" value="[[+fp.title]]" class="text" maxlength="30" size="30"/></br>\n    </p>\n    <p class="label_field_pair">\n    <label for="longtitle">[[+fp.longtitlelabel]]</label>\n    <input type="text" name="longtitle" id="longtitle" value="[[+fp.longtitle]]" class="text" maxlength="60" size="90"/></br>\n    </p>\n    <p class="label_field_pair">\n    <label for="description">[[+fp.descriptionlabel]]</label>\n    <textarea id="description" name="description" style="width:85%; height:10%;">[[+fp.description]]</textarea></br>\n    </p>\n    <p class="label_field_pair">\n    <label for="alias" >[[+fp.aliaslabel]]</label>\n    <input type="text" name="alias" id="alias" value="[[+fp.alias]]" class="text" /></br>\n    </p>\n    <p class="label_field_pair">\n    <label for="summary">[[+fp.summarylabel]]</label>\n    <textarea id="summary" name="summary" style="width:85%; height:10%;">[[+fp.summary]]</textarea></br>\n    </p>\n    <p class="label_field_pair">\n    <label for="menutitle">[[+fp.menutitlelabel]]</label>\n    <input type="text" name="menutitle" id="menutitle" value="[[+fp.menutitle]]" class="text" />\n    </p></br>\n    <p class="label_field_pair">\n    <label for="menuindex">[[+fp.menuindexlabel]]</label>\n    <input type="text" name="menuindex" id="menuindex" value="[[+fp.menuindex]]" class="text" maxlength="3" size="3"/></br>\n    </p></br>\n    <p class="label_checkbox_pair">\n    <label for="hidemenus">[[+fp.hidemenulabel]]</label>\n    <input type="checkbox" name="hidemenus" id="hidemenus" value="[[+fp.hidemenus]]" class="text" [[+fp.hidemenuscheck]] />       </p>\n    <p class="label_checkbox_pair">\n    <label for="publish">[[+fp.publishlabel]]</label>\n    <input type="checkbox" name="publish" id="publish" value="[[+fp.publish]]" class="text" [[+fp.publishcheck]] /></br>\n    </p>\n<textarea id="formcontent" name="formcontent" class="tinymce">[[+fp.formcontent]]</textarea>\n<!-- Some tinymce integration calls -->\n	<a href="javascript:;" onmousedown="$(''#formcontent'').tinymce().show();">[Show]</a>\n	<a href="javascript:;" onmousedown="$(''#formcontent'').tinymce().hide();">[Hide]</a>\n	<a href="javascript:;" onmousedown="$(''#formcontent'').tinymce().execCommand(''Bold'');">[Bold]</a>\n	<a href="javascript:;" onmousedown="alert($(''#formcontent'').html());">[Get contents]</a>\n	<a href="javascript:;" onmousedown="alert($(''#formcontent'').tinymce().selection.getContent());">[Get selected HTML]</a>\n	<a href="javascript:;" onmousedown="alert($(''#formcontent'').tinymce().selection.getContent({format : ''text''}));">[Get selected text]</a>\n	<a href="javascript:;" onmousedown="alert($(''#formcontent'').tinymce().selection.getNode().nodeName);">[Get selected element]</a>\n	<a href="javascript:;" onmousedown="$(''#formcontent'').tinymce().execCommand(''mceInsertContent'',false,''<b>Hello world!!</b>'');">[Insert HTML]</a>\n	<a href="javascript:;" onmousedown="$(''#formcontent'').tinymce().execCommand(''mceReplaceContent'',false,''<b>{$selection}</b>'');">[Replace selection]</a>\n<br/><br/><a href="javascript:toggleEditor(''formcontent'');">Add/Remove editor</a>\n<br/><br/><input type="submit" name="save" value="[[+fp.savebuttonlabel]]" class="buttons" title="[[+fp.savebuttonlabel]]" />\n<input  onClick="parent.$(''a.colorbox'').colorbox.close();return false;" type="reset" value="[[+fp.cancelbuttonlabel]]" name="cancel" class="buttons" title="[[+fp.cancelbuttonlabel]]" />\n    <p>[[+fp.error_message]]</p>\n </form>\n</div>', 0, NULL),
(18, 'latestNewsFooterTpl', '', 0, 9, 0, '<li>\n  <strong><a href="[[~[[+id]]]]">[[+pagetitle]]</a></strong>\n <!-- [[+publishedon:notempty=`<br /> [[+publishedon:strtotime:date=`%b %d, %Y`]]`]]--><br />\n[[+introtext]]\n</li>', 0, 'a:0:{}'),
(8, 'wfRow', '', 0, 0, 0, '<li [[+wf.classes]]><a href="[[+wf.link]]" title="[[+wf.title]]">[[+wf.linktext]]</a></li>\n', 0, 'a:0:{}'),
(12, 'head', '', 0, 12, 0, '<!DOCTYPE html>\n<html lang="en">\n<head>\n<meta charset="[[++modx_charset]]">\n<base href="[[++site_url]]">\n<title>[[++site_name]] | [[*pagetitle]]</title>\n<meta name="description" content="[[*description]]">\n<meta name="keywords" content="[[*keywords]]">\n<link href="assets/templates/wedding/styles/main.css" rel="stylesheet" type="text/css" media="screen"/>\n<!--[if lte IE 8]>\n  <link rel="stylesheet" href="assets/templates/wedding/styles/ie.css"/>\n<![endif]-->\n<!--[if lte IE 8]><script src="http://html5shiv.googlecode.com/svn/trunk/html5.js"></script><![endif]-->\n<link rel="stylesheet" href="assets/templates/wedding/styles/jquery-ui-1.8.11.custom.css">\n<link rel="stylesheet" type="text/css" href="assets/templates/wedding/bx_styles/bx_styles.css" />\n<script src="http://code.jquery.com/jquery-latest.js" type="text/javascript"></script>\n</head>', 0, 'a:0:{}'),
