(''thread'',$type)) {\n    if (empty($scriptProperties[''thread''])) return 0;\n\n    $c->where(array(\n        ''thread'' => $scriptProperties[''thread''],\n    ));\n}\n/* filter by family */\nif (in_array(''family'',$type)) {\n    if (empty($scriptProperties[''family''])) return 0;\n    $c->where(array(\n        ''quipComment.thread:LIKE'' => $scriptProperties[''family''],\n    ));\n}\n\n$c->where(array(\n    ''quipComment.approved'' => true,\n    ''quipComment.deleted'' => false,\n));\n$output = $modx->getCount(''quipComment'',$c);\n\n/* output */\n$toPlaceholder = $modx->getOption(''toPlaceholder'',$scriptProperties,false);\nif ($toPlaceholder) {\n    $modx->setPlaceholder($toPlaceholder,$output);\n    return '''';\n}\nreturn $output;', 0, 'a:5:{s:4:"type";a:6:{s:4:"name";s:4:"type";s:4:"desc";s:25:"quip.prop_count_type_desc";s:4:"type";s:4:"list";s:7:"options";a:3:{i:0;a:2:{s:4:"text";s:11:"quip.thread";s:5:"value";s:6:"thread";}i:1;a:2:{s:4:"text";s:9:"quip.user";s:5:"value";s:4:"user";}i:2;a:2:{s:4:"text";s:11:"quip.family";s:5:"value";s:6:"family";}}s:5:"value";s:6:"thread";s:7:"lexicon";s:15:"quip:properties";}s:6:"thread";a:6:{s:4:"name";s:6:"thread";s:4:"desc";s:27:"quip.prop_count_thread_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:25:"quip.prop_count_user_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:6:"family";a:6:{s:4:"name";s:6:"family";s:4:"desc";s:27:"quip.prop_count_family_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:13:"toPlaceholder";a:6:{s:4:"name";s:13:"toPlaceholder";s:4:"desc";s:34:"quip.prop_count_toplaceholder_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}}', '');
