(32, 'frontpageCreate', 'Frontpage create resource control snippet', 0, 10, 0, '/**\r\n * Frontpage Editor Create snippet\r\n *\r\n * @author    S. Hamblett <steve.hamblett@linux.com>\r\n * @copyright 2010 S. Hamblett\r\n * @license   GPLv3 http://www.gnu.org/licenses/gpl.html\r\n */\r\n\r\n\r\n/* Create a Frontpage class */\r\n$corePath = $modx->getOption(''frontpage.core_path'',null,$modx->getOption(''core_path'').''components/frontpage/'');\r\ninclude_once $corePath . "model/frontpage/frontpage.class.php";\r\n$fp = new Frontpage($modx);\r\n$fp->initialize($scriptProperties);\r\n\r\n/* Exit if we have not been invoked from frontpage itself */\r\nif ( $_REQUEST[''frontpage''] != 1 ) return;\r\n\r\n/* Get the parameters */\r\n$source = $_REQUEST[''source''];\r\n$parent = $_REQUEST[''parent''];\r\n\r\n\r\n/* Select the correct method*/\r\nif ( isset($_POST[''save''])) {\r\n   $fp->saveCreatePage($source);\r\n} else {\r\n   $fp->createPage($source, $parent);\r\n}\r\n\r\nreturn;', 0, NULL, ''),
(42, 'frontpageAJAX', 'Frontpage AJAX resource control snippet', 0, 10, 0, '/**\r\n * Frontpage Editor AJAX snippet\r\n *\r\n * @author    S. Hamblett <steve.hamblett@linux.com>\r\n * @copyright 2011 S. Hamblett\r\n * @license   GPLv3 http://www.gnu.org/licenses/gpl.html\r\n */\r\n\r\n\r\n\r\n/* Create a Frontpage class */\r\n$corePath = $modx->getOption(''frontpage.core_path'',null,$modx->getOption(''core_path'').''components/frontpage/'');\r\ninclude_once $corePath . "model/frontpage/frontpage.class.php";\r\n$fp = new Frontpage($modx);\r\n$fp->initialize($scriptProperties);\r\n\r\n/* Exit if we have not been invoked from frontpage itself */\r\nif ( $_REQUEST[''frontpage''] != 1 ) return;\r\n\r\nreturn $fp->ajaxCreateRedirect();', 0, NULL, ''),
(43, 'registrationFormAJAXProcessor', 'AJAX processing for registration', 0, 20, 0, '/**\n * Wedding site Registration processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Validate the nominated remote fields, validator requires a simple ''true'' or ''false'' \n *  value returning, indicating passed validation or failed respectively */\n\n/* Email */\nif ( $_GET[''registrationEmail''] != "" ) {\n	\n	/* Check if we already have one in use */\n	$userEmail = $_GET[''registrationEmail''];\n	$c = $modx->newQuery(''modUser'');\n    $c->leftJoin(''modUserProfile'',''Profile'');\n    $c->where(array(''Profile.email:=''  => $userEmail));\n    $count = $modx->getCount(''modUser'',$c);\n    \n    if ( $count == 0 ) {\n		return ''true'';\n	} else {\n		return ''false'';\n	}\n}\n\n/* If we have reached here its an unknown field so fail validation */\nreturn ''false'';', 0, 'a:0:{}', '');
