(44, 'registrationFormJSInclude', 'Includes the JS for the registration form', 0, 20, 0, '/**\n * Wedding site Registration processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Set our jQuery paths */\n$validatorPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$validatorPath .= ''js/jquery-validation-1.8.1'';\n$assets_url = $modx->getOption(''assets_url'');\n\n/* Include the validation CSS */\n$validationCSS = ''assets/templates/wedding/styles/validation.css'';\n$modx->regClientCSS($validationCSS);\n\n/* Include the includes */\n$html1 = $validatorPath . ''/lib/jquery.metadata.js'';\n$html2 = $validatorPath . ''/jquery.validate.js'';\n$modx->regClientStartupScript($html1);\n$modx->regClientStartupScript($html2);\n\n/* Include the code */\n$initialise = ''<script>jQuery.validator.setDefaults({\n	debug: '' . $debug . '',\n	success: "valid"\n});\n\n// Check for generally unwanted characters\n  jQuery.validator.addMethod("validcharsgeneral", function (value) {\n          var result = true;\n          // unwanted character list\n          var iChars = "!@#$%^&*()+=[]\\\\\\'';,/{}|\\":<>?.";\n          for (var i = 0; i < value.length; i++) {\n             if (iChars.indexOf(value.charAt(i)) != -1) {\n                return false;\n             }\n          }\n          return result;\n\n  }, "Invalid characters entered");\n\n \n  // Check for username specific unwanted characters\n  jQuery.validator.addMethod("validcharsusername", function (value) {\n          var result = true;\n          // unwanted character list\n          var iChars = " !@#$%^&*()+=[]\\\\\\'';,/{}|\\":<>?";\n          for (var i = 0; i < value.length; i++) {\n             if (iChars.indexOf(value.charAt(i)) != -1) {\n                return false;\n             }\n          }\n          return result;\n\n   }, "Invalid characters entered");\n   \n</script>'';\n\n$context = $modx->context->get(''key'');\n$ajaxLink = $modx->makeURL($registrationAJAXPage, $context, "", "full");\n\n$ready = ''<script>$(document).ready(function(){\n    $("#registrationForm").validate({\n      rules: { \n             registrationEmail: {    \n                remote: "'' . $ajaxLink . ''" \n            }, \n            userPassword: {  \n                minlength: 8\n            }, \n            userPasswordRetype: { \n                minlength: 8, \n                equalTo: "#userPassword" \n            }\n        }, \n        messages: { \n            userPassword: { \n                rangelength: jQuery.format("Enter at least {0} characters.") \n            }, \n            userPasswordRetype: { \n                minlength: jQuery.format("Enter at least {0} characters."), \n                equalTo: "Not the same as above!" \n            }, \n            registrationEmail: { \n                remote: "Sorry, already in use.",\n                email: "Sorry - this is not valid."\n            }             \n				\n	    },		\n            \n		    errorElement: "strong",\n            errorPlacement: function(error, element) {                                                    \n				element.parent().children("label").append(error);			\n		},\n            unhighlight: function(element) {\n                  /* Remove the strong tag */\n                  var label = $(element.form).find("label[for=" + element.id + "]");\n                  var labelhtml = $(label).html();\n                  var newhtml = labelhtml.substring(0, labelhtml.indexOf("<strong"));\n                  if ( newhtml.length != 0) {\n                       $(label).html(newhtml);\n                  }\n                },\n            invalidHandler: function() { \n                     alert("Please correct the errors and re-submit the form"); \n                }\n  });\n\n});</script>'';\n\n$modx->regClientStartupScript($initialise, true);\n$modx->regClientStartupScript($ready, true);', 0, 'a:2:{s:20:"registrationAJAXPag