(60, 'userEventsPageJSInclude', '', 0, 27, 0, '/**\n * Wedding site User Events processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Set our jQuery and datatables paths */\n$jQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n \n$datatablesMediaPath = $jQueryPath  . ''js/DataTables-1.8.1/media/'';\n$datatablesJSPath = $datatablesMediaPath . ''js/'';\n$jeditablePath = $jQueryPath . ''js/jeditable/'';\n\n/* Include datatables itself */\n$html1 = $datatablesJSPath . ''jquery.dataTables.min.js'';\n$modx->regClientStartupScript($html1);\n\n/* And jeditable */\n$html2 = $jeditablePath . ''jquery.jeditable.js'';\n$modx->regClientStartupScript($html2);\n\n/* And jeditable datepicker */\n$html3 = $jQueryPath . ''js/jquery.jeditable.datepicker.js'';\n$modx->regClientStartupScript($html3);\n\n$context = $modx->context->get(''key'');\n$ajaxEditableLink = $modx->makeURL($ajaxEditable, $context, "", "full");\n\n/* Include the setup code */\n$eventList = ''<script>$(document).ready(function(){\n    \n   var gTable = $("#eventList").dataTable();\n    \n   /* Free form edit entries */\n    $(".eventEditable", gTable.fnGetNodes()).editable( "'' . $ajaxEditableLink . ''", {\n        \n        "id"   : "elementid",\n        "callback": function( sValue, y ) {\n            var aPos = gTable.fnGetPosition( this );\n            if ( sValue == "" ) {\n                this.editing = true;\n                this.reset();\n                this.editing = false;\n            } else {\n                gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n            }\n        },\n        "submitdata": function ( value, settings ) {\n            var ret_arr = gTable.fnGetPosition( this );\n            var col = ret_arr[2];\n            var row = ret_arr[0];\n            var data_row = gTable.fnGetData(row);\n            var primaryKey = data_row[0];\n            var oSettings = gTable.fnSettings();\n            var columnName = oSettings.aoColumns[col].sTitle;  \n             return {\n                "row_id": primaryKey,\n                "column": columnName,\n                "type": "normaledit"\n            };\n        },\n        "height": "14px",\n        "tooltip"   : "Click to edit..."\n    } );\n        \n    /* Date edit entries */\n    $(".eventEditableDate", gTable.fnGetNodes()).editable( "'' . $ajaxEditableLink . ''", {\n        \n        "id"   : "elementid",\n        "callback": function( sValue, y ) {\n            var aPos = gTable.fnGetPosition( this );\n            if ( sValue == "" ) {\n                this.editing = true;\n                this.reset();\n                this.editing = false;\n            } else {\n                gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n            }\n        },\n        "submitdata": function ( value, settings ) {\n            var ret_arr = gTable.fnGetPosition( this );\n            var col = ret_arr[2];\n            var row = ret_arr[0];\n            var data_row = gTable.fnGetData(row);\n            var primaryKey = data_row[0];\n            var oSettings = gTable.fnSettings();\n            var columnName = oSettings.aoColumns[col].sTitle;  \n             return {\n                "row_id": primaryKey,\n                "column": columnName,\n                "type" : "dateedit"\n            };\n        },\n        "height": "14px",\n        "tooltip"   : "Click to edit...",\n        "type" : "datepicker",\n        datepicker: {\n        changeMonth: true,\n        changeYear: true,\n        showOn: "both",\n    minDate: -2, maxDate: "+48M +28D",\n        dateFormat: "yy-mm-dd"}\n    } );\n\n  /* Add the events list active state AJAX processing */\n   activeLinkEl = "";\n   $(".userEventActiveLink").bind("click",function(event){\n      activeLinkEl = this;\n      event.preventDefault();       \n      $.get(this.href,"", function(response){\n             var activeLink = $(activeLinkEl);\n            /* Toggle the class */\n            if ( activeLink.hasClass("on")) {\n                  activeLink.removeClass("on");\n                  activeLink.addClass("off");\n            } else {\n                 activeLink.removeClass("off");\n                 activeLink.addClass("on");\n            }\n        });\n     });\n     \n    \n});</script>'';\n\n\n$modx->regClientStartupScript($eventList, true);', 0, 'a:1:{s:12:"ajaxEditable";a:6:{s:4:"name";s:12:"ajaxEditable";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"54";s:7:"lexicon";N;}}', ''),
(61, 'userEventsPageInitialiseTables', '', 0, 27, 0, '/**\n * Wedding site Event Settings processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the event list\n */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userSettingError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userSettingError'', "Error - No user found please log in");\n    return;\n}\n\n/* Get the wedding users events */\n$events = $weddingUser->getMany(''Events'');\n\n/* Now Iterate through the events and create the table body */\n$output = "";\n$context = $modx->context->get(''key'');\nforeach ($events as $event) {\n    \n    $eventArray = $event->toArray();\n    \n    /* Make the edit link */\n    $args = "&eventId=" . $eventArray[''id''];\n    $editURL = $modx->makeURL($userEditEventPage, $context, $args, "full" );\n    \n    /* Make the active link */\n    $args = "&row_id=" . $eventArray[''id''];\n    $args .= "&type=activeedit";\n    $activeURL = $modx->makeURL($userEventAJAX, $context, $args, "full" );\n    \n    /* Calculate the total guests, only active guests count */\n    $eventGuests = $event->getMany(''guestEvents''); \n    $totalGuests = 0;\n    foreach ( $eventGuests as $guestPtr ) {\n        \n        $guest = $guestPtr->getOne(''weddingUserGuest'');\n        $active = $guest->get(''active'');\n        if ( $active ) $totalGuests++; \n        \n    }\n    \n    /* Get the active on/off state */\n    $onOff = "off";\n    if ( $eventArray[''active''] == 1 ) $onOff = "on";\n    \n    $placeHolderArray = array ( ''userEventId'' => $eventArray[''id''],\n                                ''userEventName'' => $eventArray[''name''],\n                                ''userEventDate'' => strftime(''%Y-%m-%d'' ,$eventArray[''date'']),\n                                ''userEventLocation'' => $eventArray[''location''],\n                                ''userEventStartTime'' => $eventArray[''startTime''],\n                                ''userEventEndTime'' => $eventArray[''endTime''],\n                                ''userEventMaxGuests'' => $eventArray[''maxGuests''],\n                                ''userEventTotalGuests'' => $totalGuests,\n                                ''userEventEditLink'' => $editURL,\n                                ''userEventActiveLink'' => $activeURL,\n                                ''userEventOnOff'' => $onOff);\n    \n    $chunk = $modx->getChunk(''userEventsList'', $placeHolderArray);\n    $output .= $chunk;\n    \n}\n\n$modx->setPlaceholder(''userEventList'', $output);\n\nreturn;', 0, 'a:2:{s:13:"userEventAJAX";a:6:{s:4:"name";s:13:"userEventAJAX";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"54";s:7:"lexicon";N;}s:17:"userEditEventPage";a:6:{s:4:"name";s:17:"userEditEventPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"53";s:7:"lexicon";N;}}', ''),
(62, 'userEventsPageEditableAJAXProcessor', '', 0, 27, 0, '/**\n * Wedding site Event list AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Assemble the parameters from the request data */\n$eventId = $_REQUEST[''row_id''];\n$newValue = strip_tags($_REQUEST[''value'']);\n$columnName = $_REQUEST[''column''];\n$updateType = $_REQUEST[''type''];\n$echoValue = $newValue;\n\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ( $user ) $userId = $user->get(''id'');\nif ( $userId == "" ) {\n    \n    echo "Error! No logged in user";\n    return;\n}\n\n  \n/* Get the event data */\n$eventObject = $modx->getObject(''weddingUserEvent'', array(''id'' => $eventId));\nif ( !$eventObject ) {\n    \n    echo "Error! No event found";\n    return;\n}\n\n$user = $eventObject->get(''user'');\n\n/* Process the edit update */\nswitch ( $updateType ) {\n    \n    case ''dateedit'' :\n        \n        if ( $columnName == ''Date'') {\n            \n            $dateValue = strtotime($newValue);\n            $eventObject->set(''date'', $dateValue);\n            \n        }\n        \n        break;\n\n   case ''activeedit'' :\n        \n        /* Just a straight toggle */\n        $newActiveState = 1;\n        $activeState = $eventObject->get(''active'');\n        if ( $activeState )  $newActiveState = 0;\n        $eventObject->set(''active'', $newActiveState);\n        \n        break;\n        \n    \n    case ''normaledit'' :\n        \n        /* Update the event depending on the column name */\n        if ( $columnName == ''Name'') $eventObject->set(''name'', $newValue);\n        if ( $columnName == ''Location'') $eventObject->set(''location'', $newValue);\n        if ( $columnName == ''Start Time'') $eventObject->set(''startTime'', $newValue);\n        if ( $columnName == ''End Time'') $eventObject->set(''endTime'', $newValue);\n        if ( $columnName == ''Max. Guests'') $eventObject->set(''maxGuests'', $newValue);\n        \n        break;\n    \n    default:\n        \n        /* Shouldn''t happen */\n        $echoValue = "Error! Invalid update type";\n        break;\n                \n}\n\n  /* Save the update */\nif ( !$eventObject->save() ) $echoValue = "Error! Can''t save event";\n  \n/* Return the output */\necho $echoValue;', 0, 'a:0:{}', ''),
(63, 'userEventsAddEditJSInclude', '', 0, 27, 0, '/**\n * Wedding site Event Add/Edit processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Set our jQuery paths */\n$validatorPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$validatorPath .= ''js/jquery-validation-1.8.1'';\n$assets_url = $modx->getOption(''assets_url'');\n\n/* Include the validation CSS */\n$validationCSS = ''assets/templates/wedding/styles/validation.css'';\n$modx->regClientCSS($validationCSS);\n\n/* Include the includes */\n$html1 = $validatorPath . ''/lib/jquery.metadata.js'';\n$html2 = $validatorPath . ''/jquery.validate.js'';\n$modx->regClientStartupScript($html1);\n$modx->regClientStartupScript($html2);\n\n/* Include the code */\n$initialise = ''<script>jQuery.validator.setDefaults({\n  debug: '' . $debug . '',\n  success: "valid"\n});\n\n// Check for generally unwanted characters, allow ":" for time fields\n  jQuery.validator.addMethod("validcharsgeneral", function (value) {\n          var result = true;\n          // unwanted character list\n          var iChars = "!@#$%^&*()=[]\\\\\\'';,/{}|\\"<>?";\n          for (var i = 0; i < value.length; i++) {\n             if (iChars.indexOf(value.charAt(i)) != -1) {\n                return false;\n             }\n          }\n          return result;\n\n  }, "Invalid characters entered");\n  \n</script>'';\n\n/* Add/Edit form */\n$form = ''<script>$(document).ready(function(){\n    $("#eventAddEditForm").validate({\n        \n         messages: { \n  \n            userEventsName: {\n                required: "This field is required."\n            }\n        }, \n            errorPlacement: function(error, element) {                                                    \n        element.parent().children("#formvalidationerror").append(error);        \n    },\n            invalidHandler: function() { \n                     alert("Please correct the errors on the form and re-submit it"); \n                }\n  });\n         \n  });</script>'';\n\n/* Date Picker */\n$datePicker = ''<script>$(document).ready(function(){\n  $(function() {\n    $( "#userEventsDate" ).datepicker({\n      showOn: "both",\n      buttonImage: "'' . $assets_url. ''templates/wedding/images/calendar.gif",\n      buttonImageOnly: true,\n      minDate: -2, maxDate: "+48M +28D",\n      changeYear: true,\n      changeMonth: true,\n      dateFormat: "yy-mm-dd"\n    });\n  });\n    \n});</script>'';\n\n$modx->regClientStartupScript($initialise, true);\n$modx->regClientStartupScript($form, true);\n$modx->regClientStartupScript($datePicker, true);', 0, 'a:1:{s:5:"debug";a:6:{s:4:"name";s:5:"debug";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:5:"false";s:7:"lexicon";N;}}', ''),
(64, 'userEventsAddEditInitialiseForms', '', 0, 27, 0, '/**\n * Wedding site User Event processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the event edit/add form\n */\n\n$modx->setPlaceholder(''userGuestError'',"");\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEventError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEventError'', "Error - No user found please log in");\n    return;\n}\n\n/* Check for add or edit */\nif ( isset($_GET[''eventId'']) ) {\n    \n    /* Edit call, get the event data */\n    $eventId = $_GET[''eventId''];\n    $weddingEvent = $modx->getObject(''weddingUserEvent'', array(''id''=>$eventId));\n    if ($weddingEvent == null ) {            \n        $modx->setPlaceholder(''userEventError'', "Error - No event details found");     \n        return;\n    }\n         \n    /* Set the placeholders */\n    $eventArray = $weddingEvent->toArray(); \n    $modx->setPlaceholder(''userEventsId'', $eventId);\n    $modx->setPlaceholder(''userEventsName'', $eventArray[''name'']);\n    $date = strftime(''%Y-%m-%d'',$eventArray[''date'']);\n    $modx->setPlaceholder(''userEventsDate'', $date);\n    $modx->setPlaceholder(''userEventsLocation'', $eventArray[''location'']);\n    $modx->setPlaceholder(''userEventsStartTime'', $eventArray[''startTime'']);\n    $modx->setPlaceholder(''userEventsEndTime'', $eventArray[''endTime'']);\n    $modx->setPlaceholder(''userEventsMaxGuests'', $eventArray[''maxGuests'']);\n    $modx->setPlaceholder(''userEventsActive'', $eventArray[''active''] ? "checked" : "");\n       \n}', 0, NULL, ''),
(65, 'userEventsAddEditPostProcessor', '', 0, 27, 0, '/**\n * Wedding site Events add/edit  processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  The parameters from the settings forms form are already valid at this point, we only need\n * to update the wedding user\n */\n\n/* Check for an event add/edit submit operation */\nif ( isset($_REQUEST[''userEventsName'']) ) {\n  \n  /* Get the MODX user */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ( $userId == "" ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No MODX user found please log in");\n        return;\n    }\n    \n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ( $weddingUserObject == null ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No Wedding user found please log in");\n        return;\n    }\n  \n    /* Check for an event Id, if none this is an add */\n    if ( $_REQUEST[''userEventsId''] == "" ) {\n        \n        $eventObject = $modx->newObject(''weddingUserEvent'');\n        $updateType = "Add";\n        \n    } else {\n        \n    $eventObject = $modx->getObject(''weddingUserEvent'', \n                                     array(''id'' => $_REQUEST[''userEventsId'']));\n    $updateType ="Update"; \n    \n    }\n    \n    if ( $eventObject == null ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No Event with this ID found ");\n        return;\n    }\n    \n    $eventObject->set(''name'', $_REQUEST[''userEventsName'']);\n    $eventObject->set(''date'', strtotime($_REQUEST[''userEventsDate'']));\n    $eventObject->set(''location'', $_REQUEST[''userEventsLocation'']);\n    $eventObject->set(''startTime'', $_REQUEST[''userEventsStartTime'']);\n    $eventObject->set(''endTime'', $_REQUEST[''userEventsEndTime'']);\n    $eventObject->set(''maxGuests'', $_REQUEST[''userEventsMaxGuests'']);\n  $eventObject->set(''active'', $_REQUEST[''userEventsActive'']);\n        \n    /* Save the event */\n    if ( $updateType == "Add" ) {\n        \n        $weddingUserObject->addMany($eventObject, ''Events'');\n        if ( $weddingUserObject->save() == false ) {\n            $modx->setPlaceholder(''userSettingError'', "Error - Failed to create this wedding user guest"); \n            return;\n        }\n        \n    } else {\n        \n        if (!$eventObject->save()) \n            $modx->setPlaceholder(''userSettingError'', "Error - Failed to update this wedding user guest");    \n        \n    }\n          \n    /* Redirect back to the event list */\n    $context = $modx->context->get(''key'');\n    $pageURL = $modx->makeURL($eventListPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:1:{s:13:"eventListPage";a:6:{s:4:"name";s:13:"eventListPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"48";s:7:"lexicon";N;}}', ''),
(66, 'cPanelAddSubDomain', 'Add a user subdomain via the cPanel API', 0, 29, 0, '/**\n * Wedding site CPanelAddSubDomain\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Interfaces with the Cpanel XML-API to allow sub domain updates.\n * \n * Parameters :- \n * \n * $subDomainName - The name of the subdomain to add, e.g. janiceswedding \n */\n\n/* Add the cPanel API path and include the class */\n$cPanelPath = $modx->getOption(''ws_cpanelapi.core_path'',null,$modx->getOption(''core_path'').''components/ws_cpanelapi/'');\ninclude_once $cPanelPath . "xmlapi.php";\n\n/* Construct the class, this also auths us */\n$cPanelApi = new xmlapi($host, $user, $password);\n\n/* Add the new domain */\n$cPanelApi->api1_query($user,''SubDomain'',''addsubdomain'',\n                       array($subDomainName,''theweddingwebsitegroup.com'',0,0,''/public_html''));', 0, 'a:3:{s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"wwwthewe";s:7:"lexicon";N;}s:8:"password";a:6:{s:4:"name";s:8:"password";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:14:"j4n1c3pass-THE";s:7:"lexicon";N;}s:4:"host";a:6:{s:4:"name";s:4:"host";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"127.0.0.1";s:7:"lexicon";N;}}', ''),
(67, 'cPanelDeleteSubDomain', 'Delete a subdomain using the cPanel XMLAPI', 0, 29, 0, '/**\n * Wedding site CPanelDeleteSubDomain\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Interfaces with the Cpanel XML-API to allow sub domain updates.\n * \n * Parameters :- \n * \n * $subDomainName - The name of the subdomain to delete, e.g. janiceswedding \n */\n\n/* Add the cPanel API path and include the class */\n$cPanelPath = $modx->getOption(''ws_cpanelapi.core_path'',null,$modx->getOption(''core_path'').''components/ws_cpanelapi/'');\ninclude_once $cPanelPath . "xmlapi.php";\n\n/* Construct the class, this also auths us */\n$cPanelApi = new xmlapi($host, $user, $password);\n\n/* Delete the sub domain */\n$cPanelApi->api1_query($user,''SubDomain'',''delsubdomain'',\n                       array($subDomainName));', 0, 'a:3:{s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"wwwthewe";s:7:"lexicon";N;}s:8:"password";a:6:{s:4:"name";s:8:"password";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:14:"j4n1c3pass-THE";s:7:"lexicon";N;}s:4:"host";a:6:{s:4:"name";s:4:"host";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"127.0.0.1";s:7:"lexicon";N;}}', ''),
(68, 'createUserPageSet', 'Creates a user page set from the default page set', 0, 23, 0, '/**\n * Wedding site creatUserPageSet\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the user page set under the users area from the default set.\n * \n * Parameters :- \n * \n * $name - The name of the users desired subdomain e.g. janiceswedding\n *  \n */\n\n/* Duplicate the users page set from the default set */\n$defaultUserPage = $modx->getObject(''modResource'', $defaultUserPageSetContainerId);\n$newResource = $defaultUserPage->duplicate(array(\n    ''newName'' => $name,\n    ''duplicateChildren'' => true,\n    ''prefixDuplicate'' => false,\n));\n\n/* Reparent the new resource to the user page area */\n$newResource->set(''parent'', $userPagesParentContainerId);\n\n/* Publish this resource and its children */\n$newResource->set(''published'', true);\n$newResource->save();\n\n$newResourceId = $newResource->get(''id'');\n$children = $modx->getcollection(''modResource'', array(''parent'' => $newResourceId));\n\nforeach ( $children as $child ) {\n    \n    $child->set(''published'', true);\n    $child->save();\n}\n\n/* We need to clear the cache to regen the alias map */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n            "{$resource->context_key}/",\n        ),\n        array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n        )\n    );\n\n/* Done */', 0, 'a:2:{s:29:"defaultUserPageSetContainerId";a:6:{s:4:"name";s:29:"defaultUserPageSetContainerId";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"60";s:7:"lexicon";N;}s:26:"userPagesParentContainerId";a:6:{s:4:"name";s:26:"userPagesParentContainerId";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
(70, 'userFirstLogin', '', 0, 23, 0, '/**\n * Wedding site userFirstLogin\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Redirects to the user settings page if we don''t have a valid set\n * of user wediing site parameters. \n * \n *  \n *  \n */\n\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return;\n$user = $modx->user;\n$userId = $user->get(''id'');\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check for an incomplete parameter set */\n$websiteComplete = $attributes[''websiteDetails''];\n$personalComplete = $attributes[''personalDetails''];\nif ( !($websiteComplete && $personalComplete)) {\n    \n    $pageURL = $modx->makeURL($userSettingsPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:1:{s:16:"userSettingsPage";a:6:{s:4:"name";s:16:"userSettingsPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"30";s:7:"lexicon";N;}}', ''),
(69, 'getParentAlias', '', 0, 23, 0, '$parent = $modx->resource->get(''parent'');\n$parentResource = $modx->getObject(''modResource'', $parent);\nif ( !$parentResource ) return "None";\nreturn $parentResource->get(''alias'');', 0, NULL, ''),
(71, 'cPanelChangeSubDomain', 'Change a sub domains name', 0, 29, 0, '/**\n * Wedding site CPanelChangeSubDomain\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Interfaces with the Cpanel XML-API to allow sub domain updates.\n * \n * Parameters :- \n * \n * $to - The name of the subdomain to change to, e.g. janiceswedding \n * $from - The name of the existing subdomain.\n */\n\n/* Add the cPanel API path and include the class */\n$cPanelPath = $modx->getOption(''ws_cpanelapi.core_path'',null,$modx->getOption(''core_path'').''components/ws_cpanelapi/'');\ninclude_once $cPanelPath . "xmlapi.php";\n\n/* Construct the class, this also auths us */\n$cPanelApi = new xmlapi($host, $user, $password);\n\n/* Add the new sub domain */\n$cPanelApi->api1_query($user,''SubDomain'',''addsubdomain'',\n                       array($to,''theweddingwebsitegroup.com'',0,0,''/public_html''));\n\n/* Delete the existing sub domain */\n$cPanelApi->api1_query($user,''SubDomain'',''delsubdomain'',\n                       array($from));', 0, 'a:3:{s:8:"password";a:6:{s:4:"name";s:8:"password";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:14:"j4n1c3pass-THE";s:7:"lexicon";N;}s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"wwwthewe";s:7:"lexicon";N;}s:4:"host";a:6:{s:4:"name";s:4:"host";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"127.0.0.1";s:7:"lexicon";N;}}', ''),
(72, 'designFormJSInclude', '', 0, 31, 0, '/**\n * Wedding site Design page processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Include our JS for this page */\n$assets_url = $modx->getOption(''assets_url'');\n$tim = $assets_url . "templates/wedding/js/tim.js";\n$modx->regClientStartupScript($tim);\n$slimbox = $assets_url . "templates/wedding/js/slimbox-2/js/slimbox2.js";\n$modx->regClientStartupScript($slimbox);\n$slimboxCSS = $assets_url . "templates/wedding/js/slimbox-2/css/slimbox2.css";\n$modx->regClientCSS($slimboxCSS);\n$timCSS = $assets_url . "templates/wedding/styles/tim.css";\n$modx->regClientCSS($timCSS);\n\n/* Page code */\n$ready = ''<script>$(document).ready(function(){\n    \n   /* Filter change function */\n   $("#designThemesFilter").change(function() {\n\n         /* Hide all themes */\n        $(".designThemeForm").hide();\n\n        /* Show the one filtered */\n        var themeSelectedFilter = $("#designThemesFilter :selected").val();\n        var themeSelected = "#designThemeForm_"+themeSelectedFilter;\n        $(themeSelected).show();\n    \n   });\n   \n   /* Set the first theme */\n   $("#designThemesFilter").trigger("change");\n       \n});</script>'';\n\n$modx->regClientStartupScript($ready, true);', 0, 'a:0:{}', ''),
(73, 'designsPageFormInitialise', '', 0, 31, 0, '/**\n * Wedding site Designs processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/*  Initialise the design page forms\n */\n\n/* Check for logged in user */\n$loggedIn = false;\n$context = $modx->context->get(''key'');\nif ($modx->user->isAuthenticated($context))\n    $loggedIn = true;\n\n/* Get the theme templates */\n$themesString = $modx->runSnippet(''getThemeTemplates'');\n$themeArray = json_decode($themesString, true);\n$themeData = $themeArray[0];\n$themeNames = $themeArray[1];\n\n/* Set the theme names in the filter and output the whole page */\n$themeNameOutput = "";\nforeach ($themeNames as $themeName) {\n\n    /* Theme filter */\n    $themeNameOutput .= $modx->getChunk(''designThemeNames'', array(''themeNameDisplay'' => ucfirst($themeName),\n        ''themeName'' => $themeName));\n\n    /* Get the themes styles */\n    $stylesString = $modx->runSnippet(''getThemeStyles'', array(''themeName'' => $themeName,\n        ''themeData'' => $themeData));\n    $styles = json_decode($stylesString, true);\n    $stylesOutput = "";\n    foreach ($styles as $styleName) {\n\n        /* Get the styles colours */\n        $coloursString = $modx->runSnippet(''getStyleColours'', array(''themeName'' => $themeName,\n            ''styleName'' => $styleName,\n            ''themeData'' => $themeData));\n\n        $colours = json_decode($coloursString, true);\n        $coloursOutput = "";\n        $thumbsOutput = "";\n\n        /* Colours and thumbs */\n        foreach ($colours as $colourName) {\n\n            /* Check for logged in or not and switch chunks */\n            if (!$loggedIn) {\n\n                $coloursOutput .= $modx->getChunk(''designColours'', array(''designStyleColour'' => $colourName,\n                    ''designStyleColourDisplay'' => ucfirst($colourName)));\n            } else {\n                \n                $coloursOutput .= $modx->getChunk(''designColoursLoggedIn'', array(''designStyleColour'' => $colourName,\n                    ''designStyleColourDisplay'' => ucfirst($colourName),\n                    ''designStyleName'' => $styleName,\n                    ''themeName'' => $themeName));\n            }\n            \n            $thumbsOutput .= $modx->getChunk(''designThumbs'', array(''designStyleColour'' => $colourName,\n                ''designStyleColourDisplay'' => ucfirst($colourName),\n                ''designStyleNameDisplay'' => ucfirst($styleName),\n                ''designStyleName'' => $styleName,\n                ''designThemeName'' => $themeName));\n        }\n\n        /* Styles page code */\n\n        /* Get the selection button if logged in */\n        $designSelectButton = "";\n        if ( $loggedIn ) {\n            $designSelectButton = $modx->getChunk(''designSelectButton'', array(''designStyleName'' => $styleName,\n                ''themeName'' => $themeName));\n            \n        }\n        \n        $stylesOutput .= $modx->getChunk(''designsPageFormStyle'', array(''styleNameDisplay'' => ucfirst($styleName),\n            ''styleName'' => $styleName,\n            ''themeName'' => $themeName,\n            ''designColours'' => $coloursOutput,\n            ''designThumbs'' => $thumbsOutput,\n            ''designSelectButton'' => $designSelectButton));\n    }\n\n    /* Theme page code */\n    if ( isset($_REQUEST[''settings''])) $designFromSettings = "true";\n    $themeOutput .= $modx->getChunk(''designsPageFormTheme'', array(''themeName'' => $themeName,\n        ''designStyles'' => $stylesOutput,\n        ''designFromSettings'' => $designFromSettings));\n}\n\n/* Set the page theme filter placeholder */\n$modx->setPlaceholder(''designThemeNames'', $themeNameOutput);\n\n/* Set the page themes  placeholder */\n$modx->setPlaceholder(''designThemes'', $themeOutput);', 0, 'a:0:{}', ''),
(74, 'mailMan', 'General site mail manager', 0, 33, 0, '/**\n * Wedding site mailMan\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Site mail management snippet\n * \n * Parameters :-\n * \n *  to : Recipient e-mail address, default is site admin\n *  from : Sender e-mail address, default is site admin\n *  fromName : Sender name, default site administrator\n *  message : The message text\n *  subject : Subject line\n */\n\n$adminEmail = $modx->getOption(''emailsender'');\n$to = isset($to) ? $to : $adminEmail;\n$from = isset($from) ? $from : $adminEmail;\n$fromName = isset($fromName) ? $fromName : "The Wedding Websites Site Administrator";\n\n/* Get the mail service if we have to */\nif ( !$modx->mail ) $modx->getService(''mail'', ''mail.modPHPMailer'');\n\n/* Set the mail parameters */        \n$modx->mail->set(modMail::MAIL_FROM, $from);\n$modx->mail->set(modMail::MAIL_FROM_NAME, $fromName);\n$modx->mail->address(''to'', $to);\n$modx->mail->set(modMail::MAIL_SUBJECT, $subject);\n$modx->mail->set(modMail::MAIL_BODY, $message);\n\n/* Send the mail */\n$modx->mail->send();\n$modx->mail->reset();', 0, NULL, ''),
(75, 'EasyCopyright', '', 0, 34, 0, '/*\n#EasyCopyright#\n\n##About##\n\n\n* By Cameron Gilroy\n* EasyCopyright\n* Version: 1.1\n* Based in part on Copyright by AMDbuilder\n* Creator: Cameron Gilroy http://www.camerongilroy.com\n* Support URL: http://www.camerongilroy.com/\n* GitHub: http://github.com/camgill/EasyCopyright\n\nEasyCopyright is a simple snippet for ModX Revolution that inserts the the current year and the site name. Check the example below.\n\n*© +CameronGilroy.com+ 2009-2011 - Powered by +MODx+*\n\n###Thanks###\n\nA big thank you goes to the creators of MODx Revolution!  \n[dflock](https://github.com/dflock/ "dflock"): Thanks for the new features added to 1.1!\n\n###Usage###\n\n<code>[[!EasyCopyright]]</code> - Outputs *© [[Your Site name here]] 2011 - Powered by MODx*  \n<code>[[!EasyCopyright? &StartYear=`2009`]]</code> - Outputs *© [[Your Site name here]] 2009-2011 - Powered by MODx*\n\n\n##Configuration##\n\nThe variables that are available are listed below with a description.\nYou can also call the contents of a chunk into any of these fields like this <code>[[!EasyCopyright? &Powered=`[[$Your_Chunk_Name]]`]]</code>\n\n* Name - Your site name\n* Start Year - What ever you put in!\n* Powered - is the link to the ModX site\n* YearSeparator - is the separator that goes between the Start Year and the current year\n* PoweredBySeparator - is the separator that goes between the current Year and Powered by\n\n###Defaults###\n\n* Name - Your site name\n* Start Year - No Default must be called in the snippet\n* Powered - is the link to the ModX site\n* YearSeparator - "-"\n* PoweredBySeparator - "-"\n*/\n\n$Name = isset($Name)? $Name : "[[++site_name]]";\n$Powered = isset($Powered)? $Powered : "Powered by <a href=''http://www.modxcms.com''>MODx</a>";\n$YearSeparator = isset($YearSeparator)? $YearSeparator : "-";\n$PoweredBySeparator = isset($PoweredBySeparator)? $PoweredBySeparator : "-";\n$cYear = date("Y");\n$StartYear = isset($StartYear)? $StartYear : $cYear;\n$years = ($cYear > $StartYear) ? $StartYear.$YearSeparator.$cYear : $cYear;\n\necho "&copy; $Name $years &nbsp;$PoweredBySeparator&nbsp; $Powered";', 0, 'a:0:{}', ''),
(76, 'getThemeTemplates', 'Gets the user theme templates into array format', 0, 31, 0, '/**\n * Wedding site getThemeTemplates\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * \n * Returns :- two data structures, themeTemplates containing the theme\n * templates in array form and themeArray containing the unique theme names.\n */\n\n/* Get all the sub categories of the theme category */\n$themeCategories = $modx->getCollection(''modCategory'', array(''parent'' => $themeCategoryValue));\n\n/* Now get all the templates */\n$themeTemplateNameArray = array();\nforeach ( $themeCategories as $themeCategory ) {\n    \n    $categoryId = $themeCategory->get(''id'');\n    $themeTemplates = $modx->getCollection(''modTemplate'', array(''category'' => $categoryId));\n    foreach ( $themeTemplates as $themeTemplate ) {\n        \n        $templateName = $themeTemplate->get(''templatename'');\n        $templateId = $themeTemplate->get(''id'');\n        $themeTemplateNameArray[$templateName] = $templateId;\n    }\n}\n\n/* Get the number of unique themes and create a mapping structure */\n$themeArray = array();\n$themeTemplates = array();\nforeach ( $themeTemplateNameArray as $name => $id) {\n    \n    $tempThemeArray = explode(''-'', $name);\n    $tempThemeArray[] = $id;\n    $themeTemplates[] = $tempThemeArray;\n    $themeArray[] = $tempThemeArray[0];\n    \n}\n\n/* Unique the themes */\n$themeArray = array_unique($themeArray);\n\n/* Return the data */\n$returnArray = array();\n$returnArray[0] = $themeTemplates;\n$returnArray[1] = $themeArray;\nreturn json_encode($returnArray);', 0, 'a:1:{s:18:"themeCategoryValue";a:6:{s:4:"name";s:18:"themeCategoryValue";s:4:"desc";s:51:"The id of the cateogory holding all the user themes";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"32";s:7:"lexicon";N;}}', ''),
(77, 'getThemeStyles', 'Gets a themes styles', 0, 31, 0, '/**\n * Wedding site getThemeStyles\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters :-\n * \n * themeName - the name of the theme\n * themeData :- the theme template data structure as returned by getThemeTemplates\n * \n * Returns :- an array containing the themes style names\n */\n\n$stylesArray = array();\nforeach ( $themeData as $theme ) {\n    \n    if ( $theme[0] != $themeName ) continue;\n    $stylesArray[] = $theme[1];\n}\n\n/* Unique the array */\n$stylesArray = array_unique($stylesArray);\n\n/* Sort Alphabetically */\nsort($stylesArray);\n\n/* Return the data */\nreturn json_encode($stylesArray);', 0, 'a:0:{}', ''),
(78, 'getStyleColours', 'Gets a styles associated colours', 0, 31, 0, '/**\n * Wedding site getStyleColours\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters :-\n * \n * themeName - the name of the theme\n * styleName - the name of the style\n * themeData :- the theme template data structure as returned by getThemeTemplates\n * \n * Returns :- an array containing the themes style colours\n */\n\n$coloursArray = array();\nforeach ( $themeData as $theme ) {\n    \n    if ( $theme[0] != $themeName ) continue;\n    if ( $theme[1] != $styleName ) continue;\n    $coloursArray[] = $theme[2];\n}\n\n/* Unique the array */\n$coloursArray = array_unique($coloursArray);\n\n/* Sort Alphabetically */\nsort($coloursArray);\n\n/* Return the data */\nreturn json_encode($coloursArray);', 0, NULL, ''),
(79, 'designsPageFormPostProcessor', 'Post processor for the designs page', 0, 31, 0, '/**\n * Wedding site Designs processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the themes */\n$themesString = $modx->runSnippet(''getThemeTemplates'');\n$themeArray = json_decode($themesString, true);\n$themeData = $themeArray[0];\n$themeNames = $themeArray[1];\n\n/* Check for a submission button for each theme and its styles */\n$themeFound = false;\nforeach ($themeNames as $themeName) {\n\n    if ( $themeFound ) break; \n    /* Get the themes styles */\n    $stylesString = $modx->runSnippet(''getThemeStyles'', array(''themeName'' => $themeName,\n        ''themeData'' => $themeData));\n    $styles = json_decode($stylesString, true);\n    $stylesOutput = "";\n    foreach ($styles as $styleName) {\n        \n        if ( $themeFound ) break;\n        $buttonName = $themeName . "-" . $styleName;\n        if (isset($_REQUEST[$buttonName])) {\n            \n            /* Got a submission, get the colour and generate the template name */\n            $radioButtonName = "radio-" . $buttonName; \n            $themeColourString = $_REQUEST[$radioButtonName];\n            $colourArray = explode(''-'', $themeColourString);\n            $colourName = $colourArray[2];\n            $templateName = $buttonName . "-" . $colourName;\n            $themeFound = true;\n             break;\n        }\n    }\n}\n\n/* Update the users theme attribute and the users resources */\nif ( $themeFound ) {\n    \n    /* Get the MODX user */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ( $userId == "" ) return;\n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ( $weddingUserObject == null ) return;\n    $weddingUser = $weddingUserObject->getOne(''Attributes'');\n    /* Create the theme name for the user */\n    $nameArray = explode(''-'', $templateName);\n    $userTemplateName = ucfirst($nameArray[0]) . " " . ucfirst($nameArray[1]) . " " . ucfirst($nameArray[2]);\n  $weddingUser->set(''theme'', $userTemplateName);\n    $weddingUser->save();\n    \n    /* Update the resources */\n    $templateId = $modx->runSnippet(''getThemeTemplateId'', array(''themeName'' => $nameArray[0],\n        ''styleName'' => $nameArray[1],\n        ''colourName'' => $nameArray[2],\n        ''themeData'' => $themeData));\n    \n    $templateId = intval($templateId);\n    \n    /* Get user container */\n    $website = $weddingUser->get(''website'');  \n    $c = $modx->newQuery(''modResource'');\n    $c->where(array(''alias:=''  => $website));\n    $userContainer = $modx->getObject(''modResource'', $c);\n    \n/* If no container just exit */\n    if ( !$userContainer ) return;\n\n    /* Get the children */\n    $children = $userContainer->getMany(''Children'');\n    foreach ( $children as $child ) {\n        \n        $child->set(''template'', $templateId);\n        $child->save();\n    }\n    \n    /* Clear the cache */\n    $context = $modx->context->get(''key'');\n    $cacheManager= $modx->getCacheManager();\n    $cacheManager->clearCache(array (\n            "{$context}/",\n        ),\n        array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n        )\n    );\n            \n    /* If we''ve come from the settings page redirect back */\n    if ( $_REQUEST[''designFromSettings''] == ''true'' ) {\n  \n        $params = array(''restore'' => 1);\n        $pageURL = $modx->makeURL($userSettingsPage, $context, $params, "full");\n         header("Location: {$pageURL}");\n    }\n}', 0, 'a:1:{s:16:"userSettingsPage";a:6:{s:4:"name";s:16:"userSettingsPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"30";s:7:"lexicon";N;}}', ''),
(83, 'getThemeTemplateId', 'Gets a themes template id', 0, 31, 0, '/**\n * Wedding site getThemeTemplateId\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters :-\n * \n * themeName - the name of the theme\n * styleName - the name of the style\n * colourName - the name of the colour\n * themeData :- the theme template data structure as returned by getThemeTemplates\n * \n * Returns :- the id of the themes template\n */\n\nforeach ( $themeData as $theme ) {\n    \n    if ( ($theme[0] == $themeName) &&\n         ($theme[1] == $styleName) &&\n         ($theme[2] == $colourName)) {\n        \n            return $theme[3];         \n   }\n}', 0, 'a:0:{}', ''),
(84, 'userHomePageJSInclude', 'User Home JS includer', 0, 25, 0, '/**\n * Wedding site User Home processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n\n/* Scripts */\n$websiteLinks = ''<script>$(document).ready(function(){\n    \n   $(".btn-slide").click(function(event){\n            event.preventDefault();\n            $("#panel").slideToggle("slow");\n            $(this).toggleClass("active");\n        });\n        \n    $(function() {\n        $( ".user-menu" ).sortable();\n        $( ".user-menu" ).disableSelection();\n    });\n    \n    /* Add the page status list AJAX processing */\n  currentLink = "";\n  $(".ajaxLink").bind("click",function(event){\n      event.preventDefault();\n            currentLink = this;\n        $.get(this.href,"",function(response){\n               if ( response == "on" ) {\n                    currentLink.className = currentLink.className.replace(/\\boff\\b/,"");\n                    currentLink.className += " on";\n               } else {\n                    currentLink.className = currentLink.className.replace(/\\bon\\b/,"");\n                    currentLink.className += " off";\n               }\n        })  \n     });\n    \n});</script>'';\n\n$modx->regClientStartupScript($websiteLinks, true);', 0, NULL, ''),
(85, 'userHomePageInitialise', '', 0, 25, 0, '/**\n * Wedding site User Home processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users home page */\n $modx->setPlaceholder(''userHomeWebsiteError'',"");\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userHomeWebsiteError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userHomeWebsiteError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Tutorials */\n$tutorialsContainer = $modx->getObject(''modResource'', $userTutorialsContainerId);\n$children = $tutorialsContainer->getMany(''Children'');\n$context = $modx->context->get(''key'');\n$tutorialsOutput = "";\nforeach ( $children as $child ) {\n    \n    $id = $child->get(''id'');\n    $title = $child->get(''pagetitle'');\n    $pageLink = $modx->makeURL($id, $context, "", "full");\n    $linkParams = array(''userHomeTutorialTitle'' =>  $title ,\n                        ''userHomeTutorialLink'' => $pageLink);\n                                     \n    \n    $tutorialsOutput .= $modx->getChunk(''userHomeTutorialsBody'', $linkParams);\n}\n\n$modx->setPlaceholder(''userHomeTutorialsBody'', $tutorialsOutput);', 0, 'a:1:{s:24:"userTutorialsContainerId";a:6:{s:4:"name";s:24:"userTutorialsContainerId";s:4:"desc";s:29:"Id of the Tutorials container";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"252";s:7:"lexicon";N;}}', ''),
(86, 'userHomeAJAXProcessor', '', 0, 25, 0, '/**\n * Wedding site User home AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Assemble the parameters from the request data */\n$id = $_REQUEST[''id''];\n\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ( $user ) $userId = $user->get(''id'');\nif ( $userId == "" ) {\n    \n    echo "Error! No logged in user";\n    return;\n}\n\n/* Toggle the current state of published */\n$resource = $modx->getObject(''modResource'', $id);\nif ( !$resource ) return "Error - can''t find resource";\n$published = $resource->get(''published'');\n$newPublished = 0;\n$output = ''off'';\nif ( $published == 0 ) {\n    \n    $newPublished = 1;\n    $output = "on";\n}\n$resource->set(''published'', $newPublished);\n$resource->save();\n/* Clear the cache */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n        "{$resource->context_key}/",\n    ),\n    array(\n        ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n        ''publishing'' => true\n    )\n);\n\n/* Return the output */\necho $output;', 0, NULL, ''),
(89, 'userAdminWebPageLinks', 'Draws the web page links in the user administration area', 0, 23, 0, '/**\n * Wedding site userAdminWebPageLinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Draws the user admin area sidebar web page links\n * \n *  \n *  Parameters :-\n * \n * attributes - an array of the users attributes\n * \n * Returns - nothing, sets placeholders only\n */\n\n$website = $attributes[''website''];\n$c = $modx->newQuery(''modResource'');\n$c->where(array(''alias:=''  => $website));\n$userContainer = $modx->getObject(''modResource'', $c);\n\n/* Get the children */\n$children = $userContainer->getMany(''Children'');\n$context = $modx->context->get(''key'');\n$weblinkOutput = "";\nforeach ( $children as $child ) {\n    \n    $id = $child->get(''id'');\n    $title = $child->get(''pagetitle'');\n    $published = $child->get(''published'');\n    $published = $published == 0 ? "off" : "on"; \n    $pageLink = $modx->makeURL($id, $context, "", "full");\n    $args = array(''id'' => $id);\n    $ajaxLink = $modx->makeURL($userHomeAJAXPage, $context, $args, "full");\n    $linkParams = array(''userHomeWeblinkLink'' =>  $pageLink ,\n                                     ''userHomeWeblinkTitle'' => $title,\n                                     ''userHomeWeblinkPublished'' => $published,\n                                     ''userHomeWeblinkAJAX'' => $ajaxLink);\n    \n    $webLink .= $modx->getChunk(''userHomeWebPagesLinks'', $linkParams);\n}\n\n$modx->setPlaceholder(''userWebPageslinks'',  $webLink);', 0, 'a:1:{s:16:"userHomeAJAXPage";a:6:{s:4:"name";s:16:"userHomeAJAXPage";s:4:"desc";s:44:"User administration area home page AJAX link";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"251";s:7:"lexicon";N;}}', ''),
(90, 'userAdminSupportLinks', 'Draws the user administration area support links', 0, 23, 0, '/**\n * Wedding site userAdminSupportLinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Draws the user admin area sidebar support links\n * \n *  \n *  Parameters :-\n * \n * attributes - an array of the users attributes\n * \n * Returns - nothing, sets placeholders only\n */\n\n$supportContainer = $modx->getObject(''modResource'', $userSupportContainerId);\n$children = $supportContainer->getMany(''Children'');\n$context = $modx->context->get(''key'');\n$supportOutput = "";\nforeach ( $children as $child ) {\n    \n    $id = $child->get(''id'');\n    $title = $child->get(''pagetitle'');\n    $pageLink = $modx->makeURL($id, $context, "", "full");\n    $linkParams = array(''userHomeSupportTitle'' =>  $title ,\n                        ''userHomeSupportLink'' => $pageLink);\n                                     \n    \n    $supportOutput .= $modx->getChunk(''userHomeSupportBody'', $linkParams);\n}\n\n$modx->setPlaceholder(''userHomeSupportBody'', $supportOutput);', 0, 'a:1:{s:22:"userSupportContainerId";a:6:{s:4:"name";s:22:"userSupportContainerId";s:4:"desc";s:32:"Id of the support page container";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"261";s:7:"lexicon";N;}}', '');
