(31, 'registrationForm', 'Registration Form', 0, 20, 0, '<script type="text/javascript">\n var RecaptchaOptions = {\n    theme : ''clean''\n };\n </script>\n<form id="registrationForm" method="post" action="/[[~[[*id]]]]">\n<input id="registrationType" name="registrationType" type="hidden" value="[[+registrationType]]" />\n          <fieldset>\n              <legend class="dec">Personal details</legend>\n            \n                        <ol>\n                          <li><label for="userSettingsFirstName">Your first name:<span class="required-icon"></span>\n                    </label>\n                    <input id="userSettingsFirstName" class="text required validcharsgeneral" name="userSettingsFirstName" type="text" value="[[+userSettingsFirstName]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>\n            <li><label for="userSettingsLastName">Your last name:<span class="required-icon"></span>\n                    </label>\n                    <input id="userSettingsLastName" class="text required validcharsgeneral" name="userSettingsLastName" type="text" value="[[+userSettingsLastName]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>\n           \n            <li><label for="userSettingsTel">Telephone:\n                    </label>\n                    <input id="userSettingsTel" class="text validcharsgeneral" name="userSettingsTel" type="text" value="[[+userSettingsTel]]"/>\n                     <div id="formvalidationerror" ></div>\n                </li>\n                           <li><label for="userSettingsAddressStreet">Your address:<span class="required-icon"></span>\n                    </label>\n                    <input id="userSettingsAddressStreet" class="text required validcharsaddress" name="userSettingsAddressStreet" type="text" value="[[+userSettingsAddressStreet]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>\n         <!--   <li><label for="userSettingsCity">City:\n                    </label>\n                    <input id="userSettingsCity" class="text required validcharsgeneral" name="userSettingsCity" type="text" value="[[+userSettingsCity]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>-->\n            <li><label for="userSettingsCountry">\n                    Country:\n            </label>\n              <div class="select-bg">\n                <select id="userSettingsCountry" name="userSettingsCountry" >\n                        [[+userSettingsCountries]]\n                </select>\n              </div>\n                <div id="formvalidationerror" ></div>\n\n            </li>\n</ol>\n                     </fieldset>\n                        <fieldset>\n              <legend class="dec">Website Details</legend>\n            <p>Please enter a website name for your wedding website. If for instance you chose <strong>steveandjune</strong> for your website name your website\n            URL will be <strong>http://steveandjune.theweddingwebsitegroup.com</strong></p>\n              <ol>\n                <li><label for="userRegistrationWebsiteName">Your Website Name:<span class="required-icon"></span>\n                </label>\n                <input id="userRegistrationWebsiteName" class="text required validcharsURL" name="userRegistrationWebsiteName" type="text" /><span id="website-name-suffix">.theweddingwebsitegroup.com</span>\n                <div id="formvalidationerror" ></div>\n            </li>\n            \n                <li><label for="userSettingsPartner1">Your name: <span class="required-icon"></span><em>(this will display on the website)</em>\n                </label>\n                <input id="userSettingsPartner1" class="text required validcharsgeneral" name="userSettingsPartner1" type="text" value="[[+userSettingsPartner1]]"/>\n                <div id="formvalidationerror" ></div>\n            </li>\n                <li><label for="userSettingsPartner2">Your partner''s name: <span class="required-icon"></span><em>(this will display on the website)</em>\n                </label>\n                <input id="userSettingsPartner2" class="text required validcharsgeneral" name="userSettingsPartner2" type="text" value="[[+userSettingsPartner2]]"/>\n                <div id="formvalidationerror" ></div>\n            </li>\n            <li><label for="userSettingsDatepicker">Date of the wedding:<span class="required-icon"></span>\n                </label>\n                <input id="userSettingsDatepicker" class="text required" name="userSettingsDatepicker" type="text" value="[[+userSettingsDatepicker]]"/>\n                <div id="formvalidationerror" ></div>\n            </li>\n                  \n              </ol>\n              </fieldset>\n                      <fieldset>\n                        <legend class="dec">User details</legend>\n                        <p>Please enter your <strong>email address</strong> and choose a password to start your 7 day trial</p>\n                         <ol>\n                           <li><label for="registrationEmail">Email (this will be your username):<span class="required-icon"></span>\n                        </label>\n                          <input id="registrationEmail" class="text required email" name="registrationEmail" type="text" />\n                        </li>\n<li><label for="userPassword">Choose a password:<span class="required-icon"></span>\n                          </label>\n                          <input id="userPassword" class="text required" name="userPassword" type="password" />\n                         </li>\n<li><label for="userPasswordRetype">Retype password:<span class="required-icon"></span>\n                        </label>\n                          <input id="userPasswordRetype" class="text required" name="userPasswordRetype" type="password" />\n                        </li>\n<li>\n                          <fieldset>\n                           <legend>Agree terms<span class="required-icon"></span></legend><br />\n                           <ol>\n<li> \n<label for="agreeTerms">I agree to the <a href="[[~36~]]" title="terms and conditions">terms and conditions</a></label><input id="agreeTerms" class="checkbox required" name="agreeTerms" type="checkbox" value="1" />\n                            \n                           </li>\n</ol>\n                       </fieldset>\n                       </li>\n</ol>\n                       </fieldset>\n<!-- [[+registration.recaptcha]] -->\n                    <fieldset class="submit">\n                     \n                      <input class="button" type="submit" name="registrationSubmitTrial" value="TRY FOR FREE" />\n                       <!-- or\n                      <input class="button-alt" type="submit" name ="registrationSubmitPaid" value="Buy package" />-->\n                 </fieldset>\n\n                 \n        </form>\n\n <div class="servererror" ><strong>[[+registrationError]]</strong></div>', 0, 'a:0:{}'),
(71, 'userSettingsPersonalIncomplete', '', 0, 24, 0, '<div id="firstLogin"><a class="inline cboxElement" href="#firstLoginContent"></a>\n    <div id="firstLoginContent">\n        <p class="text dec">Welcome to Wedding Websites UK.</p>\n        <p class="text">Your Personal details are incomplete.</p>\n        <p class="text">Please add this information using the forms on this page.</p>\n        <p class="text">Please complete all the required fields and press SAVE.</p>\n        <p class="text">Thank you.</p>\n    </div>\n</div>', 0, 'a:0:{}'),
(72, 'userSettingsWebsiteIncomplete', '', 0, 24, 0, '<div id="firstLogin"><a class="inline cboxElement" href="#firstLoginContent"></a>\n    <div id="firstLoginContent">\n        <p class="text dec">Welcome to Wedding Websites UK.</p>\n        <p class="text">Your Website details are incomplete.</p>\n        <p class="text">In particular, you MUST enter your Website URL.</p>\n        <p class="text">Please add this information using the forms on this page.</p>\n        <p class="text">Please complete all the required fields and press SAVE.</p>\n        <p class="text">Thank you.</p>\n    </div>\n</div>', 0, 'a:0:{}');
