(43, 'userSettingsPersonalForm', '', 0, 24, 0, '<form id="personalForm" method="post" action="/[[~[[*id]]]]">\n    <input type="hidden" name="userSettingsPersonalSubmit" value="true"/>\n    <fieldset>\n        <ol>\n            <li><label for="userSettingsFirstName">Your first name:\n                    </label>\n                    <input id="userSettingsFirstName" class="text required validcharsgeneral  tobackend" name="userSettingsFirstName" type="text" value="[[+userSettingsFirstName]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>\n            <li><label for="userSettingsLastName">Your last name:\n                    </label>\n                    <input id="userSettingsLastName" class="text required validcharsgeneral  tobackend" name="userSettingsLastName" type="text" value="[[+userSettingsLastName]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>\n            <li><label for="userSettingsEmail">Email:\n                    </label>\n                    <input id="userSettingsEmail" class="text required email  tobackend" name="userSettingsEmail" type="text" value="[[+userSettingsEmail]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>\n            <li><label for="userSettingsAddressStreet">Your address:\n                    </label>\n                    <input id="userSettingsAddressStreet" class="text required validcharsaddress  tobackend" name="userSettingsAddressStreet" type="text" value="[[+userSettingsAddressStreet]]"/>\n                    <div id="formvalidationerror" ></div>\n            </li>\n            <li><label for="userSettingsTel">Telephone:\n                    </label>\n                    <input id="userSettingsTel" class="text required validcharsgeneral tobackend" name="userSettingsTel" type="text" value="[[+userSettingsTel]]"/>\n                     <div id="formvalidationerror" ></div>\n                </li>\n            <li><label for="userSet