(16, 'QuipResourceCleaner', 'Cleans up threads when a Resource is removed.', 0, 0, 0, '/**\n * Quip\n *\n * Copyright 2010 by Shaun McCormick <shaun@modx.com>\n *\n * This file is part of Quip, a simpel commenting component for MODx Revolution.\n *\n * Quip is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Quip is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Quip; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package quip\n */\n/**\n * Handles removal of threads if a Resource is deleted.\n * \n * @package quip\n */\n$quip = $modx->getService(''quip'',''Quip'',$modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'').''model/quip/'',$scriptProperties);\nif (!($quip instanceof Quip)) return;\n\nswitch ($modx->event->name) {\n    case ''OnEmptyTrash'':\n        foreach ($scriptProperties[''ids''] as $id) {\n            if (empty($id)) continue;\n\n            $threads = $modx->getCollection(''quipThread'',array(''resource'' => $id));\n            foreach ($threads as $thread) {\n                $modx->log(modX::LOG_LEVEL_INFO,''[Quip] Removing thread: ''.$thread->get(''name''));\n                $thread->remove();\n            }\n        }\n        break;\n}\nreturn;', 0, NULL, 0, ''),
(11, 'GalleryCustomTV', '', 0, 0, 0, '/**\n * Handles plugin events for Gallery''s Custom TV\n * \n * @package gallery\n */\n$corePath = $modx->getOption(''gallery.core_path'',null,$modx->getOption(''core_path'').''components/gallery/'');\nswitch ($modx->event->name) {\n    case ''OnTVInputRenderList'':\n        $modx->event->output($corePath.''elements/tv/input/'');\n        break;\n    case ''OnTVOutputRenderList'':\n        $modx->event->output($corePath.''elements/tv/output/'');\n        break;\n    case ''OnTVInputPropertiesList'':\n        $modx->event->output($corePath.''elements/tv/inputoptions/'');\n        break;\n    case ''OnTVOutputRenderPropertiesList'':\n        $modx->event->output($corePath.''elements/tv/properties/'');\n        break;\n    case ''OnDocFormPrerender'':\n        $gallery = $modx->getService(''gallery'',''Gallery'',$modx->getOption(''gallery.core_path'',null,$modx->getOption(''core_path'').''components/gallery/'').''model/gallery/'',$scriptProperties);\n        if (!($gallery instanceof Gallery)) return '''';\n\n        /* assign gallery lang to JS */\n        $modx->response->addLangTopic(''gallery:tv'');\n\n        /* get gallery action */\n        $action = $modx->getObject(''modAction'',array(\n            ''namespace'' => ''gallery'',\n            ''controller'' => ''index'',\n        ));\n        $modx->regClientStartupHTMLBlock(''<script type="text/javascript">\n        Ext.onReady(function() {\n            GAL.config = {};\n            GAL.config.connector_url = "''.$gallery->config[''connectorUrl''].''";\n            GAL.action = "''.($action ? $action->get(''id'') : 0).''";\n        });\n        </script>'');\n        $modx->regClientStartupScript($gallery->config[''assetsUrl''].''js/mgr/tv/Spotlight.js'');\n        $modx->regClientStartupScript($gallery->config[''assetsUrl''].''js/mgr/gallery.js'');\n        $modx->regClientStartupScript($gallery->config[''assetsUrl''].''js/mgr/widgets/album/album.items.view.js'');\n        $modx->regClientStartupScript($gallery->config[''assetsUrl''].''js/mgr/widgets/album/album.tree.js'');\n        $modx->regClientStartupScript($gallery->config[''assetsUrl''].''js/mgr/tv/gal.browser.js'');\n        $modx->regClientStartupScript($gallery->config[''assetsUrl''].''js/mgr/tv/galtv.js'');\n        $modx->regClientCSS($gallery->config[''cssUrl''].''mgr.css'');\n        break;\n}\nreturn;', 0, NULL, 0, ''),
(3, 'TinyMCE', 'TinyMCE 4.2.3-pl plugin for MODx Revolution', 0, 0, 0, '/**\n * TinyMCE RichText Editor Plugin\n *\n * Events: OnRichTextEditorInit, OnRichTextEditorRegister,\n * OnBeforeManagerPageInit, OnRichTextBrowserInit\n *\n * @author Jeff Whitfield <jeff@collabpad.com>\n * @author Shaun McCormick <shaun@collabpad.com>\n *\n * @package tinymce\n * @subpackage build\n */\nif ($modx->event->name == ''OnRichTextEditorRegister'') {\n    $modx->event->output(''TinyMCE'');\n    return;\n}\nrequire_once $modx->getOption(''tiny.core_path'',null,$modx->getOption(''core_path'').''components/tinymce/'').''tinymce.class.php'';\n$tiny = new TinyMCE($modx,$scriptProperties);\n\n$useEditor = $modx->getOption(''use_editor'',null,false);\n$whichEditor = $modx->getOption(''which_editor'',null,'''');\n\n/* Handle event */\nswitch ($modx->event->name) {\n    case ''OnRichTextEditorInit'':\n        if ($useEditor && $whichEditor == ''TinyMCE'') {\n            if (isset($forfrontend) || $modx->context->get(''key'') != ''mgr'') {\n                $def = $modx->getOption(''cultureKey'',null,$modx->getOption(''manager_language'',null,''en''));\n                $tiny->properties[''language''] = $modx->getOption(''fe_editor_lang'',array(),$def);\n                $tiny->properties[''frontend''] = true;\n                unset($def);\n            }\n            /* commenting these out as it causes problems with richtext tvs */\n            //if (isset($scriptProperties[''resource'']) && !$resource->get(''richtext'')) return;\n            //if (!isset($scriptProperties[''resource'']) && !$modx->getOption(''richtext_default'',null,false)) return;\n            $tiny->setProperties($scriptProperties);\n            $html = $tiny->initialize();\n            $modx->event->output($html);\n            unset($html);\n        }\n        break;\n    case ''OnRichTextBrowserInit'':\n        if ($useEditor && $whichEditor == ''TinyMCE'') {\n            $modx->regClientStartupScript($tiny->config[''assetsUrl''].''jscripts/tiny_mce/tiny_mce_popup.js'');\n            if (file_exists($tiny->config[''assetsPath''].''jscripts/tiny_mce/langs/''.$tiny->properties[''language''].''.js'')) {\n                $modx->regClientStartupScript($tiny->config[''assetsUrl''].''jscripts/tiny_mce/langs/''.$tiny->properties[''language''].''.js'');\n            } else {\n                $modx->regClientStartupScript($tiny->config[''assetsUrl''].''jscripts/tiny_mce/langs/en.js'');\n            }\n            $modx->regClientStartupScript($tiny->config[''assetsUrl''].''tiny.browser.js'');\n            $modx->getVersionData();\n            $inRevo20 = (boolean)version_compare($modx->version[''full_version''],''2.1.0-rc1'',''<'');\n            $modx->regClientStartupHTMLBlock(''<script type="text/javascript">var inRevo20 = ''.($inRevo20 ? 1 : 0).'';</script>'');\n            $modx->event->output(''Tiny.browserCallback'');\n        }\n        return '''';\n        break;\n\n   default: break;\n}\nreturn;', 0, 'a:39:{s:22:"accessibility_warnings";a:6:{s:4:"name";s:22:"accessibility_warnings";s:4:"desc";s:315:"If this option is set to true some accessibility warnings will be presented to the user if they miss specifying that information. This option is set to true by default, since we should all try to make this world a better place for disabled people. But if you are annoyed with the warnings, set this option to false.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:23:"apply_source_formatting";a:6:{s:4:"name";s:23:"apply_source_formatting";s:4:"desc";s:229:"This option enables you to tell TinyMCE to apply some source formatting to the output HTML code. With source formatting, the output HTML code is indented and formatted. Without source formatting, the output HTML is more compact. ";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:15:"button_tile_map";a:6:{s:4:"name";s:15:"button_tile_map";s:4:"desc";s:338:"If this option is set to true TinyMCE will use tiled images instead of individual images for most of the editor controls. This produces faster loading time since only one GIF image needs to be loaded instead of a GIF for each individual button. This option is set to false by default since it doesn''t work with some DOCTYPE declarations. ";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"lexicon";N;}s:7:"cleanup";a:6:{s:4:"name";s:7:"cleanup";s:4:"desc";s:331:"This option enables or disables the built-in clean up functionality. TinyMCE is equipped with powerful clean up functionality that enables you to specify what elements and attributes are allowed and how HTML contents should be generated. This option is set to true by default, but if you want to disable it you may set it to false.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:18:"cleanup_on_startup";a:6:{s:4:"name";s:18:"cleanup_on_startup";s:4:"desc";s:135:"If you set this option to true, TinyMCE will perform a HTML cleanup call when the editor loads. This option is set to false by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"lexicon";N;}s:22:"convert_fonts_to_spans";a:6:{s:4:"name";s:22:"convert_fonts_to_spans";s:4:"desc";s:348:"If you set this option to true, TinyMCE will convert all font elements to span elements and generate span elements instead of font elements. This option should be used in order to get more W3C compatible code, since font elements are deprecated. How sizes get converted can be controlled by the font_size_classes and font_size_style_values options.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:23:"convert_newlines_to_brs";a:6:{s:4:"name";s:23:"convert_newlines_to_brs";s:4:"desc";s:128:"If you set this option to true, newline characters codes get converted into br elements. This option is set to false by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"lexicon";N;}s:12:"convert_urls";a:6:{s:4:"name";s:12:"convert_urls";s:4:"desc";s:495:"This option enables you to control whether TinyMCE is to be clever and restore URLs to their original values. URLs are automatically converted (messed up) by default because the built-in browser logic works this way. There is no way to get the real URL unless you store it away. If you set this option to false it will try to keep these URLs intact. This option is set to true by default, which means URLs will be forced to be either absolute or relative depending on the state of relative_urls.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:11:"dialog_type";a:6:{s:4:"name";s:11:"dialog_type";s:4:"desc";s:246:"This option enables you to specify how dialogs/popups should be opened. Possible values are "window" and "modal", where the window option opens a normal window and the dialog option opens a modal dialog. This option is set to "window" by default.";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"name";s:6:"Window";i:0;s:6:"window";}i:1;a:2:{s:4:"name";s:5:"Modal";i:0;s:5:"modal";}}s:5:"value";s:6:"window";s:7:"lexicon";N;}s:14:"directionality";a:6:{s:4:"name";s:14:"directionality";s:4:"desc";s:261:"This option specifies the default writing direction. Some languages (Like Hebrew, Arabic, Urdu...) write from right to left instead of left to right. The default value of this option is "ltr" but if you want to use from right to left mode specify "rtl" instead.";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"name";s:13:"Left to Right";s:5:"value";s:3:"ltr";}i:1;a:2:{s:4:"name";s:13:"Right to Left";s:5:"value";s:3:"rtl";}}s:5:"value";s:3:"ltr";s:7:"lexicon";N;}s:14:"element_format";a:6:{s:4:"name";s:14:"element_format";s:4:"desc";s:198:"This option enables control if elements should be in html or xhtml mode. xhtml is the default state for this option. This means that for example <br /> will be <br> if you set this option to "html".";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"name";s:5:"XHTML";s:5:"value";s:5:"xhtml";}i:1;a:2:{s:4:"name";s:4:"HTML";s:5:"value";s:4:"html";}}s:5:"value";s:5:"xhtml";s:7:"lexicon";N;}s:15:"entity_encoding";a:6:{s:4:"name";s:15:"entity_encoding";s:4:"desc";s:70:"This option controls how entities/characters get processed by TinyMCE.";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:4:"name";s:4:"None";s:5:"value";s:0:"";}i:1;a:2:{s:4:"name";s:5:"Named";s:5:"value";s:5:"named";}i:2;a:2:{s:4:"name";s:7:"Numeric";s:5:"value";s:7:"numeric";}i:3;a:2:{s:4:"name";s:3:"Raw";s:5:"value";s:3:"raw";}}s:5:"value";s:0:"";s:7:"lexicon";N;}s:16:"force_p_newlines";a:6:{s:4:"name";s:16:"force_p_newlines";s:4:"desc";s:147:"This option enables you to disable/enable the creation of paragraphs on return/enter in Mozilla/Firefox. The default value of this option is true. ";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:22:"force_hex_style_colors";a:6:{s:4:"name";s:22:"force_hex_style_colors";s:4:"desc";s:277:"This option enables you to control TinyMCE to force the color format to use hexadecimal instead of rgb strings. It converts for example "color: rgb(255, 255, 0)" to "#FFFF00". This option is set to true by default since otherwice MSIE and Firefox would differ in this behavior.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:6:"height";a:6:{s:4:"name";s:6:"height";s:4:"desc";s:38:"Sets the height of the TinyMCE editor.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:5:"400px";s:7:"lexicon";N;}s:11:"indentation";a:6:{s:4:"name";s:11:"indentation";s:4:"desc";s:139:"This option allows specification of the indentation level for indent/outdent buttons in the UI. This defaults to 30px but can be any value.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:4:"30px";s:7:"lexicon";N;}s:16:"invalid_elements";a:6:{s:4:"name";s:16:"invalid_elements";s:4:"desc";s:163:"This option should contain a comma separated list of element names to exclude from the content. Elements in this list will removed when TinyMCE executes a cleanup.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";N;}s:6:"nowrap";a:6:{s:4:"name";s:6:"nowrap";s:4:"desc";s:212:"This nowrap option enables you to control how whitespace is to be wordwrapped within the editor. This option is set to false by default, but if you enable it by setting it to true editor contents will never wrap.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"lexicon";N;}s:15:"object_resizing";a:6:{s:4:"name";s:15:"object_resizing";s:4:"desc";s:148:"This option gives you the ability to turn on/off the inline resizing controls of tables and images in Firefox/Mozilla. These are enabled by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:12:"path_options";a:6:{s:4:"name";s:12:"path_options";s:4:"desc";s:119:"Sets a group of options. Note: This will override the relative_urls, document_base_url and remove_script_host settings.";s:4:"type";s:9:"textfield";s:7:"options";a:3:{i:0;a:2:{s:4:"name";s:17:"Document Relative";s:5:"value";s:11:"docrelative";}i:1;a:2:{s:4:"name";s:13:"Root Relative";s:5:"value";s:12:"rootrelative";}i:2;a:2:{s:4:"name";s:13:"Full Path URL";s:5:"value";s:11:"fullpathurl";}}s:5:"value";s:11:"docrelative";s:7:"lexicon";N;}s:28:"plugin_insertdate_dateFormat";a:6:{s:4:"name";s:28:"plugin_insertdate_dateFormat";s:4:"desc";s:53:"Formatting of dates when using the InsertDate plugin.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:8:"%Y-%m-%d";s:7:"lexicon";N;}s:28:"plugin_insertdate_timeFormat";a:6:{s:4:"name";s:28:"plugin_insertdate_timeFormat";s:4:"desc";s:53:"Formatting of times when using the InsertDate plugin.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:8:"%H:%M:%S";s:7:"lexicon";N;}s:12:"preformatted";a:6:{s:4:"name";s:12:"preformatted";s:4:"desc";s:225:"If you enable this feature, whitespace such as tabs and spaces will be preserved. Much like the behavior of a <pre> element. This can be handy when integrating TinyMCE with webmail clients. This option is disabled by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:13:"relative_urls";a:6:{s:4:"name";s:13:"relative_urls";s:4:"desc";s:231:"If this option is set to true, all URLs returned from the file manager will be relative from the specified document_base_url. If it is set to false all URLs will be converted to absolute URLs. This option is set to true by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:17:"remove_linebreaks";a:6:{s:4:"name";s:17:"remove_linebreaks";s:4:"desc";s:531:"This option controls whether line break characters should be removed from output HTML. This option is enabled by default because there are differences between browser implementations regarding what to do with white space in the DOM. Gecko and Safari place white space in text nodes in the DOM. IE and Opera remove them from the DOM and therefore the line breaks will automatically be removed in those. This option will normalize this behavior when enabled (true) and all browsers will have a white-space-stripped DOM serialization.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"lexicon";N;}s:18:"remove_script_host";a:6:{s:4:"name";s:18:"remove_script_host";s:4:"desc";s:221:"If this option is enabled the protocol and host part of the URLs returned from the file manager will be removed. This option is only used if the relative_urls option is set to false. This option is set to true by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:20:"remove_trailing_nbsp";a:6:{s:4:"name";s:20:"remove_trailing_nbsp";s:4:"desc";s:392:"This option enables you to specify that TinyMCE should remove any traling &nbsp; characters in block elements if you start to write inside them. Paragraphs are default padded with a &nbsp; and if you write text into such paragraphs the space will remain. Setting this option to true will remove the space. This option is set to false by default since the cursor jumps a bit in Gecko browsers.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"lexicon";N;}s:4:"skin";a:6:{s:4:"name";s:4:"skin";s:4:"desc";s:330:"This option enables you to specify what skin you want to use with your theme. A skin is basically a CSS file that gets loaded from the skins directory inside the theme. The advanced theme that TinyMCE comes with has two skins, these are called "default" and "o2k7". We added another skin named "cirkuit" that is chosen by default.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:7:"cirkuit";s:7:"lexicon";N;}s:12:"skin_variant";a:6:{s:4:"name";s:12:"skin_variant";s:4:"desc";s:403:"This option enables you to specify a variant for the skin, for example "silver" or "black". "default" skin does not offer any variant, whereas "o2k7" default offers "silver" or "black" variants to the default one. For the "cirkuit" skin there''s one variant named "silver". When creating a skin, additional variants may also be created, by adding ui_[variant_name].css files alongside the default ui.css.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";N;}s:20:"table_inline_editing";a:6:{s:4:"name";s:20:"table_inline_editing";s:4:"desc";s:231:"This option gives you the ability to turn on/off the inline table editing controls in Firefox/Mozilla. According to the TinyMCE documentation, these controls are somewhat buggy and not redesignable, so they are disabled by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:22:"theme_advanced_disable";a:6:{s:4:"name";s:22:"theme_advanced_disable";s:4:"desc";s:111:"This option should contain a comma separated list of controls to disable from any toolbar row/panel in TinyMCE.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";N;}s:19:"theme_advanced_path";a:6:{s:4:"name";s:19:"theme_advanced_path";s:4:"desc";s:331:"This option gives you the ability to enable/disable the element path. This option is only useful if the theme_advanced_statusbar_location option is set to "top" or "bottom". This option is set to "true" by default. Setting this option to "false" will effectively hide the path tool, though it still takes up room in the Status Bar.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:32:"theme_advanced_resize_horizontal";a:6:{s:4:"name";s:32:"theme_advanced_resize_horizontal";s:4:"desc";s:319:"This option gives you the ability to enable/disable the horizontal resizing. This option is only useful if the theme_advanced_statusbar_location option is set to "top" or "bottom" and when the theme_advanced_resizing is set to true. This option is set to true by default, allowing both resizing horizontal and vertical.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:23:"theme_advanced_resizing";a:6:{s:4:"name";s:23:"theme_advanced_resizing";s:4:"desc";s:216:"This option gives you the ability to enable/disable the resizing button. This option is only useful if the theme_advanced_statusbar_location option is set to "top" or "bottom". This option is set to false by default.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:33:"theme_advanced_statusbar_location";a:6:{s:4:"name";s:33:"theme_advanced_statusbar_location";s:4:"desc";s:257:"This option enables you to specify where the element statusbar with the path and resize tool should be located. This option can be set to "top" or "bottom". The default value is set to "top". This option can only be used when the theme is set to "advanced".";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"name";s:3:"Top";s:5:"value";s:3:"top";}i:1;a:2:{s:4:"name";s:6:"Bottom";s:5:"value";s:6:"bottom";}}s:5:"value";s:6:"bottom";s:7:"lexicon";N;}s:28:"theme_advanced_toolbar_align";a:6:{s:4:"name";s:28:"theme_advanced_toolbar_align";s:4:"desc";s:187:"This option enables you to specify the alignment of the toolbar, this value can be "left", "right" or "center" (the default). This option can only be used when theme is set to "advanced".";s:4:"type";s:9:"textfield";s:7:"options";a:3:{i:0;a:2:{s:4:"name";s:6:"Center";s:5:"value";s:6:"center";}i:1;a:2:{s:4:"name";s:4:"Left";s:5:"value";s:4:"left";}i:2;a:2:{s:4:"name";s:5:"Right";s:5:"value";s:5:"right";}}s:5:"value";s:4:"left";s:7:"lexicon";N;}s:31:"theme_advanced_toolbar_location";a:6:{s:4:"name";s:31:"theme_advanced_toolbar_location";s:4:"desc";s:191:"\nThis option enables you to specify where the toolbar should be located. This option can be set to "top" or "bottom" (the defualt). This option can only be used when theme is set to advanced.";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"name";s:3:"Top";s:5:"value";s:3:"top";}i:1;a:2:{s:4:"name";s:6:"Bottom";s:5:"value";s:6:"bottom";}}s:5:"value";s:3:"top";s:7:"lexicon";N;}s:5:"width";a:6:{s:4:"name";s:5:"width";s:4:"desc";s:32:"The width of the TinyMCE editor.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:3:"95%";s:7:"lexicon";N;}s:33:"template_selected_content_classes";a:6:{s:4:"name";s:33:"template_selected_content_classes";s:4:"desc";s:234:"Specify a list of CSS class names for the template plugin. They must be separated by spaces. Any template element with one of the specified CSS classes will have its content replaced by the selected editor content when first inserted.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";N;}}', 0, ''),
(4, 'CodeMirror', 'CodeMirror 1.1.0-pl plugin for MODx Revolution', 0, 0, 0, '/**\n * @package codemirror\n */\nif ($modx->event->name == ''OnRichTextEditorRegister'') {\n    $modx->event->output(''CodeMirror'');\n    return;\n}\nif ($modx->getOption(''which_element_editor'',null,''CodeMirror'') != ''CodeMirror'') return;\nif (!$modx->getOption(''use_editor'',null,true)) return;\nif (!$modx->getOption(''codemirror.enable'',null,true)) return;\n\n$assetsUrl = $modx->getOption(''codemirror.assets_url'',$scriptProperties,$modx->getOption(''assets_url'').''components/codemirror/'');\n\n$options = array(\n    ''modx_path'' => $assetsUrl,\n    ''modx_delay'' => $modx->getOption(''loader_delay'',$scriptProperties,300),\n    ''lineNumbers'' => (boolean)$modx->getOption(''line_numbers'',$scriptProperties,true),\n    ''textWrapping'' => (boolean)$modx->getOption(''soft_wrap'',$scriptProperties,true),\n    ''tabMode'' => $modx->getOption(''tab_mode'',$scriptProperties,''shift''),\n    ''indentUnit'' => (int)$modx->getOption(''indent_unit'',$scriptProperties,2),\n);\n\n$load = false;\nswitch ($modx->event->name) {\n    case ''OnSnipFormPrerender'':\n        $options[''modx_loader''] = ''onSnippet'';\n        $modx->regClientCSS($assetsUrl.''css/cm.css'');\n        $modx->regClientStartupHTMLBlock(''<script type="text/javascript">MODx.codem = ''.$modx->toJSON($options).'';</script>'');\n        $modx->regClientStartupScript($assetsUrl.''cm/js/codemirror.js'');\n        $modx->regClientStartupScript($assetsUrl.''js/cm.js'');\n        break;\n    case ''OnTempFormPrerender'':\n        $options[''modx_loader''] = ''onTemplate'';\n        $modx->regClientCSS($assetsUrl.''css/cm.css'');\n        $modx->regClientStartupHTMLBlock(''<script type="text/javascript">MODx.codem = ''.$modx->toJSON($options).'';</script>'');\n        $modx->regClientStartupScript($assetsUrl.''cm/js/codemirror.js'');\n        $modx->regClientStartupScript($assetsUrl.''js/cm.js'');\n        break;\n    case ''OnChunkFormPrerender'':\n        $options[''modx_loader''] = ''onChunk'';\n        $modx->regClientCSS($assetsUrl.''css/cm.css'');\n        $modx->regClientStartupHTMLBlock(''<script type="text/javascript">MODx.codem = ''.$modx->toJSON($options).'';</script>'');\n        $modx->regClientStartupScript($assetsUrl.''cm/js/codemirror.js'');\n        $modx->regClientStartupScript($assetsUrl.''js/cm.js'');\n        break;\n    case ''OnPluginFormPrerender'':\n        $options[''modx_loader''] = ''onPlugin'';\n        $modx->regClientCSS($assetsUrl.''css/cm.css'');\n        $modx->regClientStartupHTMLBlock(''<script type="text/javascript">MODx.codem = ''.$modx->toJSON($options).'';</script>'');\n        $modx->regClientStartupScript($assetsUrl.''cm/js/codemirror.js'');\n        $modx->regClientStartupScript($assetsUrl.''js/cm.js'');\n        break;\n    /* disabling TVs for now, since it causes problems with newlines\n    case ''OnTVFormPrerender'':\n        $options[''modx_loader''] = ''onTV'';\n        $options[''height''] = ''250px'';\n        $modx->regClientCSS($assetsUrl.''css/cm.css'');\n        $modx->regClientStartupHTMLBlock(''<script type="text/javascript">MODx.codem = ''.$modx->toJSON($options).'';</script>'');\n        $modx->regClientStartupScript($assetsUrl.''cm/js/codemirror.js'');\n        $modx->regClientStartupScript($assetsUrl.''js/cm.js'');\n        break;*/\n    case ''OnFileEditFormPrerender'':\n        $options[''modx_loader''] = ''onFile'';\n        $modx->regClientCSS($assetsUrl.''css/cm.css'');\n        $modx->regClientStartupHTMLBlock(''<script type="text/javascript">MODx.codem = ''.$modx->toJSON($options).'';</script>'');\n        $modx->regClientStartupScript($assetsUrl.''cm/js/codemirror.js'');\n        $modx->regClientStartupScript($assetsUrl.''js/cm.js'');\n        break;\n    /* debated whether or not to use */\n    case ''OnRichTextEditorInit'':\n        break;\n    case ''OnRichTextBrowserInit'':\n        break;\n}\n\nreturn;', 0, 'a:5:{s:12:"line_numbers";a:6:{s:4:"name";s:12:"line_numbers";s:4:"desc";s:39:"Whether or not to display line numbers.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:9:"soft_wrap";a:6:{s:4:"name";s:9:"soft_wrap";s:4:"desc";s:37:"Whether or not to soft wrap the text.";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";N;}s:8:"tab_mode";a:6:{s:4:"name";s:8:"tab_mode";s:4:"desc";s:120:"The effect of pressing tab. Defaults to tab moves text one indent_unit right, shift+tab moves text one indent_unit left.";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:4:"text";s:5:"Shift";s:5:"value";s:5:"shift";}i:1;a:2:{s:4:"text";s:6:"Indent";s:5:"value";s:6:"indent";}i:2;a:2:{s:4:"text";s:8:"4 Spaces";s:5:"value";s:6:"spaces";}i:3;a:2:{s:4:"text";s:15:"Browser Default";s:5:"value";s:7:"default";}}s:5:"value";s:5:"shift";s:7:"lexicon";N;}s:11:"indent_unit";a:6:{s:4:"name";s:11:"indent_unit";s:4:"desc";s:44:"The number of spaces to use for indentation.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";i:2;s:7:"lexicon";N;}s:12:"loader_delay";a:6:{s:4:"name";s:12:"loader_delay";s:4:"desc";s:135:"The number of milliseconds to wait before loading the RTE on New elements. Make a larger number if you are experiencing loading errors.";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";i:300;s:7:"lexicon";N;}}', 0, ''),
(9, 'ArchivistFurl', 'Handles FURLs for Archivist.', 0, 0, 0, '/**\n * Archivist\n *\n * Copyright 2010-2011 by Shaun McCormick <shaun@modx.com>\n *\n * This file is part of Archivist, a simple archive navigation system for MODx\n * Revolution.\n *\n * Archivist is free software; you can redistribute it and/or modify it under\n * the terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Archivist is distributed in the hope that it will be useful, but WITHOUT\n * ANY WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS\n * FOR A PARTICULAR PURPOSE. See the GNU General Public License for more\n * details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Archivist; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package archivist\n */\n/**\n * @var modX $modx\n * @package archivist\n */\nif ($modx->event->name != ''OnPageNotFound'') return;\n\n$archiveIds = $modx->getOption(''archivist.archive_ids'',null,'''');\nif (empty($archiveIds)) return;\n$archiveIds = explode('','',$archiveIds);\n\n/* handle redirects */\n$search = $_SERVER[''REQUEST_URI''];\n$base_url = $modx->getOption(''base_url'');\nif ($base_url != ''/'') {\n    $search = str_replace($base_url,'''',$search);\n}\n$search = trim($search, ''/'');\n\n/* get resource to redirect to */\n$resourceId = false;\n$prefix = ''arc_'';\nforeach ($archiveIds as $archive) {\n    $archive = explode('':'',$archive);\n    $archiveId = $archive[0];\n    $alias = array_search($archiveId,$modx->aliasMap);\n    if ($alias && strpos($search,$alias) !== false) {\n        $search = str_replace($alias,'''',$search);\n        $resourceId = $archiveId;\n        if (isset($archive[1])) $prefix = $archive[1];\n    }\n}\nif (!$resourceId) return;\n\n/* figure out archiving */\n$params = explode(''/'', $search);\nif (count($params) < 1) return;\n\n/* tag handling! */\nif ($params[0] == ''tags'') {\n    $_REQUEST[''tag''] = $params[1];\n} else {\n    /* set Archivist parameters for date-based archives */\n    $_REQUEST[$prefix.''year''] = $params[0];\n    if (isset($params[1])) $_REQUEST[$prefix.''month''] = $params[1];\n    if (isset($params[2])) $_REQUEST[$prefix.''day''] = $params[2];\n}\n\n/* forward */\n$modx->sendForward($resourceId);\nreturn;', 0, NULL, 0, ''),
(12, 'phpThumbOfCacheManager', 'Handles cache cleaning when clearing the Site Cache.', 0, 0, 0, '/**\n * Handles cache management for phpthumbof filter\n * \n * @package phpthumbof\n */\nif (empty($results)) $results = array();\n\nswitch ($modx->event->name) {\n    case ''OnSiteRefresh'':\n        if (!$modx->loadClass(''modPhpThumb'',$modx->getOption(''core_path'').''model/phpthumb/'',true,true)) {\n            $modx->log(modX::LOG_LEVEL_ERROR,''[phpThumbOf] Could not load modPhpThumb class in plugin.'');\n            return;\n        }\n        $assetsPath = $modx->getOption(''phpthumbof.assets_path'',$scriptProperties,$modx->getOption(''assets_path'').''components/phpthumbof/'');\n        $phpThumb = new modPhpThumb($modx);\n        $cacheDir = $assetsPath.''cache/'';\n\n        /* clear local cache */\n        if (!empty($cacheDir)) {\n            foreach (new DirectoryIterator($cacheDir) as $file) {\n                if (!$file->isFile()) continue;\n                @unlink($file->getPathname());\n            }\n        }\n\n        /* if using amazon s3, clear our cache there */\n        $useS3 = $modx->getOption(''phpthumbof.use_s3'',$scriptProperties,false);\n        if ($useS3) {\n            $modelPath = $modx->getOption(''phpthumbof.core_path'',null,$modx->getOption(''core_path'').''components/phpthumbof/'').''model/'';\n            $modaws = $modx->getService(''modaws'',''modAws'',$modelPath.''aws/'',$scriptProperties);\n            $s3path = $modx->getOption(''phpthumbof.s3_path'',null,''phpthumbof/'');\n            \n            $list = $modaws->getObjectList($s3path);\n            if (!empty($list) && is_array($list)) {\n                foreach ($list as $obj) {\n                    if (empty($obj->Key)) continue;\n\n                    $results[] = $modaws->deleteObject($obj->Key);\n                }\n            }\n        }\n\n        break;\n}\nreturn;', 0, NULL, 0, ''),
(17, 'Protect', 'Wedding Site FE page password protected checker plugin', 0, 59, 0, '/**\n * Wedding site FE page password protected checker plugin\n *\n * @author    S. Hamblett <steve.hamblett@linux.com>\n * @copyright 2012 S. Hamblett\n * @license   GPLv3 http://www.gnu.org/licenses/gpl.html\n */\n$output = $modx->resource->_output;\n\n/* Check if we are Front end, if not return */\n$type = $modx->runSnippet(''userGetURLType'');\n\nif ($type != ''front'')\n    return $output;\n\n/* Get the website and the user details */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Get the user details */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes)\n    return $output;\n$attributes = $weddingUserAttributes->toArray();\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$weddingUser = $modx->getObject(''weddingUser'', $weddingUserId);\nif (!$weddingUser)\n    return $output;\n\n/* Check for a time out or a log in response */\nif (isset($_SESSION[$website])) {\n\n    $now = time();\n    if (($now - ($validFor * 60) ) <= $_SESSION[$website]) {\n\n        /* Still logged in */\n        return $output;\n        \n    } else {\n\n        /* Log the user out */\n        unset($_SESSION[$website]);\n    }\n    \n} else {\n    \n    if ($_SERVER[''PHP_AUTH_PW''] == $attributes[''websitePassword'']) {\n\n        /* Always set after initial auth */\n        $_SESSION[$website] = time();\n        return $output;\n    }\n}\n\n/* Check if the website or the page we are on is password protected */\n$protected = false;\nif ($attributes[''passwordProtect''] == 1)\n    $protected = true;\n$resourceId = $modx->resource->get(''id'');\n$pages = $weddingUser->getMany(''Pages'');\nforeach ($pages as $page) {\n    if ($page->get(''pageId'') == $resourceId) {\n        $pageSelected = $page->toArray();\n        break;\n    }\n}\nif (empty($pageSelected))\n    return $output;\nif ($pageSelected[''passwordProtect''] == 1)\n    $protected = true;\n\n\n/* Do the protection if needed.\n * if we are now more than xx away from first auth fail the request \n */\n\nif ($protected) {\n\n    /* Ok, we need to ask for the password */\n    header(''WWW-Authenticate: Basic realm="Please enter your user(visitor) and password to view this page"'');\n    header(''HTTP/1.0 401 Unauthorized'');\n    echo "You must enter a valid password to view this page\\n";\n    return;\n    \n} else {\n\n    /* Not protected or authorized */\n    return $output;\n}', 0, 'a:1:{s:8:"validFor";a:6:{s:4:"name";s:8:"validFor";s:4:"desc";s:93:"How long the browsing user has before he is prompted to answer the password again. In minutes";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"60";s:7:"lexicon";N;}}', 0, ''),
(13, 'Clear User Data', 'Wedding Site user data clear up plugin', 0, 43, 0, '/**\n * Wedding site Clear User data plugin\n *\n * @author    S. Hamblett <steve.hamblett@linux.com>\n * @copyright 2011 S. Hamblett\n * @license   GPLv3 http://www.gnu.org/licenses/gpl.html\n */\n\n/* Clear the user data now the user is being removed */\nif ($reallyClear == 1) {\n\n    /* Get the wedding user details we need */\n    $userId = $user->get(''id'');\n    if ($userId == "")\n        return;\n    $weddingUser = $modx->getObject(''weddingUser'', $userId);\n    if (!$weddingUser)\n        return;\n    $weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n    $attributes = $weddingUser->attributes->toArray();\n    $website = $attributes[''website''];\n    if ($website == "")\n        return;\n\n    /* Remove the users page set */\n    $c = $modx->newQuery(''modResource'');\n    $c->where(array(''alias:='' => $website));\n    $userContainer = $modx->getObject(''modResource'', $c);\n    if (!$userContainer)\n        return;\n    $id = $userContainer->get(''id'');\n    $modx->runProcessor(''resource/delete'', array(''id'' => $id));\n\n    /* Remove the users subdomain mapping */\n    $modx->runSnippet(''cPanelDeleteSubDomain'', array(''subDomainName'' => $website));\n\n    /* Remove the users directories */\n    $modx->runSnippet(''removeUserDirectories'', array(''archive'' => false, ''website'' => $website));\n\n    /* Remove the users RSVP messages */\n    $c = $modx->newQuery(''modUserMessage'');\n    $c->where(array(\n        ''recipient'' => $userId\n    ));\n    $messages = $modx->getCollection(''modUserMessage'', $c);\n    foreach ($messages as $message)\n        $message->remove();\n\n    /* Remove the users guestbook signings */\n\n    /* Include Quip */\n    $corePath = $modx->getOption(''quip.core_path'', null, $modx->getOption(''core_path'') . ''components/quip/'');\n    $modx->addPackage(''quip'', $corePath . ''model/'');\n    \n    /* Remove the users  thread */\n    $userName = $user->get(''username'');\n    $thread = $modx->getObject(''quipThread'', $userName);\n    if ( $thread ) $thread->remove();\n\n}', 0, 'a:1:{s:11:"reallyClear";a:6:{s:4:"name";s:11:"reallyClear";s:4:"desc";s:58:"If set really removes the user data, otherwise archives it";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:1:"1";s:7:"lexicon";N;}}', 0, ''),
(15, 'URL Check', 'Wedding Site URL Checker plugin', 0, 49, 0, '/**\n * Wedding site URL Check plugin\n *\n * @author    S. Hamblett <steve.hamblett@linux.com>\n * @copyright 2012 S. Hamblett\n * @license   GPLv3 http://www.gnu.org/licenses/gpl.html\n */\n\n/* Get the URL */\n$pageURL .= $_SERVER["SERVER_NAME"] . $_SERVER["REQUEST_URI"];\n  \n/* If we are going to the login page, allow it all the time */\nif (preg_match(''/user-login/'', $pageURL) != 0)\n    return;\n\n/* Initialise */\n$redirectToLogin = false;\n\n\n/* Get login status */\n$context = $modx->context->get(''key'');\n$loggedIn = $modx->user->isAuthenticated($context);\n\nif (!$loggedIn) {\n\n    /* If we are trying to access the user admin area we must be logged in */\n    if (preg_match(''/user-administration/'', $pageURL) != 0)\n        $redirectToLogin = true;\n\n    /* Redirect to login if we have to ie the checks above fail */\n    if ($redirectToLogin) {\n\n        $loginURL = $modx->makeURL($loginPageId, $context, "", "full");\n        $modx->sendRedirect($loginURL);\n    }\n}', 0, 'a:1:{s:11:"loginPageId";a:6:{s:4:"name";s:11:"loginPageId";s:4:"desc";s:29:"Resource id of the login page";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:2:"34";s:7:"lexicon";N;}}', 0, ''),
(18, 'Captcha', '<b>3.2.4-beta1</b> CAPTCHA Login Plugin', 0, 0, 0, '/**\r\n * Captcha plugin\r\n *\r\n * Copyright 2011 Bob Ray\r\n *\r\n * @author Bob Ray\r\n * @editor Shaun McCormick <shaun@collabpad.com>\r\n * @created 09/23/2008\r\n * @version 3.2.4\r\n * 1/17/11\r\n *\r\n * Captcha is free software; you can redistribute it and/or modify it\r\n * under the terms of the GNU General Public License as published by the Free\r\n * Software Foundation; either version 2 of the License, or (at your option) any\r\n * later version.\r\n *\r\n * Captcha is distributed in the hope that it will be useful, but WITHOUT ANY\r\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\r\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\r\n *\r\n * You should have received a copy of the GNU General Public License along with\r\n * Captcha; if not, write to the Free Software Foundation, Inc., 59 Temple\r\n * Place, Suite 330, Boston, MA 02111-1307 USA\r\n *\r\n * @package captcha\r\n */\r\n\r\n/**\r\n * MODx Captcha plugin\r\n *\r\n * Description: Captcha plugin for MODx login verification\r\n * Events: OnBeforeManagerLogin, OnManagerLoginFormRender\r\n *\r\n * @package captcha\r\n *\r\n * @property\r\n */\r\n\r\nswitch ($modx->event->name) {\r\n    case ''OnBeforeManagerLogin'': /* register only for backend */\r\n        $rt = true;\r\n        $modx->lexicon->load(''captcha:default'');\r\n        if ($modx->getOption(''captcha.enabled'',null,true)) {\r\n            if (!isset ($_SESSION[''veriword''])) {\r\n                 $rt = ''$_SESSION Variable not set'';\r\n\r\n            }  else if ($_SESSION[''veriword''] != $_POST[''captcha_code'']) {\r\n                /*$rt = "Debug: No Match: SESSION:".$_SESSION[''veriword'']." captcha_code:".$_POST[''captcha_code'']; */\r\n                if ($modx->getOption(''captcha.use_mathstring'',null,true)) {\r\n                    $rt=$modx->lexicon(''login_mathstring_error'');\r\n                } else {\r\n                    $rt=$modx->lexicon(''login_captcha_error'');\r\n                }\r\n            }\r\n        }\r\n\r\n        $modx->event->_output = $rt;\r\n        break;\r\n\r\n    case ''OnManagerLoginFormRender'': /* register only for backend */\r\n        $rt = '''';\r\n\r\n        if ($modx->getOption(''captcha.enabled'',null,true)) {\r\n\r\n            $modx->lexicon->load(''captcha:default'');\r\n\r\n            if ($modx->getOption(''captcha.use_mathstring'',null,true)) {\r\n                $alt = $modx->lexicon(''login_mathstring_message'');\r\n            } else {\r\n                $alt = $modx->lexicon(''login_captcha_message'');\r\n            }\r\n\r\n            $captcha_image= ''<a href="''.$_SERVER[''PHP_SELF''].''" class="loginCaptcha"><img src="''.$modx->getOption(''site_url'').''assets/components/captcha/captcha.php?rand=''.rand().''" alt="''.$alt.''" /></a>'';\r\n\r\n            if ($modx->getOption(''captcha_use_mathstring'',null,true)) {\r\n                $captcha_prompt = ''<p>''.$modx->lexicon("login_mathstring_message").''</p>'';\r\n                $captcha_input= ''<br /><br /><label>''.$modx->lexicon("captcha_mathstring_code").'': <input type="text" name="captcha_code" value="" /></label>'';\r\n            } else {\r\n                $captcha_prompt = ''<p>''.$modx->lexicon("login_captcha_message").''</p>'';\r\n                $captcha_input= ''<br /><br /><label>''.$modx->lexicon("captcha_code").'': <input type="text" name="captcha_code" value="" /></label>'';\r\n            }\r\n\r\n            $rt = ''<hr />''.$captcha_prompt.$captcha_image.$captcha_input;\r\n        }\r\n\r\n        $modx->event->_output = $rt;\r\n        break;\r\n}', 0, NULL, 0, ''),
(20, 'Redirector', 'Handles site redirects.', 0, 0, 0, '/**\n * @package redirector\n */\n/* load redirector class */\n$corePath =  $modx->getOption(''redirector.core_path'',$scriptProperties,$modx->getOption(''core_path'').''components/redirector/'');\n$redirector = $modx->getService(''redirector'',''Redirector'',$corePath.''model/redirector/'',$scriptProperties);\nif (!($redirector instanceof Redirector)) return '''';\n\n/* handle redirects */\n$search = $_SERVER[''REQUEST_URI''];\n$baseUrl = $modx->getOption(''base_url'',null,MODX_BASE_URL);\nif (!empty($baseUrl) && $baseUrl != ''/'' && $baseUrl != '' '') {\n    $search = str_replace($baseUrl,'''',$search);\n}\n$search = ltrim($search,''/'');\n$extPos = strrpos($search, ''.'');\nif ($extPos) {\n    if ($search) {\n        $redirect = $modx->getObject(''modRedirect'', array(\n            ''pattern'' => $search,\n            ''active'' => 1,\n        ));\n        if ($redirect) {\n            $target = $redirect->get(''target'');\n            $modx->parser->processElementTags('''', $target, true, true);\n            if ($target != $modx->resourceIdentifier && $target != $search) {\n                if (!strpos($target, ''://'')) {\n                    $target = $modx->getOption(''site_url'').$target;\n                }\n                $modx->log(modX::LOG_LEVEL_INFO, ''Redirector plugin redirecting request for '' . $search . '' to '' . $target);\n                header(''HTTP/1.1 301 Moved Permanently'');\n                $modx->sendRedirect($target);\n            }\n        }\n    }\n    $search = substr($search, 0, $extPos);\n}\nif ($search) {\n    $redirect = $modx->getObject(''modRedirect'', array(''pattern'' => $search, ''active'' => 1));\n    if ($redirect) {\n        $target = $redirect->get(''target'');\n        $modx->parser->processElementTags('''', $target, true, true);\n        if ($target != $modx->resourceIdentifier && $target != $search) {\n            if (!strpos($target, ''://'')) {\n                $target = $modx->getOption(''site_url'').$target;\n            }\n            $modx->log(modX::LOG_LEVEL_INFO, ''Redirector plugin redirecting request for '' . $search . '' to '' . $target);\n            header(''HTTP/1.1 301 Moved Permanently'');\n            $modx->sendRedirect($target);\n        }\n    }\n}\nreturn;', 0, NULL, 0, '');
