(154, 'QuipCount', 'An assistance snippet for getting thread/user comment counts.', 0, 1, 0, '/**\n * Quip\n *\n * Copyright 2010 by Shaun McCormick <shaun@modx.com>\n *\n * This file is part of Quip, a simpel commenting component for MODx Revolution.\n *\n * Quip is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Quip is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Quip; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package quip\n */\n/**\n * QuipCount\n *\n * Gets the total # of comments in a thread or by a user.\n *\n * @name QuipCount\n * @author Shaun McCormick <shaun@modx.com>\n * @package quip\n */\n$quip = $modx->getService(''quip'',''Quip'',$modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'').''model/quip/'',$scriptProperties);\nif (!($quip instanceof Quip)) return '''';\n\n$type = $modx->getOption(''type'',$scriptProperties,''thread'');\n$type = explode('','',$type);\n\n$total = 0;\n$c = $modx->newQuery(''quipComment'');\n\n/* filter by user */\nif (in_array(''user'',$type)) {\n    if (empty($scriptProperties[''user''])) return 0;\n\n    $c->innerJoin(''modUser'',''Author'');\n    if (is_numeric($scriptProperties[''user''])) {\n        $c->where(array(\n            ''Author.id'' => $scriptProperties[''user''],\n        ));\n    } else {\n        $c->where(array(\n            ''Author.username'' => $scriptProperties[''user''],\n        ));\n    }\n}\n/* filter by thread */\nif (in_array(''thread'',$type)) {\n    if (empty($scriptProperties[''thread''])) return 0;\n\n    $c->where(array(\n        ''thread'' => $scriptProperties[''thread''],\n    ));\n}\n/* filter by family */\nif (in_array(''family'',$type)) {\n    if (empty($scriptProperties[''family''])) return 0;\n    $c->where(array(\n        ''quipComment.thread:LIKE'' => $scriptProperties[''family''],\n    ));\n}\n\n$c->where(array(\n    ''quipComment.approved'' => true,\n    ''quipComment.deleted'' => false,\n));\n$output = $modx->getCount(''quipComment'',$c);\n\n/* output */\n$toPlaceholder = $modx->getOption(''toPlaceholder'',$scriptProperties,false);\nif ($toPlaceholder) {\n    $modx->setPlaceholder($toPlaceholder,$output);\n    return '''';\n}\nreturn $output;', 0, 'a:5:{s:4:"type";a:6:{s:4:"name";s:4:"type";s:4:"desc";s:25:"quip.prop_count_type_desc";s:4:"type";s:4:"list";s:7:"options";a:3:{i:0;a:2:{s:4:"text";s:11:"quip.thread";s:5:"value";s:6:"thread";}i:1;a:2:{s:4:"text";s:9:"quip.user";s:5:"value";s:4:"user";}i:2;a:2:{s:4:"text";s:11:"quip.family";s:5:"value";s:6:"family";}}s:5:"value";s:6:"thread";s:7:"lexicon";s:15:"quip:properties";}s:6:"thread";a:6:{s:4:"name";s:6:"thread";s:4:"desc";s:27:"quip.prop_count_thread_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:25:"quip.prop_count_user_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:6:"family";a:6:{s:4:"name";s:6:"family";s:4:"desc";s:27:"quip.prop_count_family_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}s:13:"toPlaceholder";a:6:{s:4:"name";s:13:"toPlaceholder";s:4:"desc";s:34:"quip.prop_count_toplaceholder_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:15:"quip:properties";}}', ''),
(155, 'QuipLatestComments', 'An assistance snippet for getting the latest comments for the whole site or a thread or user.', 0, 1, 0, '/**\n * Quip\n *\n * Copyright 2010 by Shaun McCormick <shaun@modxcms.com>\n *\n * This file is part of Quip, a simpel commenting component for MODx Revolution.\n *\n * Quip is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Quip is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Quip; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package quip\n */\n/**\n * QuipLatestComments\n *\n * Gets latest comments in a thread or by a user.\n *\n * @name QuipLatestComments\n * @author Shaun McCormick <shaun@modxcms.com>\n * @package quip\n */\n$quip = $modx->getService(''quip'',''Quip'',$modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'').''model/quip/'',$scriptProperties);\nif (!($quip instanceof Quip)) return '''';\n\n/* setup default properties */\n$type = $modx->getOption(''type'',$scriptProperties,''all'');\n$tpl = $modx->getOption(''tpl'',$scriptProperties,''quipLatestComment'');\n$limit = $modx->getOption(''limit'',$scriptProperties,5);\n$start = $modx->getOption(''start'',$scriptProperties,0);\n$sortBy = $modx->getOption(''sortBy'',$scriptProperties,''createdon'');\n$sortByAlias = $modx->getOption(''sortByAlias'',$scriptProperties,''quipComment'');\n$sortDir = $modx->getOption(''sortDir'',$scriptProperties,''DESC'');\n\n$rowCss = $modx->getOption(''rowCss'',$scriptProperties,''quip-latest-comment'');\n$altRowCss = $modx->getOption(''altRowCss'',$scriptProperties,''quip-latest-comment-alt'');\n$dateFormat = $modx->getOption(''dateFormat'',$scriptProperties,''%b %d, %Y at %I:%M %p'');\n$stripTags = $modx->getOption(''stripTags'',$scriptProperties,true);\n$bodyLimit = $modx->getOption(''bodyLimit'',$scriptProperties,30);\n$contexts = $modx->getOption(''contexts'',$scriptProperties,'''');\n\n/* build query by type */\n$c = $modx->newQuery(''quipComment'');\n$c->select(array(\n    ''quipComment.*'',\n    ''Resource.pagetitle'',\n));\n$c->leftJoin(''modUser'',''Author'');\n$c->leftJoin(''modResource'',''Resource'');\n$c->where(array(\n    ''quipComment.approved'' => true,\n));\nif (!empty($contexts)) {\n    $c->where(array(\n        ''Resource.context_key:IN'' => explode('','',$contexts),\n    ));\n}\nswitch ($type) {\n    case ''user'':\n        if (empty($scriptProperties[''user''])) return '''';\n        if (is_numeric($scriptProperties[''user''])) {\n            $c->where(array(\n                ''Author.id'' => $scriptProperties[''user''],\n            ));\n        } else {\n            $c->where(array(\n                ''Author.username'' => $scriptProperties[''user''],\n            ));\n        }\n        break;\n    case ''thread'':\n        if (empty($scriptProperties[''thread''])) return '''';\n        $c = $modx->newQuery(''quipComment'');\n        $c->where(array(\n            ''quipComment.thread'' => $scriptProperties[''thread''],\n        ));\n        break;\n    case ''family'':\n        if (empty($scriptProperties[''family''])) return '''';\n        $c = $modx->newQuery(''quipComment'');\n        $c->where(array(\n            ''quipComment.thread:LIKE'' => $scriptProperties[''family''],\n        ));\n        break;\n    case ''all'':\n    default:\n        break;\n}\n$c->where(array(\n    ''quipComment.deleted'' => false,\n));\n$c->sortby(''`''.$sortByAlias.''`.`''.$sortBy.''`'',$sortDir);\n$c->limit($limit,$start);\n$comments = $modx->getCollection(''quipComment'',$c);\n\n/* iterate */\n$pagePlaceholders = array();\n$output = array();\n$alt = false;\nforeach ($comments as $comment) {\n    $commentArray = $comment->toArray();\n    $commentArray[''bodyLimit''] = $bodyLimit;\n    $commentArray[''cls''] = $rowCss;\n    if ($altRowCss && $alt) $commentArray[''alt''] = '' ''.$altRowCss;\n    $commentArray[''url''] = $comment->makeUrl();\n\n    if (!empty($stripTags)) { $commentArray[''body''] = strip_tags($commentArray[''body'']); }\n    \n    $commentArray[''ago''] = $quip->getTimeAgo($commentArray[''createdon'']);\n    \n    $output[] = $quip->getChunk($tpl,$commentArray);\n    $alt = !$alt;\n}\n\n/* set page placeholders */\n$pagePlaceholders = array();\n$pagePlaceholders[''resource''] = $commentArray[''resource''];\n$pagePlaceholders[''pagetitle''] = $commentArray[''pagetitle''];\n$placeholderPrefix = $modx->getOption(''placeholderPrefix'',$scriptProperties,''quip.latest'');\n$modx->toPlaceholders($pagePlaceholders,$placeholderPrefix);\n\n/* output */\n$outputSeparator = $modx->getOption(''outputSeparator'',$scriptProperties,"\\n");\n$output = implode($outputSeparator,$output);\n$toPlaceholder = $modx->getOption(''toPlaceholder'',$scriptProperties,false);\nif ($toPlaceholder) {\n    $modx->setPlaceholder($toPlaceholder,$output);\n    return '''';\n}\nreturn $output;', 0, 'a:18:{s:4:"type";a:6:{s:4:"name";s:4:"type";s:4:"desc";s:24:"quip.prop_late_type_desc";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:4:"name";s:8:"quip.all";s:5:"value";s:3:"all";}i:1;a:2:{s:4:"name";s:11:"quip.thread";s:5:"value";s:6:"thread";}i:2;a:2:{s:4:"name";s:11:"quip.family";s:5:"value";s:6:"family";}i:3;a:2:{s:4:"name";s:9:"quip.user";s:5:"value";s:4:"user";}}s:5:"value";s:3:"all";s:7:"lexicon";s:15:"quip:properties";}s:3:"tpl";a:6:{s:4:"name";s:3:"tpl";s:4:"desc";s:23:"quip.prop_late_tpl_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:17:"quipLatestComment";s:7:"lexicon";s:15:"q