(44, 'registrationFormJSInclude', 'Includes the JS for the registration form', 0, 20, 0, '/**\n * Wedding site Registration processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Set our jQuery paths */\n$validatorPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'').''js/jquery-validation-1.8.1'';\n\n/* Include the validation CSS */\n$modx->regClientCSS(''assets/templates/wedding/styles/validation.css'');\n\n/* Include the includes */\n$modx->regClientStartupScript($validatorPath . ''/lib/jquery.metadata.js'');\n$modx->regClientStartupScript($validatorPath . ''/jquery.validate.js'');\n\n/* Ajax Link */\n$registrationFormAjax = $modx->makeURL($modx->getOption(''registrationAJAXPage''), \n                                $modx->context->get(''key''), '''', ''full'');\n$registrationFormAjaxLink = ''"'' . $registrationFormAjax . ''"'';\n\n$ready = ''<script>\n    \n     /* WS object */\n        WS = new Object();\n        \n        WS.registrationFormAjaxLink= '' . $registrationFormAjaxLink . '';\n            \n</script>'';\n\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n$registration = $javascriptPath . "js/pages/registration/registration.js";\n$modx->regClientStartupScript($registration);\n\n/* Date Picker */\n$datePicker = ''<script>$(document).ready(function(){\n  $(function() {\n    $( "#userSettingsDatepicker" ).datepicker({\n      showOn: "both",\n      buttonImage: "'' . $modx->getOption(''assets_url''). ''templates/wedding/images/calendar.gif",\n      buttonImageOnly: true,\n      minDate: -2, maxDate: "+48M +28D",\n      changeYear: true,\n      changeMonth: true,\n      dateFormat: "dd/mm/y"\n    });\n  });\n    \n});</script>'';\n\n\n$modx->regClientStartupScript($ready, true);\n$modx->regClientStartupScript($datePicker, true);', 0, 'a:1:{s:5:"debug";a:6:{s:4:"name";s:5:"debug";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:5:"false";s:7:"lexicon";N;}}', ''),
(209, 'YouTubeFeedUploads', '', 0, 76, 0, '/*\n* @author Phil Steer\n* @package JSONDerulo\n* @site https://github.com/pdincubus/JSONDerulo\n* Fetches YouTube user uploads feed in JSON format and allows templating via chunk\n*/\n\n$cacheTime = 43200; // 12 hours\n$feedUrl = ''http://gdata.youtube.com/feeds/api/users/{username}/uploads?max-results={limit}&alt=json'';\n\n$ch = null;\n\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, 2);\n$excludeEmpty = explode('','', $modx->getOption(''excludeEmpty'', $scriptProperties, ''link''));\n$feeds = explode('','', $modx->getOption(''users'', $scriptProperties, ''''));\n\n$rawFeedData = array();\n\nforeach ($feeds as $username) {\n	$cacheId = ''youtubeuploadsfeed-''.$username;\n  \n	if (($json = $modx->cacheManager->get($cacheId)) === null) {\n		if ($ch === null) {\n			$ch = curl_init();\n			curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n		}\n\n		curl_setopt_array($ch, array(\n		  CURLOPT_URL => str_replace(array(''{username}'', ''{limit}''), array($username, $limit), $feedUrl),\n		));\n\n\n	  	$json = curl_exec($ch);\n		if (empty($json)) {\n			continue;\n		}\n\n		$modx->cacheManager->set($cacheId, $json, $cacheTime);\n	}\n\n	$feed = json_decode($json);\n  \n	if ($feed === null) {\n		continue;\n	}\n    \n    $feeditems = $feed->feed;\n\n	foreach ($feeditems->entry as $video) {\n\n		foreach ($excludeEmpty as $k) {\n			if ($video->$k == '''') {\n				continue 2;\n			}\n		}	  \n\n	  	$videoId = substr($video->id->{''$t''},42);\n\n		$rawFeedData[] = array(\n		  	''published'' => strtotime($video->published->{''$t''}),\n		  	''picture'' => $video->{''media$group''}->{''media$thumbnail''}[0]->url,\n		  	''title'' => $video->title->{''$t''},\n		  	''ytlink'' => $video->link[0]->href,\n		  	''embedlink'' => ''https://www.youtube.com/v/'' .$videoId. ''?fs=1'',\n		    ''author'' => $video->author[0]->name->{''$t''},\n		);\n	}\n}\n\nif ($ch !== null) {\n	curl_close($ch);\n}\n\n$output = '''';\n\nforeach ($rawFeedData as $image) {\n	$output .= $modx->getChunk($tpl, $image);\n}\n\nreturn $output;', 0, 'a:0:{}', ''),
(33, 'Login', 'Displays a login and logout form.', 0, 11, 0, '/**\n * Login\n *\n * Copyright 2010 by Jason Coward <jason@modx.com> and Shaun McCormick\n * <shaun@modx.com>\n *\n * Login is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * Login is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Login; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package login\n */\n/**\n * MODx Login Snippet\n *\n * This snippet handles login POSTs, sending the user back to where they came from or to a specific\n * location if specified in the POST.\n *\n * @package login\n *\n * @property textfield actionKey The REQUEST variable containing the action to take.\n * @property textfield loginKey The actionKey for login.\n * @property textfield logoutKey The actionKey for logout.\n * @property list tplType The type of template to expect for the views:\n *  modChunk - name of chunk to use\n *  file - full path to file to use as tpl\n *  embedded - the tpl is embedded in the page content\n *  inline - the tpl is inline content provided directly\n * @property textfield loginTpl The template for the login view (content based on tplType)\n * @property textfield logoutTpl The template for the logout view (content based on tplType)\n * @property textfield errTpl The template for any errors that occur when processing an view\n * @property list errTplType The type of template to expect for the error messages:\n *  modChunk - name of chunk to use\n *  file - full path to file to use as tpl\n *  inline - the tpl is inline content provided directly\n * @property integer logoutResourceId An explicit resource id to redirect users to on logout\n * @property string loginMsg The string to use for the login action. Defaults to\n * the lexicon string "login".\n * @property string logoutMsg The string to use for the logout action. Defaults\n * to the lexicon string "login.logout"\n */\n$corePath = $modx->getOption(''login.core_path'',$scriptProperties,$modx->getOption(''core_path'',null,MODX_CORE_PATH).''components/login/'');\n$login = $modx->getService(''login'',''Login'',$corePath.''model/login/'',$scriptProperties);\nif (!is_object($login) || !($login instanceof Login)) return '''';\n\n$output = '''';\n$modx->lexicon->load(''login:default'');\n\n/* setup default properties */\n$preHooks = $modx->getOption(''preHooks'',$scriptProperties,'''');\n$loginTpl = $modx->getOption(''loginTpl'',$scriptProperties,''lgnLoginTpl'');\n$logoutTpl = $modx->getOption(''logoutTpl'',$scriptProperties,''lgnLogoutTpl'');\n$loginMsg = $modx->getOption(''loginMsg'',$scriptProperties,'''');\n$logoutMsg = $modx->getOption(''logoutMsg'',$scriptProperties,'''');\n$tplType = $modx->getOption(''tplType'',$scriptProperties,''modChunk'');\n$actionKey = $modx->getOption(''actionKey'',$scriptProperties,''service'');\n$loginKey = $modx->getOption(''loginKey'',$scriptProperties,''login'');\n$logoutKey = $modx->getOption(''logoutKey'',$scriptProperties,''logout'');\n$errorPrefix = $modx->getOption(''errorPrefix'',$scriptProperties,''error'');\n$errTpl = $modx->getOption(''errTpl'',$scriptProperties,''lgnErrTpl'');\n$errTplType = $modx->getOption(''errTplType'',$scriptProperties,''modChunk'');\n$rememberMeKey = $modx->getOption(''rememberMeKey'',$scriptProperties,''rememberme'');\n$loginContext = isset($_REQUEST[''login_context'']) && !empty($_REQUEST[''login_context'']) ? $_REQUEST[''login_context''] : $modx->context->get(''key'');\n$contexts = !empty($scriptProperties[''co