(141, 'getGuestsGUID', '', 0, 61, 0, '/**\n * Wedding site getGuestsGUID\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a guests unique guid\n * \n * Parameters :- \n * \n * $guestId - The id of the guest\n *  \n */\n$guest = $modx->getObject(''weddingUserGuest'', $guestId);\nif (!$guest)\n    return "No guest";\n$guestArray = $guest->toArray();\n$guestName = $guestArray[''name''];\n$guestEmail = $guestArray[''email''];\n$checksum = 0;\n$length = strlen($guestEmail);\nfor ($count = 0; $count <= $length; $count++) {\n\n    $checksum += ord($guestEmail[$count]);\n}\n\n$checksum %= 256;\n$guestNameArray = explode('' '', $guestName);\n$guidArray = array();\n$guidArray[] = $guestNameArray[0];\n$guidArray[] = $guestArray[''id''];\n$guidArray[] = $checksum;\n$guid = implode(''-'', $guidArray);\n\nreturn $guid;', 0, NULL, ''),
(190, 'userGuestsExportInitialisePage', '', 0, 26, 0, '/**\n * Wedding site User Guest processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the guest export page\n */\n\n/* Generate the page navigation links */\n$modx->runSnippet(''getGuestEventPageLinks'');', 0, NULL, ''),
(206, 'TwitterFeed', '', 0, 76, 0, '/*\n* @author Phil Steer\n* @package JSONDerulo\n* @site https://github.com/pdincubus/JSONDerulo\n* Fetches Twitter feed in JSON format and allows templating via chunk\n*/\n\n$cacheTime = 43200; // 12 hours\n$feedUrl = ''https://api.twitter.com/1/statuses/user_timeline.json?screen_name={username}&count={limit}&include_rts={includeRTs}'';\n\n$ch = null;\n\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, 2);\n$includeRTs = $modx->getOption(''includeRTs'', $scriptProperties, 1);\n$excludeEmpty = explode('','', $modx->getOption(''excludeEmpty'', $scriptProperties, ''text''));\n$feeds = explode('','', $modx->getOption(''users'', $scriptProperties, ''twitter''));\n\n$rawFeedData = array();\n\nforeach ($feeds as $username) {\n	$cacheId = ''twitterfeed-''.$username;\n  \n	if (($json = $modx->cacheManager->get($cacheId)) === null) {\n		if ($ch === null) {\n			$ch = curl_init();\n			curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n			curl_setopt($ch,CURLOPT_SSL_VERIFYPEER,0);\n		}\n\n		curl_setopt_array($ch, array(\n			CURLOPT_URL => str_replace(array(''{username}'', ''{limit}'', ''{includeRTs}''), array($username, $limit, $includeRTs), $feedUrl),\n		));\n\n		$json = curl_exec($ch);\n		if (empty($json)) {\n			continue;\n		}\n\n		$modx->cacheManager->set($cacheId, $json, $cacheTime);\n	}\n\n	$feed = json_decode($json);\n\n	if ($feed === null) {\n		continue;\n	}\n\n	$i = 0;\n\n	foreach ($feed as $message) {\n		foreach ($excludeEmpty as $k) {\n			if ($message->$k == '''') {\n				continue 2;\n			}\n		}\n        \n        $rawFeedData[$i] = array(\n            ''id'' => $message->id_str,\n    		''message'' => $message->text,\n    		''created'' => strtotime($message->created_at),\n    		''picture'' => $message->user->profile_image_url,\n    		''title'' => $message->user->name,\n    		''username'' => $message->user->screen_name,\n            ''retweetCount'' => $message->retweet_c