(142, 'userEditRSVPJSInclude', '', 0, 45, 0, '/** \n * Wedding site User Edit RSVP processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n\n/* Common Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) return;\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n$website = ''"'' . $website . ''"'';\n\n/* RSVP */\n$rsvp = $javascriptPath . "js/pages/user/edit/rsvp/rsvp.js";\n$modx->regClientStartupScript($rsvp);\n\n/* Pajinate */\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\n$modx->regClientStartupScript($pajinate);\n\n$context = $modx->context->get(''key'');\n$RSVPAJAXProcessorId = $modx->getOption(''RSVPAJAXProcessorId'');\n$RSVPAJAXProcessorLink = $modx->makeURL($RSVPAJAXProcessorId, $context, "", "full");\n$RSVPAJAXProcessorLink = ''"'' . $RSVPAJAXProcessorLink . ''"'';\n\n$ready = ''<script>\n    \n        WS.rsvpAJAXProcessorLink = '' . $RSVPAJAXProcessorLink . '';       \n       \n</script>'';\n$modx->regClientStartupScript($ready, true);', 0, 'a:0:{}', ''),
(143, 'userEditRSVPAJAXProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit RSVP AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user) {\n    $userId = $user->get(''id'');\n    if ($userId == "")\n        return "Error! No logged in user";\n}\n\n\n/* Get the command type and switch */\nif (!isset($_REQUEST[''command''])) {\n\n    $response[''error''] = "No command specified";\n    $output = json_encode($response);\n    return $output;\n}\n\n$command = $_REQUEST[''command''];\n$response = array();\n\nswitch ($command) {\n\n    case ''read'':\n        \n        /* Get the message details and return them */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $message = $modx->getObject(''modUserMessage'', array(''id'' => $id));\n        if (!$message) {\n\n            $response[''error''] = "No message found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        $guestId = $message->get(''sender'');\n        $guest = $modx->getObject(''weddingUserGuest'', $guestId);\n        $guestName = "Unknown";\n        if ( $guest ) $guestName = $guest->get(''name'');\n        $title = $message->get(''subject'');\n        $content = $message->get(''message'');\n        $response[''error''] = "none";\n        $response[''title''] = $title;\n        $response[''content''] = $content;\n        $response[''id''] = $id;\n        $response[''guestName''] = $guestName;\n        \n        /* Mark the message as read */\n        $message->set(''read'', 1);\n        $message->save();\n        $output = json_encode($response);\n\n        break;\n        \n   case ''delete'':\n        \n        /* Get the blog resource id */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $message = $modx->getObject(''modUserMessage'', array(''id'' => $id));\n        if (!$message) {\n\n            $response[''error''] = "No message found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        \n        /* Delete it */\n        $message->remove();\n        \n        break;\n\n    default:\n        \n        return "Unknown command";\n}\n\n/* Return the response */\nreturn $output;', 0, NULL, ''),
(144, 'userEditRSVPPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit RSVP processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ($userId == "") {\n    $modx->setPlaceholder(''userEditRSVPError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ($weddingUser == null) {\n    $modx->setPlaceholder(''userEditRSVPError'', "Error - No user found please log in");\n    return;\n}\n\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check common form processing */\n$modx->runSnippet(''userEditCommonPostProcessor'');', 0, NULL, ''),
(145, 'getUserRSVPMessageLink', '', 0, 61, 0, '/**\n * Wedding site getRSVPMessageLink\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a users RSVP page link and message count\n * \n * Parameters :- \n * \n *  \n */\n\n/* Get the user details */\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return "Error no authenticated user";\n\n$user = $modx->user;\n$userId = $user->get(''id'');\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n\n/* Get the users messages */\n$c = $modx->newQuery(''modUserMessage'');\n$c->where(array(\n    ''recipient'' => $modx->user->get(''id'')\n));\n$c->andCondition(array(\n    ''read'' => 0\n));\n$messages = $modx->getCollection(''modUserMessage'', $c);\n$count = count($messages);\n\n/* Get the users rsvp page id */\n$c = $modx->newQuery(''weddingUserPage'');\n$c->where(array(''editType:='' => ''rsvp''));\n\n$pages = $weddingUser->getMany(''Pages'', $c);\nif (!$pages) {\n    $modx->setPlaceholder(''userInviteError'', "Error - No user page data found");\n    return;\n}\n$pages = array_values($pages);\n$pageId = $pages[0]->get(''pageId'');\n$params = array(''userPage'' => $pageId);\n\n/* Create the link to the RSVP edit page */\n$RSVPEditPageId = $modx->getOption(''RSVPEditPageId'');\n$pageLink = $modx->makeURL($RSVPEditPageId, $context, $params, ''full'');\n$pageLink = htmlspecialchars_decode($pageLink);\n\n/* Return the output */\n$output = $modx->getChunk(''userRSVPMessageCount'', array(''userRSVPMessageLink'' => $pageLink,\n                                                        ''userRSVPMessageCount'' => $count));\nreturn $output;', 0, 'a:0:{}', ''),
(146, 'getUserManagementPage', '', 0, 61, 0, '/**\n * Wedding site getUserManagementPage\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a users management page link\n * \n * Parameters :- \n * \n * $page - one of guest, event, invite, home, setting\n *  \n */\n\n/* Get the user details */\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return "Error no authenticated user";\n\nswitch ( $page ) {\n    \n    case ''event'' :\n        \n        $eventPageId = $modx->getOption(''eventPageId'');\n        $pageLink = $modx->makeURL($eventPageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    case ''guest'' :\n        \n        $guestPageId = $modx->getOption(''guestPageId'');\n        $pageLink = $modx->makeURL($guestPageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    case ''invite'' :\n        \n        $invitePageId = $modx->getOption(''invitePageId'');\n        $pageLink = $modx->makeURL($invitePageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    case ''setting'' :\n        \n        $settingPageId = $modx->getOption(''settingPageId'');\n        $pageLink = $modx->makeURL($settingPageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    default :\n    case ''home'' :\n    \n        $homePageId = $modx->getOption(''homePageId'');\n        $pageLink = $modx->makeURL($homePageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n}\n\nreturn $pageId;', 0, 'a:0:{}', ''),
(168, 'userCreateWebsite', '', 0, 62, 0, '/**\n * Wedding site userCreateWebsite\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates a users website\n * \n * Parameters :-\n *\n * website - the website name\n * \n * \n * Returns :-\n * \n * None\n *\n * \n */\n/* Sanity check the website */\nif ($website == "") {\n$modx->log(modX::LOG_LEVEL_ERROR, "Create User - No website set");\n    return;\n}\n$modx->log(modX::LOG_LEVEL_ERROR, "Create User - Creating website $website");\n\n/* Create the users pages */\n$modx->runSnippet(''createUserPageSet'', array(''name'' => $website));\n\n/* Create the sub domain */\n$modx->runSnippet(''cPanelAddSubDomain'', array(''subDomainName'' => $website));\n\n/* Create the user directories */\n$modx->runSnippet(''createUserDirectories'');', 0, NULL, ''),
(198, 'packageUpgradeCallback', '', 0, 72, 0, '/**\n * Wedding site Packages processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userPackageError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userPackageError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$userName = $user->get(''username'');\n\n/* Page layout */\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n \n/* Check and process the Paypal IPN callback */\n$paypalPath = $modx->getOption(''ws_paypal.core_path'',null,$modx->getOption(''core_path'').''components/ws_paypal/'');\ninclude_once $paypalPath . "paypal_class.php";\n\n$payPal = new paypal_class();\n\nswitch ($_GET[''action'']) {\n    \n    case ''success'':      // Order was successful...\n   \n      /* Update the database */\n      if ( isset($_REQUEST[''item_number'']) ) {\n          \n          $itemNumber = $_REQUEST[''item_number''];\n          $packageArray = explode(''-'', $itemNumber);\n          $packageType = end($packageArray);\n          $weddingUser->set(''packageType'', $packageType);\n          $weddingUser->save();\n          \n          /* Output success chunk */\n          $output = $modx->getChunk(''packagesPaypalSuccess'');\n          \n      }  else {\n          \n          $modx->log(modX::LOG_LEVEL_ERROR, "WS - Paypal - Item Number not set in Paypal response for user $userName");\n          $output = $modx->getChunk(''packagesPaypalFail'');\n           \n      }\n      \n      break;\n      \n   case ''cancel'':       // Order was canceled...\n\n      // The order was canceled before being completed.\n \n      $modx->log(modX::LOG_LEVEL_ERROR, "WS - Paypal - Order cancelled for user $userName"); \n      \n      /* Output cancel chunk */\n      $output = $modx->getChunk(''packagesPaypalCancelled''); \n       \n      break;\n      \n   case ''ipn'':          // Paypal is calling page for IPN validation...\n      \n      if (!$payPal->validate_ipn()) {\n          \n         $modx->log(modX::LOG_LEVEL_ERROR, "WS - Paypal - IPN failed validation for user $userName"); \n         \n         /* Output validate fail chunk */\n         $output = $modx->getChunk(''packagesPaypalIPNFail'');\n      }\n     \n      break;\n    \n    default:\n       \n       $modx->log(modX::LOG_LEVEL_ERROR, "WS - Paypal - No action for user $userName"); \n       \n       /* Output the no response chunk */\n       $output = $modx->getChunk(''packagesPaypalNoResponse'');\n       \n       break;\n        \n        \n}\n\n$modx->setPlaceholder(''packagesPaypalResponse'', $output);', 0, 'a:0:{}', ''),
(148, 'locator', 'A Simple IP address to country location snippet', 0, 57, 0, '/**\r\n * Locator - A Simple IP address to country location snippet\r\n *\r\n * @author S. Hamblett <steve.hamblett@linux.com> 2012\r\n *\r\n * @package  locator\r\n * \r\n * Parameters :-\r\n * \r\n * ipAddress            - IP address in standard xxx.xxx.xxx.xxx format, if the string\r\n *                               ''remote'' is specified then the incoming IP address as returned by\r\n *                               $_SERVER["REMOTE_ADDR"] is used.\r\n * tpl                        - Output template to use, if not set only placeholders are set\r\n * toJSON               - Return the dataset as a JSON string defaults to 0\r\n\r\n * \r\n * Placeholders, all prefixed with locator :-\r\n * \r\n * ipAddress                     - IP address supplied\r\n * countryCode2               - 2 letter country code based on ISO 3166\r\n * countryCode3               - 3 letter country code based on ISO 3166\r\n * countryName                - Country name based on ISO 3166\r\n * modxCountryCode        - Country code from the MODX countries file(en)\r\n * error                              - Set to ''None'' if no error or an error code string.\r\n * \r\n * Examples : \r\n * \r\n *           Place holders only - [[!locator? &ipAddress=`82.28.123.128` ]] \r\n *           Plus template and incoming IP address  - [[!locator? &ipAddress=`remote` &tpl=`locatorChunk` ]]\r\n *           To JSON - [[!locator? &ipAddress=`82.28.123.128` &toJSON=`1` ]]\r\n */\r\n\r\n/* Add the locator package, we need to do this because of our table prefix */\r\n$modelPath = $modx->getOption(''locator.core_path'', null, $modx->getOption(''core_path'') . ''components/locator/'') . ''model/'';\r\n$modx->addPackage(''locator'', $modelPath, ''lo_'');\r\n\r\n/* Initialise the parameter set */\r\n$ipAddress = $ipAddress == ''remote'' ? $_SERVER["REMOTE_ADDR"] : $ipAddress;\r\n$tpl = !empty($tpl) ? $tpl : '''';\r\n$toJSON = $toJSON == 1 ? true : false;\r\n\r\n/* Convert the IP address */\r\n$ipNumber = ip2long($ipAddress);\r\nif ($ipNumber === false) {\r\n\r\n    $modx->toPlaceholder(''error'', ''IP Address Conversion Error'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''error'' => ''IP Address Conversion Error''));\r\n        return $output;\r\n    }\r\n    return;\r\n}\r\n\r\n/* Do the look up */\r\n$c = $modx->newQuery(''locatorCountry'');\r\n$c->where(array(''ipFrom:<='' => $ipNumber));\r\n$c->andCondition(array(''ipTo:>='' => $ipNumber));\r\n$countryObject = $modx->getObject(''locatorCountry'', $c);\r\nif (!$countryObject) {\r\n\r\n    $modx->toPlaceholder(''error'', ''No Country Found'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''locator.error'' => ''No Country Found''));\r\n        return $output;\r\n    }\r\n    return;\r\n}\r\n\r\n$countryInfo = $countryObject->toArray();\r\n$countryInfo[''ipAddress''] = $ipAddress;\r\n\r\n/* Get the MODX  country number, use ''en'' for ISO 3166 */\r\ninclude $modx->getOption(''core_path'') . "lexicon/country/en.inc.php";\r\nif (!isset($_country_lang)) {\r\n\r\n    $modx->toPlaceholder(''error'', ''No MODX Country File'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''locator.error'' => ''No MODX Country File''));\r\n        return $output;\r\n    }\r\n    return;\r\n}\r\nforeach ($_country_lang as $countryKey => $countryName) {\r\n\r\n    if (trim(strtolower($countryName)) == trim(strtolower($countryInfo[''countryName''])))\r\n        $countryInfo[''modxCountryCode''] = $countryKey;\r\n}\r\n\r\nif ( !isset( $countryInfo[''modxCountryCode''] )) {\r\n    \r\n     $modx->toPlaceholder(''error'', ''No MODX Country Code'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''locator.error'' => ''No MODX Country Code''));\r\n        return $output;\r\n    }\r\n    return;\r\n    \r\n}\r\n/* Return the country information */\r\n$countryInfo[''error''] = ''None'';\r\n$modx->toPlaceholders($countryInfo, ''locator'');\r\n\r\nif ($toJSON) {\r\n\r\n    $countryString = json_encode($countryInfo);\r\n    return $countryString;\r\n}\r\n\r\nif ($tpl != '''') {\r\n    $output = $modx->getChunk($tpl, $countryInfo);\r\n    return $output;\r\n}', 0, NULL, ''),
(149, 'userEditGuestbookInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users guestbook page */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditGuestbookError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditGuestError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$context = $modx->context->get(''key'');\n$userArray = $user->toArray();\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Initialise the common form part */\n$modx->runSnippet(''userEditCommonInitialise'');\n\n/* Include Quip */\n$corePath = $modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'');\n$modx->addPackage(''quip'',$corePath . ''model/'');\n\n/* Get the users comment list */\n$c = $modx->newQuery(''quipComment'');\n$c->where(array(''quipComment.thread'' => $userArray[''username'']));\n$c->sortBy(''createdon'', ''DESC'');\n$comments = $modx->getCollection(''quipComment'',$c);\n\n$output = "";\n$pageId = $modx->resource->get(''id'');\n\nforeach ( $comments as $comment ) {\n    \n    $commentArray = $comment->toArray();\n    $id = $commentArray[''id''];\n    $args = array(''id'' => $id);\n    $commentLink = $modx->makeURL($pageId, $context, $args, "full");\n    $name = $commentArray[''name''];\n    $date = $commentArray[''createdon''];\n    $dateTime = strtotime($date);\n    $dateString = strftime(''%d/%m/%y'', $dateTime);\n     if ( $attributes[''moderateGuestbook''] == 1 ) {\n        \n        /* Moderate output required */\n        $approved = $commentArray[''approved''];\n        $onOff = "on";\n        if ( $approved == 0 ) $onOff = ''off'';\n        $params = array(''userEditGuestbookMessageDate'' => $dateString,\n                        ''userEditGuestbookMessageLink'' => $commentLink,\n                        ''userEditGuestbookMessageName'' => $name,\n                        ''userEditGuestbookMessageAJAXLink'' => $commentLink,\n                       ''userEditGuestbookModerate'' => $onOff);\n        $output .= $modx->getChunk(''userEditGuestbookListItemModerate'', $params);\n        \n    } else {\n        \n    \n        $params = array(''userEditGuestbookMessageDate'' => $dateString,\n                        ''userEditGuestbookMessageLink'' => $commentLink,\n                        ''userEditGuestbookMessageName'' => $name,\n                        ''userEditGuestbookMessageAJAXLink'' => $commentLink);\n        $output .= $modx->getChunk(''userEditGuestbookListItem'', $params);\n    \n    }\n    \n    \n}\n\n/* Set the placeholder */\n$modx->setPlaceholder(''userEditGuestbookMessageList'', $output);', 0, 'a:0:{}', ''),
(150, 'userEditGuestbookJSInclude', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n\n/* Common Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) return;\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n$website = ''"'' . $website . ''"'';\n\n/* Guestbook */\n$guestbook = $javascriptPath . "js/pages/user/edit/guestbook/guestbook.js";\n$modx->regClientStartupScript($guestbook);\n\n/* Pajinate */\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\n$modx->regClientStartupScript($pajinate);\n\n$context = $modx->context->get(''key'');\n$guestbookAJAXProcessorId = $modx->getOption(''guestbookAJAXProcessorId'');\n$guestbookAJAXProcessorLink = $modx->makeURL($guestbookAJAXProcessorId, $context, "", "full");\n$guestbookAJAXProcessorLink = ''"'' . $guestbookAJAXProcessorLink . ''"'';\n\n$ready = ''<script>\n    \n        WS.guestbookAJAXProcessorLink = '' . $guestbookAJAXProcessorLink . '';       \n       \n</script>'';\n$modx->regClientStartupScript($ready, true);', 0, 'a:0:{}', ''),
(151, 'userEditGuestbookAJAXProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user) {\n    $userId = $user->get(''id'');\n    if ($userId == "")\n        return "Error! No logged in user";\n}\n\n\n/* Get the command type and switch */\nif (!isset($_REQUEST[''command''])) {\n\n    $response[''error''] = "No command specified";\n    $output = json_encode($response);\n    return $output;\n}\n\n$command = $_REQUEST[''command''];\n$response = array();\n\n/* Include Quip */\n$corePath = $modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'');\n$modx->addPackage(''quip'',$corePath . ''model/'');\n\nswitch ($command) {\n\n    case ''read'':\n        \n        /* Get the comment details and return them */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $comment = $modx->getObject(''quipComment'', array(''id'' => $id));\n        if (!$comment) {\n\n            $response[''error''] = "No comment found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        $commentArray = $comment->toArray();\n        $response[''name''] = $commentArray[''name''];\n        $response[''email''] = $commentArray[''email''];\n        $response[''body''] = $commentArray[''body''];\n        $response[''error''] = "none";\n        $output = json_encode($response);\n\n        break;\n        \n    case ''delete'':\n        \n        /* Get the comment resource id */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $comment = $modx->getObject(''quipComment'', array(''id'' => $id));\n        if (!$comment) {\n\n            $response[''error''] = "No comment found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        \n        /* Delete it */\n        $comment->remove();\n        \n        break;\n     case ''moderate'':\n\n        /* Get the comment resource id */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $comment = $modx->getObject(''quipComment'', array(''id'' => $id));\n        if (!$comment) {\n\n            $response[''error''] = "No comment found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n\n        /* Moderate it */\n        $approved = $comment->get(''approved'');\n        $newApproved = 0;\n        if ($approved == 0)\n            $newApproved = 1;\n        $comment->set(''approved'', $newApproved);\n        if ($newApproved == 1) {\n\n            $comment->set(''approvedby'', $userId);\n            $approvedOn = strftime(''%Y-%m-%d %T'');\n            $comment->set(''approvedon'', $approvedOn);\n        } else {\n\n            $comment->set(''approvedby'', 0);\n            $comment->set(''approvedon'', ''0000-00-00 00:00:00'');\n        }\n\n        /* Save the comment */\n        $comment->save();\n\n        break;\n    \n    default:\n        \n        return "Unknown command";\n}\n\n/* Return the response */\nreturn $output;', 0, 'a:0:{}', '');
INSERT INTO `modx_site_snippets` (`id`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`) VALUES
(152, 'userEditGuestbookPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditGuestbookError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditGuestbookError'', "Error - No user found please log in");\n    return;\n}\n\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check common form processing */\n$modx->runSnippet(''userEditCommonPostProcessor'');', 0, NULL, ''),
(161, 'userGenerateFEWeblinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEWeblinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for user domains\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n  \n/* Get the parent container */\n$parent = $modx->resource->get(''parent'');\n\n/* Add the back to Blogs link if we are on a blog page */\n$output = "";\n$parentResource = $modx->getObject(''modResource'', $parent);\n$type = $parentResource->get(''longtitle'');\n\nif ($type == ''blog'') {\n\n    /* Add the back to Blogs link */\n    $context = $modx->context->get(''key'');\n    $backToBlogsLink = $modx->makeURL($parent, $context, '''', ''full'');\n    $output = $modx->getChunk(''userFEBlogBackToBlogs'', array(''backToBlogsLink'' => $backToBlogsLink));\n}\n\n/* Call Wayfinder as we would normally but with a ''full'' url */\n$output .= $modx->runSnippet(''Wayfinder'', array(''startId'' => $parent,\n    ''outerTpl'' => ''wed1-Outer'',\n    ''rowTpl'' => ''wed1-Row'',\n    ''level'' => 1,\n    ''hereClass'' => ''active'',\n    ''textOfLinks'' => ''pagetitle'',\n    ''scheme'' => ''full''));\n\n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */\n$urlType = $modx->runSnippet(''userGetURLType'');\nif ($urlType != ''front'')\n    return $output;\n\n/* Get the website name */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Replace the Wayfinder generated ''www'' with the website domain */\n$output = str_replace(''www'', $website, $output);\n\n/* Remove the ''users'' path */\n$output = str_replace("/users/$website", '''', $output);\n\nreturn $output;', 0, 'a:0:{}', ''),
(160, 'userPreviewNavigationLink', '', 0, 62, 0, '/**\n * Wedding site userPreviewNavigationLink\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Adds a back to Admin link if a logged in user is previewing\n * \n *  \n */\n\n/* If logged in add the admin link */\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return;\n$modx->runSnippet(''getGuestEventPageLinks'');\n$output = $modx->getChunk(''userPreviewNavigation'');\nreturn $output;', 0, NULL, ''),
(162, 'userGetURLType', '', 0, 62, 0, '/**\n * Wedding site userGetURLType\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets the users type, this can be one of :-\n * \n * front - an access to the front end pages of a users web site\n * back  - an access to the www section of the site visible to non logged in users\n * user  - an access to the www section of the site whilst logged in\n * \n * Parameters :-\n * \n * None\n *  \n * Returns :-\n * \n * User type, either front, back or user\n */\n\n$pageURL = $_SERVER["SERVER_NAME"] . $_SERVER["REQUEST_URI"];\n\n/* Parse the url */\n$urlArray = parse_url($pageURL);\n$pathArray = explode(''/'', $urlArray[path] );\n\n\n/* Check for the admin(www) domain */\n$hostArray = explode(''.'', $pathArray[0]);\nif ( $hostArray[0] == ''www'') {\n    \n    $context = $modx->context->get(''key'');\n    if ( $modx->user->isAuthenticated($context) ) {\n        \n        /* Logged in user */\n        return ''user'';\n    \n    }\n    \n    /* Must be back end */\n    return ''back'';\n    \n}\n\n/* Must be a front end user */\nreturn ''front'';', 0, NULL, ''),
(163, 'userRedirectToHome', '', 0, 62, 0, '/**\n * Wedding site userRedirectToHOme\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Redirects refs to a user container to the home page depending\n * on the domain we have come from\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - None, issues a redirect\n * \n */\n/* Are we front or back end */\n$userType = $modx->runSnippet(''userGetURLType'');\nif ($userType != ''front'') {\n\n    /* Use first child redirect */\n    $modx->runSnippet(''FirstChildRedirect'');\n    \n} else {\n\n    /* Redirect using the users domain */\n    $website = $modx->runSnippet(''userGetWebsiteFromURL'');\n    $pageURL = $_SERVER["SERVER_NAME"];\n    $redirectURL = ''http://'' . $pageURL . ''/home.html'';\n    $modx->sendRedirect($redirectURL);\n}', 0, 'a:0:{}', ''),
(176, 'userGenerateFEGuestbooklinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEGuestbooklinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for the users guestbook\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n\n/* Get the users username */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Get the user details */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$modxUser = $modx->getObject(''modUser'', $weddingUserId);\nif (!$modxUser) return;\n$userProfile = $modxUser->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$userArray = $modxUser->toArray();\n\n/* Run quip */\n$output = $modx->runSnippet(''Quip'',  array(''thread'' =>  $userArray[''username''],\n                                                                     ''tplComment'' => ''userGuestbookFEComment'',\n                                                                     ''tplComments'' => ''userGuestbookFEComments'',\n                                                                     ''limit'' => 7,\n                                                                     ''sortDir'' => ''DESC'',\n''closeAfter'' => 0));  \n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */        \n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'') return $output;\n\n/* Generate the FE URL link */\n$alias = $modx->resource->get(''alias'');  \n$domain = $modx->getOption(''domain'');  \n$protocol = $modx->getOption(''server_protocol'');\n$tld = $modx->getOption(''tld'');\n$contentType = $modx->resource->get(''contentType'');\n$contentTypeArray = explode(''/'', $contentType);\n$link = $protocol . ''://'' . $website . ''.'' . $domain . ''.'' . $tld . ''/'' . $alias . ''.'' . $contentTypeArray[1];  \n$search = "users/$website/sign-guestbook.html";\n$output = str_replace($search, $link, $output);\n  \nreturn $output;', 0, 'a:0:{}', ''),
(201, 'DeliciousFeed', '', 0, 76, 0, '/*\n* @author Phil Steer\n* @package JSONDerulo\n* @site https://github.com/pdincubus/JSONDerulo\n* Fetches Delicious feed in JSON format and allows templating via chunk\n*/\n\n$cacheTime = 43200; // 12 hours\n$feedUrl = ''http://feeds.delicious.com/v2/json/{username}?count={limit}'';\n\n$ch = null;\n\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, 2);\n$excludeEmpty = explode('','', $modx->getOption(''excludeEmpty'', $scriptProperties, ''d''));\n$feeds = explode('','', $modx->getOption(''users'', $scriptProperties, ''''));\n\n$rawFeedData = array();\n\nforeach ($feeds as $username) {\n	$cacheId = ''deliciousfeed-''.$username;\n  \n	if (($json = $modx->cacheManager->get($cacheId)) === null) {\n		if ($ch === null) {\n			$ch = curl_init();\n			curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n		}\n\n		curl_setopt_array($ch, array(\n		  CURLOPT_URL => str_replace(array(''{username}'', ''{limit}''), array($username, $limit), $feedUrl),\n		));\n\n		$json = curl_exec($ch);\n		if (empty($json)) {\n			continue;\n		}\n\n		$modx->cacheManager->set($cacheId, $json, $cacheTime);\n	}\n\n	$feed = json_decode($json);\n\n	if ($feed === null) {\n		continue;\n	}\n\n\n	foreach ($feed as $item) {\n		foreach ($excludeEmpty as $k) {\n			if ($item->$k == '''') {\n				continue 2;\n			}\n		}\n\n		$rawFeedData[] = array(\n			''title'' => $item->d,\n			''link'' => $item->u,\n			''date'' => strtotime($item->dt),\n		  	''description'' => $item->n,\n			''username'' => $username,\n		);\n\n	}\n \n}\n\nif ($ch !== null) {\n	curl_close($ch);\n}\n\n$output = '''';\nforeach ($rawFeedData as $item) {\n	$output .= $modx->getChunk($tpl, $item);\n}\n\nreturn $output;', 0, 'a:0:{}', ''),
(164, 'userFEDisplayCountdown', '', 0, 50, 0, '/**\n * Wedding site userFEDisplayCountdown\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Displays the countdown if selected by the user\n * \n * Parameters :-\n * \n * none\n *  \n * Returns :-\n * \n * Countdown html\n */\n/* Get the user details */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$displayCountdown = $weddingUserAttributes->get(''displayCountdown'');\nif ( $displayCountdown == 1 ) {\n    \n    $output = $modx->getChunk(''userCountdown'');\n    return $output;\n}', 0, 'a:0:{}', ''),
(165, 'userFEGenerateSocialLinks', '', 0, 50, 0, '/**\n * Wedding site userFEGenerateSocialLinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates a users social links\n * \n * Parameters :-\n * \n * none\n *  \n * Returns :-\n * \n * Social link html\n */\n\n/* Get the user details */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$attributes = $weddingUserAttributes->toArray();\n\n$output = "";\n\n/* Generate each link if not empty */\nif ( $attributes[''socialGoogle''] != "" ) {\n    \n    $socialLink = "https://plus.google.com/" . $attributes[''socialGoogle''];\n    $socialClass = "google";\n    $socialTitle = "Google";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialFacebook''] != "" ) {\n    \n    $socialLink = "http://www.facebook.com/" . $attributes[''socialFacebook''];\n    $socialClass = "face";\n    $socialTitle = "Facebook";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialTwitter''] != "" ) {\n    \n    $socialLink = "http://twitter.com/#!/" . $attributes[''socialTwitter''];\n    $socialClass = "twit";\n    $socialTitle = "Twitter";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialOther1''] != "" ) {\n    \n    $socialLink = "http://" . $attributes[''socialOther1''];\n    $socialClass = "other1";\n    $socialTitle = "Other1";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialOther2''] != "" ) {\n    \n    $socialLink = "https://" . $attributes[''socialOther2''];\n    $socialClass = "other2";\n    $socialTitle = "Other2";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\n/* Return the links */\nreturn $output;', 0, NULL, ''),
(166, 'registrationFormInitialise', '', 0, 20, 0, '/* Get the recaptcah library */\r\n$recaptchaPath = $modx->getOption(''ws_recaptcha.core_path'',null,$modx->getOption(''core_path'').''components/ws_recaptcha/'');\r\ninclude_once $recaptchaPath . "recaptchalib.php";\r\n\r\n/* Set the recaptcha placeholder */\r\n$output = recaptcha_get_html($publickey, $_SESSION[''recaptcha_error'']);\r\n$modx->setPlaceholder(''registration.recaptcha'', $output);\r\n\r\n/* Country processing */\r\n$country = strlen($profile[''country'']) != 0  ? $profile[''country''] : ''None'';\r\ninclude_once $modx->getOption(''core_path'').''lexicon/country/en.inc.php'';\r\n$countriesOutput = "";\r\n\r\nif ( $country == "None" ) {\r\n    \r\n    /* Nothing set, autodetect */\r\n    $modxCountryKey =  array_search(''United Kingdom'', $_country_lang );\r\n    $countryInfoString = $modx->runSnippet(''locator'', array(''ipAddress''=> ''remote'', ''toJSON'' => 1));\r\n    $countryInfo = json_decode( $countryInfoString, true);\r\n    if ( $countryInfo[''error''] == ''None'') {\r\n        \r\n        $modxCountryKey = $countryInfo[''modxCountryCode''];\r\n      \r\n        \r\n    }\r\n    \r\n} else {\r\n    \r\n    $modxCountryKey = array_search($country, $_country_lang );\r\n}\r\n\r\n\r\n\r\nforeach ( $_country_lang as $countryKey => $countryName ) {\r\n    \r\n    $countrySelected = "";\r\n    if (   $modxCountryKey  == $countryKey ) $countrySelected = '' selected="yes" '';\r\n    $countriesOutput .= $modx->getChunk(''userSettingsCountry'', array( \r\n                                          ''countrySelected'' => $countrySelected,\r\n                                          ''countryValue'' => $countryKey,\r\n                                          ''countryName'' => $countryName));\r\n}\r\n$modx->setPlaceholder(''userSettingsCountries'', $countriesOutput);\r\n\r\n/* Registration type */\r\n$registrationType = "normal";\r\nif ( (isset($_REQUEST[''type'']) ) && ($_SESSION[''registrationTestCouple''] == ''valid'') ) {\r\n    \r\n    $registrationType = "testCouple";\r\n    $_SESSION[''registrationTestCouple''] = '''';\r\n}\r\n$modx->setPlaceholder(''registrationType'', $registrationType);', 0, 'a:1:{s:9:"publickey";a:6:{s:4:"name";s:9:"publickey";s:4:"desc";s:24:"Public key for recaptcha";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:40:"6LcMd88SAAAAACah58iR7rlrFwOtNqywmrcZsQbE";s:7:"lexicon";N;}}', ''),
(167, 'userRenameWebsite', '', 0, 62, 0, '/**\n * Wedding site userRenameWebsite\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Renames s a users website\n * \n * Parameters :-\n *\n * website - the new website name\n * existingWebsite - the existing website name\n * \n * \n * Returns :-\n * \n * None\n *\n * \n */\n/* Sanity check the parameters */\nif (($website == "") || ($existingWebsite == ""))\n    return;\n\n/* Change the sub domain name */\n$modx->runSnippet(''cPanelChangeSubDomain'', array(''from'' => $existingWebsite,\n    ''to'' => $website));\n/* Rename the user directory */\n$modx->runSnippet(''renameUserDirectory'', array(''from'' => $existingWebsite,\n    ''to'' => $website));\n\n/* Adjust the existing user alias */\n$c = $modx->newQuery(''modResource'');\n$c->where(array(''alias:='' => $existingWebsite));\n$userPagesParentContainerId = $modx->getOption(''userPagesParentContainerId'');\n$c->andCondition(array(''parent:='' => $userPagesParentContainerId));\n$userContainer = $modx->getObject(''modResource'', $c);\n\nif ($userContainer) {\n\n    $userContainer->set(''alias'', $website);\n    $userContainer->set(''pagetitle'', $website);\n    $userContainer->save();\n    $cacheManager = $modx->getCacheManager();\n    $cacheManager->clearCache(array(\n        "{$resource->context_key}/",\n            ), array(\n        ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n        ''publishing'' => true\n            )\n    );\n}', 0, 'a:0:{}', ''),
(175, 'userGenerateFEBloglinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEBloglinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for the users blogs\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n\n/* Get the parents, which is where we are */\n$parent = $modx->resource->get(''id'');\n\n/* Call getArchive as we would normally but with a ''full'' url*/\n$output = $modx->runSnippet(''getArchives'', array(''parents'' => $parent,\n                                               ''tpl'' => ''userFEBlogArchiveTemplate'',\n                                               ''limit'' => ''50'',\n                                               ''sortby'' => ''editedon'',\n                                               ''includeContent'' => 1  \n                                                ));\n\n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */        \n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'') return $output;\n\n/* Get the website name */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Replace the Wayfinder generated ''www'' with the website domain */\n$output = str_replace(''www'', $website, $output);\n\n/* Remove the ''users'' path */\n$output = str_replace("/users/$website", '''', $output);\n\nreturn $output;', 0, 'a:0:{}', ''),
(169, 'createUserDirectoriesManager', '', 0, 62, 0, '/**\n * Wedding site creatUserDirectoriesManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the users directory set for use by the backend management component\n * \n * Parameters :- \n * \n * id - the wedding user id\n *  \n*/\n\n/* Get the directories */\n$uploadDirectory = $modx->runSnippet(''userGetDirectoryManager'', array(''directory'' => "uploads",\n                                                                      ''id'' => $id));\n$galleryDirectory = $modx->runSnippet(''userGetDirectoryManager'', array(''directory'' => "gallery",\n                                                                      ''id'' => $id)); \n$imagesDirectory = $modx->runSnippet(''userGetDirectoryManager'', array(''directory'' => "images",\n                                                                      ''id'' => $id)); \n\n/* Create them */\n@mkdir($uploadDirectory, 0755, true);\n@mkdir($galleryDirectory, 0755, true);\n@mkdir($imagesDirectory, 0755, true);', 0, NULL, ''),
(170, 'userCreateWebsiteManager', '', 0, 62, 0, '/**\n * Wedding site userCreateWebsiteManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates a users website when called from the backend management component\n * \n * Parameters :-\n *\n * website - the website name\n * id - the wedding user id\n * \n * \n * Returns :-\n * \n * None\n *\n * \n */\n/* Sanity check the website */\nif ($website == "")\n    return;\n\n/* Create the users pages */\n$modx->runSnippet(''createUserPageSetManager'', array(''name'' => $website, ''id'' => $id ));\n\n/* Create the sub domain */\n$modx->runSnippet(''cPanelAddSubDomain'', array(''subDomainName'' => $website));\n\n/* Create the user directories */\n$modx->runSnippet(''createUserDirectoriesManager'', array(''id'' => $id));', 0, NULL, ''),
(171, 'userGetDirectoryManager', '', 0, 62, 0, '/**\n * Wedding site userGetDirectoryManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a users directory path snippet for use by the backend management component\n * \n * Parameters :-\n * \n *  directory : The directory needed, one of uploads, \n *  gallery, images\n *  id - the wedding user id\n *  \n * Returns :-\n * \n * path - the directory path for the user, ''error'' means no user logged in\n */\n\n/* Get the user details */\n$weddingUser = $modx->getObject(''weddingUser'', $id);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n\n/* Construct the path */\n$assetsPath = $modx->getOption(''assets_path'');\n$userDirectory = $assetsPath . "users" .  DIRECTORY_SEPARATOR  .  $website .  DIRECTORY_SEPARATOR  . $directory;\nreturn $userDirectory;', 0, NULL, ''),
(172, 'createUserPageSetManager', '', 0, 62, 0, '/**\n * Wedding site creatUserPageSetManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the user page set under the users area from the default set.\n * For use by the backend maangement component\n * \n * Parameters :- \n * \n * $name - The name of the users desired subdomain e.g. janiceswedding\n * $id - the id of the wedding user\n *  \n */\n\n/* Sanity check the name */\nif ( $name == "" ) $name = "nouser";\n\n$weddingUserObject = $modx->getObject(''weddingUser'', $id);\nif ( $weddingUserObject == null ) return;\n\n    \n/* Duplicate the users page set from the default set */\n$defaultUserPageSetContainerId = $modx->getOption(''defaultUserPageSetContainerId'');\n$defaultUserPage = $modx->getObject(''modResource'', $defaultUserPageSetContainerId);\n$newResource = $defaultUserPage->duplicate(array(\n    ''newName'' => $name,\n    ''duplicateChildren'' => true,\n    ''prefixDuplicate'' => false,\n));\n\n/* Reparent the new resource to the user page area */\n$userPagesParentContainerId = $modx->getOption(''userPagesParentContainerId'');\n$newResource->set(''parent'', $userPagesParentContainerId);\n\n/* Publish this resource and its children */\n$newResource->set(''published'', true);\n$newResource->save();\n\n$newResourceId = $newResource->get(''id'');\n$children = $modx->getcollection(''modResource'', array(''parent'' => $newResourceId));\n\nforeach ( $children as $child ) {\n    \n    $child->set(''published'', true);\n    $child->save();\n    \n    /* Add details to the user page table */\n    $pageObject = $modx->newObject(''weddingUserPage'');\n    $childId = $child->get(''id'');\n    $editType = $child->get(''longtitle'');\n    $pageObject->set(''pageId'', $childId);\n    $pageObject->set(''editType'', $editType);\n    $weddingUserObject->addMany($pageObject, ''Pages'');\n    $weddingUserObject->save();\n}\n\n/* We need to clear the cache to regen the alias map */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n            "{$resource->context_key}/",\n        ),\n        array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n        )\n    );\n\n/* Done */', 0, 'a:0:{}', ''),
(173, 'getPackageTypes', '', 0, 61, 0, '/**\n * Wedding site getPackageTypes\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * \n * Returns :- an array of available package types\n *\n */\n\n$packageArray = explode('','', $packageTypes);\nreturn json_encode($packageArray);', 0, 'a:1:{s:12:"packageTypes";a:6:{s:4:"name";s:12:"packageTypes";s:4:"desc";s:37:"Comma seperated list of package types";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:24:"Trial,Bronze,Silver,Gold";s:7:"lexicon";N;}}', ''),
(177, 'userGenerateFEGuestbookSignlinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEGuestbookSignlinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for the users guestbook signing form\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n\n/* Get the users username */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Get the user details */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$weddingUserArray = $weddingUserAttributes->toArray();\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$modxUser = $modx->getObject(''modUser'', $weddingUserId);\nif (!$modxUser) return;\n$userArray = $modxUser->toArray();\n$moderate = $weddingUserArray[''moderateGuestbook''];\n\n/* Run quip reply */\n$output = $modx->runSnippet(''QuipReply'',  array(''thread'' =>  $userArray[''username''],\n                                                                     ''tplAddComment'' => ''userGuestbookFEMessage'',\n                                                                     ''moderate'' => $moderate,\n                                                                     ''requirePreview'' => 0,\n''closeAfter'' => 0));\n\n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */        \n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'') return $output;\n\n/* Generate the FE URL link */\n$alias = $modx->resource->get(''alias'');  \n$domain = $modx->getOption(''domain'');  \n$protocol = $modx->getOption(''server_protocol'');\n$tld = $modx->getOption(''tld'');\n$contentType = $modx->resource->get(''contentType'');\n$contentTypeArray = explode(''/'', $contentType);\n$link = $protocol . ''://'' . $website . ''.'' . $domain . ''.'' . $tld . ''/'' . $alias . ''.'' . $contentTypeArray[1];  \n$search = "users/$website/sign-guestbook.html";\n$output = str_replace($search, $link, $output);\n\nreturn $output;', 0, 'a:0:{}', ''),
(211, 'TwitterFeedNew', '', 0, 76, 0, '/*\n* @author Phil Steer\n* @package JSONDerulo\n* @site https://github.com/pdincubus/JSONDerulo\n* Fetches Twitter feed in JSON format and allows templating via chunk\n* Updated to work with the new API awfulness\n*/\n\n$cacheTime = 43200; // 12 hours\n\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, 2);\n$includeRTs = $modx->getOption(''includeRTs'', $scriptProperties, 1);\n$excludeEmpty = explode('','', $modx->getOption(''excludeEmpty'', $scriptProperties, ''text''));\n$feeds = $modx->getOption(''users'', $scriptProperties, ''twitter'');\n$consumerKey = $modx->getOption(''consumerKey'', $scriptProperties, '''');\n$consumerSecret =	$modx->getOption(''consumerSecret'', $scriptProperties, '''');\n$accessToken =	$modx->getOption(''accessToken'', $scriptProperties, '''');\n$accessTokenSecret =	$modx->getOption(''accessTokenSecret'', $scriptProperties, '''');\n\n$rawFeedData = array();\n\n$cacheId = ''twitterfeednew-''.$username;\n\nif (($json = $modx->cacheManager->get($cacheId)) === null) {\n		require_once $modx->getOption(''core_path'').''components/jsonderulo/twitteroauth/twitteroauth.php'';\n		$fetch = new TwitterOAuth($consumerKey, $consumerSecret, $accessToken, $accessTokenSecret);\n		$fetch->format = ''json'';\n		$fetch->decode_json = FALSE;\n		$fetch->ssl_verifypeer = FALSE;\n		$json = $fetch->get(''statuses/user_timeline'', array(''include_rts'' => $includeRTs, ''count'' => $limit));\n		\n		if (empty($json)) {\n				continue;\n		}\n		\n		$modx->cacheManager->set($cacheId, $json, $cacheTime);\n}\n\n$feed = json_decode($json);\n\nif ($feed === null) {\n		continue;\n}\n\n$i = 0;\n\nforeach ($feed as $message) {\n		foreach ($excludeEmpty as $k) {\n				if ($message->$k == '''') {\n						continue 2;\n				}\n		}\n		\n		$rawFeedData[$i] = array(\n				''id'' => $message->id_str,\n				''message'' => $message->text,\n				''created'' => strtotime($message->created_at),\n				''picture'' => $message->user->profile_image_url,\n				''title'' => $message->user->name,\n				''username'' => $message->user->screen_name,\n				''retweetCount'' => $message->retweet_count,\n				''isRetweet'' => ''0'',\n		);\n		\n		if(isset($message->retweeted_status)){\n				$rawFeedData[$i][''originalAuthorPicture''] = $message->retweeted_status->user->profile_image_url;\n				$rawFeedData[$i][''originalAuthor''] = $message->retweeted_status->user->name;\n				$rawFeedData[$i][''originalUsername''] = $message->retweeted_status->user->screen_name;\n				$rawFeedData[$i][''isRetweet''] = ''1'';\n				$rawFeedData[$i][''originalId''] = $message->retweeted_status->id;\n		}\n		\n		$i++;\n}\n\n\n$output = '''';\nforeach ($rawFeedData as $message) {\n		$output .= $modx->getChunk($tpl, $message);\n}\n\nreturn $output;', 0, 'a:0:{}', ''),
(178, 'ditsnewsconfirm', 'Confirm (opt-in) newsletter subscription', 0, 63, 0, 'require_once MODX_CORE_PATH.''/components/ditsnews/model/ditsnews/ditsnews.class.php'';\n$ditsnews = new Ditsnews($modx);\n\nif( $ditsnews->confirmSignup() )\n{\n return $modx->lexicon(''ditsnews.subscribers.confirm.success'');\n}\nelse\n{\n return $modx->lexicon(''ditsnews.subscribers.confirm.err'');\n}', 0, NULL, ''),
(179, 'ditsnewsPlaceholders', 'Use this snippet in your newsletter template to convert placeholders', 0, 63, 0, 'if (!isset($modx->ditsnews)) {\n    $modx->addPackage(''ditsnews'', $modx->getOption(''core_path'').''components/ditsnews/model/'');\n    $modx->ditsnews = $modx->getService(''ditsnews'', ''DitsNews'', $modx->getOption(''core_path'').''components/ditsnews/model/ditsnews/'');\n}\n \n$dn =& $modx->ditsnews;\nreturn $dn->setPlaceholders($scriptProperties);', 0, NULL, ''),
(180, 'ditsnewssignup', 'Add newsletter subscriber (or return error)', 0, 63, 0, 'require_once MODX_CORE_PATH.''/components/ditsnews/model/ditsnews/ditsnews.class.php'';\n$ditsnews = new Ditsnews($modx);\n\nif( $ditsnews->signup($scriptProperties[''fields''], $scriptProperties[''confirmPage'']) ) {\n  return true;\n}\nelse {\n  $scriptProperties[''hook'']->errors[''signup''] = $ditsnews->errormsg;\n  return false;\n}', 0, NULL, ''),
(181, 'ditsnewsunsubscribe', 'Removes subscriber', 0, 63, 0, 'require_once MODX_CORE_PATH.''/components/ditsnews/model/ditsnews/ditsnews.class.php'';\n$ditsnews = new Ditsnews($modx);\n\nif( $ditsnews->unsubscribe() )\n{\n return $modx->lexicon(''ditsnews.subscribers.unsubscribe.success'');\n}\nelse\n{\n return $modx->lexicon(''ditsnews.subscribers.unsubscribe.err'');\n}', 0, NULL, ''),
(182, 'packageExpiryCheck', '', 0, 33, 0, '/**\n * Wedding site packageExpiryCheck\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Site package expiry check snippet\n * \n * Called on a regular(cron) basis from the Dits Newsletter cron interface\n * \n * Parameters :-\n * \n *  None:\n */\n\n/* Get all the ''trial users'' who haven''t been processed */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''packageType:='' => ''Trial''));\n$c->andCondition(array(''expiryCount:<''  => 3));\n$weddingUsers = $modx->getCollection(''weddingUserAttribute'', $c);\n\nif ( count($weddingUsers) == 0 ) {\n    \n    $modx->log(modX::LOG_LEVEL_ERROR,''WS - Package expiry check - no users found for expiry'');\n    return;\n}\n$number = count($weddingUsers);\n$modx->log(modX::LOG_LEVEL_ERROR,"WS - Package expiry check - $number users found for expiry");\n \n/* Check for test mode */\nif ( $testMode == 1 ) {\n    \n    $modx->log(modX::LOG_LEVEL_ERROR,''WS - Package expiry check - test mode - exiting'');\n    return;\n}\n\n/* Process each user */\n$now = time();\nforeach ($weddingUsers as $weddingUser  ) {\n    \n    $registrationDate = $weddingUser->get(''registrationDate'');\n    $userId = $weddingUser->get(''user'');\n    $modxUser = $modx->getObject(''modUser'', $userId);\n    if (!$modxUser) continue;\n  \n    /* Check for test couples */\n    $testCouple = $weddingUser->get(''hearAbout'');\n    if ( $testCouple == ''Yes'') {\n        \n        $oneYear = strtotime("+1 year", $registrationDate);\n        if ($now >= $oneYear) {\n            \n            $weddingUser->set(''hearAbout'', '''');\n            \n        } else {\n            \n            continue;\n            \n        }\n        \n    }\n  \n    $expiryCount = $weddingUser->get(''expiryCount'');\n    $userName = $modxUser->get(''username'');\n    $modx->log(modX::LOG_LEVEL_ERROR,"WS - Package expiry check - user - $userName");\n \n    switch ( $expiryCount ) {\n        \n       /* 2 days before */\n        case 0: \n            \n            $fiveDays = strtotime("+5 day",  $registrationDate);\n            if ( $now >= $fiveDays ) {\n                $modx->log(modX::LOG_LEVEL_ERROR,"WS - Package expiry check - user - $userName - 5 days");   \n                $weddingUser->set(''expiryCount'', 1);\n                $weddingUser->save();\n                $userName = $modxUser->get(''username'');\n                $firstName = $weddingUser->get(''firstName'');\n                $message = $modx->getChunk(''userPackageDaysLeft'', array(''daysLeft'' => 2,\n                                                                                                                   ''firstName'' => $firstName));\n                $modx->runSnippet(''mailMan'', array(''to'' => $userName,\n                                                                         ''subject'' => $subject,\n                                                                         ''message'' => $message));\n                \n            }\n            \n            break;\n        \n        /* 1 day before */\n        case 1:\n             \n             $sixDays = strtotime("+6 day",  $registrationDate);\n             if ( $now >= $sixDays ) {\n                $modx->log(modX::LOG_LEVEL_ERROR,"WS - Package expiry check - user - $userName - 6 days");     \n                $weddingUser->set(''expiryCount'', 2);\n                $weddingUser->save();\n                $userName = $modxUser->get(''username'');\n                $firstName = $weddingUser->get(''firstName'');\n                $message = $modx->getChunk(''userPackageDaysLeft'', array(''daysLeft'' => 1,\n                                                                                                                   ''firstName'' => $firstName));\n                $modx->runSnippet(''mailMan'', array(''to'' => $userName,\n                                                                         ''subject'' => $subject,\n                                                                         ''message'' => $message));\n                \n            }\n            \n            break;\n        \n       /* Block check */\n        case 2:\n            \n             $sevenDays = strtotime("+7 day",  $registrationDate);\n             if ( $now >= $sevenDays ) {\n                 $modx->log(modX::LOG_LEVEL_ERROR,"WS - Package expiry check - user - $userName - 7 days");    \n                 $weddingUser->set(''expiryCount'', 3);\n                 $weddingUser->save();\n                 $userProfile = $modxUser->getOne(''Profile'');\n                 $userProfile->set(''blocked'', 1);\n                 $userProfile->save();\n                 \n             }\n             \n            break;\n        \n        default:\n            \n    }\n    \n}\n\nreturn;', 0, 'a:2:{s:7:"subject";a:6:{s:4:"name";s:7:"subject";s:4:"desc";s:37:"The subject of package reminder mails";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:61:"The Wedding Website Group - Trial package expiration reminder";s:7:"lexicon";N;}s:8:"testMode";a:6:{s:4:"name";s:8:"testMode";s:4:"desc";s:67:"If set to 1 the expiry check is not performed, used in development.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:1:"0";s:7:"lexicon";N;}}', ''),
(183, 'userValidateRegistration', '', 0, 62, 0, '/**\n * Wedding site userValidateRegistration\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Validates a users registration from the email validation link.\n * \n *  \n *  Parameters :- \n * uid - User id \n * \n * Returns - None, issues a redirect or outputs  a chunk\n * \n */\n\n/* Check for a UID */\nif ( !isset($_REQUEST[''uid''] )) {\n    \n    $output = $modx->getChunk(''registrationValidateNormal'');\n    return $output;\n}\n\n/* Process the registration */\n$uid = $_REQUEST[''uid''];\nif ( $uid == "" ) {\n    \n    $output = $modx->getChunk(''registrationValidateNoUser'');\n    return $output;\n    \n}\n$user = $modx->getObject(''modUser'', $uid);\nif (!$user) {\n    \n    $output = $modx->getChunk(''registrationValidateNoUser'');\n    return $output;\n}\n\n$user->profile = $user->getOne(''Profile'');\n$weddingUser = $modx->getObject(''weddingUser'', $uid);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n\n/* Check for a registration date of 0 */\nif ( $weddingUser->attributes->get(''registrationDate'') != 0 ) {\n    \n    $output = $modx->getChunk(''registrationValidateAlreadyRegistered'');\n    return $output;\n    \n}\n\n/* Create the users website and associated data */\n$website = $weddingUser->attributes->get(''website'');\n$modx->runSnippet(''userCreateWebsiteRegistration'', array(''website'' => $website));\n\n/* Ok, complete the registration */\n$weddingUser->attributes->set(''registrationDate'', time());\n$weddingUser->save();\n$user->profile->set(''blocked'', 0);\n$user->save();\n\n  \n/* Redirect to login */\n$context = $modx->context->get(''key'');\n$userLoginPage = $modx->getOption(''userLoginPage'');\n$redirectURL = $modx->makeURL($userLoginPage, $context, "", ''full'');\n$modx->sendRedirect($redirectURL);', 0, 'a:0:{}', ''),
(184, 'userGuestsExportPostProcessor', '', 0, 26, 0, '/**\n * Wedding site Guests export  processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/*  Export the guest list and guest data if requested\n */\n\n/* Check for a submit */\nif ($_REQUEST[''userGuestsExportSubmit''] == ''Export'') {\n\n    /* Get the user details */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ($userId == "") {\n        $modx->setPlaceholder(''userGuestError'', "Error - No MODX user found please log in");\n        return;\n    }\n\n    $weddingUser = $modx->getObject(''weddingUser'', $userId);\n    if (!$weddingUser) {\n        $modx->setPlaceholder(''userGuestError'', "Error - No Wedding user found please log in");\n        return;\n    }\n\n    /* Get a file and open it */\n    $filename = tempnam(sys_get_temp_dir(), "csv");\n    $file = fopen($filename, "w");\n\n    /* Get the guests and events */\n    $guests = $weddingUser->getMany(''Guests'');\n    $events = $weddingUser->getMany(''Events'');\n\n    /* Build the column headers */\n    $eventIdArray = array();\n    $columnHeader = array();\n    $coulumnHeader[] = ''DBID'';\n    $coulumnHeader[] = ''NAME'';\n    foreach ($events as $event) {\n\n        if ($event->get(''active'') == 1) {\n\n            $name = $event->get(''name'');\n            $coulumnHeader[] = strtoupper($name);\n            $eventId = $event->get(''id'');\n            $eventIdArray[] = $eventId;\n        }\n    }\n\n    $coulumnHeader[] = ''GUEST OF'';\n    $coulumnHeader[] = ''GUID'';\n    $coulumnHeader[] = ''ACTIVE'';\n\n    /* Write them out */\n    fputcsv($file, $coulumnHeader);\n\n    /* Construct the row data */\n    foreach ($guests as $guest) {\n\n        $guestArray = $guest->toArray();\n        \n        $rowData = array();\n        $rowData[] = $guestArray[''id''];\n        $rowData[] = $guestArray[''name''];\n        \n\n        /* Get the event array into a string we can use in the table body for this guest */\n        $attendOutput = "";\n        foreach ($eventIdArray as $id) {\n\n            /* See if we are invited and what attendance there has been */\n            $guestEvent = $modx->getObject(''guestEvents'', array(\n                ''guestId'' => $guestArray[''id''],\n                ''eventId'' => $id));\n\n            if (!$guestEvent) {\n\n                $attend = "Not Invited";\n            } else {\n\n                /* Invited , check status */\n                $attend = "Invited";\n                $attendance = $guestEvent->get(''willAttend'');\n                if ($attendance == 1) {\n\n                    $attend = "Yes";\n                } else {\n\n                    $rsvpOnline = $guestEvent->get(''RSVPdOnline'');\n                    $rsvpManual = $guestEvent->get(''RSVPdManual'');\n                    if ($rsvpOnline || $rsvpManual) {\n\n                        $attend = "No";\n                    }\n                }\n            }\n            $rowData[] = $attend;\n        }\n        \n        $rowData[] = $guestArray[''guestOf''];\n        $rowData[] = $modx->runSnippet(''getGuestsGUID'', array(''guestId'' => $guestArray[''id''] ));\n        $rowData[] = $guestArray[''active''] ? "Yes" : "No";\n        \n        /* Write the row out*/\n        fputcsv($file, $rowData);\n    }\n    \n    /* Output guest details if requested */\n    if ($_REQUEST[''userGuestsExportGuestDetails''] == ''1'') {\n        \n        fputs($file, PHP_EOL);\n        fputs($file, PHP_EOL);\n        \n        /* Column headers */\n        $guestDetailsHeader = array();\n        $guestDetailsHeader[] = ''ID'';\n        $guestDetailsHeader[] = ''NAME'';\n        $guestDetailsHeader[] = ''EMAIL'';\n        $guestDetailsHeader[] = ''ADDRESS 1'';\n        $guestDetailsHeader[] = ''ADDRESS 2'';\n        $guestDetailsHeader[] = ''CITY'';\n        $guestDetailsHeader[] = ''POSTCODE'';\n        $guestDetailsHeader[] = ''TELEPHONE'';\n        $guestDetailsHeader[] = ''GUEST OF'';\n        $guestDetailsHeader[] = ''PARTY'';\n        $guestDetailsHeader[] = ''ACTIVE'';\n        \n        /* Write them out */\n        fputcsv($file, $guestDetailsHeader);\n        \n        /* Construct the row data */\n        foreach ($guests as $guest) {\n\n            $guestDetailsArray = $guest->toArray();\n        \n            $rowDetailsData = array();\n            $rowDetailsData[] = $guestDetailsArray[''id''];\n            $rowDetailsData[] = $guestDetailsArray[''name''];\n            $rowDetailsData[] = $guestDetailsArray[''email''];\n            $rowDetailsData[] = $guestDetailsArray[''address1''];\n            $rowDetailsData[] = $guestDetailsArray[''address2''];\n            $rowDetailsData[] = $guestDetailsArray[''city''];\n            $rowDetailsData[] = $guestDetailsArray[''postCode''];\n            $rowDetailsData[] = $guestDetailsArray[''telephone''];\n            $rowDetailsData[] = $guestDetailsArray[''guestOf''];\n            $rowDetailsData[] = $guestDetailsArray[''party''];\n            $rowDetailsData[] = $guestDetailsArray[''active''] ? "Yes" : "No";\n            \n            /* Write the row out*/\n            fputcsv($file, $rowDetailsData);\n            \n        }\n    }\n\n    /* Close the file */\n    fclose($file);\n\n    /* Set the content of the output page */\n    $output = file_get_contents($filename);\n    unlink($filename);\n\n    $resource = $modx->getObject(''modResource'', array(''id'' => $userGuestCSVPage));\n    if ($resource) {\n\n        $resource->set(''content'', $output);\n        $resource->save();\n    }\n\n    /* Redirect to the CSV generation page */\n    $context = $modx->context->get(''key'');\n    $pageURL = $modx->makeURL($userGuestCSVPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:1:{s:16:"userGuestCSVPage";a:6:{s:4:"name";s:16:"userGuestCSVPage";s:4:"desc";s:34:"Page id of the CSV generation page";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"1587";s:7:"lexicon";N;}}', ''),
(210, 'ZooToolFeed', '', 0, 76, 0, '/*\n* @author Phil Steer\n* @package JSONDerulo\n* @site https://github.com/pdincubus/JSONDerulo\n* Fetches ZooTool feed in JSON format and allows templating via chunk\n*/\n\n$cacheTime = 43200; // 12 hours\n$feedUrl = ''http://zootool.com/api/users/items/?username={username}&apikey={apikey}&limit={limit}'';\n\n$ch = null;\n\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, 2);\n$excludeEmpty = explode('','', $modx->getOption(''excludeEmpty'', $scriptProperties, ''image''));\n$feeds = explode('','', $modx->getOption(''users'', $scriptProperties, ''''));\n$apiKey = $modx->getOption(''apiKey'', $scriptProperties, '''');\n\n$rawFeedData = array();\n\nforeach ($feeds as $username) {\n	$cacheId = ''zootoolfeed-''.$username;\n  \n	if (($json = $modx->cacheManager->get($cacheId)) === null) {\n		if ($ch === null) {\n			$ch = curl_init();\n			curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n		}\n\n		curl_setopt_array($ch, array(\n		  CURLOPT_URL => str_replace(array(''{apikey}'', ''{username}'', ''{limit}''), array($apiKey, $username, $limit), $feedUrl),\n		));\n\n\n	  	$json = curl_exec($ch);\n		if (empty($json)) {\n			continue;\n		}\n\n		$modx->cacheManager->set($cacheId, $json, $cacheTime);\n	}\n\n	$feed = json_decode($json);\n  \n	if ($feed === null) {\n		continue;\n	}\n\n	foreach ($feed as $image) {\n		foreach ($excludeEmpty as $k) {\n			if ($image->$k == '''') {\n				continue 2;\n			}\n		}\n\n		$rawFeedData[] = array(\n			''date'' => $image->added,\n			''picture'' => $image->image,\n			''title'' => $image->title,\n			''username'' => $username,\n		  	''referrer'' => $image->referer,\n		  	''permalink'' => $image->permalink,\n		);\n\n	}\n}\n\nif ($ch !== null) {\n	curl_close($ch);\n}\n\n$output = '''';\nforeach ($rawFeedData as $image) {\n	$output .= $modx->getChunk($tpl, $image);\n}\n\nreturn $output;', 0, 'a:0:{}', ''),
(187, 'createUserPageSetRegistration', '', 0, 62, 0, '/**\n * Wedding site creatUserPageSetRegistration\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the user page set under the users area from the default set.\n * \n * Parameters :- \n * \n * $name - The name of the users desired subdomain e.g. janiceswedding\n *  \n */\n\n/* Sanity check the name */\nif ( $name == "" ) $name = "nouser";\n\n$weddingUserAttribute = $modx->getObject(''weddingUserAttribute'', array(''website'' => $name));\nif ( !$weddingUserAttribute ) return;\n\n$userId = $weddingUserAttribute->get(''user'');\n$weddingUserObject = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUserObject == null ) return;\n\n    \n/* Duplicate the users page set from the default set */\n$defaultUserPageSetContainerId = $modx->getOption(''defaultUserPageSetContainerId'');\n$defaultUserPage = $modx->getObject(''modResource'', $defaultUserPageSetContainerId);\n$newResource = $defaultUserPage->duplicate(array(\n    ''newName'' => $name,\n    ''duplicateChildren'' => true,\n    ''prefixDuplicate'' => false,\n));\n\n/* Reparent the new resource to the user page area */\n$userPagesParentContainerId = $modx->getOption(''userPagesParentContainerId'');\n$newResource->set(''parent'', $userPagesParentContainerId);\n\n/* Publish this resource and its children */\n$newResource->set(''published'', true);\n$newResource->save();\n\n$newResourceId = $newResource->get(''id'');\n$children = $modx->getcollection(''modResource'', array(''parent'' => $newResourceId));\n\nforeach ( $children as $child ) {\n    \n    $child->set(''published'', true);\n    $child->save();\n    \n    /* Add details to the user page table */\n    $pageObject = $modx->newObject(''weddingUserPage'');\n    $childId = $child->get(''id'');\n    $editType = $child->get(''longtitle'');\n    $pageObject->set(''pageId'', $childId);\n    $pageObject->set(''editType'', $editType);\n    $weddingUserObject->addMany($pageObject, ''Pages'');\n    $weddingUserObject->save();\n}\n\n/* We need to clear the cache to regen the alias map */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n            "{$resource->context_key}/",\n        ),\n        array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n        )\n    );\n\n/* Done */', 0, 'a:0:{}', ''),
(188, 'createUserDirectoriesRegistration', '', 0, 62, 0, '/**\n * Wedding site creatUserDirectoriesRegistration\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the users directory set\n * \n * Parameters :- \n * \n * website : The website name of the user\n * \n * None\n *  \n*/\n\n/* Get the directories */\n$uploadDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => ''uploads'', ''website'' => $website));\n$galleryDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => ''gallery'', ''website'' => $website)); \n$imagesDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => ''images'', ''website'' => $website)); \n\n/* Create them */\n@mkdir($uploadDirectory, 0755, true);\n@mkdir($galleryDirectory, 0755, true);\n@mkdir($imagesDirectory, 0755, true);', 0, NULL, '');
INSERT INTO `modx_site_snippets` (`id`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`) VALUES
(212, 'AppDotNetFeed', '', 0, 76, 0, '/*\n* @author Phil Steer\n* @package JSONDerulo\n* @site https://github.com/pdincubus/JSONDerulo\n* Fetches Twitter feed in JSON format and allows templating via chunk\n*/\n\n$cacheTime = 43200; // 12 hours\n$feedUrl = ''https://alpha-api.app.net/stream/0/users/{userId}/posts?count={limit}'';\n\n$ch = null;\n\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, 2);\n$excludeEmpty = explode('','', $modx->getOption(''excludeEmpty'', $scriptProperties, ''text''));\n$feeds = explode('','', $modx->getOption(''userId'', $scriptProperties, ''pd''));\n\n$rawFeedData = array();\n\nforeach ($feeds as $user) {\n		$cacheId = ''appdotnetfeed-''.$user;\n		\n		if (($json = $modx->cacheManager->get($cacheId)) === null) {\n				if ($ch === null) {\n						$ch = curl_init();\n						curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n						curl_setopt($ch,CURLOPT_SSL_VERIFYPEER,0);\n				}\n				\n				curl_setopt_array($ch, array(\n						CURLOPT_URL => str_replace(array(''{userId}'', ''{limit}''), array($user, $limit), $feedUrl),\n				));\n				\n				$json = curl_exec($ch);\n				if (empty($json)) {\n						continue;\n				}\n				\n				$modx->cacheManager->set($cacheId, $json, $cacheTime);\n		}\n\n		$feed = json_decode($json);\n\n		if ($feed === null) {\n				continue;\n		}\n\n		$feeditems = $feed->data;\n\n		$i = 0;\n\n		foreach ($feeditems as $message) {\n				foreach ($excludeEmpty as $k) {\n						if ($message->$k == '''') {\n								continue 2;\n						}\n				}\n\n				$rawFeedData[$i] = array(\n						''id'' => $message->id,\n    		''text'' => $message->text,\n						''html'' => $message->html,\n						''created'' => strtotime($message->created_at),\n						''picture'' => $message->user->avatar_image->url,\n						''title'' => $message->user->name,\n						''username'' => $message->user->username,\n						''profile'' => $message->user->canonical_url,\n 					''postUrl'' => $message->canonical_url,\n				);\n\n				$i++;\n		}\n}\n\nif ($ch !== null) {\n		curl_close($ch);\n}\n\n$output = '''';\nforeach ($rawFeedData as $message) {\n		$output .= $modx->getChunk($tpl, $message);\n}\n\nreturn $output;', 0, 'a:0:{}', ''),
(197, 'packagesInitialisePage', '', 0, 72, 0, '/**\n * Wedding site Registration processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Get the country */\n$alias = $modx->resource->get(''alias'');\n$aliasArray = explode(''-'', $alias);\n$country = end($aliasArray);\n\n/* Button chunk names */\n$bronzeName = ''packagesBronze'' . $country;\n$silverName = ''packagesSilver'' . $country;\n$goldName = ''packagesGold'' . $country;\n\n/* Check for a logged in user */\n$placeholders = array();\n$context = $modx->context->get(''key'');\nif (!$modx->user->isAuthenticated($context)) {\n\n    $bronze = $modx->getChunk($bronzeName, $placeholders);\n    $silver = $modx->getChunk($silverName, $placeholders);\n    $gold = $modx->getChunk($goldName, $placeholders);\n} else {\n\n    /* Get the users details for paypal */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ($userId == "") {\n        $modx->setPlaceholder(''userPackageError'', "Error - No user found please log in");\n        return;\n    }\n    $weddingUser = $modx->getObject(''weddingUser'', $userId);\n    if ($weddingUser == null) {\n        $modx->setPlaceholder(''userPackageError'', "Error - No user found please log in");\n        return;\n    }\n\n    $userProfile = $user->getOne(''Profile'');\n    $profile = $userProfile->toArray();\n    $weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n    $attributes = $weddingUser->attributes->toArray();\n    \n    /* Set the placeholder values */\n   $itemNumberBronze = $userId. ''-Bronze'' ;\n   $itemNumberSilver = $userId. ''-Silver'' ;\n   $itemNumberGold = $userId. ''-Gold'' ;\n   $placeholders[''itemNumberBronze''] = $itemNumberBronze;\n   $placeholders[''itemNumberSilver''] = $itemNumberSilver;\n   $placeholders[''itemNumberGold''] = $itemNumberGold;\n   \n   \n    /* Switch on Package type */\n    $packageType = $attributes[''packageType''];\n    switch ( $packageType) {\n        \n        case ''Trial'' :\n            \n            /* All valid */\n            $bronze = $modx->getChunk($bronzeName, $placeholders);\n            $silver = $modx->getChunk($silverName, $placeholders);\n            $gold = $modx->getChunk($goldName, $placeholders);\n            break;    \n        \n        case ''Bronze'' :\n            \n            /* Gold and Silver valid */\n            $bronze = $modx->getChunk(''packagesInvalid'');\n            $silver = $modx->getChunk($silverName, $placeholders);\n            $gold = $modx->getChunk($goldName, $placeholders);\n            break;\n        \n        case ''Silver'' :\n            \n             /* Gold valid */\n            $bronze = $modx->getChunk(''packagesInvalid'');\n            $silver = $modx->getChunk(''packagesInvalid'');\n            $gold = $modx->getChunk($goldName, $placeholders);\n            break;\n        \n        case ''Gold'' :\n            \n            /* None valid */\n            $bronze = $modx->getChunk(''packagesInvalid'');\n            $silver = $modx->getChunk(''packagesInvalid'');\n            $gold = $modx->getChunk(''packagesInvalid'');\n            break;\n    }\n    \n}\n\n\n/* Set the placeholders */\n$modx->setPlaceholder(''packagesBronze'', $bronze);\n$modx->setPlaceholder(''packagesSilver'', $silver);\n$modx->setPlaceholder(''packagesGold'', $gold);', 0, NULL, ''),
(191, 'userGuestsExportJSInclude', '', 0, 26, 0, '/**\n * Wedding site Guest Export processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* WS Javascript */\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n\n/* Guests  */\n$export = $javascriptPath . "js/pages/user/guests/export.js";\n$modx->regClientStartupScript($export);', 0, NULL, ''),
(192, 'userFEBlogPage', '', 0, 52, 0, '/**\n * Wedding site User Front End Blog processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Get the users website */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Get the blog count */\n$blogCount = $modx->runSnippet(''getUserBlogCount'', array(''website'' => $website));\n\n/* Select the chunk */\n$chunk = $modx->getChunk(''userFEBlogBlogsExist'');\nif ( intval($blogCount) == 0 ) $chunk = $modx->getChunk(''userFEBlogNoBlogs'');\n\n$modx->setPlaceholder(''userFEBlogPage'', $chunk);', 0, 'a:0:{}', ''),
(193, 'getUserBlogCount', '', 0, 61, 0, '/**\n * Wedding site getUserBlogCount\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters - website the users website name\n * Returns :- a count of the users blogs\n */\n\n/* Get the Wedding user */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return ''0'';\n$attributes = $weddingUserAttributes->toArray();\n$weddingUserObject = $modx->getObject(''weddingUser'', $attributes[''user'']);\nif (!$weddingUserObject) return ''0'';\n \n/* Get the users blog page */\n$c = $modx->newQuery(''weddingUserPage'');\n$c->where(array(''editType:='' => ''blog''));\n$pages = $weddingUserObject->getMany(''Pages'', $c);\nif (!$pages) return ''0'';\n$pages = array_values($pages);\n$pageId = $pages[0]->get(''pageId'');\n  \n/* Get the children, sorted by menuindex */\n$c = $modx->newQuery(''modResource'');\n$c->where((array(''parent:=''  => $pageId)));\n$children = $modx->getCollection(''modResource'',$c);\n  \nreturn count($children);', 0, 'a:0:{}', ''),
(194, 'userGenerateFEWebsiteLink', '', 0, 62, 0, '/**\r\n * Wedding site userGenerateFEWebsiteLink\r\n *\r\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\r\n *\r\n * Generates a link to the users website for quick access\r\n * \r\n *  \r\n *  Parameters :- None\r\n * \r\n * \r\n * Returns - html for the generated link\r\n * \r\n */\r\n\r\n/* Get the user details */\r\n$context = $modx->context->get(''key'');\r\nif ( !$modx->user->isAuthenticated($context) ) return;\r\n$user = $modx->user;\r\n$userId = $user->get(''id'');\r\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\r\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\r\n$attributes = $weddingUser->attributes->toArray();\r\n\r\n/* Generate the link */\r\n$website = $attributes[''website''];\r\n$domain = $modx->getOption(''domain'');\r\n$tld = $modx->getOption(''tld'');\r\n$linkTitle = $website . ''.'' . $domain . ''.'' . $tld;\r\n$link = ''http://'' . $linkTitle;\r\n$output = $modx->getChunk(''userWebsiteCopyLink'', array(''linktitle'' => $linkTitle,\r\n                                                       ''link'' => $link));\r\nreturn $output;', 0, 'a:0:{}', ''),
(202, 'FlickrFeed', '', 0, 76, 0, '/*\n* @author Phil Steer\n* @package JSONDerulo\n* @site https://github.com/pdincubus/JSONDerulo\n* Fetches Flickr feed in JSON format and allows templating via chunk\n*/\n$cacheTime = 43200; // 12 hours\n$feedUrl = ''http://api.flickr.com/services/rest/?method=flickr.photos.search&format=json&nojsoncallback=1&api_key={apikey}&user_id={userid}&per_page={limit}&extras=url_m,url_l,date_upload'';\n\n$ch = null;\n\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, 2);\n$excludeEmpty = explode('','', $modx->getOption(''excludeEmpty'', $scriptProperties, ''url_m''));\n$feeds = explode('','', $modx->getOption(''users'', $scriptProperties, ''3''));\n$apiKey = $modx->getOption(''apiKey'', $scriptProperties, '''');\n$userName = $modx->getOption(''userName'', $scriptProperties, '''');\n\n$rawFeedData = array();\n\nforeach ($feeds as $userId) {\n	$cacheId = ''flickrfeed-''.$userId;\n  \n	if (($json = $modx->cacheManager->get($cacheId)) === null) {\n		if ($ch === null) {\n			$ch = curl_init();\n			curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n		}\n\n		curl_setopt_array($ch, array(\n		  CURLOPT_URL => str_replace(array(''{apikey}'', ''{userid}'', ''{limit}''), array($apiKey, $userId, $limit), $feedUrl),\n		));\n\n\n	  	$json = curl_exec($ch);\n		if (empty($json)) {\n			continue;\n		}\n\n		$modx->cacheManager->set($cacheId, $json, $cacheTime);\n	}\n\n	$feed = json_decode($json);\n  \n	if ($feed === null) {\n		continue;\n	}\n\n	foreach ($feed->photos->photo as $photo) {\n		foreach ($excludeEmpty as $k) {\n			if ($photo->$k == '''') {\n				continue 2;\n			}\n		}\n\n		$rawFeedData[] = array(\n			''id'' => $photo->id,\n			''created'' => $photo->dateupload,\n			''picture'' => $photo->url_m,\n			''picturelarge'' => $photo->url_l,\n			''title'' => $photo->title,\n			''username'' => $userName,\n		);\n	}\n}\n\nif ($ch !== null) {\n	curl_close($ch);\n}\n\n$output = '''';\nforeach ($rawFeedData as $photo) {\n	$output .= $modx->getChunk($tpl, $photo);\n}\n\nreturn $output;', 0, 'a:0:{}', ''),
(195, 'holdingPage', '', 0, 69, 0, '/**\n * Wedding site holdingPage\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters - none\n * Returns :- the relevant chunk to display on the holding page or\n *            a redirect to the registration page\n */\n\n/* Get the number of test couples */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''hearAbout:='' => ''Yes''));\n$testerCount = $modx->getCount(''weddingUserAttribute'', $c);\n\n/* Get the registration page and context */\n$registrationPage = $modx->getOption(''registrationPage'');\n$context = $modx->context->get(''key'');\n\n/* Check for the limit reached */\nif ( $testerCount == $maxTestCouples ) {\n    \n    $registrationURL = $modx->makeURL($registrationPage, $context, '''', ''full'');\n    header("Location: {$registrationURL}");\n}\n\n/* Set the test couple valid session variable */\n$_SESSION[''registrationTestCouple''] = ''valid'';\n\n/* Output the register chunk */\n$registrationURL = $modx->makeURL($registrationPage, $context, array(''type'' => ''testcouple''), ''full'');\n$output = $modx->getChunk(''holdingFreeSlots'', array(''registrationLink'' => $registrationURL ));\nreturn $output;', 0, 'a:1:{s:14:"maxTestCouples";a:6:{s:4:"name";s:14:"maxTestCouples";s:4:"desc";s:66:"Number of couples allowed to register for the one years free trial";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"25";s:7:"lexicon";N;}}', ''),
(213, 'time', 'returns the time/date add date example [[!time:date=`%Y`]]', 0, 0, 0, 'return time();', 0, 'a:0:{}', '');
INSERT INTO `modx_site_templates` (`id`, `templatename`, `description`, `editor_type`, `category`, `icon`, `template_type`, `content`, `locked`, `properties`) VALUES
(1, 'wed-home', 'Template', 0, 0, '', 0, '[[$head]]\n\n[[$headerWithMenu]]\n  \n <!-- page title -->\n      <section class="home_feature">\n            <div class="wrapper">\n            \n            <div id="welcome">\n              <div id="welcome-text">\n               [[*TVpage-title]]\n            </div>\n\n              <div id="slider-wrapper">\n\n<ul id="slider">\n   <li class="slider1">\n   <div class="div-container">\n     <h2>Share your Day</h2>\n     <p>With Friends and Family<br /> all over the world</p>\n     <p><a href="[[~4]]" title="#" class="button-alt"><span>SIGN <em>up</em> NOW</span></a></p>\n     </div>\n  </li>\n  <li class="slider2">\n   <div class="div-container">\n    <h2>Beautiful designs</h2><p>Professionally designed <br /> themes to match your scheme</p>\n    <p><a href="[[~5]]" title="#" class="button-alt"><span>VIEW <em>the</em> DESIGNS</span></a></p>\n    </div>\n  </li>\n   <li class="slider3">\n   <div class="div-container">\n   <h2>Easy Updates</h2><p>Control your content<br />from an intuitive admin area</p>\n    <p><a href="[[~4]]" title="#" class="button-alt"><span><em>TRY for FREE</em></span></a></p>\n    </div>\n  </li>\n   <li class="slider4">\n   <div class="div-container">\n    <h2>Save time &amp; money</h2><p>Send invites online.<br />Keep a blog to update your guests.<br /> Upload your giflist.</p>\n    <p><a href="[[~3]]" title="#" class="button-alt"><span>VIEW <em>more</em> FEATURES</span></a></p>\n    </div>\n  </li>\n  \n</ul>\n\n</div><!-- / slider-wrapper -->\n              <script src="assets/templates/wedding/jquery.bxSlider.min.js" type="text/javascript"></script>\n<script type="text/javascript">\n    $(document).ready(function(){\n        $(''#slider'').bxSlider({\n      mode: ''vertical'',\n     controls: false,\n     pager: true,\n     randomStart: true,\n     speed: 500,\n     pause: 4000, \n     auto: true\n  }); \n  });\n</script>\n        </div>\n</div> \n              \n            </div><!--.wrapper--