(1661, 'document', 'text/html', 'Test newsletter 1', '', '', 'test-newsletter-1', '', 1, 0, 0, 1658, 0, '', '<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd">\r\n<html>\r\n<head>\r\n<title></title>\r\n<meta http-equiv="Content-Type" content="text/html; charset=utf-8">\r\n<style type="text/css">\r\na:link, a:visited {\r\n	color: #ee942a;\r\n	text-decoration: underline;\r\n}\r\na:hover {\r\n	color: #a0105c;\r\n}\r\n</style>\r\n<meta name="robots" content="noindex,nofollow"></meta>\r\n<meta property="og:title" content="Experience Zouk"></meta>\r\n</head>\r\n<body bgcolor="#000000" leftmargin="0" topmargin="0" marginwidth="0" marginheight="0">\r\n<table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n  <tr>\r\n     <tr>\r\n                <td style="height:20px;" >&nbsp;</td>\r\n              </tr>\r\n    <td valign="top" bgcolor="#000000">\r\n    <table width="625" border="0" align="center" cellpadding="0" cellspacing="0">\r\n    <tr>\r\n	   <td width="312" valign="top" cellspacing="0" cellpadding="0" border="0">\r\n		<img src="images/img-logo.gif" width="625" height="100">\r\n		</td>\r\n	</tr>\r\n      <tr>\r\n        <td valign="top">\r\n        <table width="625" height="400" border="0" cellpadding="0" cellspacing="0">\r\n     	\r\n          <tr>\r\n            <td colspan="2"  bgcolor="#282828" style="height:1px;" ></td>\r\n          </tr>\r\n          <tr>\r\n            <td valign="top"><img src="images/img-curry.jpg" width="625" height="395"/></td>\r\n          </tr>\r\n          \r\n        </table></td>\r\n      </tr>\r\n      \r\n      <tr>\r\n      	<td bgcolor="#000000" height="5" width="100%"></td>\r\n      </tr>\r\n      <tr>\r\n        <td valign="top" bgcolor="#000000"><table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n          <tr>\r\n            <td valign="top">\r\n            <table width="100%" border="0" cellspacing="0" cellpadding="0">\r\n           \r\n              <tr>\r\n                <td>\r\n                \r\n                \r\n                \r\n                	<table width="625" height="400" cellspacing="0" cellpadding="0" border="0">\r\n                \r\n                		<tr>\r\n                			<td width="312" valign="top" cellspacing="0" cellpadding="0" border="0">\r\n                			<img src="images/img-interior.jpg" width="312" height="180">\r\n                			<p style="font-size: 15px; line-height: 18px; color: #ffffff;font-family:Helvetica, Arial, sans-serif;">Celebrate your graduation at Zouk and enjoy outstanding Indian food in a relaxed and friendly environment.</p>\r\n                			</td>\r\n                			<td valign="middle" width="13"></td>\r\n                			<td width="300" valign="top" cellspacing="0" cellpadding="0" border="0">\r\n                			<img src="images/img-gulab.jpg" width="300" height="180">\r\n                			<p style="font-size: 15px; line-height: 18px; color: #ffffff;font-family:Helvetica, Arial, sans-serif;">Zouk offers a truly unique dining experience with a delicious selection of authentic Indian and Pakistani food for diners to enjoy. Our theatre style kitchen adds to the atmosphere and monthly specials. Indulgent desserts and relaxed and stylish surroundings all make Zouk the perfect destination for a night to remember.</p>\r\n                			</td>\r\n                		</tr>\r\n                	</table>\r\n                	\r\n                	\r\n                </td>\r\n              </tr>\r\n          \r\n\r\n         \r\n         \r\n          \r\n              <tr>\r\n                <td style="height:20px;" >&nbsp;</td>\r\n              </tr>\r\n              \r\n              <tr>\r\n                <td style="font-family:Helvetica, Arial, sans-serif;font-size:24px;line-height:28px;color:#ffffff;padding-top:0px;padding-bottom:0px;padding-right:0px;padding-left:0px;text-align:center;" >1312 Leeds Road, Bradford BD3 8LF</td>\r\n                </tr>\r\n                \r\n                <tr>\r\n                 <td style="font-family:Helvetica, Arial, sans-serif;padding-top:0px;padding-bottom:0px;padding-right:0px;padding-left:0px;text-align:center;" >\r\n                 <p style="color: #ee942a; font-size: 30px; line-height: 32px;">01274 258 025 |</span><a href="http://www.zoukteabar.co.uk" style="text-decoration:none;" > www.zoukteabar.co.uk</a></p>\r\n                 </td>\r\n                 </tr>\r\n\r\n              </tr>\r\n              <tr>\r\n                <td style="height:20px;" >&nbsp;</td>\r\n              </tr>\r\n              \r\n            </table></td>\r\n          </tr>\r\n        </table></td>\r\n        </tr>\r\n    </table></td>\r\n  </tr>\r\n</table>\r\n</body>\r\n</html>\r\n\r\n<!-- Localized -->', 1, 0, 2, 1, 0, 1, 1335908212, 0, 0, 0, 0, 0, 1335908212, 1, '', 0, 0, 0, 0, 0, 0, 0, 'modDocument', 'web', 1),
