(118, 'galAlbumRowTpl', '', 0, 2, 0, '<li[[+cls:notempty=` class="[[+cls]]"`]]><a href="[[~[[*id]]? &[[+albumRequestVar]]=`[[+id]]`]]">[[+showName:notempty=`[[+name]]`]]</a></li>', 0, ''),
(119, 'galItemThumb', '', 0, 2, 0, '<div class="[[+cls]]">\n    <a href="[[+linkToImage:if=`[[+linkToImage]]`:is=`1`:then=`[[+image_absolute]]`:else=`[[~[[*id]]?\n            &[[+imageGetParam]]=`[[+id]]`\n            &[[+albumRequestVar]]=`[[+album]]`\n            &[[+tagRequestVar]]=`[[+tag]]` ]]`]]">\n\n        <img class="[[+imgCls]]" src="[[+thumbnail]]" alt="[[+name]]" />\n    </a>\n</div>', 0, ''),
(3, 'ditsnewssignup', 'Signup form', 0, 0, 0, '<!--\n* FormIt is required!\n* Fields used: firstname, lastname, company, email\n* The ''ditsnewssignup'' hook (snippet) adds the subscriber or returns an error message.\n* Redirect to a ''thankyou'' page!\n* Don''t forget to specify the group(s) the subscriber should be added to (see below)\n* Of course, you can also use this in a "normal" contactform.\n-->\n\n[[!FormIt? &hooks=`spam,ditsnewssignup,redirect` &redirectTo=`2` &validate=`email:required:email` ]]\n<form action="/[[~[[*id]]]]" method="post">\n\n<p>Company:<br />\n<input name="company" type="text" value="[[+fi.company]]" /></p>\n\n<p>First name:<br />\n<input name="firstname" type="text" value="[[+fi.firstname]]" /></p>\n\n<p>Last name:<br />\n<input name="lastname" type="text" value="[[+fi.lastname]]" /></p>\n\n<p>Email:<br />\n<input name="email" type="text" value="[[+fi.email]]" [[!+fi.error.email:notempty=`class="error"`]] /></p>\n\n<input type="hidden" name="groups[]" value="1" /><!-- First group to add subscriber to. Ignored if not public! -->\n<input type="hidden" name="groups[]" value="2" /><!-- Second group to add subscriber to. Ignored if not public! -->\n\n<input type="submit" value="Subscribe" />\n</form>', 0, NULL),
(4, 'ditsnewssignupmail', 'Confirmation message after newsletter signup', 0, 0, 0, 'Dear [[+firstname:default=`Subscriber`]],<br />\n<br />\nThank you for subscribing to our newsletter. Please, click <a href="[[+confirmUrl]]">here</a> to confirm your subscription.', 0, NULL),
