(9, 'footer', 'footer content', 0, 12, 0, '\n   <section class="content-lower">\n     <div class="wrapper">\n\n      <div class="column">\n              <h4>Navigate</h4>\n              [[!Wayfinder? &startId=`0` &level=`1` &outerTpl=`wfOuter-footer` &RowTpl=`wfRow-footer`]]  \n            </div>\n          \n\n            <div class="column">\n  <h4>Latest News</h4>\n              <ul class="txtlist2">\n[[!getResources?\n  &parents=`6`\n  &hideContainers=`1`\n  &tpl=`latestNewsFooterTpl`\n  &limit=`3`\n  &sortby=`publishedon`\n   &showHidden=`1`             \n]]\n              </ul>        </div>\n\n          \n          \n   <div class="column">\n                    <h4>Newsletter</h4>\n         \n        \n          [[!FormIt? &hooks=`spam,ditsnewssignup,redirect` &redirectTo=`1468` &validate=`email:required:email` ]]\n<form id="ditsnews2" name="ditsnews2" action="/[[~[[*id]]]]" method="post">\n<fieldset class="login">\n <legend>Sign up to the newsletter</legend>\n<input  class="required email" name="email" type="text" value="Email Address" [[!+fi.error.email:notempty=`class="error"`]] />  \n<input  type="submit" value="Sign Up"/>\n</fieldset>\n</form>\n\n      </div>\n      \n          \n          \n          \n  <div class="column">\n         <h4>Connect with us</h4>\n                <ul class="social-nav">\n                  <li><a href="#" class="twit"><span>Twitter</span></a></li>\n                  <li><a href="#" class="face"><span>Facebook</span></a></li>\n                  <li><a href="#" class="rss"><span>rss</span></a></li>\n    \n                </ul>\n      </div>\n    \n\n      \n    </div><!--.wrapper-->\n    \n        \n  </section><!--Lower section-->\n\n\n\n <footer>\n         <div class="wrapper">\n           <p><a href="http://www.theweddingwebsitegroup.com" title="wedding websites">The Wedding Website Group</a> is a trading style of <a href="http://www.webpods.co.uk" title="webpods websites"> Webpods</a>\n             <p>Copyright 2012 © <strong></strong>All Rights Reserved.</p>\n           <a href="http://www.9thwave.co.uk">Design by: 9thwave.co.uk</a>\n            </div><!--.wrapper-->\n        </footer>\n<script type="text/javascript" src="https://ajax.googleapis.com/ajax/libs/jqueryui/1.8.17/jquery-ui.min.js"></script>\n<script type="text/javascript" src="assets/templates/wedding/js/jquery.tinyTips.js"></script>\n    <script type="text/javascript">\n    $(document).ready(function() {\n      $(''a.information, a.colour-label'').tinyTips(''light'', ''title'');\n    });\n    </script>\n\n\n</body>\n</html>', 0, 'a:0:{}'),
