(121, 'userEditHomeForm', '', 0, 45, 0, '<div class="user-edit-left">\n<form id="userEditHomeForm" method="post" action="/[[~[[*id]]]]">\n    <fieldset>\n        <ol>\n            <li><span class="user-edit-label">Website active:<a href="#" title="#" class="information"><span>Information</span></a></span>\n                <input id="userEditWebsiteYes" class="radio-btn" name="userEditWebsiteProtect" type="radio" value="1" [[+yesSelected]]/><label class="radio-label">Yes</label>\n                <input id="userEditWebsiteNo" class="radio-btn" name="userEditWebsiteProtect" type="radio" value="0" [[+noSelected]] /><label class="radio-label">No</label>\n            </li>\n\n        </ol>\n    </fieldset>\n\n</div>\n<div class="user-edit-right">\n \n    <input class="btn-save-changes" type="submit" name="userEditWebsiteProtectSubmit" value="Save Changes" />\n\n</div>\n\n</form>\n    \n<div class="servererror"><strong>[[+userEditHomeError]]</strong></div>', 0, 'a:0:{}'),
