(124, 'userEditCommonForm', '', 0, 45, 0, '<form id="userEditCommonForm" [[+userEditGenericSubmit]] method="post" action="/[[~[[*id]]]]?userPage=[[+userPageId]]">\n    <fieldset>\n<input id="userCommonUserPageId" name="userCommonUserPageId" type ="hidden" value="[[+userPageId]]"/>\n        <ol>\n            <li><label class="user-edit-label">Page name:</label>\n                <input class="text" name="userCommonPageTitle" id="userCommonPageTitle" type="text" value="[[+userEditCommonPageTitle]]"/></li>\n\n\n            <li><span class="user-edit-label">Webpage active:<a href="#" title="#" class="information"><span>Information</span></a></span>\n                <input id="userCommonPageActiveYes" class="radio-btn" name="userCommonPageActive" type="radio" value="1" [[+userEditCommonYesSelected]]/><label class="radio-label">Yes</label>\n\n                <input id="userCommonPageActiveNo" class="radio-btn" name="userCommonPageActive" type="radio" value="0" [[+userEditCommonNoSelected]]/><label class="radio-label">No</label>\n            </li>\n<li><span class="user-edit-label">Password protect:<a href="#" title="#" class="information"><span>Information</span></a></span>\n                <input id="userCommonPageProtectYes" class="radio-btn" name="userCommonPageProtect" type="radio" value="1" [[+userEditCommonProtectYesSelected]]/><label class="radio-label">Yes</label>\n\n                <input id="userCommonPageProtectNo" class="radio-btn" name="userCommonPageProtect" type="radio" value="0" [[+userEditCommonProtectNoSelected]]/><label class="radio-label">No</label>\n            </li>\n        </ol>\n    </fieldset>\n\n</div>\n<div class="user-edit-right">\n<input class="btn-save-changes" type="submit" name="userEditCommonFormSubmit" value="Save Changes" />\n</div>\n\n</form>\n<div class="servererror"><strong>[[+userEditCommonError]]</strong></div>', 0, 'a:0:{}'),
