(15, 'blogPost', 'Display blog posts', 0, 9, 0, '<div class="post">\n    <h2 class="title"><a href="[[~[[+id]]]]">[[+pagetitle]]</a></h2>\n \n  <p class="postmeta"><span class="date">[[+publishedon:strtotime:date=`%d.%b.%g`]]</span> <a href="[[~[[+id]]]]#comments" class="comments">\n    Comments ([[!QuipCount? &thread=`blog-post-[[+id]]`]])\n  </a>\n [[+tv.tags:notempty=` <span class="tags">Tags:\n[[!tolinks? &items=`[[+tv.tags]]` &key=`tag` &target=`1`]]\n</span>`]]</p>\n\n    <div class="entry-post">\n   [[+tv.blog-summary]]\n    </div>\n  \n<p>  <a href="[[~[[+id]]]]" class="blue-button"><span>Read more</span></a></p>\n\n</div>\n<div class="divider-blog">&nbsp;</div>\n\n\n\n', 0, 'a:0:{}'),
(16, 'quip-comments-tpl', '', 0, 9, 0, '<li class="[[+cls]]" id="[[+idprefix]][[+id]]" [[+depth_margin:notempty=`style="padding-left: [[+depth_margin]]px"`]]>\n<div id="[[+idprefix]][[+id]]-div" class="quip-comment-body [[+alt]]">\n    <div class="quip-comment-right">\n      [[+gravatarUrl:notempty=`<img src="http://www.gravatar.com/avatar/d5d19ef012e9936f15641fb2a531b5a4.png" class="quip-avatar" alt="" />`]]\n    </div>\n \n    <p class="quip-comment-meta">\n        <span class="quip-comment-author">[[+authorName]]:</span><br />\n        <span class="quip-comment-createdon"><a href="[[+url]]">[[+createdon]]</a>\n        [[+approved:if=`[[+approved]]`:is=`1`:then=``:else=`- <em>[[%quip.unapproved? &namespace=`quip` &topic=`default`]]</em>`]]\n        </span>\n    </p>\n \n    <div class="quip-comment-text">\n        <p>[[+body]]</p>\n \n        [[+replyUrl:notempty=`<p><span class="quip-reply-link"><a href="[[+replyUrl]]">[[%quip.reply? &namespace=`quip` &topic=`default`]]</a></span></p>`]]\n    </div>\n \n    <div class="quip-comment-options">\n        [[+report]]\n        [[+options]]\n    </div>\n    <div class="quip-break"></div>\n</div>\n    [[+children:notempty=`<ol class="quip-comment-list">[[+children]]</ol>`]]\n</li>', 0, 'a:0:{}'),
