(120, 'userEditHomePage', '', 0, 45, 0, '<section class="content-main">  \n    <div class="wrapper-twocol">\n\n        <aside class="left210">\n\n            [[$userHomeWebPages]]\n            <h2 class="user-side-title">Support &amp; help</h2>\n            [[$userHomeSupport]]\n\n\n        </aside>       \n\n       <!-- <article class="main-article">\n            <h2>Edit:  <span class="page-name">Home</span></h2>\n\n            <div class="wrapper-user-area">\n\n                [[$userEditHomeForm]]\n\n                <div class="divider">&nbsp;</div>\n            </div>\n             \n      <!-- / wrapper content area -->\n\n         <!--    <p class="dec">Select a page to edit from the list on the left.</p>\n        </article>\n      -->\n        <article class="main-article">\n            <h2>Welcome to your admin area</h2>\n            <p>This is the admin area for creating your website. We''ve produced some tutorials to get you started. If you can''t wait to read the tutorials then click on the web pages link to the left and start editing your pages.</p>\n            <div class="user-box1">\n                <h3><a href="[[~30]]">Update your account settings</a></h3>\n            </div>\n            <div class="user-box2">\n                <h3><a href="[[~31]]">Start editing your pages</a></h3>\n            </div>\n            <div class="user-box3">\n                <h3><a href="[[~32]]">Manage your guests &amp; events</a></h3>\n            </div>\n\n        </article>\n\n        <div class="clearer"></div>\n\n    </div><!--.wrapper-->\n</section>\n', 0, 'a:0:{}'),
(126, 'userEditGalleryItemForm', '', 0, 45, 0, '<div class="wrapper-user-area">\n\n    <div class="user-edit-left">\n        <form id="userEditGalleryItemAlbumForm" method="post" action="/[[~[[*id]]]]?userPage=[[+userPageId]]">\n            <fieldset>\n                <ol>\n                    <li><label class="user-edit-label">Album name:</label>\n                        <input class="text" name="userEditGalleryItemAlbumName"  id="userEditGalleryItemAlbumName"  type="text" /></li>\n\n                    <li><label class="user-edit-label" for="">Album Description:</label>\n                        <textarea name="userEditGalleryItemAlbumDescription" rows="4" cols="20" id="userEditGalleryItemAlbumDescription"></textarea></li>\n\n                </ol>\n            </fieldset>\n\n    </div>\n    <div class="user-edit-right">\n        <input class="btn-save-changes" type="button" name="userEditGalleryItemAlbumSubmit" value="Save Changes" onClick="WS.albumDetailUpdate();"/><br/></br/><br/>\n     <div><a class="blue-button" onclick="location.reload(true);"><span>Back to Albums...</span></a></div>    \n\n    </div>\n\n</form>\n\n<div class="servererror"><strong></strong></div>\n\n<div class="divider">&nbsp;</div>\n</div><!-- / wrapper user area -->\n\n\n<div id="gallery-panel">\n    <!--<ul class="user-buttons"> </ul>-->\n    <div class="gallery-inner">\n        <div id="userEditGalleryItems" class="gallery-items">\n            \n\n        </div><!-- / gallery-items -->\n\n        <div class="gallery-detail">\n            <div class="gallery-detail-thumb">\n                <img class="gallery-itemEditImage" src="assets/templates/wedding/images/small/ornate-classic-silverBlue.jpg" alt="" />\n                <div class="divider"> </div>\n            </div>\n            <div class="gallery-detail-info">\n                 <form id="userEditGalleryItemDetailForm" method="post" action="/[[~[[*id]]]]?userPage=[[+userPageId]]">\n                     <input type="hidden" name="userEditGalleryItemId" id="userEditGalleryItemId">\n                  <fieldset>\n                    <ol>\n                       <li><label>Photo Name:</label><input type="text" name="userEditGalleryItemDisplayName" id="userEditGalleryItemDisplayName" class="text"></li>\n                       <li><label>Photo Description:</label><textarea name="userEditGalleryItemDescription" id="userEditGalleryItemDescription" class=""></textarea></li>\n                       <!--<li><label>Active:</label><input type="checkbox" name="userEditGalleryItemActive" id="userEditGalleryItemActive" class="checkbox" value="1"></li>-->\n                    </ol>\n                     </fieldset>\n                    </form>\n                        <p>Delete Photo<a title="Delete" class="off userEditGalleryItemDelete" href=""></a></p>\n                        <p id="userEditGalleryItemLegend" ></p>\n  </div>\n\n        </div><!-- / gallery-detail -->\n\n\n    </div><!-- / gallery-inner -->\n    <div id="uploader">\n        <p>You browser doesn''t have Flash, Silverlight, Gears, BrowserPlus or HTML5 support.</p>\n    </div>\n\n</div><!-- / gallery-panel -->\n', 0, 'a:0:{}'),
