(31, 'registrationForm', 'Registration Form', 0, 20, 0, '<script type="text/javascript">\n var RecaptchaOptions = {\n    theme : ''clean''\n };\n </script>\n\n<form id="registrationForm" method="post" action="/[[~[[*id]]]]">\n          <fieldset>\n              <legend class="dec">Personal details</legend>\n            \n                        <ol>\n  <li><label for="userSettingsFirstName">Your first name:\n                    </label>\n                    <input id="userSettingsFirstName" class="text required validcharsgeneral  tobackend" name="userSettingsFirstName" type="text" value="[[+userSettingsFirstName]]"/>\n            </li>\n            <li><label for="userSettingsLastName">Your last name:\n                    </label>\n                    <input id="userSettingsLastName" class="text required validcharsgeneral  tobackend" name="userSettingsLastName" type="text" value="[[+userSettingsLastName]]"/>\n            </li>\n            <li><label for="userSettingsAddressStreet">Your address (street):\n                    </label>\n                    <input id="userSettingsAddressStreet" class="text required validcharsgeneral  tobackend" name="userSettingsAddressStreet" type="text" value="[[+userSettingsAddressStreet]]"/>\n            </li>\n            <li><label for="userSettingsTel">Telephone:\n                    </label>\n                    <input id="userSettingsTel" class="text validcharsgeneral tobackend" name="userSettingsTel" type="text" value="[[+userSettingsTel]]"/>\n                </li>\n            <li><label for="userSettingsCity">City:\n                    </label>\n                    <input id="userSettingsCity" class="text required validcharsgeneral  tobackend" name="userSettingsCity" type="text" value="[[+userSettingsCity]]"/>\n            </li>\n            <li><label for="userSettingsCountry">\n                    Country:\n            </label>\n              <div class="select-bg">\n                <select id="userSettingsCountry" name="