(174, 'userEditRSVPMessageItemForm', '', 0, 45, 0, '<div id="userEditRSVPMessageTextarea">\n    \n    <form id="userEditRSVPMessageForm" method="post" action="/[[~[[*id]]]]?userPage=[[+userPageId]]">\n        <fieldset>\n        <ol>\n            <li><label for="userEditRSVPMessageSender">From:\n                </label>\n                <input class="text" type="text"  id="userEditRSVPMessageSender" name="userEditRSVPMessageSender" readonly />\n            </li>\n            <li><label for="userEditRSVPMessageTitle">Subject:\n                </label>\n                <input class="message-subject" type="text"  id="userEditRSVPMessageTitle" name="userEditRSVPMessageTitle" readonly />\n            </li>\n        </ol>\n</fieldset>\n        <textarea id="userEditRSVPMessageContent" name="userEditRSVPMessageContent" class="text" rows="10" style="width:100%" readonly>\n        [[+userEditRSVPMessageContent]]\n        </textarea>\n </form>\n    \n</div>', 0, NULL),
