(178, 'locatorTpl', 'Locator output chunk', 0, 57, 0, '<section>\r\n    <p>IP Address        : [[+locator.ipAddress]]</p>\r\n    <p>Country Code 2    : [[+locator.countryCode2]]</p>\r\n    <p>Country Code 3    : [[+locator.countryCode3]]</p>\r\n    <p>Country Name      : [[+locator.countryName]]</p>\r\n    <p>MODX Country Code : [[+locator.modxCountryCode]]</p>\r\n    <p>Error Status      : [[+locator.error]]</p>\r\n</section>', 0, NULL),
(179, 'userEditGuestbookPage', '', 0, 45, 0, '<section class="content-main">  \n    <div class="wrapper-twocol">\n\n        <aside class="left210">\n\n            [[$userHomeWebPages]]\n            <h2 class="user-side-title">Support &amp; help</h2>\n            [[$userHomeSupport]]\n\n\n        </aside>       \n\n        <article class="main-article">\n            <h2>Edit:  <span class="page-name">Guestbook</span></h2>\n\n            <div id="userEditGuestBookEditCommon" class="wrapper-user-area">\n                <div  class="user-edit-left">\n                \n                    [[$userEditCommonForm]]\n\n                <div class="divider">&nbsp;</div>\n            </div><!-- / wrapper content area -->\n\n            <div id="guestbookList">\n                [[$userEditGuestbookListForm]]\n            </div>\n            <div id="guestbookItem" class="container">\n                [[$userEditGuestbookItemForm]]\n            </div>\n\n        </article>\n\n\n        <div class="clearer"></div>\n\n    </div><!--.wrapper-->\n</section>\n', 0, NULL),
(180, 'userGuestbookFEPage', '', 0, 58, 0, '<section>\n    <div id="userGuestbookFEForm">\n        <a class="open-form" href="#"><h2>Please sign our Guestbook.</h2></a>\n        <div id="userGuestbookFEFormWrapper">\n            [[!userGenerateFEGuestbookSignlinks]]\n        </div>\n    </div>\n    <div id="userGuestbookFEComments">\n        [[!userGenerateFEGuestbooklinks]]\n    </div>\n</section>', 0, NULL),
(181, 'userGuestbookFEMessage', '', 0, 58, 0, '<span class="quip-success" id="quip-success-[[+idprefix]]">[[+successMsg]]</span>\n \n<form id="quip-add-comment-[[+idprefix]]" action="[[+url]]#quip-comment-preview-box-[[+idprefix]]" method="post">\n<div class="quip-comment quip-add-comment">\n    <input type="hidden" id="userGuestbookFEnospam" name="userGuestbookFEnospam" value="0" />\n    <input type="hidden" name="nospam" value="[[+nospam]]" />\n    <input type="hidden" name="thread" value="[[+thread]]" />\n    <input type="hidden" name="parent" value="[[+parent]]" />\n    <input type="hidden" name="auth_nonce" value="[[+auth_nonce]]" />\n    <input type="hidden" name="preview_mode" value="[[+preview_mode]]" />\n<input type="hidden" name="quip-post" value="1" />\n \n     <div class="quip-fld">\n        <label for="quip-comment-name-[[+idprefix]]">[[%quip.name? &namespace=`quip` &topic=`default`]]:<span class="quip-error">[[+error.name]]</span></label>\n        <input type="text" name="name" id="quip-comment-name-[[+idprefix]]"/>\n        <br />\n    </div>\n     \n    <div class="quip-fld">\n        <label for="quip-comment-email-[[+idprefix]]">[[%quip.email]]:<span class="quip-error">[[+error.email]]</span></label>\n        <input type="text" name="email" id="quip-comment-email-[[+idprefix]]" />\n        <br />\n    </div>\n     \n    <p><span class="quip-error">[[+error.comment]]</span></p>\n    <textarea name="comment" id="quip-comment-box-[[+idprefix]]" rows="5">[[+comment]]</textarea>\n     \n \n    <button type="submit" id ="userGuestbookFEFormSubmit" name="quip-post" value="1">Sign</button>\n     \n    <br class="clear" />\n</div>\n</form>', 0, NULL),
(182, 'userGuestbookFEComment', '', 0, 58, 0, '<li class="[[+cls]]" id="[[+idprefix]][[+id]]" [[+depth_margin:notempty=`style="padding-left: [[+depth_margin]]px"`]]>\n<div id="[[+idprefix]][[+id]]-div" class="quip-comment-body [[+alt]]">\n    <div class="quip-comment-right">\n    </div>\n \n    <p class="quip-comment-meta">\n        <span class="quip-comment-author">[[+authorName]]:</span><br />\n        <span class="quip-comment-createdon"><a href="[[+url]]">[[+createdon]]</a>\n        [[+approved:if=`[[+approved]]`:is=`1`:then=``:else=`- <em>[[%quip.unapproved? &namespace=`quip` &topic=`default`]]</em>`]]\n        </span>\n    </p>\n \n    <div class="quip-comment-text">\n        <p>[[+body]]</p>\n \n    </div>\n \n    <div class="quip-comment-options">\n       \n    </div>\n    <div class="quip-break"></div>\n</div>\n    [[+children:notempty=`<ol class="quip-comment-list">[[+children]]</ol>`]]\n</li>', 0, NULL),
(183, 'userGuestbookFEComments', '', 0, 58, 0, '<div class="quip">\n    <h3>Signatures([[+total]])</h3>\n     \n    <div id="quip-topofcomments-[[+idprefix]]"></div>\n \n    [[+comments:notempty=`<ol class="quip-comment-list container" id="guestbookComments">\n        <div class="content">\n    [[+comments]]\n        </div>\n<div class="page_navigation pagination"></div>\n    </ol>`]]\n</div>', 0, NULL);
