(159, 'userGuestbookFEJSInclude', '', 0, 58, 0, '/**\n * Wedding site User Front End Gusetbook processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'', null, $modx->getOption(''assets_url'') . ''components/ws_jQuery/'');\n\n/* Pajinate */\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\n$modx->regClientStartupScript($pajinate);\n\n/* Head scripts */\n$head1 = ''<script>	\n        \n     $(document).ready(function(){\n     \n        /* Properties */\n       WS = new Object(); \n       WS.submitForm = false;\n       WS.doSubmit = function() {\n           $.post(document.URL, $("#quip-add-comment-qcom").serialize(), function(data) {\n	window.location.reload();\n           }).error(function(jqXHR, textStatus, errorThrown) { \n                      window.location.reload();\n                      });\n       };\n        \n        /* Hide the comment form */\n        $("#userGuestbookFEFormWrapper").hide();\n        \n        /* Form opener functionality */\n        $(".open-form").bind("click", function(event){\n               event.preventDefault();\n               $("#userGuestbookFEFormWrapper").show();\n               $("#userGuestbookFEnospam").val(1);\n               WS.submitForm = true;\n          });\n    \n        /* Form submit functionality */\n        $("#quip-add-comment-qcom").bind("submit", function(event){\n               if ( WS.submitForm ) WS.doSubmit();\n               event.preventDefault();\n               return false;\n        });\n\n         /* Pagination */\n        $("#guestbookComments").pajinate({\n					items_per_page : 5,\n                                        nav_label_first : "<< ",\n					nav_label_last : " >>",\n					nav_label_prev : "< ",\n					nav_label_next : " >"\n	});\n        \n     });\n     \n</script>'';\n$modx->regClientStartupScript($head1, true);', 0, NULL, '