(13, 'FormItAutoResponder', 'Custom hook for FormIt to handle Auto-Response emails.', 0, 3, 0, '/**\n * FormIt\n *\n * Copyright 2009-2011 by Shaun McCormick <shaun@modx.com>\n *\n * FormIt is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * FormIt is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * FormIt; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package formit\n */\n/**\n * A custom hook for auto-responders.\n * \n * @package formit\n */\n/* setup default properties */\n$tpl = $modx->getOption(''fiarTpl'',$scriptProperties,''fiarTpl'');\n$mailFrom = $modx->getOption(''fiarFrom'',$scriptProperties,$modx->getOption(''emailsender''));\n$mailFromName = $modx->getOption(''fiarFromName'',$scriptProperties,$modx->getOption(''site_name''));\n$mailSender = $modx->getOption(''fiarSender'',$scriptProperties,$modx->getOption(''emailsender''));\n$mailSubject = $modx->getOption(''fiarSubject'',$scriptProperties,''[[++site_name]] Auto-Responder'');\n$mailSubject = str_replace(array(''[[++site_name]]'',''[[++emailsender]]''),array($modx->getOption(''site_name''),$modx->getOption(''emailsender'')),$mailSubject);\n$mailReplyTo = $modx->getOption(''fiarReplyTo'',$scriptProperties,$mailFrom);\n$isHtml = $modx->getOption(''fiarHtml'',$scriptProperties,true);\n$toField = $modx->getOption(''fiarToField'',$scriptProperties,''email'');\nif (empty($fields[$toField])) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''[FormIt] Auto-responder could not find field `''.$toField.''` in form submission.'');\n    return false;\n}\n\n/* setup placeholders */\n$placeholders = $fields;\n$mailTo= $fields[$toField];\n\n$message = $formit->getChunk($tpl,$placeholders);\n\n$modx->getService(''mail'', ''mail.modPHPMailer'');\n$modx->mail->reset();\n$modx->mail->set(modMail::MAIL_BODY,$message);\n$modx->mail->set(modMail::MAIL_FROM,$hook->_process($mailFrom,$placeholders));\n$modx->mail->set(modMail::MAIL_FROM_NAME,$hook->_process($mailFromName,$placeholders));\n$modx->mail->set(modMail::MAIL_SENDER,$hook->_process($mailSender,$placeholders));\n$modx->mail->set(modMail::MAIL_SUBJECT,$hook->_process($mailSubject,$placeholders));\n$modx->mail->address(''to'',$mailTo);\n$modx->mail->setHTML($isHtml);\n\n/* reply to */\n$emailReplyTo = $modx->getOption(''fiarReplyTo'',$scriptProperties,$mailFrom);\n$emailReplyTo = $hook->_process($emailReplyTo,$fields);\n$emailReplyToName = $modx->getOption(''fiarReplyToName'',$scriptProperties,$mailFromName);\n$emailReplyToName = $hook->_process($emailReplyToName,$fields);\n$modx->mail->address(''reply-to'',$emailReplyTo,$emailReplyToName);\n\n/* cc */\n$emailCC = $modx->getOption(''fiarCC'',$scriptProperties,'''');\nif (!empty($emailCC)) {\n    $emailCCName = $modx->getOption(''fiarCCName'',$scriptProperties,'''');\n    $emailCC = explode('','',$emailCC);\n    $emailCCName = explode('','',$emailCCName);\n    $numAddresses = count($emailCC);\n    for ($i=0;$i<$numAddresses;$i++) {\n        $etn = !empty($emailCCName[$i]) ? $emailCCName[$i] : '''';\n        if (!empty($etn)) $etn = $hook->_process($etn,$fields);\n        $emailCC[$i] = $hook->_process($emailCC[$i],$fields);\n        $modx->mail->address(''cc'',$emailCC[$i],$etn);\n    }\n}\n\n/* bcc */\n$emailBCC = $modx->getOption(''fiarBCC'',$scriptProperties,'''');\nif (!empty($emailBCC)) {\n    $emailBCCName = $modx->getOption(''fiarBCCName'',$scriptProperties,'''');\n    $emailBCC = explode('','',$emailBCC);\n    $emailBCCName = explode('','',$emailBCCName);\n    $numAddresses = count($emailBCC);\n    for ($i=0;$i<$numAddresses;$i++) {\n        $etn = !empty($emailBCCName[$i]) ? $emailBCCName[$i] : '''';\n        if (!empty($etn)) $etn = $hook->_process($etn,$fields);\n        $emailBCC[$i] = $hook->_process($emailBCC[$i],$fields);\n        $modx->mail->address(''bcc'',$emailBCC[$i],$etn);\n    }\n}\n\nif (!$modx->mail->send()) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''[FormIt] An error occurred while trying to send the auto-responder email: ''.$modx->mail->mailer->ErrorInfo);\n    return false;\n}\n$modx->mail->reset();\nreturn true;', 0, NULL, ''),
(14, 'FormItRetriever', 'Fetches a form submission for a user for displaying on a thank you page.', 0, 3, 0, '/**\n * FormIt\n *\n * Copyright 2009-2011 by Shaun McCormick <shaun@modx.com>\n *\n * FormIt is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * FormIt is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * FormIt; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package formit\n */\n/**\n * Retrieves a prior form submission that was stored with the &store property\n * in a FormIt call.\n * \n * @package formit\n */\nrequire_once $modx->getOption(''formit.core_path'',null,$modx->getOption(''core_path'').''components/formit/'').''model/formit/formit.class.php'';\n$fi = new FormIt($modx,$scriptProperties);\n\n/* setup properties */\n$placeholderPrefix = $modx->getOption(''placeholderPrefix'',$scriptProperties,''fi.'');\n$eraseOnLoad = $modx->getOption(''eraseOnLoad'',$scriptProperties,false);\n$redirecToOnNotFound = $modx->getOption(''redirectToOnNotFound'',$scriptProperties,false);\n\n/* fetch data from cache and set to placeholders */\n$cacheKey = $fi->getStoreKey();\n$data = $modx->cacheManager->get($cacheKey);\nif (!empty($data)) {\n    /* set data to placeholders */\n    $modx->setPlaceholders($data,$placeholderPrefix);\n    \n    /* if set, erase the data on load, otherwise depend on cache expiry time */\n    if ($eraseOnLoad) {\n        $modx->cacheManager->delete($cacheKey);\n    }\n/* if the data''s not found, and we want to redirect somewhere if so, do here */\n} else if (!empty($redirecToOnNotFound)) {\n    $url = $modx->makeUrl($redirecToOnNotFound);\n    $modx->sendRedirect($url);\n}\nreturn '''';', 0, 'a:3:{s:17:"placeholderPrefix";a:6:{s:4:"name";s:17:"placeholderPrefix";s:4:"desc";s:31:"prop_fir.placeholderprefix_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:3:"fi.";s:7:"lexicon";s:17:"formit:properties";}s:20:"redirectToOnNotFound";a:6:{s:4:"name";s:20:"redirectToOnNotFound";s:4:"desc";s:34:"prop_fir.redirecttoonnotfound_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:17:"formit:properties";}s:11:"eraseOnLoad";a:6:{s:4:"name";s:11:"eraseOnLoad";s:4:"desc";s:25:"prop_fir.eraseonload_desc";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"lexicon";s:17:"formit:properties";}}', ''),
(15, 'FormItIsChecked', 'A custom output filter used with checkboxes/radios for checking checked status.', 0, 3, 0, '/**\n * Custom output filter that returns checked="checked" if the value is set\n *\n * @package formit\n */\n$output = '' '';\nif ($input == $options) {\n    $output = '' checked="checked"'';\n}\nif (strpos($input,'','') !== false) {\n    $input = explode('','',$input);\n    if (in_array($options,$input)) {\n        $output = '' checked="checked"'';\n    }\n}\nreturn $output;', 0, NULL, ''),
(16, 'FormItIsSelected', 'A custom output filter used with dropdowns for checking selected status.', 0, 3, 0, '/**\n * Custom output filter that returns checked="checked" if the value is set\n *\n * @package formit\n */\n$output = '' '';\nif ($input == $options) {\n    $output = '' selected="selected"'';\n}\nif (strpos($input,'','') !== false) {\n    $input = explode('','',$input);\n    if (in_array($options,$input)) {\n        $output = '' selected="selected"'';\n    }\n}\nreturn $output;', 0, NULL, ''),
(45, 'registrationFormPostProcessor', 'Processor for the registration form', 0, 20, 0, '/**\n * Wedding site Registration processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/*  The parameters from the registration form are already valid at this point, we only need\n * to create the wedding user \n */\n\n$packageType = "None";\n\nif ($_REQUEST[''registrationSubmitTrial''] != "") {\n\n    $packageType = ''Trial'';\n}\n\nif ($_REQUEST[''registrationSubmitPaid''] != "") {\n\n    $packageType = ''Paid'';\n}\n\n/* If we have a package type we have a submit operation */\nif ($packageType != "None") {\n\n    /* Check recaptcha */\n    $recaptchaPath = $modx->getOption(''ws_recaptcha.core_path'', null, $modx->getOption(''core_path'') . ''components/ws_recaptcha/'');\n    include_once $recaptchaPath . "recaptchalib.php";\n\n    $resp = recaptcha_check_answer($privatekey, $_SERVER["REMOTE_ADDR"], $_REQUEST["recaptcha_challenge_field"], $_REQUEST["recaptcha_response_field"]);\n\n    if ($resp->error != "") {\n\n        /* Set the error code and return */\n        $_SESSION[''recaptcha_error''] = $resp->error;\n        return;\n    }\n\n    $_SESSION[''recaptcha_error''] = "";\n\n\n    /* Create the MODX user */\n    $user = $modx->newObject(''modUser'');\n    $profile = $modx->newObject(''modUserProfile'');\n    $user->set(''username'', $_REQUEST[''registrationEmail'']);\n    $user->set(''active'', 1);\n    $user->set(''password'', md5($_REQUEST[''userPassword'']));\n    $profile->set(''email'', $_REQUEST[''registrationEmail'']);\n    $profile->set(''logincount'', 0);\n    $profile->set(''blocked'', 1);\n    $user->addOne($profile, ''Profile'');\n\n    if (!$user->save()) {\n        $modx->setPlaceholder(''registrationError'', "Error - Failed to create this system user");\n    }\n\n    /* Now add the wedding use details */\n    $id = $user->get(''id'');\n    $weddingUser = $modx->newObject(''weddingUserAttribute'', array(''user'' => $id));\n    $weddingUser->set(''packageType'', $packageType);\n    if (!$weddingUser->save()) {\n        $modx->setPlaceholder(''registrationError'', "Error - Failed to create this wedding user");\n    }\n\n    /* Convert into a wedding user */\n    $user->set(''class_key'', ''weddingUser'');\n    $user->save();\n\n    /* Personal/Website details */\n    $weddingUser->set(''firstName'', $_REQUEST[''userSettingsFirstName'']);\n    $weddingUser->set(''lastName'', $_REQUEST[''userSettingsLastName'']);  \n$weddingUser->set(''website'', $_REQUEST[''userRegistrationWebsiteName'']);   \n    include $modx->getOption(''core_path'').''lexicon/country/en.inc.php'';\n    $country = $_country_lang[$_REQUEST[''userSettingsCountry'']];\n    $profile->set(''country'', $country);\n    $profile->set(''city'', $_REQUEST[''userSettingsCity'']);\n    $profile->set(''address'', $_REQUEST[''userSettingsAddressStreet'']);\n    $profile->set(''phone'', $_REQUEST[''userSettingsTel'']);\n    /* Use this a first login marker now */\n    $weddingUser->set(''personalDetails'', 1);\n    /* Save the info */\n    $weddingUser->save();\n    $profile->save();\n\n    /* Send the registration emails to the site admin and the user */\n    $messageParameters = array();\n    $messageParameters[''username''] = $_REQUEST[''registrationEmail''];\n    $messageParameters[''password''] = $_REQUEST[''userPassword''];\n    $messageParameters[''type''] = $packageType;\n    $messageParameters[''adminemail''] = $modx->getOption(''emailsender'');\n    /* Create the validation link */\n    $context = $modx->context->get(''key'');\n    $params = array(''uid'' => $id);\n    $validationLink = $modx->makeURL($registrationValidate, $context, $params, ''full'');\n    $messageParameters[''registrationValidateLink''] = $validationLink;\n    \n    /* Admin */\n    $subject = "New Registration";\n    $message = $modx->getChunk(''registrationAdminMail'', $messageParameters);\n    $modx->runSnippet(''mailMan'', array(''subject'' => $subject,\n        ''message'' => $message,\n        ''to'' => $messageParameters[''adminemail'']));\n\n    /* User */\n    $subject = "Wedding Websites UK - New Registration";\n    $message = $modx->getChunk(''registrationUserMail'', $messageParameters);\n    $to = $_REQUEST[''registrationEmail''];\n    $modx->runSnippet(''mailMan'', array(''subject'' => $subject,\n        ''message'' => $message,\n        ''to'' => $to));\n\n    /* Go to the Thank You page */\n    $context = $modx->context->get(''key'');\n    $pageURL = $modx->makeURL($thankYouPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:3:{s:12:"thankYouPage";a:6:{s:4:"name";s:12:"thankYouPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"1500";s:7:"lexicon";N;}s:10:"privatekey";a:6:{s:4:"name";s:10:"privatekey";s:4:"desc";s:21:"Recaptcha private key";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:40:"6LcMd88SAAAAANXTtwGRe1qenGd7QRjKYoKqS5go";s:7:"lexicon";N;}s:20:"registrationValidate";a:6:{s:4:"name";s:20:"registrationValidate";s:4:"desc";s:38:"Id of the registration validation page";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"1501";s:7:"lexicon";N;}}', '');
