(97, 'designPreviewAJAXProcessor', 'Design preview AJAX processor', 0, 31, 0, '/**\n * Wedding site Design page processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the URL parameters */\n$theme = $_REQUEST[''theme''];\n$style = $_REQUEST[''style''];\n$colour = $_REQUEST[''colour''];\n\n/* Get this templates preview set */\n$templateName = $theme . "-" . $style . "-" . $colour;\n$templateName = strtolower($templateName);\n$previewContainerId = $modx->runSnippet(''designsGetPreviewSet'', array(''templateName'' => $templateName));\n$previewContainer = $modx->getObject(''modResource'', $previewContainerId);\nif ( !$previewContainer ) return;\n$context = $modx->context->get(''key'');\n\n/* Build the link array */\n$urlArray = array();\n$children = $previewContainer->getMany(''Children'');\nforeach ( $children as $child ) {\n        \n    $id = $child->get(''id'');\n    $childLink = $modx->makeURL($id, $context, "", "full");\n    $urlArray[] = $childLink;\n}\n\n$dataArray = array();\n\n/* Generate the title */\n$title = "<strong>". ucfirst($theme) . " " . ucfirst($style) . " " . ucfirst($colour) . "</strong>";\n\n/* Build the data array */\n$dataArray[''links''] = $urlArray;\n$dataArray[''title''] = $title;\n\n/* Return the data array */\n$dataString = json_encode($dataArray);\necho $dataString;', 0, 'a:2:{s:24:"designPreviewContainerId";a:6:{s:4:"name";s:24:"designPreviewContainerId";s:4:"desc";s:24:"Design preview container";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"473";s:7:"lexicon";N;}s:15:"previewNonePage";a:6:{s:4:"name";s:15:"previewNonePage";s:4:"desc";s:39:"Indicates no preview sets are avaialble";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"510";s:7:"lexicon";N;}}', ''),
(43, 'registrationFormAJAXProcessor', 'AJAX processing for registration', 0, 20, 0, '/**\n * Wedding site Registration processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Validate the nominated remote fields, validator requires a simple ''true'' or ''false'' \n *  value returning, indicating passed validation or failed respectively */\n\n/* Email */\nif ( $_GET[''registrationEmail''] != "" ) {\n	\n    /* Check if we already have a user with this name*/\n    $userEmail = $_GET[''registrationEmail''];\n    $user = $modx->getObject(''modUser'', array(''username'' => $userEmail));\n    \n    if ( $user ) {\n		return ''false'';\n	} else {\n		return ''true'';\n	}\n}\n\n/* Website name */\nif ($_GET[''userRegistrationWebsiteName''] != "") {\n\n    /* Check if we already have one in use by another user */\n    $websiteName = $_GET[''userRegistrationWebsiteName''];\n    $user = $modx->getObject(''weddingUserAttribute'', array(''website'' => $websiteName));\n\n    if  ( $user ) {\n        return ''false'';\n    } else {\n        return ''true'';\n    }\n}\n\n/* If we have reached here its an unknown field so fail validation */\nreturn ''false'';', 0, 'a:0:{}', '');
