(34, 'ForgotPassword', 'Displays a forgot password form.', 0, 11, 0, '/**\n * ForgotPassword\n *\n * Copyright 2010 by Jason Coward <jason@modx.com> and Shaun McCormick\n * <shaun@modx.com>\n *\n * ForgotPassword is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * ForgotPassword is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * ForgotPassword; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package login\n */\n/**\n * MODx ForgotPassword Snippet. Displays a form for users who have forgotten\n * their password and gives them the ability to retrieve it.\n *\n * @package login\n */\n$model_path = $modx->getOption(''core_path'').''components/login/model/login/'';\n$Login = $modx->getService(''login'',''Login'',$model_path,$scriptProperties);\n\n$modx->lexicon->load(''login:forgotpassword'');\n\n/* set default properties */\n$tpl = !empty($tpl) ? $tpl : ''lgnForgotPassTpl'';\n$tplType = !empty($tplType) ? $tplType : ''modChunk'';\n$sentTpl = !empty($sentTpl) ? $sentTpl : ''lgnForgotPassSentTpl'';\n$sentTplType = !empty($sentTplType) ? $sentTplType : ''modChunk'';\n$emailTpl = !empty($emailTpl) ? $emailTpl : ''lgnForgotPassEmail'';\n$emailTplType = !empty($emailTplType) ? $emailTplType : ''modChunk'';\n$emailSubject = !empty($emailSubject) ? $emailSubject : '''';\n$resetResourceId = !empty($resetResourceId) ? $resetResourceId : 1;\n$redirectTo = $modx->getOption(''redirectTo'',$scriptProperties,false);\n$redirectParams = $modx->getOption(''redirectParams'',$scriptProperties,'''');\n\n/* get the request URI */\n$phs = array(\n    ''loginfp.request_uri'' => empty($_POST[''request_uri'']) ? $Login->getRequestURI() : $_POST[''request_uri''],\n);\n\nif (!empty($_POST[''login_fp_service''])) {\n    $success = false;\n    $field = ''username'';\n    $alias = ''modUser'';\n    if (empty($_POST[''username'']) && !empty($_POST[''email''])) {\n        $field = ''email'';\n        $alias = ''Profile'';\n    }\n\n    /* get the user dependent on the retrieval method */\n    $user = $Login->getUserByField($field,$_POST[$field],$alias);\n    if ($user == null) {\n        $phs[''loginfp.errors''] = $modx->lexicon(''login.user_err_nf_''.$field);\n    } else {\n        $phs[''email''] = $user->get(''email'');\n\n        /* generate a password and encode it and the username into the url */\n        $pword = $Login->generatePassword();\n        $confirmParams = ''lp=''.urlencode(base64_encode($pword));\n        $confirmParams .= ''&lu=''.urlencode(base64_encode($user->get(''username'')));\n        $confirmUrl = $modx->makeUrl($resetResourceId,'''',$confirmParams,''full'');\n\n        /* set the email properties */\n        $emailProperties = $user->toArray();\n        $emailProperties[''confirmUrl''] = $confirmUrl;\n        $emailProperties[''password''] = $pword;\n        $emailProperties[''tpl''] = $emailTpl;\n        $emailProperties[''tplType''] = $emailTplType;\n\n        /* now set new password to cache to prevent middleman attacks */\n        $modx->cacheManager->set(''login/resetpassword/''.$user->get(''username''),$pword);\n\n        $subject = !empty($emailSubject) ? $emailSubject : $modx->getOption(''login.forgot_password_email_subject'',$scriptProperties,$modx->lexicon(''login.forgot_password_email_subject''));\n        $Login->sendEmail($user->get(''email''),$user->get(''username''),$subject,$emailProperties);\n        $tpl = $sentTpl;\n\n        /* if redirecting, do so here */\n        if (!empty($redirectTo)) {\n            if (!empty($redirectParams)) $redirectParams = $modx->fromJSON($redirectParams);\n            $url = $modx->makeUrl($redirectTo,'''',$redirectParams,''full'');\n            $modx->sendRedirect($url);\n        }\n    }\n}\nif (!empty($_POST)) {\n    foreach ($_POST as $k => $v) {\n        $phs[''loginfp.post.''.$k] = str_replace(array(''['','']''),array(''&#91;'',''&#93''),$v);\n    }\n}\n\n$output = $Login->getChunk($tpl,$phs,$tplType);\n\nreturn $output;', 0, 'a:10:{s:8:"emailTpl";a:6:{s:4:"name";s:8:"emailTpl";s:4:"desc";s:33:"prop_forgotpassword.emailtpl_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:18:"lgnForgotPassEmail";s:7:"lexicon";s:16:"login:properties";}s:12:"emailTplType";a:6:{s:4:"name";s:12:"emailTplType";s:4:"desc";s:37:"prop_forgotpassword.emailtpltype_desc";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:4:"name";s:18:"opt_register.chunk";s:5:"value";s:8:"modChunk";}i:1;a:2:{s:4:"name";s:17:"opt_register.file";s:5:"value";s:4:"file";}i:2;a:2:{s:4:"name";s:19:"opt_register.inline";s:5:"value";s:6:"inline";}i:3;a:2:{s:4:"name";s:21:"opt_register.embedded";s:5:"value";s:8:"embedded";}}s:5:"value";s:8:"modChunk";s:7:"lexicon";s:16:"login:properties";}s:7:"sentTpl";a:6:{s:4:"name";s:7:"sentTpl";s:4:"desc";s:32:"prop_forgotpassword.senttpl_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:20:"lgnForgotPassSentTpl";s:7:"lexicon";s:16:"login:properties";}s:11:"sentTplType";a:6:{s:4:"name";s:11:"sentTplType";s:4:"desc";s:36:"prop_forgotpassword.senttpltype_desc";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:4:"name";s:18:"opt_register.chunk";s:5:"value";s:8:"modChunk";}i:1;a:2:{s:4:"name";s:17:"opt_register.file";s:5:"value";s:4:"file";}i:2;a:2:{s:4:"name";s:19:"opt_register.inline";s:5:"value";s:6:"inline";}i:3;a:2:{s:4:"name";s:21:"opt_register.embedded";s:5:"value";s:8:"embedded";}}s:5:"value";s:8:"modChunk";s:7:"lexicon";s:16:"login:properties";}s:3:"tpl";a:6:{s:4:"name";s:3:"tpl";s:4:"desc";s:28:"prop_forgotpassword.tpl_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:16:"lgnForgotPassTpl";s:7:"lexicon";s:16:"login:properties";}s:7:"tplType";a:6:{s:4:"name";s:7:"tplType";s:4:"desc";s:32:"prop_forgotpassword.tpltype_desc";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:4:"name";s:18:"opt_register.chunk";s:5:"value";s:8:"modChunk";}i:1;a:2:{s:4:"name";s:17:"opt_register.file";s:5:"value";s:4:"file";}i:2;a:2:{s:4:"name";s:19:"opt_register.inline";s:5:"value";s:6:"inline";}i:3;a:2:{s:4:"name";s:21:"opt_register.embedded";s:5:"value";s:8:"embedded";}}s:5:"value";s:8:"modChunk";s:7:"lexicon";s:16:"login:properties";}s:12:"emailSubject";a:6:{s:4:"name";s:12:"emailSubject";s:4:"desc";s:37:"prop_forgotpassword.emailsubject_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}s:15:"resetResourceId";a:6:{s:4:"name";s:15:"resetResourceId";s:4:"desc";s:40:"prop_forgotpassword.resetresourceid_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";i:1;s:7:"lexicon";s:16:"login:properties";}s:10:"redirectTo";a:6:{s:4:"name";s:10:"redirectTo";s:4:"desc";s:35:"prop_forgotpassword.redirectto_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}s:14:"redirectParams";a:6:{s:4:"name";s:14:"redirectParams";s:4:"desc";s:39:"prop_forgotpassword.redirectparams_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"login:properties";}}', ''),
(35, 'ResetPassword', 'Resets a password from a confirmation email.', 0, 11, 0, '/**\n * ResetPassword\n *\n * Copyright 2010 by Shaun McCormick <shaun@modx.com>\n *\n * ResetPassword is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * ResetPassword is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * ResetPassword; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package login\n */\n/**\n * MODx ResetPassword Snippet. Snippet to place on an activation\n * page that the user using the ForgotPassword snippet would be sent to via the\n * reset email.\n *\n * @package login\n */\nif (empty($_REQUEST[''lp'']) || empty($_REQUEST[''lu''])) {\n    return '''';\n}\n$model_path = $modx->getOption(''core_path'').''components/login/model/login/'';\n$Login = $modx->getService(''login'',''Login'',$model_path,$scriptProperties);\n\n/* setup default properties */\n$tpl = !empty($tpl) ? $tpl : ''lgnResetPassTpl'';\n$tplType = !empty($tplType) ? $tplType : ''modChunk'';\n$loginResourceId = !empty($loginResourceId) ? $loginResourceId : 1;\n$debug = isset($debug) ? $debug : false;\n\n/* get user from query params */\n$username = base64_decode(urldecode($_REQUEST[''lu'']));\n$password = base64_decode(urldecode($_REQUEST[''lp'']));\n\n/* validate we have correct user */\n$user = $modx->getObject(''modUser'',array(''username'' => $username));\nif ($user == null) return '''';\n\n/* validate password to prevent middleman attacks */\n$cacheKey = ''login/resetpassword/''.$user->get(''username'');\n$cachePass = $modx->cacheManager->get($cacheKey);\nif ($cachePass != $password) return '''';\n$modx->cacheManager->delete($cacheKey);\n\n/* change password */\n$user->set(''password'',md5($password));\nif (!$debug) {\n    if ($user->save() == false) return '''';\n}\n\n$modx->invokeEvent(''OnWebChangePassword'', array (\n    ''userid'' => $user->get(''id''),\n    ''username'' => $user->get(''username''),\n    ''userpassword'' => $password,\n    ''user'' => &$user,\n    ''newpassword'' => $password,\n));\n$modx->invokeEvent(''OnUserChangePassword'', array (\n    ''userid'' => $user->get(''id''),\n    ''username'' => $user->get(''username''),\n    ''userpassword'' => $password,\n    ''user'' => &$user,\n    ''newpassword'' => $password,\n));\n\n$phs = array(\n    ''username'' => $user->get(''username''),\n    ''loginUrl'' => $modx->makeUrl($loginResourceId),\n);\n\n$output = $Login->getChunk($tpl,$phs,$tplType);\n\nreturn $output;', 0, 'a:3:{s:3:"tpl";a:6:{s:4:"name";s:3:"tpl";s:4:"desc";s:27:"prop_resetpassword.tpl_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:15:"lgnResetPassTpl";s:7:"lexicon";s:16:"login:properties";}s:7:"tplType";a:6:{s:4:"name";s:7:"tplType";s:4:"desc";s:31:"prop_resetpassword.tpltype_desc";s:4:"type";s:4:"list";s:7:"options";a:4:{i:0;a:2:{s:4:"name";s:18:"opt_register.chunk";s:5:"value";s:8:"modChunk";}i:1;a:2:{s:4:"name";s:17:"opt_register.file";s:5:"value";s:4:"file";}i:2;a:2:{s:4:"name";s:19:"opt_register.inline";s:5:"value";s:6:"inline";}i:3;a:2:{s:4:"name";s:21:"opt_register.embedded";s:5:"value";s:8:"embedded";}}s:5:"value";s:8:"modChunk";s:7:"lexicon";s:16:"login:properties";}s:15:"loginResourceId";a:6:{s:4:"name";s:15:"loginResourceId";s:4:"desc";s:39:"prop_resetpassword.loginresourceid_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";i:1;s:7:"lexicon";s:16:"login:properties";}}', '');
