 \n  \n            /* Attach datatables */\n            $("#invitesGuestList").css("width", "100%");\n            WS.gTable =  $("#invitesGuestList").dataTable({\n                "bDestroy": true});\n                \n            /* Apply the jEditable handlers to the table */\n            $(".guestEditableYN", WS.gTable.fnGetNodes()).editable( '' . $ajaxLink . '', {\n\n                "id"   : "elementid",\n                "callback": function( sValue, y ) {\n                    var aPos = WS.gTable.fnGetPosition( this );\n                    WS.gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n                },\n                "submitdata": function ( value, settings ) {\n                    var ret_arr = WS.gTable.fnGetPosition( this );\n                    var row = ret_arr[0];\n                    var data_row = WS.gTable.fnGetData(row);\n                    var primaryKey = data_row[0];\n                    var col = ret_arr[2];\n                    var oSettings = WS.gTable.fnSettings();\n                    var columnName = oSettings.aoColumns[col].sTitle;  \n                    return {\n                        "row_id": primaryKey,\n                        "column": columnName,\n                        "type"  : "yesnoedit"\n                    };\n                },\n                "height": "14px",\n                "type" : "select",\n                "data" : '' . $selector . ''\n                "tooltip"   : "Click to edit..."\n            });\n\n            $(".guestEditable", WS.gTable.fnGetNodes()).editable( '' . $ajaxLink . '', {\n\n                    "id"   : "elementid",\n                    "callback": function( sValue, y ) {\n                        var aPos = WS.gTable.fnGetPosition( this );\n                        if ( sValue == "" ) {\n                            this.editing = true;\n                            this.reset();\n                            this.editing = false;\n                        } else {\n                            WS.gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n                        }\n                    },\n                    "submitdata": function ( value, settings ) {\n                        var ret_arr = WS.gTable.fnGetPosition( this );\n                        var col = ret_arr[2];\n                        var row = ret_arr[0];\n                        var data_row = WS.gTable.fnGetData(row);\n                        var primaryKey = data_row[0];\n                        var oSettings = WS.gTable.fnSettings();\n                        var columnName = oSettings.aoColumns[col].sTitle;  \n                        return {\n                            "row_id": primaryKey,\n                            "column": columnName,\n                            "type"  : "normaledit"\n                        };\n                    },\n                    "height": "14px",\n                    "tooltip"   : "Click to edit..."\n                });        \n         });\n   }; \n   WS.themeSelect = function(link){\n        var theme = $(link).attr("id");\n        $("#userInvitesEventsTheme").val(theme);\n    };\n    WS.filterTheme = function() {\n       /* Hide all the themes */\n       var themes = $(".invite-theme").hide();\n       $(themes).each(function() {\n          var linkId = $(this).attr("id");\n          var themeArray = linkId.split("-");\n          var theme = themeArray[0];\n          if ( theme == WS.currentFilterTheme)  {\n            if ( WS.firstTheme == "" ) WS.firstTheme = this;\n            $(this).show();\n          }\n       });\n    };\n    WS.filterColour = function() {\n       /* Hide all the themes */\n       var colours = $(".invite-theme").hide();\n       $(colours).each(function() {\n          var linkId = $(this).attr("id");\n          var colourArray = linkId.split("-");\n          var colour = colourArray[2];\n          if ( colour == WS.currentFilterColour) {\n            if ( WS.firstColour == "" ) WS.firstColour = this;\n            $(this).show();\n          }  \n       });\n    };\n    \n        \n</script>'';\n$modx->regClientStartupScript($setup, true);\n\n/* Page script for the event select  and the theme selection */\n$page1 = ''<script>\n    \n$(document).ready(function(){  \n      \n    $("#userInvitesEventsEventSelection").change(function() {\n        var id = $("#userInvitesEventsEventSelection").val();\n        WS.getEventData(id);\n    });\n    \n    /* Trigger the currently selected event */\n    $("#userInvitesEventsEventSelection").trigger("change");\n    \n   /* Theme selection */\n   $(".invite-theme").bind("click", function(){\n      var link = this;  \n      WS.themeSelect(link);\n      });\n      \n    /* Theme filtering */\n    \n    /* Theme */\n    $("#userInvitesThemeSelection").change(function() {\n        WS.currentFilterTheme = $("#userInvitesThemeSelection").val();\n        WS.firstTheme = "";\n        WS.filterTheme();\n        $(WS.firstTheme).trigger("click");\n    });\n    \n    /* Colour */\n    $("#userInvitesColourSelection").change(function() {\n       WS.currentFilterColour = $("#userInvitesColourSelection").val();\n       WS.firstColour = "";\n       WS.filterColour();\n       $(WS.firstColour).trigger("click");\n    });\n    \n     /* Datatables/Jeditable */\n     \n     /* Force submit on change of Yes/No selectors */\n     $("#invitesGuestListBody select").live("change", function () {\n             $(this).parent().submit();\n     });\n      \n});\n\n   \n    </script>'';\n$modx->regClientStartupScript($page1, true);\n\n/* Datatables/jeditable */\n\n/* Include tables the setup code */\n\n$datePicker = ''<script>$(document).ready(function(){\n  $(function() {\n    $( "#userInvitesEventsDatepicker" ).datepicker({\n      showOn: "both",\n      buttonImage: "'' . $assets_url. ''templates/wedding/images/calendar.gif",\n      buttonImageOnly: true,\n      minDate: -2, maxDate: "+48M +28D",\n      changeYear: true,\n      changeMonth: true,\n            dateFormat: "yy-mm-dd"\n    });\n  });\n    \n});</script>'';\n\n$modx->regClientStartupScript($datePicker, true);', 0, 'a:1:{s:19:"userInvitesAJAXPage";a:6:{s:4:"name";s:19:"userInvitesAJAXPage";s:4:"desc";s:30:"User Invites page AJAX page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"998";s:7:"lexicon";N;}}', ''),
(52, 'userSettingsPageAJAXProcessor', '', 0, 24, 0, '/**\n * Wedding site User Settings processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user)\n    $userId = $user->get(''id'');\nif ($userId == "") {\n\n    echo "Error! No logged in user";\n    return;\n}\n\n\n/* Check for an update command, if not its simple field validation */\nif ($_REQUEST[''command''] == "ajaxUpdate") {\n\n\n    /* Get the element and the value */\n    $element = $_REQUEST[''element''];\n    $value = $_REQUEST[''val''];\n\n    /* Get the user details */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ($weddingUserObject == null)\n        return;\n    $userProfile = $user->getOne(''Profile'');\n    $weddingUser = $weddingUserObject->getOne(''Attributes'');\n\n    /* Process the element */\n    switch ($element) {\n\n        case ''userSettingsWebsiteAddress'' :\n\n            $website = $value;\n            $websiteChanged = false;\n            $existingWebsite = $weddingUser->get(''website'');   \n            $existingWebsiteSet = true;\n            if ($existingWebsite == "")\n                $existingWebsiteSet = false;\n            if ($existingWebsite != $website)\n                $websiteChanged = true;\n            $weddingUser->set(''website'', $website);\n            \n            /* Save it now for later processing */\n            $weddingUser->save();\n\n            /* If we don''t have any existing website do the user create */\n            if (!$existingWebsiteSet) {\n\n               $modx->runSnippet(''userCreateWebsite'', array(''website'' => $website));\n                \n            } else {\n\n                /* Update, check for a website change */\n                if ($websiteChanged) {\n\n                      $modx->runSnippet(''userRenameWebsite'', array(''website'' => $website,\n                                                                   ''existingWebsite'' => $existingWebsite));\n                    \n                }\n            }\n\n            break;\n\n        case ''userSettingsPackage'' :\n\n            $weddingUser->set(''packageType'', $value);\n            $weddingUser->save();\n\n            break;\n\n        case ''userSettingsPartner1'' :\n\n            $weddingUser->set(''partnerName1'', $value);\n            $weddingUser->save();\n\n            break;\n\n        case ''userSettingsPartner2'' :\n\n            $weddingUser->set(''partnerName2'', $value);\n            $weddingUser->save();\n\n            break;\n\n        case ''userSettingsDatepicker'':\n\n            $dateText = $value;\n            $dateArray = explode(''/'', $dateText);\n            $temp = $dateArray[2];\n            $dateArray[2] = $dateArray[0];\n            $dateArray[0] = $temp;\n            $dateText = implode(''-'', $dateArray);\n            $date = strtotime($dateText);\n            $weddingUser->set(''date'', $date);\n            $weddingUser->save();\n\n            break;\n\n        case ''userSettingsTheme'' :\n\n            $weddingUser->set(''theme'', $value);\n            $weddingUser->save();\n\n            break;\n\n        case ''userSettingsPassProtect'' :\n\n            $weddingUser->set(''passwordProtect'', 0);\n            if ($value == 1)\n                $weddingUser->set(''passwordProtect'', 1);\n            $weddingUser->save();\n\n            break;\n            \n       case ''userSettingsWebsiteSearchable'' :\n\n            $weddingUser->set(''websiteSearchable'', 0);\n            if ($value == 1)\n                $weddingUser->set(''websiteSearchable'', 1);\n            $weddingUser->save();\n\n            break;\n            \n        case ''userSettingsDisplayCountdown'' :\n\n            $weddingUser->set(''displayCountdown'', 0);\n            if ($value == 1)\n                $weddingUser->set(''displayCountdown'', 1);\n            $weddingUser->save();\n\n            break;\n\n        case ''userSettingsModerateGuestbook'' :\n\n            $weddingUser->set(''moderateGuestbook'', 0);\n            if ($value == 1)\n                $weddingUser->set(''moderateGuestbook'', 1);\n            $weddingUser->save();\n\n            break;\n            \n        case ''userSettingsPassword'' :\n\n            $weddingUser->set(''websitePassword'', $value);\n            $weddingUser->save();\n\n            break;\n        \n        case ''userSettingsFirstName'' :\n            \n            $weddingUser->set(''firstName'', $value);\n            $weddingUser->save();\n\n            break;\n        \n        case ''userSettingsLastName'' :\n            \n            $weddingUser->set(''lastName'', $value);\n            $weddingUser->save();\n\n            break;\n        \n        case ''userSettingsEmail'' :\n            \n            $userProfile->set(''email'', $value);\n            $userProfile->save();\n            \n            break;\n        \n        case ''userSettingsTel'' :\n            \n            $userProfile->set(''phone'', $value);\n            $userProfile->save();\n            \n            break;\n        \n        case  ''userSettingsCountry'' :\n            \n            /* Convert the country code into text for MODX */\n            $_country_lang = array();\n            include $modx->getOption(''core_path'').''lexicon/country/en.inc.php'';\n            $country = $_country_lang[$value];\n            $userProfile->set(''country'', $country);\n            $userProfile->save();\n            \n            break;\n        \n        case ''userSettingsAddressStreet'' :\n            \n            $userProfile->set(''address'', $value);\n            $userProfile->save();\n            \n            break;\n        \n        case ''userSettingsCity'' :\n            \n            $userProfile->set(''city'', $value);\n            $userProfile->save();\n            \n            break;\n        \n        case ''userSettingsSocialFacebook'' :\n            \n            $weddingUser->set(''socialFacebook'', $value);\n            $weddingUser->save();\n\n            break;\n        \n        case ''userSettingsSocialTwitter'' :\n            \n            $weddingUser->set(''socialTwitter'', $value);\n            $weddingUser->save();\n\n            break;\n        \n        case ''userSettingsSocialGoogle'' :\n       \n            $weddingUser->set(''socialGoogle'', $value);\n            $weddingUser->save();\n\n            break;\n        \n        case ''userSettingsSocialOther1'' :\n            \n            $weddingUser->set(''socialOther1'', $value);\n            $weddingUser->save();\n\n            break;\n        \n         case ''userSettingsSocialOther2'' :\n            \n            $weddingUser->set(''socialOther2'', $modx->quote($value));\n            $weddingUser->save();\n\n            break;\n        \n        default:\n\n            break;\n        \n    }\n    \n    return;\n}\n\n/* Validate the nominated remote fields, validator requires a simple ''true'' or ''false'' \n *  value returning, indicating passed validation or failed respectively */\n\n/* Email */\nif ($_GET[''userSettingsEmail''] != "") {\n\n    /* Check if we already have one in use by another user */\n    $userEmail = $_GET[''userSettingsEmail''];\n    $id = $modx->user->get(''id'');\n    $c = $modx->newQuery(''modUser'');\n    $c->leftJoin(''modUserProfile'', ''Profile'');\n    $c->where(array(''Profile.email:='' => $userEmail));\n    $c->andCondition(array(''Profile.internalKey:!='' => $id));\n    $count = $modx->getCount(''modUser'', $c);\n\n    if ($count == 0) {\n        return ''true'';\n    } else {\n        return ''false'';\n    }\n}\n\n/* Website address */\nif ($_GET[''userSettingsWebsiteAddress''] != "") {\n\n    /* Check if we already have one in use by another user */\n    $id = $modx->user->get(''id'');\n    $webSite = $_GET[''userSettingsWebsiteAddress''];\n    $c = $modx->newQuery(''weddingUser'');\n    $c->leftJoin(''modUserProfile'', ''Profile'');\n    $c->rightJoin(''weddingUserAttribute'', ''Attributes'');\n    $c->where(array(''Attributes.website:='' => $webSite));\n    $c->andCondition(array(''Profile.internalKey:!='' => $id));\n    $users = $modx->getCollection(''weddingUser'', $c);\n\n    if (empty($users)) {\n        return ''true'';\n    } else {\n        return ''false'';\n    }\n}\n\n/* If we have reached here its an unknown field so fail validation */\nreturn ''false'';', 0, 'a:1:{s:22:"userWebpageContainerId";a:6:{s:4:"name";s:22:"userWebpageContainerId";s:4:"desc";s:20:"User pages container";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
