( this );\n             var row = ret_arr[0];\n             var data_row = gTable.fnGetData(row);\n             var primaryKey = data_row[0];\n             var col = ret_arr[2];\n             var oSettings = gTable.fnSettings();\n             var columnName = oSettings.aoColumns[col].sTitle;  \n             return {\n                "row_id": primaryKey,\n                "column": columnName,\n                "type"  : "yesnoedit"\n            };\n        },\n        "height": "14px",\n        "type" : "select",\n        "data" : '' . $selector . ''\n        "tooltip"   : "Click to edit..."\n    } );\n    \n$(".guestEditableInvite", gTable.fnGetNodes()).editable( "'' . $ajaxEditableLink . ''", {\n    \n        "id"   : "elementid",\n        "callback": function( sValue, y ) {\n            var aPos = gTable.fnGetPosition( this );\n            gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n        },\n        "submitdata": function ( value, settings ) {\n             var ret_arr = gTable.fnGetPosition( this );\n             var row = ret_arr[0];\n             var data_row = gTable.fnGetData(row);\n             var primaryKey = data_row[0];\n             var col = ret_arr[2];\n             var oSettings = gTable.fnSettings();\n             var columnName = oSettings.aoColumns[col].sTitle;  \n             return {\n                "row_id": primaryKey,\n                "column": columnName,\n                "type"  : "inviteedit"\n            };\n        },\n        "height": "14px",\n        "type" : "select",\n        "data" : '' . $inviteSelector . ''\n        "tooltip"   : "Click to edit..."\n    } );\n    \n   $(".guestEditable", gTable.fnGetNodes()).editable( "'' . $ajaxEditableLink . ''", {\n        \n        "id"   : "elementid",\n        "callback": function( sValue, y ) {\n            var aPos = gTable.fnGetPosition( this );\n            if ( sValue == "" ) {\n                this.editing = true;\n                this.reset();\n                this.editing = false;\n            } else {\n                gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n            }\n        },\n        "submitdata": function ( value, settings ) {\n            var ret_arr = gTable.fnGetPosition( this );\n            var col = ret_arr[2];\n            var row = ret_arr[0];\n            var data_row = gTable.fnGetData(row);\n            var primaryKey = data_row[0];\n            var oSettings = gTable.fnSettings();\n            var columnName = oSettings.aoColumns[col].sTitle;  \n             return {\n                "row_id": primaryKey,\n                "column": columnName,\n                "type"  : "normaledit"\n            };\n        },\n        "height": "14px",\n        "tooltip"   : "Click to edit..."\n    } );\n    \n    /* Force submit on change of Yes/No selectors */\n    $("select").live("change", function () {\n             $(this).parent().submit();\n        });\n      \n    /* Confirm box for the delete link */\n    $(".deleteLink").jConfirmAction();\n    \n});</script>'';\n\n$modx->regClientStartupScript($guestList, true);', 0, 'a:1:{s:12:"ajaxEditable";a:6:{s:4:"name";s:12:"ajaxEditable";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"52";s:7:"lexicon";N;}}', ''),
(54, 'userGuestsPageInitialiseTables', '', 0, 26, 0, '/**\n<?php\n/**\n * Wedding site Guest Settings processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the guest list\n */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userSettingError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userSettingError'', "Error - No user found please log in");\n    return;\n}\n\n/* Get the wedding users guests */\n$guests = $weddingUser->getMany(''Guests'');\n\n/* Get the wedding users events */\n$events = $weddingUser->getMany(''Events'');\n\n/* Create the table columns from the wedding users events */\n$output = "";\n$eventNameArray = array();\nforeach ( $events as $event ) {\n    \n    $activeState = $event->get(''active'');\n    /* Only active events */\n    if ( $activeState ) {\n    \n        $name = $event->get(''name'');\n        $id = $event->get(''id'');\n        $eventIdArray[] = $id;\n        $output .= "<th>" . $name . "</th>";\n    \n    }\n}\n\n/* Set the column placeholder */\n$modx->setPlaceholder(''userGuestEventColumns'', $output);\n\n/* Now Iterate through the guests and create the table body */\n$output = "";\n$context = $modx->context->get(''key'');\nforeach ($guests as $guest) {\n    \n    $guestArray = $guest->toArray();\n    \n    /* Get the event array into a string we can use in the table body for this guest */\n    $attendOutput = "";\n    foreach ( $eventIdArray as $id ) {\n    \n        /* See if we are invited and what attendance there has been */\n        $guestEvent = $modx->getObject(''guestEvents'', array(\n                               ''guestId'' => $guestArray[''id''],\n                               ''eventId'' => $id)); \n        \n        if ( !$guestEvent )  {\n            \n            $attend = "Not Invited";\n        \n        } else {\n            \n            /* Invited , check status */\n            $attend = "Invited";\n            $attendance = $guestEvent->get(''willAttend'');\n            if ( $attendance == 1 ) { \n                \n                 $attend = "Yes";\n                \n            } else {\n                \n                $rsvpOnline = $guestEvent->get(''RSVPdOnline'');\n                $rsvpManual = $guestEvent->get(''RSVPdManual'');\n                if (  $rsvpOnline ||  $rsvpManual) {\n                    \n                     $attend = "No";\n                }\n                \n            }\n        }\n         $attendOutput .= ''<td class="guestEditableInvite">'' . $attend. "</td>";\n        \n    }\n    \n   \n\n    /* Get the guests GUID */\n    $guid = $modx->runSnippet(''getGuestsGUID'', array(''guestId'' => $guestArray[''id''] ));\n    \n    /* Make the edit link */\n    $args = "&guestId=" . $guestArray[''id''];\n    $editURL = $modx->makeURL($userGuestEditPage, $context, $args, "full" );\n    \n    /* Make the delete link */\n    $args = "&row_id=" . $guestArray[''id''];\n    $args .= "&type=deleteGuest";\n    $deleteURL = $modx->makeURL($userGuestAJAX, $context, $args, "full" );\n    \n    $placeHolderArray = array ( ''userGuestId'' => $guestArray[''id''],\n                                ''userGuestName'' => $guestArray[''name''],\n                                ''userGuestEvents'' => $attendOutput,\n                                ''userGuestGuestOf'' => $guestArray[''guestOf''],\n                                ''userGuestGUID'' => $guid,\n                                ''userGuestActive'' => $guestArray[''active''] ? "Yes" : "No",\n                                ''userGuestEditLink'' => $editURL,\n                                ''userGuestDeleteLink'' => $deleteURL);\n    \n    $chunk = $modx->getChunk(''userGuestsList'', $placeHolderArray);\n    $output .= $chunk;\n    \n}\n\n$modx->setPlaceholder(''userGuestList'', $output);\n\nreturn;', 0, 'a:2:{s:13:"userGuestAJAX";a:6:{s:4:"name";s:13:"userGuestAJAX";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"52";s:7:"lexicon";N;}s:17:"userGuestEditPage";a:6:{s:4:"name";s:17:"userGuestEditPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"47";s:7:"lexicon";N;}}', '');
