(55, 'userGuestAddEditInitialiseForms', '', 0, 26, 0, '/**\n * Wedding site User Guest processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the guest edit/add form\n */\n\n$modx->setPlaceholder(''userGuestError'',"");\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userGuestError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userGuestError'', "Error - No user found please log in");\n    return;\n}\n\n/* Check for add or edit */\nif ( isset($_GET[''guestId'']) ) {\n    \n    /* Edit call, get the guest data */\n    $guestId = $_GET[''guestId''];\n    $weddingGuest = $modx->getObject(''weddingUserGuest'', array(''id''=>$guestId));\n    if ($weddingGuest == null ) {            \n        $modx->setPlaceholder(''userGuestError'', "Error - No guest details found");     \n        return;\n    }\n         \n    /* Set the placeholders */\n    $guestArray = $weddingGuest->toArray(); \n    $modx->setPlaceholder(''userGuestsId'', $guestId);\n    $modx->setPlaceholder(''userGuestsName'', $guestArray[''name'']);\n    $modx->setPlaceholder(''userGuestsEmail'', $guestArray[''email'']);\n    $modx->setPlaceholder(''userGuestsAddressLine1'', $guestArray[''address1'']);\n    $modx->setPlaceholder(''userGuestsAddressLine2'', $guestArray[''address2'']);\n    $modx->setPlaceholder(''userGuestsCity'', $guestArray[''city'']);\n    $modx->setPlaceholder(''userGuestsPostcode'', $guestArray[''postCode'']);\n    $modx->setPlaceholder(''userGuestsTel'', $guestArray[''telephone'']);\n$modx->setPlaceholder(''userGuestsGuestOf'', $guestArray[''guestOf'']);\n$modx->setPlaceholder(''userGuestsRSVP'', $guestArray[''rsvp''] ? "checked" : "");\n    $modx->setPlaceholder(''userGuestsActive'', $guestArray[''active''] ? "checked" : "");\n       \n}', 0, NULL, ''),
(56, 'userGuestsAddEditJSInclude', '', 0, 26, 0, '/**\n * Wedding site Guest Add/Edit processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Set our jQuery paths */\n$validatorPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$validatorPath .= ''js/jquery-validation-1.8.1'';\n$assets_url = $modx->getOption(''assets_url'');\n\n/* Include the validation CSS */\n$validationCSS = ''assets/templates/wedding/styles/validation.css'';\n$modx->regClientCSS($validationCSS);\n\n/* Include the includes */\n$html1 = $validatorPath . ''/lib/jquery.metadata.js'';\n$html2 = $validatorPath . ''/jquery.validate.js'';\n$modx->regClientStartupScript($html1);\n$modx->regClientStartupScript($html2);\n\n/* Include the code */\n$initialise = ''<script>jQuery.validator.setDefaults({\n	debug: '' . $debug . '',\n	success: "valid"\n});\n\n// Check for generally unwanted characters\n  jQuery.validator.addMethod("validcharsgeneral", function (value) {\n          var result = true;\n          // unwanted character list\n          var iChars = "!@#$%^&*()+=[]\\\\\\'';,/{}|\\":<>?";\n          for (var i = 0; i < value.length; i++) {\n             if (iChars.indexOf(value.charAt(i)) != -1) {\n                return false;\n             }\n          }\n          return result;\n\n  }, "Invalid characters entered");\n  \n</script>'';\n\n/* Add/Edit form */\n$form = ''<script>$(document).ready(function(){\n    $("#guestAddEditForm").validate({\n    \n        messages: { \n            \n            userGuestsEmail: { \n                email: "Sorry - this is not a valid email address."\n				\n	    },		\n            userGuestsName: {\n                required: "This field is required."\n            }, \n            userGuestsEmail: {\n                required: "This field is required."\n            }\n        }, \n            errorPlacement: function(error, element) {                                                    \n				element.parent().children("#formvalidationerror").append(error);  			\n		},\n            invalidHandler: function() { \n                     alert("Please correct the errors on the form and re-submit it"); \n                }\n  });\n  \n  /* Add the events list AJAX processing */\n  currentLink = "";\n  $(".ajaxLink").bind("click",function(event){\n      event.preventDefault();\n            var yesno = "No";\n            if ( this.classList[0]== "off" ) yesno = "Yes";\n            var args = "&value="+yesno;\n            currentLink = this;\n        $.get(this.href,args,function(response){\n               if ( response == "Yes" ) {\n                    currentLink.className = currentLink.className.replace(/\\boff\\b/,"");\n                    currentLink.className += " on";\n               } else {\n                    currentLink.className = currentLink.className.replace(/\\bon\\b/,"");\n                    currentLink.className += " off";\n               }\n        })  \n     });\n     \n  /* Add guest event checkbox handling */\n  $(".guestCheckBoxClass").change(function(){\n		if($(this).is(":checked")){\n			$(this).next("label").addClass("guestLabelSelected");\n		}else{\n			$(this).next("label").removeClass("guestLabelSelected");\n		}\n	});\n         \n  });</script>'';\n\n\n\n$modx->regClientStartupScript($initialise, true);\n$modx->regClientStartupScript($form, true);', 0, 'a:1:{s:5:"debug";a:6:{s:4:"name";s:5:"debug";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:5:"false";s:7:"lexicon";N;}}', ''),
(57, 'userGuestsAddEditPostProcessor', '', 0, 26, 0, '/**\n * Wedding site Guests add/edit  processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  The parameters from the settings forms form are already valid at this point, we only need\n * to update the wedding user\n */\n \n/* Check for a guest add/edit submit operation */\nif ( isset($_REQUEST[''userGuestsName'']) ) {\n  \n  /* Get the MODX user */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ( $userId == "" ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No MODX user found please log in");\n        return;\n    }\n    \n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ( $weddingUserObject == null ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No Wedding user found please log in");\n        return;\n    }\n  \n    /* Check for a guest Id, if none this is an add */\n    if ( $_REQUEST[''userGuestsId''] == "" ) {\n        \n        $guestObject = $modx->newObject(''weddingUserGuest'');\n        $updateType = "Add";\n        \n    } else {\n        \n    $guestObject = $modx->getObject(''weddingUserGuest'', \n                                     array(''id'' => $_REQUEST[''userGuestsId'']));\n    $updateType ="Update"; \n    \n    }\n    \n    if ( $guestObject == null ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No Guest with this ID found ");\n        return;\n    }\n    \n    $guestObject->set(''name'', $_REQUEST[''userGuestsName'']);\n    $guestObject->set(''email'', $_REQUEST[''userGuestsEmail'']);\n    $guestObject->set(''address1'', $_REQUEST[''userGuestsAddressLine1'']);\n    $guestObject->set(''address2'', $_REQUEST[''userGuestsAddressLine2'']);\n    $guestObject->set(''city'', $_REQUEST[''userGuestsCity'']);\n    $guestObject->set(''postCode'', $_REQUEST[''userGuestsPostcode'']);\n    $guestObject->set(''telephone'', $_REQUEST[''userGuestsTel'']);\n    $guestObject->set(''guestOf'', $_REQUEST[''userGuestsGuestOf'']);\n  $active = 0;\n    if ( isset($_REQUEST[''userGuestsActive''])) $active = 1;\n	$guestObject->set(''active'', $active);\n        \n    if ( $updateType == "Add" ) {\n        \n        $weddingUserObject->addMany($guestObject, ''Guests'');\n        if ( $weddingUserObject->save() == false ) {\n            $modx->setPlaceholder(''userSettingError'', "Error - Failed to create this wedding user guest"); \n            return;\n        }\n        \n        /* Process any associated events */\n        $guestId = $modx->lastInsertId();\n        $c = $modx->newQuery(''weddingUserEvent'');\n        $c->where(array(''user'' => $userId));      \n        $events = $modx->getCollection(''weddingUserEvent'',$c);\n        \n        foreach ( $events as $event ) {\n            \n            $eventId = $event->get(''id'');\n            $eventName = $event->get(''name'');\n            \n            /* Create the checkbox name */\n            $checkboxName = str_replace( '' '', '''', $eventName );\n            $checkboxName = "userGuests" . $checkboxName;\n           \n            /* Check if we have an association, no need to check for existence */\n            if ( $_REQUEST[$checkboxName] == 1 ) {\n                \n                /* Create one */\n                $guestEventObject = $modx->newObject(''guestEvents'');\n                $guestEventObject->set(''guestId'',$guestId);\n                $guestEventObject->set(''eventId'',$eventId);            \n                $guestEventObject->save();\n            }\n            \n        }\n        \n    } else {\n        \n        if (!$guestObject->save()) {\n            $modx->setPlaceholder(''userSettingError'', "Error - Failed to update this wedding user guest");    \n        }\n    }\n    \n    /* Redirect back to the guest list */\n    $context = $modx->context->get(''key'');\n    $pageURL = $modx->makeURL($guestListPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:1:{s:13:"guestListPage";a:6:{s:4:"name";s:13:"guestListPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"32";s:7:"lexicon";N;}}', ''),
(58, 'userGuestsPageEditableAJAXProcessor', '', 0, 26, 0, '/**\n * Wedding site Guest list AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Assemble the parameters from the request data */\n$guestId = $_REQUEST[''row_id''];\n$newValue = strip_tags($_REQUEST[''value'']);\n$columnName = $_REQUEST[''column''];\n$updateType = $_REQUEST[''type''];\n$echoValue = $newValue;\n\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user)\n    $userId = $user->get(''id'');\nif ($userId == "") {\n\n    echo "Error! No logged in user";\n    return;\n}\n\n\n/* Get the guest data and the associated events */\n$guestObject = $modx->getObject(''weddingUserGuest'', array(''id'' => $guestId));\nif (!$guestObject) {\n\n    echo "Error! No guest found";\n    return;\n}\n\n$user = $guestObject->get(''user'');\n\n/* Process the edit update */\nswitch ($updateType) {\n\n    case ''yesnoedit'' :\n\n        if ($columnName == "Active") {\n\n            $updateValue = 0;\n            if ($newValue == "Yes")\n                $updateValue = 1;\n            $guestObject->set(''active'', $updateValue);\n            if (!$guestObject->save())\n                $echoValue = "Error! Cant save guest";\n            break;\n        }\n\n        break;\n\n    case ''inviteedit'' :\n\n        /* Guest to Event mapping updates */\n\n        switch ($newValue) {\n\n            case ''Not Invited'' :\n\n                /* If ''Not Invited'' remove the link from this guest to the named event */\n                $guestEvents = $guestObject->getMany(''guestEvents'');\n                foreach ($guestEvents as $eventPtr) {\n\n                    $event = $eventPtr->getOne(''weddingUserEvent'');\n                    $name = $event->get(''name'');\n                    if ($name == $columnName)\n                        $eventPtr->remove();\n                }\n\n                break;\n\n            case ''Invited'' :\n\n                /* Invite the guest */\n                $eventObject = $modx->getObject(''weddingUserEvent'', array(\n                    ''user'' => $user,\n                    ''name'' => $columnName\n                        ));\n                if ($eventObject) {\n\n                    $eventId = $eventObject->get(''id'');\n\n                    /* Check for existence just in case */\n                    $checkObject = $modx->getObject(''guestEvents'', array(\n                        ''guestId'' => $guestId,\n                        ''eventId'' => $eventId\n                            ));\n                    if (!$checkObject) {\n\n                        $guestEventObject = $modx->newObject(''guestEvents'');\n                        $guestEventObject->set(''guestId'', $guestId);\n                        $guestEventObject->set(''eventId'', $eventId);\n                        $guestEventObject->set(''InviteSent'', 0);\n                        $guestEventObject->set(''RSVPdManual'', 0);\n                        $guestEventObject->set(''RSVPdOnline'', 0);\n                        $guestEventObject->set(''RSVPDate'', 0);\n                        $guestEventObject->set(''willAttend'', 0);\n                        $guestEventObject->set(''lastReminderDate'', 0);\n                        if (!$guestEventObject->save())\n                            $echoValue = "Error!";\n                    }\n                } else {\n\n                    $echoValue = "Error! No such Event";\n                }\n\n                break;\n\n            case ''Yes'' :\n\n                /* Manual attendance */\n                $eventObject = $modx->getObject(''weddingUserEvent'', array(\n                    ''user'' => $user,\n                    ''name'' => $columnName\n                        ));\n                if ($eventObject) {\n\n                    $eventId = $eventObject->get(''id'');\n\n                    /* Get the guest event object */\n                    $guestEventObject = $modx->getObject(''guestEvents'', array(\n                        ''guestId'' => $guestId,\n                        ''eventId'' => $eventId\n                            ));\n                    if (!$guestEventObject)\n                        return "Error";\n\n                    /* Update the RSVP data */\n                    $guestEventObject->set(''InviteSent'', 1);\n                    $guestEventObject->set(''RSVPdManual'', 1);\n                    $guestEventObject->set(''RSVPdOnline'', 0);\n                    $guestEventObject->set(''RSVPDate'', time());\n                    $guestEventObject->set(''willAttend'', 1);\n                    $guestEventObject->set(''lastReminderDate'', 0);\n                    if (!$guestEventObject->save())\n                        $echoValue = "Error!";\n                } else {\n\n                    $echoValue = "Error! No such Event";\n                }\n\n                break;\n\n            case ''No'' :\n\n                /* Manual non attendance */\n                $eventObject = $modx->getObject(''weddingUserEvent'', array(\n                    ''user'' => $user,\n                    ''name'' => $columnName\n                        ));\n                if ($eventObject) {\n\n                    $eventId = $eventObject->get(''id'');\n\n                    /* Get the guest event object */\n                    $guestEventObject = $modx->getObject(''guestEvents'', array(\n                        ''guestId'' => $guestId,\n                        ''eventId'' => $eventId\n                            ));\n                    if (!$guestEventObject)\n                        return "Error";\n\n                    /* Update the RSVP data */\n                    $guestEventObject->set(''InviteSent'', 1);\n                    $guestEventObject->set(''RSVPdManual'', 1);\n                    $guestEventObject->set(''RSVPdOnline'', 0);\n                    $guestEventObject->set(''RSVPDate'', time());\n                    $guestEventObject->set(''willAttend'', 0);\n                    $guestEventObject->set(''lastReminderDate'', 0);\n                    if (!$guestEventObject->save())\n                        $echoValue = "Error!";\n                } else {\n\n                    $echoValue = "Error! No such Event";\n                }\n\n                break;\n        }\n\n\n        break;\n\n\n    case ''normaledit'' :\n\n        /* Update the guest depending on the column name */\n        if ($columnName == ''Name'')\n            $guestObject->set(''name'', $newValue);\n        if ($columnName == ''Guest Of'')\n            $guestObject->set(''guestOf'', $newValue);\n        if (!$guestObject->save())\n            $echoValue = "Error! Can''t save guest";\n\n        break;\n\n    case ''deleteGuest'' :\n\n        /* Delete the guest */\n        $guestUser = $modx->getObject(''weddingUserGuest'', $guestId);\n        if ($guestUser == null)\n            return false;\n        $guestUser->remove();\n\n        /* Redirect back to the guest list page */\n        $context = $modx->context->get(''key'');\n        $pageURL = $modx->makeURL($guestListPage, $context, "", "full");\n        header("Location: {$pageURL}");\n\n        break;\n\n    default:\n\n        /* Shouldn''t happen */\n        $echoValue = "Error! Invalid update type";\n        break;\n}\n\n/* Return the output */\necho $echoValue;', 0, 'a:1:{s:13:"guestListPage";a:6:{s:4:"name";s:13:"guestListPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"32";s:7:"lexicon";N;}}', ''),
(59, 'userGuestAddEditInitialiseEvents', '', 0, 26, 0, '/**\n * Wedding site User Guest processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the guest edit/add events selection list\n */\n\n/* Get the current logged in user details, ignore errors here, these will be \n * picked up and reported on the main page form.\n */\n$user = $modx->user;\nif ( !$user ) return;\n$userId = $user->get(''id'');\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( !$weddingUser ) return;\n    \n/* Get this users events */\n$c = $modx->newQuery(''weddingUserEvent'');\n$c->where(array(''user'' => $userId));      \n$events = $modx->getCollection(''weddingUserEvent'',$c);\n    \n    \n/* Check for a guest id in the URL, if none this is an add operation */\nif ( $_REQUEST[''guestId''] == "" ) {\n    \n    /* Add */\n    $chunk = $modx->getChunk(''userGuestsAddEditHeaderAdd'');\n    $modx->setPlaceholder(''userGuestAddEditHeader'', $chunk);\n    \n    /* Generate the event list */\n    foreach ( $events as $event ) {\n        \n        $activeState = $event->get(''active'');\n        /* Only active events */\n        if ( $activeState ) {\n        \n            $eventId = $event->get(''id'');\n            $eventName = $event->get(''name'');\n            $eventNameConcat = str_replace( '' '', '''', $eventName ); \n            $placeholderArray = array ( ''eventName'' => $eventName,\n                                    ''eventNameConcat'' => $eventNameConcat);\n            $output .= $modx->getChunk(''userGuestAddEditEventListAdd'', $placeholderArray);\n          \n        }\n        \n    }\n    \n} else {\n    \n    /* Update */\n    $chunk = $modx->getChunk(''userGuestsAddEditHeaderUpdate'');\n    $modx->setPlaceholder(''userGuestAddEditHeader'', $chunk);\n    \n    /* Generate the event list */\n    $guestId = $_REQUEST[''guestId''];\n    $output = "";\n    foreach ( $events as $event ) {\n        \n        $activeState = $event->get(''active'');\n        /* Only active events */\n        if ( $activeState ) {\n            \n            $eventId = $event->get(''id'');\n            $eventName = $event->get(''name'');\n        \n            /* Check if the guest has got this event */\n            $onOff = "off";\n            if ( $modx->getObject(''guestEvents'', array(\n                                             ''guestId'' => $guestId,\n                                             ''eventId'' => $eventId)))\n            $onOff = "on";\n        \n            /* Assemble the output */\n            $context = $modx->context->get(''key'');\n            $args = "&row_id=$guestId&column=$eventName&type=yesnoedit";\n            $url = $modx->makeURL($eventAJAXLink, $context, $args, "full" );\n            $placeholderArray = array ( ''eventName'' => $eventName,\n                                    ''eventClassOnOff'' => $onOff,\n                                    ''eventOnOff'' => ucfirst($onOff),\n                                    ''eventAJAXLink'' => $url);\n       \n            $output .= $modx->getChunk(''userGuestAddEditEventListEdit'', $placeholderArray);\n            \n        }\n        \n    }\n    \n}\n\n/* Set the output placeholder */\n$modx->setPlaceholder(''userGuestAddEditEvents'', $output);', 0, 'a:1:{s:13:"eventAJAXLink";a:6:{s:4:"name";s:13:"eventAJAXLink";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"52";s:7:"lexicon";N;}}', ''),
(60, 'userEventsPageJSInclude', '', 0, 27, 0, '/**\n * Wedding site User Events processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Set our jQuery and datatables paths */\n$jQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n \n$datatablesMediaPath = $jQueryPath  . ''js/DataTables-1.8.1/media/'';\n$datatablesJSPath = $datatablesMediaPath . ''js/'';\n$jeditablePath = $jQueryPath . ''js/jeditable/'';\n\n/* Include datatables itself */\n$html1 = $datatablesJSPath . ''jquery.dataTables.min.js'';\n$modx->regClientStartupScript($html1);\n\n/* And jeditable */\n$html2 = $jeditablePath . ''jquery.jeditable.js'';\n$modx->regClientStartupScript($html2);\n\n/* And jeditable datepicker */\n$html3 = $jQueryPath . ''js/jquery.jeditable.datepicker.js'';\n$modx->regClientStartupScript($html3);\n\n$context = $modx->context->get(''key'');\n$ajaxEditableLink = $modx->makeURL($ajaxEditable, $context, "", "full");\n\n/* Include the setup code */\n$eventList = ''<script>$(document).ready(function(){\n    \n   var gTable = $("#eventList").dataTable();\n    \n   /* Free form edit entries */\n    $(".eventEditable", gTable.fnGetNodes()).editable( "'' . $ajaxEditableLink . ''", {\n        \n        "id"   : "elementid",\n        "callback": function( sValue, y ) {\n            var aPos = gTable.fnGetPosition( this );\n            if ( sValue == "" ) {\n                this.editing = true;\n                this.reset();\n                this.editing = false;\n            } else {\n                gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n            }\n        },\n        "submitdata": function ( value, settings ) {\n            var ret_arr = gTable.fnGetPosition( this );\n            var col = ret_arr[2];\n            var row = ret_arr[0];\n            var data_row = gTable.fnGetData(row);\n            var primaryKey = data_row[0];\n            var oSettings = gTable.fnSettings();\n            var columnName = oSettings.aoColumns[col].sTitle;  \n             return {\n                "row_id": primaryKey,\n                "column": columnName,\n                "type": "normaledit"\n            };\n        },\n        "height": "14px",\n        "tooltip"   : "Click to edit..."\n    } );\n        \n    /* Date edit entries */\n    $(".eventEditableDate", gTable.fnGetNodes()).editable( "'' . $ajaxEditableLink . ''", {\n        \n        "id"   : "elementid",\n        "callback": function( sValue, y ) {\n            var aPos = gTable.fnGetPosition( this );\n            if ( sValue == "" ) {\n                this.editing = true;\n                this.reset();\n                this.editing = false;\n            } else {\n                gTable.fnUpdate( sValue, aPos[0], aPos[1] );\n            }\n        },\n        "submitdata": function ( value, settings ) {\n            var ret_arr = gTable.fnGetPosition( this );\n            var col = ret_arr[2];\n            var row = ret_arr[0];\n            var data_row = gTable.fnGetData(row);\n            var primaryKey = data_row[0];\n            var oSettings = gTable.fnSettings();\n            var columnName = oSettings.aoColumns[col].sTitle;  \n             return {\n                "row_id": primaryKey,\n                "column": columnName,\n                "type" : "dateedit"\n            };\n        },\n        "height": "14px",\n        "tooltip"   : "Click to edit...",\n        "type" : "datepicker",\n        datepicker: {\n        changeMonth: true,\n        changeYear: true,\n        showOn: "both",\n    minDate: -2, maxDate: "+48M +28D",\n        dateFormat: "yy-mm-dd"}\n    } );\n\n  /* Add the events list active state AJAX processing */\n   activeLinkEl = "";\n   $(".userEventActiveLink").bind("click",function(event){\n      activeLinkEl = this;\n      event.preventDefault();       \n      $.get(this.href,"", function(response){\n             var activeLink = $(activeLinkEl);\n            /* Toggle the class */\n            if ( activeLink.hasClass("on")) {\n                  activeLink.removeClass("on");\n                  activeLink.addClass("off");\n            } else {\n                 activeLink.removeClass("off");\n                 activeLink.addClass("on");\n            }\n        });\n     });\n     \n    \n});</script>'';\n\n\n$modx->regClientStartupScript($eventList, true);', 0, 'a:1:{s:12:"ajaxEditable";a:6:{s:4:"name";s:12:"ajaxEditable";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"54";s:7:"lexicon";N;}}', ''),
(61, 'userEventsPageInitialiseTables', '', 0, 27, 0, '/**\n * Wedding site Event Settings processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the event list\n */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userSettingError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userSettingError'', "Error - No user found please log in");\n    return;\n}\n\n/* Get the wedding users events */\n$events = $weddingUser->getMany(''Events'');\n\n/* Now Iterate through the events and create the table body */\n$output = "";\n$context = $modx->context->get(''key'');\nforeach ($events as $event) {\n    \n    $eventArray = $event->toArray();\n    \n    /* Make the edit link */\n    $args = "&eventId=" . $eventArray[''id''];\n    $editURL = $modx->makeURL($userEditEventPage, $context, $args, "full" );\n    \n    /* Make the active link */\n    $args = "&row_id=" . $eventArray[''id''];\n    $args .= "&type=activeedit";\n    $activeURL = $modx->makeURL($userEventAJAX, $context, $args, "full" );\n    \n    /* Calculate the total guests, only active guests count */\n    $eventGuests = $event->getMany(''guestEvents''); \n    $totalGuests = 0;\n    foreach ( $eventGuests as $guestPtr ) {\n        \n        $guest = $guestPtr->getOne(''weddingUserGuest'');\n        $active = $guest->get(''active'');\n        if ( $active ) {\n         \n            /* Indicate attendance */\n            $willAttend = $guestPtr->get(''willAttend'');\n            if ( $willAttend == 1 ) $totalGuests++; \n            \n        }\n        \n    }\n    \n    /* Get the active on/off state */\n    $onOff = "off";\n    if ( $eventArray[''active''] == 1 ) $onOff = "on";\n    \n    $placeHolderArray = array ( ''userEventId'' => $eventArray[''id''],\n                                ''userEventName'' => $eventArray[''name''],\n                                ''userEventDate'' => strftime(''%d/%m/%y'' ,$eventArray[''date'']),\n                                ''userEventLocation'' => $eventArray[''location''],\n                                ''userEventStartTime'' => $eventArray[''startTime''],\n                                ''userEventEndTime'' => $eventArray[''endTime''],\n                                ''userEventMaxGuests'' => $eventArray[''maxGuests''],\n                                ''userEventTotalGuests'' => $totalGuests,\n                                ''userEventEditLink'' => $editURL,\n                                ''userEventActiveLink'' => $activeURL,\n                                ''userEventOnOff'' => $onOff);\n    \n    $chunk = $modx->getChunk(''userEventsList'', $placeHolderArray);\n    $output .= $chunk;\n    \n}\n\n$modx->setPlaceholder(''userEventList'', $output);\n\nreturn;', 0, 'a:2:{s:13:"userEventAJAX";a:6:{s:4:"name";s:13:"userEventAJAX";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"54";s:7:"lexicon";N;}s:17:"userEditEventPage";a:6:{s:4:"name";s:17:"userEditEventPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"53";s:7:"lexicon";N;}}', ''),
(62, 'userEventsPageEditableAJAXProcessor', '', 0, 27, 0, '/**\n * Wedding site Event list AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Assemble the parameters from the request data */\n$eventId = $_REQUEST[''row_id''];\n$newValue = strip_tags($_REQUEST[''value'']);\n$columnName = $_REQUEST[''column''];\n$updateType = $_REQUEST[''type''];\n$echoValue = $newValue;\n\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ( $user ) $userId = $user->get(''id'');\nif ( $userId == "" ) {\n    \n    echo "Error! No logged in user";\n    return;\n}\n\n  \n/* Get the event data */\n$eventObject = $modx->getObject(''weddingUserEvent'', array(''id'' => $eventId));\nif ( !$eventObject ) {\n    \n    echo "Error! No event found";\n    return;\n}\n\n$user = $eventObject->get(''user'');\n\n/* Process the edit update */\nswitch ( $updateType ) {\n    \n    case ''dateedit'' :\n        \n        if ( $columnName == ''Date'') {\n            \n            $dateValue = strtotime($newValue);\n            $eventObject->set(''date'', $dateValue);\n            \n        }\n        \n        break;\n\n   case ''activeedit'' :\n        \n        /* Just a straight toggle */\n        $newActiveState = 1;\n        $activeState = $eventObject->get(''active'');\n        if ( $activeState )  $newActiveState = 0;\n        $eventObject->set(''active'', $newActiveState);\n        \n        break;\n        \n    \n    case ''normaledit'' :\n        \n        /* Update the event depending on the column name */\n        if ( $columnName == ''Name'') $eventObject->set(''name'', $newValue);\n        if ( $columnName == ''Location'') $eventObject->set(''location'', $newValue);\n        if ( $columnName == ''Start Time'') $eventObject->set(''startTime'', $newValue);\n        if ( $columnName == ''End Time'') $eventObject->set(''endTime'', $newValue);\n        if ( $columnName == ''Max. Guests'') $eventObject->set(''maxGuests'', $newValue);\n        \n        break;\n    \n    default:\n        \n        /* Shouldn''t happen */\n        $echoValue = "Error! Invalid update type";\n        break;\n                \n}\n\n  /* Save the update */\nif ( !$eventObject->save() ) $echoValue = "Error! Can''t save event";\n  \n/* Return the output */\necho $echoValue;', 0, 'a:0:{}', ''),
(63, 'userEventsAddEditJSInclude', '', 0, 27, 0, '/**\n * Wedding site Event Add/Edit processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Set our jQuery paths */\n$validatorPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$validatorPath .= ''js/jquery-validation-1.8.1'';\n$assets_url = $modx->getOption(''assets_url'');\n\n/* Include the validation CSS */\n$validationCSS = ''assets/templates/wedding/styles/validation.css'';\n$modx->regClientCSS($validationCSS);\n\n/* Include the includes */\n$html1 = $validatorPath . ''/lib/jquery.metadata.js'';\n$html2 = $validatorPath . ''/jquery.validate.js'';\n$modx->regClientStartupScript($html1);\n$modx->regClientStartupScript($html2);\n\n/* Include the code */\n$initialise = ''<script>jQuery.validator.setDefaults({\n  debug: '' . $debug . '',\n  success: "valid"\n});\n\n// Check for generally unwanted characters, allow ":" for time fields\n  jQuery.validator.addMethod("validcharsgeneral", function (value) {\n          var result = true;\n          // unwanted character list\n          var iChars = "!@#$%^&*()=[]\\\\\\'';,/{}|\\"<>?";\n          for (var i = 0; i < value.length; i++) {\n             if (iChars.indexOf(value.charAt(i)) != -1) {\n                return false;\n             }\n          }\n          return result;\n\n  }, "Invalid characters entered");\n\n// Check for unwanted address characters\n  jQuery.validator.addMethod("validcharsaddress", function (value) {\n          var result = true;\n          // unwanted character list\n          var iChars = "!@#$%^&*()=[]\\\\\\;,/{}|\\"<>?";\n          for (var i = 0; i < value.length; i++) {\n             if (iChars.indexOf(value.charAt(i)) != -1) {\n                return false;\n             }\n          }\n          return result;\n\n  }, "Invalid characters entered");\n  \n</script>'';\n\n/* Add/Edit form */\n$form = ''<script>$(document).ready(function(){\n    $("#eventAddEditForm").validate({\n        \n         messages: { \n  \n            userEventsName: {\n                required: "This field is required."\n            }\n        }, \n            errorPlacement: function(error, element) {                                                    \n        element.parent().children("#formvalidationerror").append(error);        \n    },\n            invalidHandler: function() { \n                     alert("Please correct the errors on the form and re-submit it"); \n                }\n  });\n         \n  });</script>'';\n\n/* Date Picker */\n$datePicker = ''<script>$(document).ready(function(){\n  $(function() {\n    $( "#userEventsDate" ).datepicker({\n      showOn: "both",\n      buttonImage: "'' . $assets_url. ''templates/wedding/images/calendar.gif",\n      buttonImageOnly: true,\n      minDate: -2, maxDate: "+48M +28D",\n      changeYear: true,\n      changeMonth: true,\n      dateFormat: "dd/mm/y"\n    });\n  });\n    \n});</script>'';\n\n$modx->regClientStartupScript($initialise, true);\n$modx->regClientStartupScript($form, true);\n$modx->regClientStartupScript($datePicker, true);', 0, 'a:1:{s:5:"debug";a:6:{s:4:"name";s:5:"debug";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:5:"false";s:7:"lexicon";N;}}', ''),
(64, 'userEventsAddEditInitialiseForms', '', 0, 27, 0, '/**\n * Wedding site User Event processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the event edit/add form\n */\n\n$modx->setPlaceholder(''userGuestError'',"");\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEventError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEventError'', "Error - No user found please log in");\n    return;\n}\n\n/* Check for add or edit */\nif ( isset($_GET[''eventId'']) ) {\n    \n    /* Edit call, get the event data */\n    $eventId = $_GET[''eventId''];\n    $weddingEvent = $modx->getObject(''weddingUserEvent'', array(''id''=>$eventId));\n    if ($weddingEvent == null ) {            \n        $modx->setPlaceholder(''userEventError'', "Error - No event details found");     \n        return;\n    }\n         \n    /* Set the placeholders */\n    $eventArray = $weddingEvent->toArray(); \n    $modx->setPlaceholder(''userEventsId'', $eventId);\n    $modx->setPlaceholder(''userEventsName'', $eventArray[''name'']);\n    $date = strftime(''%d/%m/%y'',$eventArray[''date'']);\n    $modx->setPlaceholder(''userEventsDate'', $date);\n    $modx->setPlaceholder(''userEventsLocation'', $eventArray[''location'']);\n    $modx->setPlaceholder(''userEventsAddress2'', $eventArray[''address2'']);\n    $modx->setPlaceholder(''userEventsAddress3'', $eventArray[''address3'']);\n    $modx->setPlaceholder(''userEventsAddress4'', $eventArray[''address4'']);\n    $modx->setPlaceholder(''userEventsStartTime'', $eventArray[''startTime'']);\n    $modx->setPlaceholder(''userEventsEndTime'', $eventArray[''endTime'']);\n    $modx->setPlaceholder(''userEventsMaxGuests'', $eventArray[''maxGuests'']);\n    $modx->setPlaceholder(''userEventsActive'', $eventArray[''active''] ? "checked" : "");\n       \n}', 0, NULL, ''),
(65, 'userEventsAddEditPostProcessor', '', 0, 27, 0, '/**\n * Wedding site Events add/edit  processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  The parameters from the settings forms form are already valid at this point, we only need\n * to update the wedding user\n */\n\n/* Check for an event add/edit submit operation */\nif ( isset($_REQUEST[''userEventsName'']) ) {\n	\n	/* Get the MODX user */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ( $userId == "" ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No MODX user found please log in");\n        return;\n    }\n    \n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ( $weddingUserObject == null ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No Wedding user found please log in");\n        return;\n    }\n	\n    /* Check for an event Id, if none this is an add */\n    if ( $_REQUEST[''userEventsId''] == "" ) {\n        \n        $eventObject = $modx->newObject(''weddingUserEvent'');\n        $updateType = "Add";\n        \n    } else {\n        \n    $eventObject = $modx->getObject(''weddingUserEvent'', \n                                     array(''id'' => $_REQUEST[''userEventsId'']));\n    $updateType ="Update"; \n    \n    }\n    \n    if ( $eventObject == null ) {\n        $modx->setPlaceholder(''userSettingError'', "Error - No Event with this ID found ");\n        return;\n    }\n    \n    $eventObject->set(''name'', $_REQUEST[''userEventsName'']);\n    $dateText = $_REQUEST[''userEventsDate''];\n    $dateArray = explode(''/'', $dateText);\n    $temp = $dateArray[2];\n    $dateArray[2] = $dateArray[0];\n    $dateArray[0] = $temp;\n    $dateText = implode(''-'', $dateArray);\n    $date = strtotime($dateText);\n    $eventObject->set(''date'', $date);\n    $eventObject->set(''location'', $_REQUEST[''userEventsLocation'']);\n    $eventObject->set(''address2'', $_REQUEST[''userEventsAddress2'']);\n    $eventObject->set(''address3'', $_REQUEST[''userEventsAddress3'']);\n    $eventObject->set(''address4'', $_REQUEST[''userEventsAddress4'']);\n    $eventObject->set(''startTime'', $_REQUEST[''userEventsStartTime'']);\n    $eventObject->set(''endTime'', $_REQUEST[''userEventsEndTime'']);\n    $eventObject->set(''maxGuests'', $_REQUEST[''userEventsMaxGuests'']);\n	$active = 0;\n    if ( isset($_REQUEST[''userEventsActive'']) ) $active = 1;\n    $eventObject->set(''active'', $active);\n        \n    /* Save the event */\n    if ( $updateType == "Add" ) {\n        \n        $weddingUserObject->addMany($eventObject, ''Events'');\n        if ( $weddingUserObject->save() == false ) {\n            $modx->setPlaceholder(''userSettingError'', "Error - Failed to create this wedding user guest"); \n            return;\n        }\n        \n    } else {\n        \n        if (!$eventObject->save()) \n            $modx->setPlaceholder(''userSettingError'', "Error - Failed to update this wedding user guest");    \n        \n    }\n          \n    /* Redirect back to the event list */\n    $context = $modx->context->get(''key'');\n    $pageURL = $modx->makeURL($eventListPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:1:{s:13:"eventListPage";a:6:{s:4:"name";s:13:"eventListPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"48";s:7:"lexicon";N;}}', ''),
(66, 'cPanelAddSubDomain', 'Add a user subdomain via the cPanel API', 0, 29, 0, '/**\n * Wedding site CPanelAddSubDomain\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Interfaces with the Cpanel XML-API to allow sub domain updates.\n * \n * Parameters :- \n * \n * $subDomainName - The name of the subdomain to add, e.g. janiceswedding \n */\n\n/* Add the cPanel API path and include the class */\n$cPanelPath = $modx->getOption(''ws_cpanelapi.core_path'',null,$modx->getOption(''core_path'').''components/ws_cpanelapi/'');\ninclude_once $cPanelPath . "xmlapi.php";\n\n/* Construct the class, this also auths us */\n$cPanelApi = new xmlapi($host, $user, $password);\n\n/* Add the new domain */\n$cPanelApi->api1_query($user,''SubDomain'',''addsubdomain'',\n                       array($subDomainName,''theweddingwebsitegroup.com'',0,0,''/public_html''));', 0, 'a:3:{s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"wwwthewe";s:7:"lexicon";N;}s:8:"password";a:6:{s:4:"name";s:8:"password";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:14:"j4n1c3pass-THE";s:7:"lexicon";N;}s:4:"host";a:6:{s:4:"name";s:4:"host";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"127.0.0.1";s:7:"lexicon";N;}}', ''),
(67, 'cPanelDeleteSubDomain', 'Delete a subdomain using the cPanel XMLAPI', 0, 29, 0, '/**\n * Wedding site CPanelDeleteSubDomain\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Interfaces with the Cpanel XML-API to allow sub domain updates.\n * \n * Parameters :- \n * \n * $subDomainName - The name of the subdomain to delete, e.g. janiceswedding \n */\n\n/* Add the cPanel API path and include the class */\n$cPanelPath = $modx->getOption(''ws_cpanelapi.core_path'',null,$modx->getOption(''core_path'').''components/ws_cpanelapi/'');\ninclude_once $cPanelPath . "xmlapi.php";\n\n/* Construct the class, this also auths us */\n$cPanelApi = new xmlapi($host, $user, $password);\n\n/* Delete the sub domain */\n$cPanelApi->api1_query($user,''SubDomain'',''delsubdomain'',\n                       array($subDomainName));', 0, 'a:3:{s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"wwwthewe";s:7:"lexicon";N;}s:8:"password";a:6:{s:4:"name";s:8:"password";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:14:"j4n1c3pass-THE";s:7:"lexicon";N;}s:4:"host";a:6:{s:4:"name";s:4:"host";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"127.0.0.1";s:7:"lexicon";N;}}', ''),
(68, 'createUserPageSet', 'Creates a user page set from the default page set', 0, 62, 0, '/**\n * Wedding site creatUserPageSet\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the user page set under the users area from the default set.\n * \n * Parameters :- \n * \n * $name - The name of the users desired subdomain e.g. janiceswedding\n *  \n */\n\n/* Sanity check the name */\nif ( $name == "" ) $name = "nouser";\n\n/* Get the MODX user */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n\n$weddingUserObject = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUserObject == null ) return;\n\n    \n/* Duplicate the users page set from the default set */\n$defaultUserPage = $modx->getObject(''modResource'', $defaultUserPageSetContainerId);\n$newResource = $defaultUserPage->duplicate(array(\n    ''newName'' => $name,\n    ''duplicateChildren'' => true,\n    ''prefixDuplicate'' => false,\n));\n\n/* Reparent the new resource to the user page area */\n$newResource->set(''parent'', $userPagesParentContainerId);\n\n/* Publish this resource and its children */\n$newResource->set(''published'', true);\n$newResource->save();\n\n$newResourceId = $newResource->get(''id'');\n$children = $modx->getcollection(''modResource'', array(''parent'' => $newResourceId));\n\nforeach ( $children as $child ) {\n    \n    $child->set(''published'', true);\n    $child->save();\n    \n    /* Add details to the user page table */\n    $pageObject = $modx->newObject(''weddingUserPage'');\n    $childId = $child->get(''id'');\n    $editType = $child->get(''longtitle'');\n    $pageObject->set(''pageId'', $childId);\n    $pageObject->set(''editType'', $editType);\n    $weddingUserObject->addMany($pageObject, ''Pages'');\n    $weddingUserObject->save();\n}\n\n/* We need to clear the cache to regen the alias map */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n            "{$resource->context_key}/",\n        ),\n        array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n        )\n    );\n\n/* Done */', 0, 'a:2:{s:29:"defaultUserPageSetContainerId";a:6:{s:4:"name";s:29:"defaultUserPageSetContainerId";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"60";s:7:"lexicon";N;}s:26:"userPagesParentContainerId";a:6:{s:4:"name";s:26:"userPagesParentContainerId";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
(70, 'userFirstLogin', '', 0, 62, 0, '/**\n * Wedding site userFirstLogin\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Redirects to the user settings page if we don''t have a valid set\n * of user wediing site parameters. \n * \n *  \n *  \n */\n\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return;\n$user = $modx->user;\n$userId = $user->get(''id'');\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check for an incomplete parameter set */\n$websiteComplete = $attributes[''websiteDetails''];\nif ( !$websiteComplete) {\n    \n    $pageURL = $modx->makeURL($userSettingsPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:1:{s:16:"userSettingsPage";a:6:{s:4:"name";s:16:"userSettingsPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"30";s:7:"lexicon";N;}}', ''),
(69, 'getParentAlias', '', 0, 61, 0, '$parent = $modx->resource->get(''parent'');\n$parentResource = $modx->getObject(''modResource'', $parent);\nif ( !$parentResource ) return "None";\nreturn $parentResource->get(''alias'');', 0, NULL, ''),
(71, 'cPanelChangeSubDomain', 'Change a sub domains name', 0, 29, 0, '/**\n * Wedding site CPanelChangeSubDomain\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Interfaces with the Cpanel XML-API to allow sub domain updates.\n * \n * Parameters :- \n * \n * $to - The name of the subdomain to change to, e.g. janiceswedding \n * $from - The name of the existing subdomain.\n */\n\n/* Add the cPanel API path and include the class */\n$cPanelPath = $modx->getOption(''ws_cpanelapi.core_path'',null,$modx->getOption(''core_path'').''components/ws_cpanelapi/'');\ninclude_once $cPanelPath . "xmlapi.php";\n\n/* Construct the class, this also auths us */\n$cPanelApi = new xmlapi($host, $user, $password);\n\n/* Add the new sub domain */\n$cPanelApi->api1_query($user,''SubDomain'',''addsubdomain'',\n                       array($to,''theweddingwebsitegroup.com'',0,0,''/public_html''));\n\n/* Delete the existing sub domain */\n$cPanelApi->api1_query($user,''SubDomain'',''delsubdomain'',\n                       array($from));', 0, 'a:3:{s:8:"password";a:6:{s:4:"name";s:8:"password";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:14:"j4n1c3pass-THE";s:7:"lexicon";N;}s:4:"user";a:6:{s:4:"name";s:4:"user";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"wwwthewe";s:7:"lexicon";N;}s:4:"host";a:6:{s:4:"name";s:4:"host";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"127.0.0.1";s:7:"lexicon";N;}}', '');
INSERT INTO `modx_site_snippets` (`id`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`) VALUES
(72, 'designFormJSInclude', '', 0, 31, 0, '/**\r\n * Wedding site Design page processing\r\n */\r\n\r\n/* Include our JS for this page */\r\n\r\n/* WS javascript */\r\n$assets_url = $modx->getOption(''assets_url'');\r\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\r\n$designs = $javascriptPath . "js/pages/design/design.js";\r\n$modx->regClientStartupScript($designs);\r\n\r\n/* Slimbox */\r\n$slimbox = $assets_url . "templates/wedding/js/slimbox-2/js/slimbox2.js";\r\n$modx->regClientStartupScript($slimbox);\r\n$slimboxCSS = $assets_url . "templates/wedding/js/slimbox-2/css/slimbox2.css";\r\n$modx->regClientCSS($slimboxCSS);\r\n\r\n/* Colorbox */\r\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\r\n$colorBoxPath = $wsJQueryPath . ''js/colorbox/colorbox'';\r\n$html1 = $colorBoxPath . ''/jquery.colorbox-min.js'';\r\n$modx->regClientStartupScript($html1);\r\n$colorBoxCSS = $colorBoxPath . ''/colorbox-design.css'';\r\n$modx->regClientCSS($colorBoxCSS);\r\n\r\n/* Tooltip \r\n$tooltip = $wsJQueryPath . ''js/jquery.simpletip-1.3.1.min.js'';\r\n$modx->regClientStartupScript($tooltip);\r\n$tooltipContent = $modx->getChunk(''designsTooltipContent''); */\r\n\r\n/* AJAX links */\r\n$context = $modx->context->get(''key'');\r\n$ajaxPreviewLink = $modx->makeURL($ajaxPreviewPage, $context, "", "full");\r\n\r\n/* Pajinate */\r\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\r\n$modx->regClientStartupScript($pajinate);\r\n\r\n/* Page code */\r\n$ready = ''<script>$(document).ready(function(){\r\n    \r\n   /* WS object */\r\n   WS = new Object();\r\n   \r\n/* WS functions */\r\nWS.filterTheme = function(theme){\r\n        $(".designThemeForm").hide();\r\n        if ( theme == "all" ) {\r\n            window.location.reload();\r\n        } else {\r\n           var themeSelected = "#designThemeForm_"+theme;\r\n           $(themeSelected).show();\r\n             $(".page_navigation").hide();\r\n        }\r\n\r\n};\r\n\r\nWS.filterStyle = function(style){\r\n        $(".design").hide();\r\n        if ( style == "all" ) {\r\n            window.location.reload();\r\n        } else {\r\n           var styleSelected = ".design-"+style;\r\n           $(styleSelected).show();\r\n             $(".page_navigation").hide();\r\n        }\r\n\r\n};\r\n\r\nWS.filterColour = function(colour){\r\n        if ( colour != "all" ) {\r\n            $(".design").hide();\r\n            $(".blue-button").hide();\r\n            var picSelected = ".pic-"+colour;\r\n            $(".design .design-thumbs li").hide();\r\n            var showPic = ".design .design-thumbs li" + picSelected;\r\n            $(showPic).show();\r\n            var radioSelected = ".radio-"+colour;\r\n            $(".design .design-colours li input").hide();\r\n            var showRadio = ".design .design-colours li input" + radioSelected;\r\n            $(showRadio).show();\r\n            var colourSelected = ".colour-"+colour;\r\n            $(".design .design-colours li a").hide();\r\n            var showColour = ".design .design-colours li a" + colourSelected;\r\n            $(showColour).show();\r\n            $(showColour).closest(".design-colours").siblings("div").children("input").show();\r\n            $(showColour).closest(".design").show();\r\n          \r\n  } else {\r\n             \r\n             $(".design").show();\r\n             $(".design .design-thumbs li").show();\r\n             $(".design .design-colours li input").show();\r\n             $(".design .design-colours li a").show();\r\n             $(".blue-button").show();\r\n  \r\n\r\n  }\r\n\r\n\r\n};\r\n\r\n\r\n   /* Design preview */\r\n   activeLinkEl = "";\r\n   $(".colour-label").bind("click",function(event){\r\n      activeLinkEl = this;\r\n      event.preventDefault();       \r\n      $.get(this.href,"", function(response){\r\n            var activeLink = $(activeLinkEl);\r\n            /* Get the returned URL array */\r\n            var data = jQuery.parseJSON(response);\r\n            var links = data["links"];\r\n            var frameTitle = data["title"];\r\n            /* Fire up the colourbox */\r\n            $.colorbox({\r\n                              href:links[0],\r\n                              iframe:true, \r\n                              width:"''. $width .''",\r\n                              height:"'' . $height . ''",\r\n                              transition:"'' . $transition . ''",\r\n                              opacity:"'' . $opacity . ''",\r\n                              speed:"'' . $speed .''",    \r\n                              open:true,\r\n                              title: frameTitle,\r\n                              onClosed:function(){}                       \r\n\r\n            });\r\n            \r\n            \r\n        });\r\n     });  \r\n\r\n   /* Filter change theme  function */\r\n   $("#designThemesFilter").change(function() {\r\n\r\n        /* Show the selected theme */\r\n        var themeSelectedFilter = $("#designThemesFilter :selected").val();\r\n        WS.filterTheme(themeSelectedFilter);\r\n        \r\n        /* Show the selected colour */\r\n        var colourSelectedFilter = $("#designColoursFilter :selected").val();\r\n        WS.filterColour(colourSelectedFilter);        \r\n\r\n       \r\n   });\r\n   \r\n  /* Filter change style  function */\r\n   $("#designStylesFilter").change(function() {\r\n\r\n        /* Show the selected colour */\r\n        var colourSelectedFilter = $("#designColoursFilter :selected").val();\r\n        WS.filterColour(colourSelectedFilter);  \r\n  \r\n        /* Show the selected style */\r\n        var styleSelectedFilter = $("#designStylesFilter :selected").val();\r\n        WS.filterStyle(styleSelectedFilter);\r\n              \r\n\r\n       \r\n   });\r\n   \r\n  /* Filter change colour  function */\r\n   $("#designColoursFilter").change(function() {\r\n\r\n         /* Show the selected style */\r\n        var styleSelectedFilter = $("#designStylesFilter :selected").val();  \r\n       if ( styleSelectedFilter != "all" )\r\n             WS.filterStyle(styleSelectedFilter);\r\n\r\n        /* Filter by colour*/\r\n        var colourSelectedFilter = $("#designColoursFilter :selected").val();\r\n        WS.filterColour(colourSelectedFilter);\r\n        \r\n   });\r\n   \r\n  /* Tooltip on colour swatches \r\n  $(".colour-label").simpletip({\r\n\r\n   // Configuration properties\r\n   content: "''. $tooltipContent . ''",\r\n   fixed: false\r\n\r\n});*/\r\n\r\n    /* Pagination */\r\n    $("#themeFilter").pajinate({\r\n          items_per_page : 9,\r\n                    nav_label_first : "<<",\r\n          nav_label_last : ">>",\r\n          nav_label_prev : "<",\r\n          nav_label_next : ">"\r\n  });\r\n  \r\n  /* Theme selection warning */\r\n    $(".blue-button").click(function() {\r\n    \r\n        var btnName = $(this).attr("name");\r\n        var radioName = "radio-"+btnName;\r\n        var selection = "input[@name="+radioName+"]:checked";\r\n        var value = $(selection).val();\r\n        if ( value == undefined ) {\r\n        \r\n            alert("'' . $noColourSelected . ''");\r\n           return false;   \r\n        }\r\n  \r\n        return true;\r\n        \r\n      });\r\n        \r\n});</script>'';\r\n\r\n$modx->regClientStartupScript($ready, true);', 0, 'a:7:{s:15:"ajaxPreviewPage";a:6:{s:4:"name";s:15:"ajaxPreviewPage";s:4:"desc";s:34:"Id of the design preview AJAX page";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"485";s:7:"lexicon";N;}s:5:"width";a:6:{s:4:"name";s:5:"width";s:4:"desc";s:92:"Set a fixed total width. This includes borders and buttons. Example: "100%", "500px", or 500";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"90%";s:7:"lexicon";N;}s:6:"height";a:6:{s:4:"name";s:6:"height";s:4:"desc";s:93:"Set a fixed total height. This includes borders and buttons. Example: "100%", "500px", or 500";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"90%";s:7:"lexicon";N;}s:10:"transition";a:6:{s:4:"name";s:10:"transition";s:4:"desc";s:64:"The transition type. Can be set to "elastic", "fade", or "none".";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:7:"elastic";s:7:"lexicon";N;}s:7:"opacity";a:6:{s:4:"name";s:7:"opacity";s:4:"desc";s:41:"The overlay opacity level. Range: 0 to 1.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"0.35";s:7:"lexicon";N;}s:5:"speed";a:6:{s:4:"name";s:5:"speed";s:4:"desc";s:68:"Sets the speed of the fade and elastic transitions, in milliseconds.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"350";s:7:"lexicon";N;}s:16:"noColourSelected";a:6:{s:4:"name";s:16:"noColourSelected";s:4:"desc";s:32:"No colour selected alert message";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:38:"Please select a colour for this theme.";s:7:"lexicon";N;}}', ''),
(73, 'designsPageFormInitialise', '', 0, 31, 0, '/**\r\n * Wedding site Designs processing\r\n */\r\n/*  Initialise the design page forms\r\n */\r\n\r\n/* Check for logged in user */\r\n$loggedIn = false;\r\n$context = $modx->context->get(''key'');\r\nif ($modx->user->isAuthenticated($context))\r\n    $loggedIn = true;\r\n\r\n/* Get the theme templates */\r\n$themesString = $modx->runSnippet(''getThemeTemplates'');\r\n$themeArray = json_decode($themesString, true);\r\n$themeData = $themeArray[0];\r\n$themeNames = $themeArray[1];\r\n$allColours = array();\r\n\r\n/* Set the style names in the filter and output the whole page */\r\n$styleNameOutput = $modx->getChunk(''designStyleNames'', array(''styleNameDisplay'' => ''All'',\r\n        ''styleName'' => ''all''));\r\n\r\nforeach ($themeNames as $themeName) {\r\n\r\n    /* Get the themes styles */\r\n    $stylesString = $modx->runSnippet(''getThemeStyles'', array(''themeName'' => $themeName,\r\n        ''themeData'' => $themeData));\r\n    $styles = json_decode($stylesString, true);\r\n    $stylesOutput = "";\r\n    foreach ($styles as $styleName) {\r\n\r\n        /* Style filter */\r\n        $styleNameOutput .= $modx->getChunk(''designStyleNames'', array(''styleNameDisplay'' => ucfirst($styleName),\r\n        ''styleName'' => $styleName));\r\n\r\n        /* Get the styles colours */\r\n        $coloursString = $modx->runSnippet(''getStyleColours'', array(''themeName'' => $themeName,\r\n            ''styleName'' => $styleName,\r\n            ''themeData'' => $themeData));\r\n\r\n        $colours = json_decode($coloursString, true);\r\n        $coloursOutput = "";\r\n        $thumbsOutput = "";\r\n        $previewLinkGenerated = false;\r\n       \r\n        /* Colours and thumbs */\r\n        foreach ($colours as $colourName) {\r\n\r\n           /* Add to colour filter */\r\n           $allColours[] = $colourName;\r\n           \r\n            /* Preview link */\r\n            $args = array(''theme'' => $themeName,\r\n                          ''style'' => $styleName,\r\n                          ''colour'' => $colourName);\r\n            $designPreviewLink = $modx->makeURL($designPreviewAJAXPage,\r\n                                                $context,\r\n                                                $args,\r\n                                                "full");\r\n            $tooltipContent = $modx->getChunk(''designsTooltipContent''); \r\n            \r\n            /* Check for logged in or not and switch chunks */\r\n            if (!$loggedIn) {\r\n\r\n                $coloursOutput .= $modx->getChunk(''designColours'', array(''designStyleColour'' => $colourName,\r\n                    ''designStyleColourDisplay'' => ucfirst($colourName),\r\n                    ''designStyleName'' => $styleName,\r\n                    ''themeName'' => $themeName,\r\n                    ''designPreviewLink'' => $designPreviewLink,\r\n                    ''designPreviewTooltip'' =>  $tooltipContent));\r\n            } else {\r\n                \r\n                $coloursOutput .= $modx->getChunk(''designColoursLoggedIn'', array(''designStyleColour'' => $colourName,\r\n                    ''designStyleColourDisplay'' => ucfirst($colourName),\r\n                    ''designStyleName'' => $styleName,\r\n                    ''themeName'' => $themeName,\r\n                    ''designPreviewLink'' => $designPreviewLink,\r\n                    ''designPreviewTooltip'' =>  $tooltipContent));\r\n            }\r\n            \r\n            $thumbsOutput .= $modx->getChunk(''designThumbs'', array(''designStyleColour'' => $colourName,\r\n                ''designStyleColourDisplay'' => ucfirst($colourName),\r\n                ''designStyleNameDisplay'' => ucfirst($styleName),\r\n                ''designStyleName'' => $styleName,\r\n                ''designThemeName'' => $themeName));\r\n            \r\n            /* Preview button link */\r\n            if ( !$previewLinkGenerated ) {\r\n                \r\n                \r\n                $previewLinkGenerated = true;\r\n            }\r\n        }\r\n\r\n        /* Styles page code */\r\n\r\n        /* Get the selection button if logged in */\r\n        $designSelectButton = "";\r\n        if ( $loggedIn ) {\r\n            $designSelectButton = $modx->getChunk(''designSelectButton'', array(''designStyleName'' => $styleName,\r\n                ''themeName'' => $themeName));\r\n            \r\n        }\r\n        \r\n        $stylesOutput .= $modx->getChunk(''designsPageFormStyle'', array(''styleNameDisplay'' => ucfirst($styleName),\r\n            ''styleName'' => $styleName,\r\n            ''themeName'' => $themeName,\r\n            ''designColours'' => $coloursOutput,\r\n            ''designThumbs'' => $thumbsOutput,\r\n            ''designSelectButton'' => $designSelectButton));\r\n    }\r\n\r\n    /* Theme page code */\r\n    if ( isset($_REQUEST[''settings''])) $designFromSettings = "true";\r\n    $themeOutput .= $modx->getChunk(''designsPageFormTheme'', array(''themeName'' => $themeName,\r\n        ''designStyles'' => $stylesOutput,\r\n        ''designFromSettings'' => $designFromSettings));\r\n}\r\n\r\n/* Set the page theme filter placeholder */\r\n$modx->setPlaceholder(''designStyleNames'', $styleNameOutput);\r\n\r\n/* Set the page colour filter placeholder */\r\n$allColours = array_unique($allColours);\r\n$colourFilterOutput = $modx->getChunk(''designColourFilter'', array(''colourNameDisplay'' => ''All'',\r\n                                                                                                                ''colourName'' => ''all''));\r\nforeach ( $allColours as $colour ) {\r\n    \r\n    $colourFilterOutput .= $modx->getChunk(''designColourFilter'', array(''colourNameDisplay'' => ucfirst($colour),\r\n                                                                                                                ''colourName'' => $colour));\r\n    \r\n}\r\n\r\n$modx->setPlaceholder(''designColourNames'', $colourFilterOutput);\r\n\r\n\r\n/* Set the page themes  placeholder */\r\n$modx->setPlaceholder(''designThemes'', $themeOutput);', 0, 'a:1:{s:21:"designPreviewAJAXPage";a:6:{s:4:"name";s:21:"designPreviewAJAXPage";s:4:"desc";s:29:"Design preview AJAX processor";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"485";s:7:"lexicon";N;}}', ''),
(74, 'mailMan', 'General site mail manager', 0, 33, 0, '/**\n * Wedding site mailMan\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Site mail management snippet\n * \n * Parameters :-\n * \n *  to : Recipient e-mail address, default is site admin\n *  from : Sender e-mail address, default is site admin\n *  fromName : Sender name, default site administrator\n *  message : The message text\n *  subject : Subject line\n *  html : true/false indicates mail should be sent as html\n */\n\n$adminEmail = $modx->getOption(''emailsender'');\n$to = isset($to) ? $to : $adminEmail;\n$from = isset($from) ? $from : $adminEmail;\n$fromName = isset($fromName) ? $fromName : "The Wedding Websites Site Administrator";\n$html = isset($html) ? true : false;\n\n/* Get the mail service if we have to */\nif ( !$modx->mail ) $modx->getService(''mail'', ''mail.modPHPMailer'');\n\n/* Set the mail parameters */        \n$modx->mail->set(modMail::MAIL_FROM, $from);\n$modx->mail->set(modMail::MAIL_FROM_NAME, $fromName);\n$modx->mail->address(''to'', $to);\n$modx->mail->set(modMail::MAIL_SUBJECT, $subject);\n$modx->mail->set(modMail::MAIL_BODY, $message);\nif ( $html ) $modx->mail->setHTML(true);\n\n/* Send the mail */\n$modx->mail->send();\n$modx->mail->reset();', 0, NULL, ''),
(75, 'EasyCopyright', '', 0, 34, 0, '/*\n#EasyCopyright#\n\n##About##\n\n\n* By Cameron Gilroy\n* EasyCopyright\n* Version: 1.1\n* Based in part on Copyright by AMDbuilder\n* Creator: Cameron Gilroy http://www.camerongilroy.com\n* Support URL: http://www.camerongilroy.com/\n* GitHub: http://github.com/camgill/EasyCopyright\n\nEasyCopyright is a simple snippet for ModX Revolution that inserts the the current year and the site name. Check the example below.\n\n*© +CameronGilroy.com+ 2009-2011 - Powered by +MODx+*\n\n###Thanks###\n\nA big thank you goes to the creators of MODx Revolution!  \n[dflock](https://github.com/dflock/ "dflock"): Thanks for the new features added to 1.1!\n\n###Usage###\n\n<code>[[!EasyCopyright]]</code> - Outputs *© [[Your Site name here]] 2011 - Powered by MODx*  \n<code>[[!EasyCopyright? &StartYear=`2009`]]</code> - Outputs *© [[Your Site name here]] 2009-2011 - Powered by MODx*\n\n\n##Configuration##\n\nThe variables that are available are listed below with a description.\nYou can also call the contents of a chunk into any of these fields like this <code>[[!EasyCopyright? &Powered=`[[$Your_Chunk_Name]]`]]</code>\n\n* Name - Your site name\n* Start Year - What ever you put in!\n* Powered - is the link to the ModX site\n* YearSeparator - is the separator that goes between the Start Year and the current year\n* PoweredBySeparator - is the separator that goes between the current Year and Powered by\n\n###Defaults###\n\n* Name - Your site name\n* Start Year - No Default must be called in the snippet\n* Powered - is the link to the ModX site\n* YearSeparator - "-"\n* PoweredBySeparator - "-"\n*/\n\n$Name = isset($Name)? $Name : "[[++site_name]]";\n$Powered = isset($Powered)? $Powered : "Powered by <a href=''http://www.modxcms.com''>MODx</a>";\n$YearSeparator = isset($YearSeparator)? $YearSeparator : "-";\n$PoweredBySeparator = isset($PoweredBySeparator)? $PoweredBySeparator : "-";\n$cYear = date("Y");\n$StartYear = isset($StartYear)? $StartYear : $cYear;\n$years = ($cYear > $StartYear) ? $StartYear.$YearSeparator.$cYear : $cYear;\n\necho "&copy; $Name $years &nbsp;$PoweredBySeparator&nbsp; $Powered";', 0, 'a:0:{}', ''),
(76, 'getThemeTemplates', 'Gets the user theme templates into array format', 0, 31, 0, '/**\n * Wedding site getThemeTemplates\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * \n * Returns :- two data structures, themeTemplates containing the theme\n * templates in array form and themeArray containing the unique theme names.\n */\n\n/* Get all the sub categories of the theme category */\n$themeCategories = $modx->getCollection(''modCategory'', array(''parent'' => $themeCategoryValue));\n\n/* Now get all the templates */\n$themeTemplateNameArray = array();\nforeach ( $themeCategories as $themeCategory ) {\n    \n    $categoryId = $themeCategory->get(''id'');\n    $themeTemplates = $modx->getCollection(''modTemplate'', array(''category'' => $categoryId));\n    foreach ( $themeTemplates as $themeTemplate ) {\n        \n        $templateName = $themeTemplate->get(''templatename'');\n        $templateId = $themeTemplate->get(''id'');\n        $themeTemplateNameArray[$templateName] = $templateId;\n    }\n}\n\n/* Get the number of unique themes and create a mapping structure */\n$themeArray = array();\n$themeTemplates = array();\nforeach ( $themeTemplateNameArray as $name => $id) {\n    \n    $tempThemeArray = explode(''-'', $name);\n    $tempThemeArray[] = $id;\n    $themeTemplates[] = $tempThemeArray;\n    $themeArray[] = $tempThemeArray[0];\n    \n}\n\n/* Unique the themes */\n$themeArray = array_unique($themeArray);\n\n/* Return the data */\n$returnArray = array();\n$returnArray[0] = $themeTemplates;\n$returnArray[1] = $themeArray;\nreturn json_encode($returnArray);', 0, 'a:1:{s:18:"themeCategoryValue";a:6:{s:4:"name";s:18:"themeCategoryValue";s:4:"desc";s:51:"The id of the cateogory holding all the user themes";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"32";s:7:"lexicon";N;}}', ''),
(77, 'getThemeStyles', 'Gets a themes styles', 0, 31, 0, '/**\n * Wedding site getThemeStyles\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters :-\n * \n * themeName - the name of the theme\n * themeData :- the theme template data structure as returned by getThemeTemplates\n * \n * Returns :- an array containing the themes style names\n */\n\n$stylesArray = array();\nforeach ( $themeData as $theme ) {\n    \n    if ( $theme[0] != $themeName ) continue;\n    $stylesArray[] = $theme[1];\n}\n\n/* Unique the array */\n$stylesArray = array_unique($stylesArray);\n\n/* Sort Alphabetically */\nsort($stylesArray);\n\n/* Return the data */\nreturn json_encode($stylesArray);', 0, 'a:0:{}', ''),
(78, 'getStyleColours', 'Gets a styles associated colours', 0, 31, 0, '/**\n * Wedding site getStyleColours\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters :-\n * \n * themeName - the name of the theme\n * styleName - the name of the style\n * themeData :- the theme template data structure as returned by getThemeTemplates\n * \n * Returns :- an array containing the themes style colours\n */\n\n$coloursArray = array();\nforeach ( $themeData as $theme ) {\n    \n    if ( $theme[0] != $themeName ) continue;\n    if ( $theme[1] != $styleName ) continue;\n    $coloursArray[] = $theme[2];\n}\n\n/* Unique the array */\n$coloursArray = array_unique($coloursArray);\n\n/* Sort Alphabetically */\nsort($coloursArray);\n\n/* Return the data */\nreturn json_encode($coloursArray);', 0, NULL, ''),
(79, 'designsPageFormPostProcessor', 'Post processor for the designs page', 0, 31, 0, '/**\r\n * Wedding site Designs processing\r\n */\r\n\r\n/* Get the themes */\r\n$themesString = $modx->runSnippet(''getThemeTemplates'');\r\n$themeArray = json_decode($themesString, true);\r\n$themeData = $themeArray[0];\r\n$themeNames = $themeArray[1];\r\n\r\n/* Check for a submission button for each theme and its styles */\r\n$themeFound = false;\r\nforeach ($themeNames as $themeName) {\r\n\r\n    if ( $themeFound ) break; \r\n    /* Get the themes styles */\r\n    $stylesString = $modx->runSnippet(''getThemeStyles'', array(''themeName'' => $themeName,\r\n        ''themeData'' => $themeData));\r\n    $styles = json_decode($stylesString, true);\r\n    $stylesOutput = "";\r\n    foreach ($styles as $styleName) {\r\n        \r\n        if ( $themeFound ) break;\r\n        $buttonName = $themeName . "-" . $styleName;\r\n        if (isset($_REQUEST[$buttonName])) {\r\n            \r\n            /* Got a submission, get the colour and generate the template name */\r\n            $radioButtonName = "radio-" . $buttonName; \r\n            $themeColourString = $_REQUEST[$radioButtonName];\r\n            $colourArray = explode(''-'', $themeColourString);\r\n            $colourName = $colourArray[2];\r\n            $templateName = $buttonName . "-" . $colourName;\r\n            $themeFound = true;\r\n             break;\r\n        }\r\n    }\r\n}\r\n\r\n/* Update the users theme attribute and the users resources */\r\nif ( $themeFound ) {\r\n \r\n    /* Get the MODX user */\r\n    $user = $modx->user;\r\n    $userId = $user->get(''id'');\r\n    if ( $userId == "" ) return;\r\n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\r\n    if ( $weddingUserObject == null ) return;\r\n    $weddingUser = $weddingUserObject->getOne(''Attributes'');\r\n    /* Create the theme name for the user */\r\n    $nameArray = explode(''-'', $templateName);\r\n    $userTemplateName = ucfirst($nameArray[0]) . " " . ucfirst($nameArray[1]) . " " . ucfirst($nameArray[2]);\r\n  $weddingUser->set(''theme'', $userTemplateName);\r\n    $weddingUser->save();\r\n    \r\n    /* Update the resources */\r\n    $templateId = $modx->runSnippet(''getThemeTemplateId'', array(''themeName'' => $nameArray[0],\r\n        ''styleName'' => $nameArray[1],\r\n        ''colourName'' => $nameArray[2],\r\n        ''themeData'' => $themeData));\r\n    \r\n    $templateId = intval($templateId);\r\n    \r\n    /* Get user container */\r\n    $website = $weddingUser->get(''website'');  \r\n    $c = $modx->newQuery(''modResource'');\r\n    $c->where(array(''alias:=''  => $website));\r\n    $c->andCondition(array(''parent:=''  => $userWebpageContainerId));\r\n    $userContainer = $modx->getObject(''modResource'', $c);\r\n    \r\n/* If no container just exit, back to settings if needed */\r\n    if ( !$userContainer ) { \r\n        \r\n        if ( $_REQUEST[''designFromSettings''] == ''true'' ) {\r\n  \r\n            $params = array(''restore'' => 1);\r\n            $pageURL = $modx->makeURL($userSettingsPage, $context, $params, "full");\r\n            header("Location: {$pageURL}");\r\n        \r\n        } else {\r\n        \r\n            return;\r\n        }\r\n    }\r\n\r\n    /* Get the children */\r\n    $children = $userContainer->getMany(''Children'');\r\n    foreach ( $children as $child ) {\r\n        \r\n        $child->set(''template'', $templateId);\r\n        $child->save();\r\n    }\r\n    \r\n    /* Clear the cache */\r\n    $context = $modx->context->get(''key'');\r\n    $cacheManager= $modx->getCacheManager();\r\n    $cacheManager->clearCache(array (\r\n            "{$context}/",\r\n        ),\r\n        array(\r\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\r\n            ''publishing'' => true\r\n        )\r\n    );\r\n            \r\n    /* If we''ve come from the settings page redirect back */\r\n    if ( $_REQUEST[''designFromSettings''] == ''true'' ) {\r\n  \r\n        $pageURL = $modx->makeURL($userSettingsPage, $context, "", "full");\r\n         header("Location: {$pageURL}");\r\n    }\r\n}', 0, 'a:2:{s:16:"userSettingsPage";a:6:{s:4:"name";s:16:"userSettingsPage";s:4:"desc";s:0:"";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"30";s:7:"lexicon";N;}s:22:"userWebpageContainerId";a:6:{s:4:"name";s:22:"userWebpageContainerId";s:4:"desc";s:24:"Users web page container";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
(83, 'getThemeTemplateId', 'Gets a themes template id', 0, 31, 0, '/**\n * Wedding site getThemeTemplateId\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Parameters :-\n * \n * themeName - the name of the theme\n * styleName - the name of the style\n * colourName - the name of the colour\n * themeData :- the theme template data structure as returned by getThemeTemplates\n * \n * Returns :- the id of the themes template\n */\n\nforeach ( $themeData as $theme ) {\n    \n    if ( ($theme[0] == $themeName) &&\n         ($theme[1] == $styleName) &&\n         ($theme[2] == $colourName)) {\n        \n            return $theme[3];         \n   }\n}', 0, 'a:0:{}', ''),
(84, 'userHomePageJSInclude', 'User Home JS includer', 0, 25, 0, '/**\n * Wedding site User Home processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n\n/* Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);', 0, NULL, ''),
(85, 'userHomePageInitialise', '', 0, 25, 0, '/**\n * Wedding site User Home processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users home page */\n $modx->setPlaceholder(''userHomeWebsiteError'',"");\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userHomeWebsiteError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userHomeWebsiteError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Tutorials */\n$tutorialsContainer = $modx->getObject(''modResource'', $userTutorialsContainerId);\n$children = $tutorialsContainer->getMany(''Children'');\n$context = $modx->context->get(''key'');\n$tutorialsOutput = "";\nforeach ( $children as $child ) {\n    \n    $id = $child->get(''id'');\n    $title = $child->get(''pagetitle'');\n    $pageLink = $modx->makeURL($id, $context, "", "full");\n    $linkParams = array(''userHomeTutorialTitle'' =>  $title ,\n                        ''userHomeTutorialLink'' => $pageLink);\n                                     \n    \n    $tutorialsOutput .= $modx->getChunk(''userHomeTutorialsBody'', $linkParams);\n}\n\n$modx->setPlaceholder(''userHomeTutorialsBody'', $tutorialsOutput);', 0, 'a:1:{s:24:"userTutorialsContainerId";a:6:{s:4:"name";s:24:"userTutorialsContainerId";s:4:"desc";s:29:"Id of the Tutorials container";s:4:"type";s:9:"tex