(86, 'userHomeAJAXProcessor', '', 0, 25, 0, '/**\n * Wedding site User home AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Assemble the parameters from the request data */\n$id = $_REQUEST[''id''];\n\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ( $user ) $userId = $user->get(''id'');\nif ( $userId == "" ) {\n    \n    echo "Error! No logged in user";\n    return;\n}\n\n/* Toggle the current state of published */\n$resource = $modx->getObject(''modResource'', $id);\nif ( !$resource ) return "Error - can''t find resource";\n$published = $resource->get(''published'');\n$newPublished = 0;\n$output = ''off'';\nif ( $published == 0 ) {\n    \n    $newPublished = 1;\n    $output = "on";\n}\n$resource->set(''published'', $newPublished);\n$resource->save();\n/* Clear the cache */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n        "{$resource->context_key}/",\n    ),\n    array(\n        ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n        ''publishing'' => true\n    )\n);\n\n/* Return the output */\necho $output;', 0, NULL, ''),
(89, 'userAdminWebPageLinks', 'Draws the web page links in the user administration area', 0, 62, 0, '/**\n * Wedding site userAdminWebPageLinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Draws the user admin area sidebar web page links\n * \n *  \n *  Parameters :-\n * \n * attributes - an array of the users attributes\n * \n * Returns - nothing, sets placeholders only\n */\n\n$website = $attributes[''website''];\n$c = $modx->newQuery(''modResource'');\n$c->where(array(''alias:=''  => $website));\n$c->andCondition(array(''parent:=''  => $userWebpageContainerId));\n$userContainer = $modx->getObject(''modResource'', $c);\n/* If no container we can''t draw any links! Maybe this is\n * a first login case.\n */\nif ( !$userContainer ) return;\n $websiteParentId = $userContainer->get(''id'');\n \n/* Get the children, sorted by menuindex */\n$c = $modx->newQuery(''modResource'');\n$c->where((array(''parent:=''  => $websiteParentId)));\n$c->sortBy(''menuindex'', ''ASC'');\n$children = $modx->getCollection(''modResource'',$c);\n\n$context = $modx->context->get(''key'');\n$weblinkOutput = "";\nforeach ( $children as $child ) {\n    \n    $id = $child->get(''id'');\n    $title = $child->get(''pagetitle'');\n    $published = $child->get(''published'');\n    $published = $published == 0 ? "off" : "on";\n    \n    /* Get the documents edit type and generate the edit links */\n    $editType = $child->get(''longtitle'');\n    switch ( $editType ) {\n        \n        case "blog":\n            \n            $linkId = $userEditPageBlogId;\n            break;\n        \n        case "rsvp":\n            \n             $linkId = $userEditPageRSVPId;\n            break;\n        \n        case "gallery":\n            \n            $linkId = $userEditPageGalleryId;\n            break;\n        \n        case "guestbook":\n            \n            $linkId = $userEditPageGuestbookId;\n            break;\n        \n        default:\n            \n             $linkId = $userEditPageGenericId;\n            \n    }\n    $linkArgs = array(''userPage'' => $id );\n    $pageLink = $modx->makeURL($linkId, $context, $linkArgs, "full");\n    $AJAXArgs = array(''id'' => $id);\n    $ajaxLink = $modx->makeURL($userHomeAJAXPage, $context, $AJAXArgs, "full");\n    $linkParams = array(''userHomeWeblinkLink'' =>  $pageLink ,\n                                     ''userHomeWeblinkTitle'' => $title,\n                                     ''userHomeWeblinkPublished'' => $published,\n                                     ''userHomeWeblinkAJAX'' => $ajaxLink);\n    \n    $webLink .= $modx->getChunk(''userHomeWebPagesLinks'', $linkParams);\n}\n\n$modx->setPlaceholder(''userWebPageslinks'',  $webLink);', 0, 'a:7:{s:16:"userHomeAJAXPage";a:6:{s:4:"name";s:16:"userHomeAJAXPage";s:4:"desc";s:44:"User administration area home page AJAX link";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"251";s:7:"lexicon";N;}s:22:"userWebpageContainerId";a:6:{s:4:"name";s:22:"userWebpageContainerId";s:4:"desc";s:28:"Users web pages container id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}s:18:"userEditPageBlogId";a:6:{s:4:"name";s:18:"userEditPageBlogId";s:4:"desc";s:22:"Edit page type Blog id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"514";s:7:"lexicon";N;}s:18:"userEditPageRSVPId";a:6:{s:4:"name";s:18:"userEditPageRSVPId";s:4:"desc";s:27:"User edit page type RSVP id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"513";s:7:"lexicon";N;}s:21:"userEditPageGalleryId";a:6:{s:4:"name";s:21:"userEditPageGalleryId";s:4:"desc";s:30:"User edit page type Gallery Id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"512";s:7:"lexicon";N;}s:21:"userEditPageGenericId";a:6:{s:4:"name";s:21:"userEditPageGenericId";s:4:"desc";s:30:"User edit page type Generic id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"515";s:7:"lexicon";N;}s:23:"userEditPageGues