INSERT INTO `modx_site_snippets` (`id`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`) VALUES
(91, 'setTemplatePlaceholders', 'Sets wedding user attribute placeholders fro use in the templates/chunks etc.', 0, 32, 0, '/**\n * Wedding site setTemplatePlaceholders\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Sets user attribute placeholders for use in the templates\n * \n *  \n *  Parameters :-\n * \n * None\n * \n * Returns - nothing, sets placeholders only\n * \n * Placeholders - prefixed with weddinguser e.g [[+weddinguser.firstName]]\n * \n * id - The wedding users id\n * user - The wedding users user Id, i.e the MODX user id\n * firstName - The wedding users first name\n * lastName - The wedding users last name\n * partnerName1 - The wedding users partners first name\n * partnerName2 - The wedding users partners second name\n * date - Date of the wedding as a UNIX timestamp, can be formatted with \n *        output modifiers\n * date1 - The date of the wedding in the form ''July 22nd 2011''\n * website - The wedding users website address\n * theme - The wedding users theme \n * packageType - The wedding users package type\n * passwordProtect - Whether the site is password protected\n * countdown - time in days to the wedding date\n * \n * Profile details can be accessed using the ''user'' placeholder such as \n * [[+user.email]], the fields available correspondong to the ones from\n * the MODX user profile itself.\n */\n\n/* Get the current user details from the username part of the incoming URL */\n$urlArray = explode(''.'', $_SERVER[HTTP_HOST]);\n$website = $urlArray[0];\n/* Check for how we''ve accessed the page */\nif ( $website == ''www'') {\n    \n    /* Internally, get the user from the query string */\n    $queryStringArray = explode(''/'', $_REQUEST[''q'']);\n    $website = $queryStringArray[1];\n}\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:=''  => $website));\n$weddingUser = $modx->getObject(''weddingUserAttribute'', $c);\n/* If we have no user here it may be a preview, use defaults */\nif ( !$weddingUser ) {\n   \n    $modx->setPlaceholder(''weddinguser.partnerName1'', ''Someone'');\n    $modx->setPlaceholder(''weddinguser.partnerName2'', ''Somebody'');\n    $modx->setPlaceholder(''weddinguser.countdown'', 6);\n    return;\n}\n\n\n/* Wedding user */\n$attributes = $weddingUser->toArray();\n/* Format the date */\n$attributes[''date1''] = date(''F jS Y'', $attributes[''date'']);\n$modx->toPlaceholders($attributes, "weddinguser");\n\n/* Get the countdown days */\n$date = $attributes[''date''];\n$now = time();\n$dateDiffString = $modx->runSnippet(''getTimeDifference'', \n                                    array(''start'' => $now,\n                                          ''end'' => $date));\n\n$dateArray = json_decode($dateDiffString, true);\n$modx->toPlaceholder(''countdownTotalDays'', $dateArray[''days''], ''weddinguser'');\n\n/* Calculate months/days, assume a month is 30 days */\n$months = floor($dateArray[''days''] / 30);\n$days = $dateArray[''days''] % 30;\n\n$modx->toPlaceholder(''countdownDays'', $days, ''weddinguser'');\n$modx->toPlaceholder(''countdownMonths'', $months, ''weddinguser'');\n  \n/* User Profile */\n$user = $modx->getObject(''modUser'', $attributes[''user'']);\nif ( !$user ) return;\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n\n$modx->toPlaceholders($profile, "user");', 0, 'a:0:{}', ''),
(93, 'getTimeDifference', 'Gets the time difference between two dates, used for wedding countdown time etc. ', 0, 32, 0, '/**\n * Wedding site getTimeDifference\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Site time difference snippet\n * \n * Parameters :-\n *\n * start - start time unixtimestamp\n * end - end time unix timestamp\n * \n * Returns :-\n * \n * A JSON encoded array of the form \n * $diff[''days'']    = int\n * $diff[''hours'']   = int\n * $diff[''minutes''] = int\n * $diff[''seconds''] = int\n * \n */\n$uts[''start''] = $start;\n$uts[''end''] = $end;\nif( $uts[''start'']!==-1 && $uts[''end'']!==-1 )\n{\n    if( $uts[''end''] >= $uts[''start''] )\n    {\n        $diff = $uts[''end''] - $uts[''start''];\n        if( $days=intval((floor($diff/86400))) )\n            $diff = $diff % 86400;\n        if( $hours=intval((floor($diff/3600))) )\n            $diff = $diff % 3600;\n        if( $minutes=intval((floor($diff/60))) )\n            $diff = $diff % 60;\n        $diff = intval( $diff );            \n        $diffArray = array(''days''=>$days, ''hours''=>$hours, ''minutes''=>$minutes, ''seconds''=>$diff);\n        $diffArrayString = json_encode($diffArray);\n        return $diffArrayString;\n    }\n}', 0, NULL, ''),
(99, 'designsGetPreviewSet', '', 0, 31, 0, '/**\n * Wedding site Design page processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n * Parameters :- \n * \n * templateName - the name of the template in theme-style-colour format\n * \n * Returns :- The id of the preview set to use\n */\n\n/* Get the containers under the design preview pages */\n$previewContainer = $modx->getObject(''modResource'', $previewContainerId);\nif ( !$previewContainer ) return;\n$previewChildren = $previewContainer->getMany(''Children'');\n\n/* Get the set for this template */\nforeach ( $previewChildren as $previewChild ) {\n    \n    $container = $previewChild->get(''isfolder'');\n    if ( $container == 1 ) {\n        \n        $templates = $previewChild->getMany(''Children'');\n        foreach ( $templates as $template ) {\n        \n            $alias = $template->get(''alias'');\n            if ( $alias == $templateName ) {\n            \n                $id = $template->get(''id'');\n                return $id;\n            }\n        }\n    }\n}', 0, 'a:2:{s:18:"previewContainerId";a:6:{s:4:"name";s:18:"previewContainerId";s:4:"desc";s:29:"Preview set page container id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"473";s:7:"lexicon";N;}s:15:"previewNonePage";a:6:{s:4:"name";s:15:"previewNonePage";s:4:"desc";s:47:"Page id to use if no preview sets are available";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"510";s:7:"lexicon";N;}}', ''),
(98, 'UltimateParent', 'Return the "ultimate" parent of a resource.', 0, 0, 0, '/**\n * @name UltimateParent\n * @version 1.3\n * @author Susan Ottwell <sottwell@sottwell.com> March 2006\n * @author Al B <> May 18, 2007\n * @author S. Hamblett <shamblett@cwazy.co.uk>\n * @author Shaun McCormick <shaun@modx.com>\n * @author Jason Coward <modx@modx.com>\n *\n * @param &id The id of the document whose parent you want to find.\n * @param &top The top node for the search.\n * @param &topLevel The top level node for the search (root = level 1)\n *\n * @license Public Domain, use as you like.\n *\n * @example [[UltimateParent? &id=`45` &top=`6`]]\n * Will find the ultimate parent of document 45 if it is a child of document 6;\n * otherwise it will return 45.\n *\n * @example [[UltimateParent? &topLevel=`2`]]\n * Will find the ultimate parent of the current document at a depth of 2 levels\n * in the document hierarchy, with the root level being level 1.\n *\n * This snippet travels up the document tree from a specified document and\n * returns the "ultimate" parent.  Version 2.0 was rewritten to use the new\n * getParentIds function features available only in MODx 0.9.5 or later.\n *\n * Based on the original UltimateParent 1.x snippet by Susan Ottwell\n * <sottwell@sottwell.com>.  The topLevel parameter was introduced by staed and\n * adopted here.\n */\nif (!isset($modx)) return '''';\n\n$top = isset($top) && intval($top) ? $top : 0;\n$id= isset($id) && intval($id) ? intval($id) : $modx->resource->get(''id'');\n$topLevel= isset($topLevel) && intval($topLevel) ? intval($topLevel) : 0;\nif ($id && $id != $top) {\n    $pid = $id;\n    $pids = $modx->getParentIds($id);\n    if (!$topLevel || count($pids) >= $topLevel) {\n        while ($parentIds= $modx->getParentIds($id, 1)) {\n            $pid = array_pop($parentIds);\n            if ($pid == $top) {\n                break;\n            }\n            $id = $pid;\n            $parentIds = $modx->getParentIds($id);\n            if ($topLevel && count($parentIds) < $topLevel) {\n                break;\n            }\n        }\n    }\n}\nreturn $id;', 0, NULL, ''),
(100, 'designFreePreviewSet', 'Frees a design preview set', 0, 31, 0, '/**\n * Wedding site Design page processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n * Parameters :- \n * \n * setName - the name of the set to free\n * \n * Returns :- Nothing\n */\n\n/* Get the preview container */\n$c = $modx->newQuery(''modResource'');\n$c->andCondition(array(''parent:=''  => $previewContainerId));\n$c->where(array(''alias:=''  => $setName));\n$previewContainer = $modx->getObject(''modResource'', $c);\nif ( !$previewContainer) return;\n\n/* Free it */\n$previewContainer->set(''longtitle'', ''free'');\n$previewContainer->save();\n\nreturn;', 0, 'a:1:{s:18:"previewContainerId";a:6:{s:4:"name";s:18:"previewContainerId";s:4:"desc";s:33:"Design preview pages container id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"473";s:7:"lexicon";N;}}', ''),
(103, 'userEditHomePostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Home processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Check for and process a form submission */\nif ( isset($_REQUEST[''userEditWebsiteProtectSubmit'']) ) {\n            \n    /* Get the current logged in user details */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ( $userId == "" ) {\n        $modx->setPlaceholder(''userEditHomeError'', "Error - No user found please log in");\n        return;\n    }\n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ( $weddingUserObject == null ) {\n        $modx->setPlaceholder(''userEditHomeError'', "Error - No user found please log in");\n        return;\n    }\n    $weddingUser = $weddingUserObject->getOne(''Attributes'');\n    \n    $status = $_REQUEST[''userEditWebsiteProtect''];\n    $weddingUser->set(''websiteActive'', $status);\n    if (!$weddingUser->save()) {\n      $modx->setPlaceholder(''userEditHomeError'', "Error - Failed to update this wedding user");\n  }\n    \n}', 0, 'a:0:{}', ''),
(101, 'userEditHomeJSInclude', '', 0, 45, 0, '/**\n * Wedding site User Edit Home processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n\n/* Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);', 0, 'a:1:{s:14:"linkSortAJAXId";a:6:{s:4:"name";s:14:"linkSortAJAXId";s:4:"desc";s:38:"User areas web links sort AJAX page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"739";s:7:"lexicon";N;}}', ''),
(102, 'userEditHomeInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit Home processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users home page */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditHomeError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditHomeError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Website active radio buttons */\nif ( $attributes[''websiteActive''] == 1 ) {\n    \n    $modx->setPlaceholder(''yesSelected'', ''checked''); \n\n} else {\n\n    $modx->setPlaceholder(''noSelected'', ''checked''); \n   \n}', 0, 'a:0:{}', ''),
(106, 'userEditGalleryInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit Gallery processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users home page */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditGalleryError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditGalleryError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Initialise the common form part */\n$modx->runSnippet(''userEditCommonInitialise'');\n\n/* Album List */\n$weddingUser->albums = $weddingUser->getMany(''GalleryAlbum'');\n\n$albumCount = 0;\n$output = "";\n$context = $modx->context->get(''key'');\nforeach ( $weddingUser->albums as $album ) {\n    \n    $albumName = $album->get(''albumName'');\n    $albumDescription = $album->get(''albumDescription'');\n    $id = $album->get(''id'');\n    $active = $album->get(''active'');\n    $status = "on";\n    if ( $active == 0 ) $status = "off";\n    $AJAXArgs = array(''id'' => $id);\n    $ajaxLink = $modx->makeURL($galleryAJAXPageId, $context, $AJAXArgs, "full");\n    $output .= $modx->getChunk(''userEditGalleryAlbumList'', array(''albumName'' => $albumName,\n                                                                 ''albumDescription'' => $albumDescription,\n                                                                 ''galleryAlbumAJAX'' => $ajaxLink,\n                                                                 ''galleryActiveStatus'' => $status,\n                                                                 ''albumId'' => $id));\n    $albumCount++;\n    \n}\n\n/* Set the output placeholder */\n$modx->setPlaceholder(''galleryAlbumList'', $output);', 0, 'a:2:{s:13:"albumsMaxList";a:6:{s:4:"name";s:13:"albumsMaxList";s:4:"desc";s:51:"Max number of albums we allow a user to create/list";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:1:"6";s:7:"lexicon";N;}s:17:"galleryAJAXPageId";a:6:{s:4:"name";s:17:"galleryAJAXPageId";s:4:"desc";s:37:"Id of the gallery AJAX page processor";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"743";s:7:"lexicon";N;}}', ''),
(104, 'userLinkSortAJAXProcessor', '', 0, 62, 0, '/**\n * Wedding site User common link sort AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Assemble the parameters from the request data */\n$listOrder= $_REQUEST[''order''];\n$listOrderArray = json_decode($listOrder, true);\n\n/* Get the user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUserObject = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUserObject == null ) return;\n\n$weddingUser = $weddingUserObject->getOne(''Attributes'');\n$attributes = $weddingUser->toArray();\n\n/* Get the users page set container  */\n$website = $attributes[''website''];\n$c = $modx->newQuery(''modResource'');\n$c->where(array(''alias:=''  => $website));\n$c->andCondition(array(''parent:=''  => $userWebpageContainerId));\n$userContainer = $modx->getObject(''modResource'', $c);\nif ( !$userContainer ) return;\n$children = $userContainer->getMany(''Children'');\n\n/* Update the page set with the new order */\n$position = 1;\nforeach ( $listOrderArray as $entry ) {\n    \n    foreach ( $children as $child ) {\n        \n        $title = $child->get(''pagetitle'');\n        if ( $title == $entry ) {\n            \n            $menuPosition = $child->get(''menuindex'');\n            if ( $menuPosition != $position ) {\n                \n                $child->set(''menuindex'', $position);\n                $child->save();\n            }\n        }\n    }\n    \n    $position++;\n    \n}', 0, 'a:1:{s:22:"userWebpageContainerId";a:6:{s:4:"name";s:22:"userWebpageContainerId";s:4:"desc";s:32:"Users web page area container id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
(105, 'userAdminWebPageLinksCommonJS', '', 0, 62, 0, '/**\n * Wedding site userAdminWebPageLinksCommonJS\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Supplies the common JS for the user admin area sidebar links\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - a string containing the common JS\n */\n\n/* AJAX links */\n$context = $modx->context->get(''key'');\n$linkSortAJAXPage = $modx->makeURL($linkSortAJAXId, $context, "", "full");\n\n$websiteLinks = ''<script>\n    \n   \nWS = new Object();\nWS.linkPos;\n   \n$(document).ready(function(){\n   \n   $(".btn-slide").click(function(event){\n            event.preventDefault();\n            $("#panel").slideToggle("slow");\n            $(this).toggleClass("active");\n        });\n        \n    $(function() {\n        $( ".user-menu" ).sortable();\n        $( ".user-menu" ).disableSelection();\n        $( ".user-menu" ).bind( "sortupdate", function(event, ui) {\n           WS.linkPos = []; \n           $( ".user-menu-padding" ).each( function() {\n                \n                var title = $(this).attr("title");\n                WS.linkPos.push(title);\n            });\n            var linkString = JSON.stringify(WS.linkPos);\n            var args = "order="+linkString;\n            $.get("'' . $linkSortAJAXPage . ''",args);\n        });\n     });\n    \n    /* Add the page status list AJAX processing */\n  currentLink = "";\n  $(".ajaxLink").bind("click",function(event){\n      event.preventDefault();\n            currentLink = this;\n        $.get(this.href,"",function(response){\n               /* See if the link pressed is the page we are editing and \n                  set the web page active flip state */\n               var flip = false;\n               var pageQueryString = location.search;\n               var pageQueryArray = pageQueryString.split("=");\n               var pagePageId = pageQueryArray[1];\n               var linkQueryArray = currentLink.href.split("=");\n               var linkPageId = linkQueryArray[1];\n               if ( pagePageId == linkPageId) flip = true;\n               \n               /* Response processing */\n               if ( response == "on" ) {\n                    currentLink.className = currentLink.className.replace(/\\boff\\b/,"");\n                    currentLink.className += " on";\n                    /* Flip the common web page active radio buttons */\n                    if ( flip ) \n                        $("input[name=userCommonPageActive]:eq(0)").click();\n               } else {\n                    currentLink.className = currentLink.className.replace(/\\bon\\b/,"");\n                    currentLink.className += " off";\n                    /* Flip the common web page active radio buttons */\n                    if ( flip )\n                        $("input[name=userCommonPageActive]:eq(1)").click();\n               }\n        })  \n     });\n    \n});</script>'';\n\nreturn $websiteLinks;', 0, 'a:1:{s:14:"linkSortAJAXId";a:6:{s:4:"name";s:14:"linkSortAJAXId";s:4:"desc";s:52:"User area web page edit links sorting AJAX processor";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"739";s:7:"lexicon";N;}}', ''),
(107, 'userEditGalleryJSInclude', '', 0, 45, 0, '/**\n * Wedding site User Edit Gallery processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n$pluploadPath = $wsJQueryPath . ''js/plupload/js/'';\n\n/* Common Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) return;\n\n/* Get the album count and other static variables*/\n$albums = $weddingUser->getMany(''GalleryAlbum'');\n$albumCount = count($albums);\n$plupLoadFlash = $pluploadPath . "plupload.flash.swf";\n$plupLoadFlash = ''"'' . $plupLoadFlash . ''"'';\n$plupLoadSilverlight = $pluploadPath . "plupload.silverlight.xap";\n$plupLoadSilverlight = ''"'' . $plupLoadSilverlight . ''"'';\n$context = $modx->context->get(''key'');\n$uploadLink = $modx->makeURL($galleryUploadProcessorId, $context, "", "full");\n$plupLoadURL =  ''"'' . $uploadLink . ''"'';\n$galleryAJAXProcessorLink = $modx->makeURL($galleryAJAXProcessorId, $context, "", "full");\n$galleryAJAXProcessorLink = ''"'' . $galleryAJAXProcessorLink . ''"'';\n$ready = ''<script>\n    \n        WS.albumCount = '' . $albumCount . '';\n        WS.pluploadFlash = '' .  $plupLoadFlash . '';\n        WS.pluploadSilverlight = '' . $plupLoadSilverlight . '';\n        WS.pluploadURL = '' . $plupLoadURL . '';   \n        WS.galleryAJAXProcessorLink = '' . $galleryAJAXProcessorLink . '';   \n        WS.sortable;    \n            \n</script>'';\n$modx->regClientStartupScript($ready, true);\n\n/* WS javascript gallery */\n$gallery = $javascriptPath . "js/pages/user/edit/gallery/gallery.js";\n$modx->regClientStartupScript($gallery);\n\n\n/* Plupload */\n$pluploadCSS = $assets_url . "templates/wedding/styles/jquery.ui.plupload.css";\n$modx->regClientCSS($pluploadCSS);\n$modx->regClientStartupScript("http://bp.yahooapis.com/2.4.21/browserplus-min.js");\n$full = $pluploadPath . ''plupload.full.js'';\n$modx->regClientStartupScript($full);\n$ui = $pluploadPath . ''jquery.ui.plupload/jquery.ui.plupload.js'';\n$modx->regClientScript($ui);\n\n/* WS javascript plupload */\n$plupload = $javascriptPath . "js/pages/user/edit/gallery/plupload.js";\n$modx->regClientStartupScript($plupload);', 0, 'a:2:{s:24:"galleryUploadProcessorId";a:6:{s:4:"name";s:24:"galleryUploadProcessorId";s:4:"desc";s:32:"Gallery upload processor page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"744";s:7:"lexicon";N;}s:22:"galleryAJAXProcessorId";a:6:{s:4:"name";s:22:"galleryAJAXProcessorId";s:4:"desc";s:20:"Gallery AJAX page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"743";s:7:"lexicon";N;}}', ''),
(108, 'userEditCommonInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit Common processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditCommonError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditCommonError'', "Error - No user found please log in");\n    return;\n}\n\n/* Get the users FE page id */\n$userPageId = $_REQUEST[''userPage''];\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$pages = $weddingUser->getMany(''Pages'');\nforeach ( $pages as $page ) {\n    if ( $page->get(''pageId'') == $userPageId ) {\n        $pageSelected = $page->toArray();\n        break;\n    }\n}\n\n\n\n/* Set the user page id placeholder */\n$modx->setPlaceholder(''userPageId'', $userPageId);\n\n/* Set the page title */\n$userPage = $modx->getObject(''modResource'', $userPageId );\nif ( !$userPage ) {\n    $modx->setPlaceholder(''userEditCommonError'', "Error - No user page found");\n    return;\n}\n$title = $userPage->get(''pagetitle'');\n$modx->setPlaceholder(''userEditCommonPageTitle'', $title);\n\n/* Set the webpage active/inactive status */\n$published = $userPage->get(''published'');\nif ( $published == 1 ) {\n    \n    $modx->setPlaceholder(''userEditCommonYesSelected'', ''checked''); \n\n} else {\n\n    $modx->setPlaceholder(''userEditCommonNoSelected'', ''checked''); \n   \n}\n\n/* Set the page protected status */\n$protected = $pageSelected[''passwordProtect''];\nif ( $protected == 1 ) {\n    \n    $modx->setPlaceholder(''userEditCommonProtectYesSelected'', ''checked''); \n\n} else {\n\n    $modx->setPlaceholder(''userEditCommonProtectNoSelected'', ''checked''); \n   \n}\n\n\n/* Set the site preview button link */\n$context = $modx->context->get(''key'');\n$previewLink = $modx->makeURL($userPageId, $context, "", "full");\n$ready = ''<script>$(document).ready(function(){\n    \n   \n   /* Site preview */\n   $(".userPreview").attr("href", "'' . $previewLink . ''");\n        \n                 \n});</script>'';\n$modx->regClientStartupScript($ready, true);', 0, 'a:0:{}', ''),
(109, 'userEditCommonPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Common processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Check for and process a form submission */\nif ( isset($_REQUEST[''userEditCommonFormSubmit'']) ) {\n            \n    /* Get the current logged in user details */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ( $userId == "" ) {\n        $modx->setPlaceholder(''userEditCommonError'', "Error - No user found please log in");\n        return;\n    }\n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ( $weddingUserObject == null ) {\n        $modx->setPlaceholder(''userEditCommonError'', "Error - No user found please log in");\n        return;\n    }\n  \n   /* Get the users FE page id */\n \n    $userPageId = $_REQUEST[''userPage''];\n    $weddingUser = $weddingUserObject->getOne(''Attributes'');\n    $pages = $weddingUserObject->getMany(''Pages'');\n    foreach ( $pages as $page ) {\n        if ( $page->get(''pageId'') == $userPageId ) {\n            $pageSelected = $page;\n            break;\n        }\n    }\n    \n    /* Check for page attribute change */\n    \n    /* Get the page id and the page being edited */\n    $userPageId = $_REQUEST[''userCommonUserPageId''];\n    $userPage = $modx->getObject(''modResource'', $userPageId );\n    if ( !$userPage ) {\n        $modx->setPlaceholder(''userEditCommonError'', "Error - No user page found");\n        return;\n    } \n    \n    /* Title */\n    if (isset($_REQUEST[''userCommonPageTitle''])) {\n    \n        $title = $userPage->get(''pagetitle'');\n        $newTitle = strip_tags($_REQUEST[''userCommonPageTitle'']);\n        if ( $newTitle == "" ) return;\n        if ( $title != $newTitle) {\n                 \n            $userPage->set(''pagetitle'', $newTitle);\n            $userPage->save();\n        }\n    }\n    \n     /* Web page active(published) */\n    if (isset($_REQUEST[''userCommonPageActive''])) {\n        \n        $userPage->set(''published'', $_REQUEST[''userCommonPageActive'']);\n        $userPage->save();\n    }\n    \n     /* Web page protected */\n  \n    if (isset($_REQUEST[''userCommonPageProtect''])) {\n       \n        $pageSelected->set(''passwordProtect'', $_REQUEST[''userCommonPageProtect'']);\n        $pageSelected->save();\n    }\n}', 0, 'a:0:{}', ''),
(110, 'userEditGalleryPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Gallery processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditGalleryError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditGalleryError'', "Error - No user found please log in");\n    return;\n}\n\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check common form processing */\n$modx->runSnippet(''userEditCommonPostProcessor'');\n\n/* Album Create */\nif ( isset($_REQUEST[''galleryAlbumCreateSubmit''])) {\n    \n    $albumObject = $modx->newObject(''weddingUserGalleryAlbum'');\n    if ( !$albumObject ) return;\n    $albumObject->set(''albumName'', strip_tags($_REQUEST[''galleryAlbumName'']));\n    $albumObject->set(''albumDescription'', strip_tags($_REQUEST[''galleryAlbumDescription'']));\n    $albumObject->set(''active'', $_REQUEST[''galleryAlbumActive'']);\n    \n    $weddingUser->addMany($albumObject, ''GalleryAlbum'');\n    if ( $weddingUser->save() == false ) {\n            $modx->setPlaceholder(''userEditGalleryError'', "Error - Failed to create this album"); \n            return;\n        }\n    \n}', 0, NULL, ''),
(111, 'userEditGalleryAJAXProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Gallery AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Assemble the parameters from the request data */\n$id = $_REQUEST[''id''];\n$command = $_REQUEST[''command''];\n$output = "";\n\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user) {\n    $userId = $user->get(''id'');\n    if ($userId == "")  return "Error! No logged in user";\n}\n\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null )  return "Error! No logged in Wedding user";\n    \n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Get the album from the request */\n$album = $modx->getObject(''weddingUserGalleryAlbum'', $id);\nif (!$album)  return "Error - can''t find album";\n $albumId = $id;\n\n/* Switch on command type */\nswitch ($command) {\n\n    case ''status'' :\n\n        /* Toggle the current state of active for the album */\n        $active = $album->get(''active'');\n        $newActive = 0;\n        $output = ''off'';\n        if ($active == 0) {\n\n            $newActive = 1;\n            $output = "on";\n        }\n        $album->set(''active'', $newActive);\n        $album->save();\n        break;\n\n    case ''albumDetails'' :\n\n        /* Get the album details */\n        $name = $album->get(''albumName'');\n        $description = $album->get(''albumDescription'');\n        $active = $album->get(''active'');\n        $status = $active ? 1 : 0;\n        $response = array(''name'' => $name, ''description'' => $description, ''status'' => $status);\n        $output = json_encode($response);\n        break;\n\n    case ''albumDetailsUpdate'' :\n\n        /* Update the album details */\n        $album->set(''albumName'', strip_tags($_REQUEST[''name'']));\n        $album->set(''albumDescription'', strip_tags($_REQUEST[''description'']));\n        $album->save();\n        break;\n\n    case ''filesAdded'' :\n\n        /* Nothing to do here currently */\n        break;\n\n    case ''uploadComplete'':\n\n       /* Get album/user details */\n       $uploadDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => "uploads"));\n       $galleryDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => "gallery")); \n       if ( ($uploadDirectory == ''error'') || ($galleryDirectory == ''error'') ) return;   \n       $galleryURL = $modx->runSnippet(''userGetDirectoryURL'', array(''directory'' => "gallery")); \n       if ( ($galleryURL == ''error'') ) return;   \n       \n       /* Look for any zip files and expand them */\n        if (is_dir($uploadDirectory) && ($dir = opendir($uploadDirectory))) {\n            while (($file = readdir($dir)) !== false) {\n               \n                if ( ($file == ''..'') || ($file == ''.'') ) continue;  \n                $fileInfo = pathinfo($file);\n                if ( $fileInfo[''extension''] == ''zip'' ) {\n                    \n                    /* Unpack it */\n                    $zip = new ZipArchive;\n                    $res = $zip->open($uploadDirectory .  DIRECTORY_SEPARATOR . $file);\n                    if ($res === TRUE) {\n                        $zip->extractTo($uploadDirectory);\n                        $zip->close();  \n                   } else {\n                     @unlink($uploadDirectory . DIRECTORY_SEPARATOR . $file);\n                   }\n                }\n                closedir($dir);\n            }        \n        }\n        \n       /* Delete any files with an invalid extension */\n       $validExtensionsArray = explode('','', $validExtensions);\n        if (is_dir($uploadDirectory) && ($dir = opendir($uploadDirectory))) {\n            while (($file = readdir($dir)) !== false) {\n               \n                if ( ($file == ''..'') || ($file == ''.'') ) continue;  \n                $fileInfo = pathinfo($uploadDirectory . DIRECTORY_SEPARATOR . $file);\n                if ( !in_array($fileInfo[''extension''], $validExtensionsArray) ) {\n                     @unlink($uploadDirectory . DIRECTORY_SEPARATOR . $file);\n                }\n                \n            }\n        }\n        \n       /* Get the count of any existing album items for position calculation */\n       $albumItems = $modx->getCollection(''albumItems'', array(''albumId'' => $albumId));\n       $nextPosition = count($albumItems) + 1;\n       \n        /* Process the uploaded files */\n       if (is_dir($uploadDirectory) && ($dir = opendir($uploadDirectory))) {\n            while (($file = readdir($dir)) !== false) {\n               \n                if ( ($file == ''..'') || ($file == ''.'') ) continue;  \n                \n                /* Create an album item and move the file to the gallery directory */\n                $newAlbumItem = $modx->newObject(''weddingUserGalleryItem'');\n                if (! $newAlbumItem ) continue;\n                $newAlbumItem->set(''itemFileName'', $file);\n                $newAlbumItem->set(''itemPosition'', $nextPosition);\n                $nextPosition++;\n                $newAlbumItem->set(''active'', true);\n                \n                 /* Add the newly created items to the user */\n                $weddingUser->addMany( $newAlbumItem, ''GalleryItem'');\n                $weddingUser->save();\n                \n                 /* Link the items to the album */\n                $albumItemId = $modx->lastInsertId();\n                $albumItemsObject = $modx->newObject(''albumItems'');\n                if ( !$albumItemsObject) continue;\n                $albumItemsObject->set(''albumId'', $albumId);\n                $albumItemsObject->set(''itemId'', $albumItemId);\n                if ( !$albumItemsObject->save() ) continue;\n                \n                /* Move the file from the upload folder to the gallery folder */\n                $filePath = $uploadDirectory . DIRECTORY_SEPARATOR . $file;\n                $galleryPath = $galleryDirectory . DIRECTORY_SEPARATOR . $file;\n                if ( !@rename($filePath, $galleryPath ) ) continue;\n                \n            }\n            closedir($dir);\n           \n          \n        }\n        break;\n        \n     case ''albumGetItems'':\n         \n         /* Get the items for this album */\n         $galleryURL =  $modx->runSnippet(''userGetDirectoryURL'', array(''directory'' => "gallery")); \n         if ( $galleryURL == ''error''  ) return; \n         $albumItems = $modx->getCollection(''albumItems'', array(''albumId'' => $albumId));\n         $itemPositionArray = array();\n         \n         /* Sort by position */\n         foreach ( $albumItems as $albumItem ) {\n             \n             $itemId = $albumItem->get(''itemId'');\n             $galleryItem = $modx->getObject(''weddingUserGalleryItem'', $itemId);\n             if ( !$galleryItem ) continue;\n             $galleryItemPosition = $galleryItem->get(''itemPosition'');\n             $itemPositionArray[$galleryItemPosition] =  $galleryItem;\n             \n         }   \n         ksort($itemPositionArray);\n         \n         /* Output the items */\n         $itemOutput = array();\n         foreach ($itemPositionArray as $position => $galleryItem ) {\n             \n             $itemId = $galleryItem->get(''id'');\n             $itemURL =  $galleryURL . DIRECTORY_SEPARATOR;\n             $itemURL .= $galleryItem->get(''itemFileName'');\n             $itemTitle = $galleryItem->get(''itemDisplayName'');\n             $itemPosition = $galleryItem->get(''itemPosition'');\n             $itemPosition = $itemId . "-" . $itemPosition;\n             $args= array(''itemId'' => $itemId,\n                                   ''id'' => $albumId,\n                                   ''command'' => ''itemDetails'');\n             $context = $modx->context->get(''key'');\n             $itemEditLink = $modx->makeURL($galleryAJAXProcessorID, $context, $args, "full");\n             $parameters = array(''galleryItemTitle'' =>$itemTitle,\n                                               ''galleryItemSrc'' => $itemURL,\n                                               ''galleryItemLink'' => $itemEditLink,\n                                              ''galleryItemPosition'' => $itemPosition);\n             $itemOutput [] = $modx->getChunk(''userEditGalleryItem'', $parameters);\n             \n         }\n         \n         $response = array(''items'' => $itemOutput);\n         $output = json_encode($response);\n         break;\n         \n    case ''itemDetails'' :\n\n        /* Get the item details */\n        $itemId = $_REQUEST[''itemId''];\n        $galleryItemObject = $modx->getObject(''weddingUserGalleryItem'', $itemId);\n        $error = "none";\n        if ( !$galleryItemObject ) $error = "No Item Found";\n        $galleryItem = $galleryItemObject->toArray();\n        $active = $galleryItem[''active''];\n        $status = $active ? 1 : 0;\n        $galleryURL = $modx->runSnippet(''userGetDirectoryURL'', array(''directory'' => "gallery")); \n         if ( ($galleryURL == ''error'') ) return; \n        $fileURL = $galleryURL . DIRECTORY_SEPARATOR . $galleryItem[''itemFileName''];\n        \n        /* Response */\n        $response = array(''filename'' => $galleryItem[''itemFileName''],\n                          ''displayname'' => $galleryItem[''itemDisplayName''],\n                          ''description'' => $galleryItem[''itemDescription''],\n                          ''position'' => $galleryItem[''itemPosition''],\n                          ''url'' => $fileURL,\n                          ''status'' => $status,\n                          ''error'' => $error,\n                          ''id'' => $itemId);\n        $output = json_encode($response);\n        break;\n    \n     case ''albumUpdateItem'' :\n         \n         /* Update the gallery item */\n         $itemId = $_REQUEST[''userEditGalleryItemId''];\n         $galleryItem = $modx->getObject(''weddingUserGalleryItem'', $itemId);\n         if (!$galleryItem ) return;\n         $galleryItem->set(''itemDisplayName'', strip_tags($_REQUEST[''userEditGalleryItemDisplayName'']));\n         $galleryItem->set(''itemDescription'', strip_tags($_REQUEST[''userEditGalleryItemDescription'']));\n         $_REQUEST[''userEditGalleryItemActive''] == 1 ? $active = 1 : $active = 0;\n         $galleryItem->set(''active'', $active);\n         $galleryItem->save();\n         \n         break;\n     \n     case ''albumItemDelete'' :\n         \n         $itemId = $_REQUEST[''itemId''];\n         $galleryItemObject = $modx->getObject(''weddingUserGalleryItem'', $itemId);\n          if ( !$galleryItemObject ) return;\n          \n         /* Delete the file */\n         $galleryDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => "gallery")); \n         if ( $galleryDirectory == ''error'')  return;\n         $itemFilename = $galleryItemObject->get(''itemFileName'');\n         @unlink($galleryDirectory . DIRECTORY_SEPARATOR .  $itemFilename);\n         \n         /* Delete the item */\n        \n         $galleryItemObject->remove();\n         \n         /* Update the positions of the pictures */\n         $albumItems = $modx->getCollection(''albumItems'', array(''albumId'' => $albumId));\n         $itemPositionArray = array();\n         \n         /* Sort by position */\n         foreach ( $albumItems as $albumItem ) {\n             \n             $itemId = $albumItem->get(''itemId'');\n             $galleryItem = $modx->getObject(''weddingUserGalleryItem'', $itemId);\n             if ( !$galleryItem ) continue;\n             $galleryItemPosition = $galleryItem->get(''itemPosition'');\n             $itemPositionArray[$galleryItemPosition] =  $galleryItem;\n             \n         }   \n         ksort($itemPositionArray);\n         \n         /* Update the positions */\n         $newPosition = 1;\n         foreach ($itemPositionArray as $position => $galleryItem ) {\n             \n             $galleryItem->set(''itemPosition'', $newPosition);\n             $galleryItem->save();\n             $newPosition++;\n         }\n         \n         break;\n         \n    case ''galleryItemReposition'':\n   \n        /* Get the items into an array */\n        $itemArray = json_decode($_REQUEST[''positions''] , true);\n        $length = count($itemArray);\n        for ( $i = 0; $i<=$length; $i++) {\n            \n            $itemObject = $modx->getObject(''weddingUserGalleryItem'', $itemArray[$i]);\n            if ( !$itemObject ) continue;\n            $itemObject->set(''itemPosition'', $i+1);\n            $itemObject->save();\n                 \n        }\n        break;\n        \n    default:\n\n        $output = "Error - no valid command";\n        break;\n}\n\n/* Return the output */\necho $output;', 0, 'a:2:{s:22:"galleryAJAXProcessorID";a:6:{s:4:"name";s:22:"galleryAJAXProcessorID";s:4:"desc";s:30:"Gallery AJAX processor page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"743";s:7:"lexicon";N;}s:15:"validExtensions";a:6:{s:4:"name";s:15:"validExtensions";s:4:"desc";s:56:"Comma separated list of valid image extensions we accept";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:41:"jpg,jpeg,jpe,gif,png,JPG,JPEG,JPE,GIF,PNG";s:7:"lexicon";N;}}', ''),
(112, 'userEditGalleryUpload', '', 0, 45, 0, '/**\n * Wedding site User Edit Gallery Upload processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n * Derived from :-\n * Copyright 2009, Moxiecode Systems AB\n * Released under GPL License.\n *\n * License: http://www.plupload.com/license\n * Contributing: http://www.plupload.com/contributing\n */\n\n// HTTP headers for no cache etc\nheader("Expires: Mon, 26 Jul 1997 05:00:00 GMT");\nheader("Last-Modified: " . gmdate("D, d M Y H:i:s") . " GMT");\nheader("Cache-Control: no-store, no-cache, must-revalidate");\nheader("Cache-Control: post-check=0, pre-check=0", false);\nheader("Pragma: no-cache");\n\n// Get the users upload directory path\n$targetDir = $modx->runSnippet(''userGetDirectory'', array(''directory'' => "uploads"));\nif ( $targetDir == "error") die(''{"jsonrpc" : "2.0", "error" : {"code": 103, "message": "No User upload directory."}, "id" : "id"}'');\n\n// Get the input parameters\n$chunk = isset($_REQUEST["chunk"]) ? $_REQUEST["chunk"] : 0;\n$chunks = isset($_REQUEST["chunks"]) ? $_REQUEST["chunks"] : 0;\n$fileName = isset($_REQUEST["name"]) ? $_REQUEST["name"] : '''';\n\n// Clean the fileName for security reasons\n$fileName = preg_replace(''/[^\\w\\._]+/'', '''', $fileName);\n\n// Make sure the fileName is unique but only if chunking is disabled\nif ($chunks < 2 && file_exists($targetDir . DIRECTORY_SEPARATOR . $fileName)) {\n	$ext = strrpos($fileName, ''.'');\n	$fileName_a = substr($fileName, 0, $ext);\n	$fileName_b = substr($fileName, $ext);\n\n	$count = 1;\n	while (file_exists($targetDir . DIRECTORY_SEPARATOR . $fileName_a . ''_'' . $count . $fileName_b))\n		$count++;\n\n	$fileName = $fileName_a . ''_'' . $count . $fileName_b;\n}\n\n// Look for the content type header\nif (isset($_SERVER["HTTP_CONTENT_TYPE"]))\n	$contentType = $_SERVER["HTTP_CONTENT_TYPE"];\n\nif (isset($_SERVER["CONTENT_TYPE"]))\n	$contentType = $_SERVER["CONTENT_TYPE"];\n\n// Handle non multipart uploads older WebKit versions didn''t support multipart in HTML5\nif (strpos($contentType, "multipart") !== false) {\n	if (isset($_FILES[''file''][''tmp_name'']) && is_uploaded_file($_FILES[''file''][''tmp_name''])) {\n		// Open temp file\n		$out = fopen($targetDir . DIRECTORY_SEPARATOR . $fileName, $chunk == 0 ? "wb" : "ab");\n		if ($out) {\n			// Read binary input stream and append it to temp file\n			$in = fopen($_FILES[''file''][''tmp_name''], "rb");\n\n			if ($in) {\n				while ($buff = fread($in, 4096))\n					fwrite($out, $buff);\n			} else\n				die(''{"jsonrpc" : "2.0", "error" : {"code": 101, "message": "Failed to open input stream."}, "id" : "id"}'');\n			fclose($in);\n			fclose($out);\n			@unlink($_FILES[''file''][''tmp_name'']);\n		} else\n			die(''{"jsonrpc" : "2.0", "error" : {"code": 102, "message": "Failed to open output stream."}, "id" : "id"}'');\n	} else\n		die(''{"jsonrpc" : "2.0", "error" : {"code": 103, "message": "Failed to move uploaded file."}, "id" : "id"}'');\n} else {\n	// Open temp file\n	$out = fopen($targetDir . DIRECTORY_SEPARATOR . $fileName, $chunk == 0 ? "wb" : "ab");\n	if ($out) {\n		// Read binary input stream and append it to temp file\n		$in = fopen("php://input", "rb");\n\n		if ($in) {\n			while ($buff = fread($in, 4096))\n				fwrite($out, $buff);\n		} else\n			die(''{"jsonrpc" : "2.0", "error" : {"code": 101, "message": "Failed to open input stream."}, "id" : "id"}'');\n\n		fclose($in);\n		fclose($out);\n	} else\n		die(''{"jsonrpc" : "2.0", "error" : {"code": 102, "message": "Failed to open output stream."}, "id" : "id"}'');\n}\n\n// Return JSON-RPC response\ndie(''{"jsonrpc" : "2.0", "result" : null, "id" : "id"}'');', 0, NULL, ''),
(113, 'userGetDirectory', 'Gets a users specified directory path', 0, 62, 0, '/**\n * Wedding site userGetDirectory\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a users directory path snippet\n * \n * Parameters :-\n * \n *  directory : The directory needed, one of uploads, \n *  gallery, images\n *  website : If passed use this as the website name\n *  \n * Returns :-\n * \n * path - the directory path for the user.\n */\nif (!isset($website)) {\n\n    /* Get the user details */\n    $context = $modx->context->get(''key'');\n    if (!$modx->user->isAuthenticated($context))\n        return "error";\n\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    $weddingUser = $modx->getObject(''weddingUser'', $userId);\n    $weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n    $attributes = $weddingUser->attributes->toArray();\n    $website = $attributes[''website''];\n}\n\n/* Construct the path */\n$assetsPath = $modx->getOption(''assets_path'');\n$userDirectory = $assetsPath . "users" . DIRECTORY_SEPARATOR . $website . DIRECTORY_SEPARATOR . $directory;\nreturn $userDirectory;', 0, NULL, ''),
(114, 'userGetDirectoryURL', '', 0, 62, 0, '/**\n * Wedding site userGetDirectoryURL\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets users URL to a specified directory \n * \n * Parameters :-\n * \n *  directory : The URL needed, one of uploads, gallery\n *  \n * Returns :-\n * \n * url - the URL for the users gallery\n */\n\n/* Get the user type */\n$userType = $modx->runSnippet(''userGetURLType'');\n\n/* Get the website name */\nif ( $userType == ''user'' ) {\n\n    /* User logged in, get the  details */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    $weddingUser = $modx->getObject(''weddingUser'', $userId);\n    $weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n    $attributes = $weddingUser->attributes->toArray();\n    $website = $attributes[''website''];\n    \n} else {\n\n    /* Front/back end */\n    $website = $modx->runSnippet(''userGetWebsiteFromURL'');\n}\n\n/* Construct the path */\n$assetsURL = $modx->getOption(''assets_url'');\n$userURL = $assetsURL . "users" . DIRECTORY_SEPARATOR . $website . DIRECTORY_SEPARATOR . $directory;\nreturn $userURL;', 0, NULL, '');
INSERT INTO `modx_site_snippets` (`id`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`) VALUES
(115, 'phpthumbof', 'A custom output filter that generates thumbnails securely with phpThumb.', 0, 0, 0, '/**\n * phpThumbOf\n *\n * Copyright 2009-2011 by Shaun McCormick <shaun@modx.com>\n *\n * phpThumbOf is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * phpThumbOf is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * phpThumbOf; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package phpthumbof\n */\n/**\n * A custom output filter for phpThumb\n *\n * @var modX $modx\n * @var array $scriptProperties\n * @var phpThumbOf $phpThumbOf\n * @var string $input\n * @var string|array $options\n *\n * @package phpthumbof\n */\nif (empty($modx)) return '''';\nif (!$modx->loadClass(''modPhpThumb'',$modx->getOption(''core_path'').''model/phpthumb/'',true,true)) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''[phpThumbOf] Could not load modPhpThumb class.'');\n    return '''';\n}\nif (empty($input)) {\n    $modx->log(modX::LOG_LEVEL_DEBUG,''[phpThumbOf] Empty image path passed, aborting.'');\n    return '''';\n}\n$modelPath = $modx->getOption(''phpthumbof.core_path'',null,$modx->getOption(''core_path'').''components/phpthumbof/'').''model/'';\nrequire_once $modelPath.''phpthumbof/phpthumbof.class.php'';\n$phpThumbOf = new phpThumbOf($modx,$scriptProperties);\n\n$phpThumbOf->getCacheDirectory();\n$phpThumbOf->ensureCacheDirectoryIsWritable();\n\n$thumbnail = $phpThumbOf->createThumbnailObject();\n$thumbnail->setInput($input);\n$thumbnail->setOptions($options);\n$thumbnail->initializeService();\nreturn $thumbnail->render();', 0, NULL, ''),
(125, 'userFEGalleryJSInclude', '', 0, 51, 0, '/**\n * Wedding site User Front End Gallery processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'', null, $modx->getOption(''assets_url'') . ''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'', null, $modx->getOption(''assets_url'') . ''components/ws_javascript/'');\n$cssPath = $assets_url . "templates/wedding/styles/";\n\n/* Dont include the gallery if we have no albums or empty albums */\n$website = $modx->runSnippet(''userGetWebsiteFromUrl'');\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:=''  => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif ( !$weddingUserAttributes  ) return;\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$weddingUser = $modx->getObject(''weddingUser'', $weddingUserId); \nif ( !$weddingUser ) return;\n$albums = $weddingUser->getMany(''GalleryAlbum'');\n$activeAlbums = false;\nforeach ( $albums as $album ) {\n    \n    /* Check for active, no items is not active even if marked as such */\n    $active = $album->get(''active'');\n    $albumId = $album->get(''id'');\n    $albumItems = $modx->getCollection(''albumItems'', array(''albumId'' => $albumId));\n    if ( count( $albumItems) == 0) $active = 0;\n    if ( $active == 1 ) $activeAlbums = true;\n}\n\nif ( !$activeAlbums ) return;\n\n/* If we have no album selected return also */\nisset($_REQUEST[''albumId''] ) ? $albumIdPage =  $_REQUEST[''albumId'']  : $albumIdPage = 0;\nif ( $albumIdPage == 0 ) return;\n\n/* Styles */\n$css1 = $cssPath . "galleryStyle.css";\n$modx->regClientCSS($css1);\n$css2 = $cssPath . "galleryElastislide.css";\n$modx->regClientCSS($css2);\n$css3 = ''<noscript>\n  <style>\n      .es-carousel ul{\n    display:block;\n    }\n  </style>\n</noscript>'';\n$modx->regClientCSS($css3);\n\n/* Head scripts */\n$head1 = ''<script id="img-wrapper-tmpl" type="text/x-jquery-tmpl">  \n                    <div class="rg-image-wrapper">\n                            {{if itemsCount > 1}}\n                                    <div class="rg-image-nav">\n                                            <a href="#" class="rg-image-nav-prev">Previous Image</a>\n                                            <a href="#" class="rg-image-nav-next">Next Image</a>\n                                    </div>\n                            {{/if}}\n                            <div class="rg-image"></div>\n                            <div class="rg-loading"></div>\n                            <div class="rg-caption-wrapper">\n                                    <div class="rg-caption" style="display:none;">\n                                            <p></p>\n                                    </div>\n                            </div>\n                    </div>\n</script>'';\n$modx->regClientStartupScript($head1, true);\n\n/* Body scripts */\n$ui1 = $wsJQueryPath . ''js/galleryFE/jquery.tmpl.min.js"'';\n$modx->regClientScript($ui1);\n$ui2 = $wsJQueryPath . ''js/galleryFE/jquery.easing.1.3.js"'';\n$modx->regClientScript($ui2);\n$ui3 = $wsJQueryPath . ''js/galleryFE/jquery.elastislide.js"'';\n$modx->regClientScript($ui3);\n$ui4 = $javascriptPath . "js/pages/user/frontend/gallery/gallery.js";\n$modx->regClientScript($ui4);', 0, 'a:0:{}', ''),
(116, 'createUserDirectories', '', 0, 62, 0, '/**\n * Wedding site creatUserDirectories\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the users directory set\n * \n * Parameters :- \n * \n * None\n *  \n*/\n\n/* Get the directories */\n$uploadDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => "uploads"));\n$galleryDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => "gallery")); \n$imagesDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => "images")); \n\n/* Create them */\n@mkdir($uploadDirectory, 0755, true);\n@mkdir($galleryDirectory, 0755, true);\n@mkdir($imagesDirectory, 0755, true);', 0, NULL, ''),
(117, 'renameUserDirectory', '', 0, 62, 0, '/**\n * Wedding site renameUserDirectory\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Renames the users directory \n * \n * Parameters :- \n * \n * from - old name\n * to - new name\n *  \n*/\n\n$assetsPath = $modx->getOption(''assets_path'');\n$fromDir = $assetsPath . "users" .  DIRECTORY_SEPARATOR  .  $from;\n$toDir = $assetsPath . "users" .  DIRECTORY_SEPARATOR  .  $to;\n@rename($fromDir, $toDir);', 0, 'a:0:{}', ''),
(118, 'userEditGenericInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit Generic processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users home page */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditGenericError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditGenericError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Initialise the common form part */\n$modx->runSnippet(''userEditCommonInitialise'');\n\n/* Set the TinyMCE content area */\n$userPageId = $_REQUEST[''userPage''];\n\n/* Set the page content*/\n$userPage = $modx->getObject(''modResource'', $userPageId );\nif ( !$userPage ) {\n    $modx->setPlaceholder(''userEditGenericError'', "Error - No user page found");\n    return;\n}\n$content = $userPage->get(''content'');\n$modx->setPlaceholder(''userEditGenericContent'', $content);\n\n/* Allow submission from the common page header for generic pages */\n$genericSubmit = ''onSubmit="return WS.genericSubmit();"'';\n$modx->setPlaceholder(''userEditGenericSubmit'', $genericSubmit);\n\n/* Set the page id in the tiny mce form */\n$modx->setPlaceholder(''userPageId'', $userPageId);', 0, NULL, ''),
(119, 'userEditGenericJSInclude', '', 0, 45, 0, '/**\n * Wedding site User Edit Generic processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n\n/* Common Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) return;\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n$website = ''"'' . $website . ''"'';\n\n/* Generic */\n$generic = $javascriptPath . "js/pages/user/edit/generic/generic.js";\n$modx->regClientStartupScript($generic);\n\n$context = $modx->context->get(''key'');\n$genericAJAXProcessorLink = $modx->makeURL($genericAJAXProcessorId, $context, "", "full");\n$genericAJAXProcessorLink = ''"'' . $genericAJAXProcessorLink . ''"'';\n\n/* TinyMCE */\n$wsTinyMCEPath = $modx->getOption(''ws_tinyMCE.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_tinyMCE/'');\n$jqueryTinyMCE = $wsTinyMCEPath . "js/jscripts/tiny_mce/jquery.tinymce.js";\n$modx->regClientStartupScript($jqueryTinyMCE);\n\n$tinyMCEURL = $wsTinyMCEPath . "js/jscripts/tiny_mce/tiny_mce.js";\n$tinyMCEURL =  ''"'' . $tinyMCEURL . ''"'';\n$ready = ''<script>\n    \n        WS.tinymceURL = '' . $tinyMCEURL . '';\n        WS.website = '' . $website . '';\n        WS.genericAJAXProcessorLink = '' . $genericAJAXProcessorLink . '';       \n       \n</script>'';\n$modx->regClientStartupScript($ready, true);\n\n$tinyMCE = $javascriptPath . "js/pages/user/edit/generic/tinymce.js";\n$modx->regClientStartupScript($tinyMCE);', 0, 'a:1:{s:22:"genericAJAXProcessorId";a:6:{s:4:"name";s:22:"genericAJAXProcessorId";s:4:"desc";s:34:"Generic edit page AJAX resource Id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"745";s:7:"lexicon";N;}}', ''),
(120, 'userEditGenericPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Generic processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ($userId == "") {\n    $modx->setPlaceholder(''userEditGenericError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ($weddingUser == null) {\n    $modx->setPlaceholder(''userEditGenericError'', "Error - No user found please log in");\n    return;\n}\n\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check common form processing */\n$modx->runSnippet(''userEditCommonPostProcessor'');', 0, 'a:0:{}', ''),
(121, 'userEditGenericAJAXProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Generic AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Assemble the parameters from the request data */\n$id = $_REQUEST[''id''];\n$command = $_REQUEST[''command''];\n$output = "";\n\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user) {\n    $userId = $user->get(''id'');\n    if ($userId == "")\n        return "Error! No logged in user";\n}\n\n/* Save the tiny MCE edit area */\n\n$userPageId = $_REQUEST[''userPage''];\n\n/* Set the page content */\n$userPage = $modx->getObject(''modResource'', $userPageId);\nif (!$userPage) {\n    $modx->setPlaceholder(''userEditGenericError'', "Error - No user page found");\n    return "Error - No user page found";\n}\n\n/* Add the HTML Purifier  path and include the class */\n$htmlPurifierPath = $modx->getOption(''ws_htmlpurifier.core_path'', null, $modx->getOption(''core_path'') . ''components/ws_htmlpurifier/'');\ninclude_once $htmlPurifierPath . "library/HTMLPurifier.auto.php";\n$purifier = new HTMLPurifier();\n\n$content = $_REQUEST[''userEditGenericTinyMCEcontent''];\n$content = $purifier->purify($content);\n$userPage->set(''content'', $content);\n$userPage->save();\n\n/* Clear the cache */\n$cacheManager = $modx->getCacheManager();\n$cacheManager->clearCache(array(\n    "{$resource->context_key}/",\n        ), array(\n    ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n    ''publishing'' => true\n        )\n);', 0, NULL, ''),
(124, 'removeUserDirectories', '', 0, 62, 0, '/**\n * Wedding site clearUserDirectories\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Removes or archives the users directory set\n * \n * Parameters :-\n *\n * archive - if true the directory contents are archived rather than being removed \n * website - the website(directory) name\n * \n * \n * Returns :-\n * \n * None\n *\n * \n */\n/* Helper function */\nif (!function_exists(''recursiveDelete'')) {\n\n    function recursiveDelete($dir) {\n\n        $files = scandir($dir);\n\n        foreach ($files as $file) {\n\n            if ($file == ''.'' OR $file == ''..'')\n                continue;\n\n            $file = "$dir/$file";\n            if (is_dir($file)) {\n                recursiveDelete($file);\n                @rmdir($file);\n            } else {\n                @unlink($file);\n            }\n        }\n        @rmdir($dir);\n    }\n\n}\n\n/* Sanity check on the website name */\nif ( $website == "" ) return;\n\n/* Get the directory path */\n$assetsPath = $modx->getOption(''assets_path'');\n$userDirectory = $assetsPath . "users" . DIRECTORY_SEPARATOR . $website;\nif ( !file_exists($userDirectory)) return;\n\n/* Remove or archive */\nif (!$archive)\n    recursiveDelete($userDirectory);', 0, NULL, ''),
(122, 'FirstChildRedirect', 'Snippet redirecting you to the first child of a parent.', 0, 48, 0, '/**\n * @name FirstChildRedirect\n * @author Jason Coward <jason@opengeek.com>\n * @author Ryan Thrash <ryan@vertexworks.com>\n * @author Olivier B. Deland <olivier@conseilsweb.com> Refactored for Revo and\n * added functionnalities\n * @author Mark Hamstra <hello@markhamstra.com> Refactor for Revo 2.1\n * @license Public Domain\n * @version 2.3\n * @package firstchildredirect\n *\n * This snippet redirects to the first child document of a folder in which this\n * snippet is included within the content (e.g. [[FirstChildRedirect]]).  This\n * allows MODx folders to emulate the behavior of real folders since MODx\n * usually treats folders as actual documents with their own content.\n *\n * Added parameters to allow greater flexibility\n *\n * &docid=`12` (optional; default: current document)\n * Use the docid parameter to have this snippet redirect to the\n * first child document of the specified document.\n *\n * &default=`1` or &default=`site_start` (optional; default: site_start)\n * Use the default parameter to have this snippet redirect to the\n * document specified in cases where there is no children.\n * It can be a document ID or one of : site_start,site_unavailable_page,error_page,unauthorized_page\n *\n * &sortBy=`menuindex` (optional; default:menuindex)\n * Get the first child depending on this sort order\n * Can be any valid modx document field name\n *\n * &sortDir=`DESC` (optional; default:ASC)\n * Sort `ASC` for ascendant or `DESC` for descendant\n *  \n * &responseCode ("301", "302" or the complete response code, eg "HTTP/1.1 302 Moved Temporarily", defaults to 301)\n * The responsecode (statuscode) to use for sending the redirect.\n * \n */\n/* @var modX $modx\n * @var array $scriptProperties\n */ \n\n/*\n * Parameters\n * Parent doc \n */\n$docid = $modx->getOption(''docid'',$scriptProperties,null);\nif ($docid === null) { \n    $parent = $modx->resource->get(''id'');\n}\nelse {\n    $parent = $docid;\n}\n\n$respcode = $modx->getOption(''responseCode'',$scriptProperties,''301'');\n$rcodes = array(\n    ''301'' => ''HTTP/1.1 301 Moved Permanently'',\n    301 => ''HTTP/1.1 301 Moved Permanently'',\n    ''302'' => ''HTTP/1.1 302 Moved Temporarily'',\n    302 => ''HTTP/1.1 302 Moved Temporarily'',\n);\nif (isset($rcodes[$respcode])) $respcode = $rcodes[$respcode];\n$respcode = array(''responseCode'' => $respcode);\n/* default doc in case there''s no children\n * can be an id or one of: site_start, site_unavailable_page, error_page,\n * unauthorized_page\n * Default is site_start\n */\n$default = $modx->getOption(''default'',$scriptProperties,''site_start'');\nif (in_array($default,array(''site_start'',''site_unavailable_page'',''error_page'',''unauthorized_page''))) {\n    $default = $modx->getOption($default,null,1);\n} \nelse {\n    if (is_numeric($default)) { \n        $default = (int)$default;\n    }\n    else { \n        return ''Invalid &default property.''; \n    }\n}\n\n/* sort list */\n$sortBy = $modx->getOption(''sortBy'',$scriptProperties,''menuindex'');\n/* sort dir */\n$sortDir = $modx->getOption(''sortDir'',$scriptProperties,''ASC'');\n\n/*\n* Execute\n*/\n$c = $modx->newQuery(''modResource'');\n$c->limit(1);\n$c->sortby($sortBy,$sortDir);\n$c->where(array(\n    ''published'' => 1,\n    ''parent'' => $parent\n));\n\n/* @var modResource $children */\n$children = $modx->getObject(''modResource'',$c);\nif (!empty($children)) {\n    $url = $modx->makeUrl($children->get(''id''));\n    return $modx->sendRedirect($url,$respcode);\n}\n\n// If it got here, there obviously weren''t any children resources.. redirect to default.\nreturn $modx->sendRedirect($modx->makeUrl($default),$respcode);', 0, NULL, ''),
(123, 'createSitePreviewLink', '', 0, 62, 0, '/**\n * Wedding site createSitePreviewLink\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the default site preview link\n * \n * Parameters :- \n * \n * None\n *  \n */\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return;\n$user = $modx->user;\n$userId = $user->get(''id'');\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n$previewURL = $modx->getOption(''site_url'') . "users/$website";\n$modx->setPlaceholder(''userEditCommonSitePreview'', $previewURL);', 0, 'a:5:{s:5:"width";a:6:{s:4:"name";s:5:"width";s:4:"desc";s:14:"Colorbox width";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"90%";s:7:"lexicon";N;}s:6:"height";a:6:{s:4:"name";s:6:"height";s:4:"desc";s:15:"Colorbox height";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"90%";s:7:"lexicon";N;}s:10:"transition";a:6:{s:4:"name";s:10:"transition";s:4:"desc";s:19:"Colorbox transition";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:7:"elastic";s:7:"lexicon";N;}s:7:"opacity";a:6:{s:4:"name";s:7:"opacity";s:4:"desc";s:16:"Colorbox opacity";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"0.35";s:7:"lexicon";N;}s:5:"speed";a:6:{s:4:"name";s:5:"speed";s:4:"desc";s:14:"Colorbox speed";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"350";s:7:"lexicon";N;}}', ''),
(126, 'userGetWebsiteFromUrl', '', 0, 50, 0, '/**\n * Wedding site userGetWebsiteFromURL\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets the users website name from an incoming URL snippet\n * \n * Parameters :-\n * \n * None\n *  \n * Returns :-\n * \n * The website name\n */\n$pageURL = $_SERVER["SERVER_NAME"] . $_SERVER["REQUEST_URI"];\n\n/* Parse the url */\n$urlArray = parse_url($pageURL);\n$pathArray = explode(''/'', $urlArray[path]);\n  \n/* If we are not a front end user get the website from the path */\n$userType = $modx->runSnippet(''userGetURLType'');\nif ($userType != ''front'') {\n  \n    /* Logged in user */\n    if ( $userType == ''user'')\n        return $pathArray[2];\n    \n    /* Not logged in and not front end, shouldn''t happen */\n    return '''';\n}\n\n/* Return the front end users website(sub-domain) */\n$hostArray = explode(''.'', $pathArray[0]);\nreturn $hostArray[0];', 0, 'a:0:{}', ''),
(127, 'userFEGalleryPage', '', 0, 51, 0, '/**\n * Wedding site User FE Gallery Page  processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the album id if we have one */\nisset($_REQUEST[''albumId''] ) ? $albumIdPage =  $_REQUEST[''albumId'']  : $albumIdPage = 0;\n\n/* Get the website and the user details */\n$assetsURL = $modx->getOption(''assets_url'');\nif ( !isset($_REQUEST[''website''])) $website = $modx->runSnippet(''userGetWebsiteFromUrl'');\n    \n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:=''  => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif ( !$weddingUserAttributes  ) return;\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$weddingUser = $modx->getObject(''weddingUser'', $weddingUserId); \nif ( !$weddingUser ) return;\n  \n/* Construct the page */\n\n/* Album Selection */\n$albums = $weddingUser->getMany(''GalleryAlbum'');\n$output = "";\nforeach ( $albums as $album ) {\n    \n    /* Check for active, no items is not active even if marked as such */\n    $active = $album->get(''active'');\n    $albumId = $album->get(''id'');\n    $albumItems = $modx->getCollection(''albumItems'', array(''albumId'' => $albumId));\n    if ( count( $albumItems) == 0) $active = 0;\n    if ( $active == 1 ) {\n        \n        $albumName = $album->get(''albumName'');\n        $pageId = $modx->resource->get(''id'');\n        $params = array(''albumId'' => $albumId);\n        $albumLink = $modx->runSnippet(''userCreateFELink'', array(''id'' => $pageId,\n                                                           ''website'' => $website,\n                                                           ''params'' => $params));\n        $placeholderArray = array ( ''userFEgalleryAlbumLink'' => $albumLink,\n                                                    ''albumName'' => $albumName);\n        $output .= $modx->getChunk(''userFEGalleryAlbumItem'', $placeholderArray);\n    }\n    \n}\n   \n/* Check for no active albums */\n$noActiveAlbums = false;\nif ( $output == "" ) $noActiveAlbums = true;\n\n/* Set the placeholder */\n$modx->setPlaceholder(''userFEAlbumItems'',  $output);\n\n/* Item Selection */\n\n/* Check for no album id */\nif ( $albumIdPage == 0 ) {\n    \n    if ( $noActiveAlbums ) {\n        $output = $modx->getChunk(''userFEGalleryDescriptionNoAlbums'');\n    } else {\n        $output = $modx->getChunk(''userFEGalleryDescriptionNoItems'');\n    }\n    \n    $modx->setPlaceholder(''userFEGalleryAlbumDescription'', $output);\n    return;\n    \n}\n\n/* Get the gallery items and return them */\n\n/* Get the items for this album */\n$galleryURL =  $modx->runSnippet(''userGetDirectoryURL'', array(''directory'' => "gallery"));\n \n$albumItems = $modx->getCollection(''albumItems'', array(''albumId'' => $albumIdPage));\n$itemPositionArray = array();\n\n/* Sort by position */\nforeach ( $albumItems as $albumItem ) {\n\n    $itemId = $albumItem->get(''itemId'');\n    $galleryItem = $modx->getObject(''weddingUserGalleryItem'', $itemId);\n    if ( !$galleryItem ) continue;\n    $galleryItemPosition = $galleryItem->get(''itemPosition'');\n    $itemPositionArray[$galleryItemPosition] =  $galleryItem;\n\n}   \nksort($itemPositionArray);\n\n$output = "";\nforeach ($itemPositionArray as $position => $galleryItem ) {\n\n    $itemURL =  $galleryURL . DIRECTORY_SEPARATOR;\n    $itemURL .= $galleryItem->get(''itemFileName'');\n    $itemTitle = $galleryItem->get(''itemDisplayName'');\n    $itemDescription = $galleryItem->get(''itemDescription'');\n    $parameters = array(''userFEGalleryImageTitle'' =>$itemTitle,\n                                    ''userFEGalleryImage'' => $itemURL,\n                                    ''userFEGalleryImageDescription'' => $itemDescription);\n    $output  .= $modx->getChunk(''userFeGalleryItem'', $parameters);\n\n}\n\n/* Items and the album description */\n$modx->setPlaceholder(''userFEGalleryItems'', $output);\n$output = $modx->getChunk(''userFeGalleryElastislideFormat'');\n$modx->setPlaceholder(''userFEGalleryElastislideFormat'', $output);\n$albumObject = $modx->getObject(''weddingUserGalleryAlbum'', array(''id'' => $albumIdPage));\nif ( !$albumObject ) return;\n$albumDescription = $albumObject->get(''albumDescription'');\n$albumDescriptionOutput = $modx->getChunk(''userFEGalleryAlbumDescription'', \n                                                                           array(''userFEGalleryDescription'' => $albumDescription));\n$modx->setPlaceholder(''userFEGalleryAlbumDescription'', $albumDescriptionOutput);', 0, 'a:0:{}', ''),
(174, 'userCreateFELink', '', 0, 50, 0, '/**\n * Wedding site userCreateFELink\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates FE links. MUST use this to do this so all FE links\n * not in the sidebar, ie Wayfinder generated are generated relative \n * to the users FE website(subdomain).\n * \n * Parameters :- \n * \n * website - the users website name\n * id - the page id of the link\n * params - an array of parameters\n * \n * Returns \n * \n * The front end link\n *  \n */\n\n/* Make the link as normal using makeURL */\n$context = $modx->context->get(''key'');\n$link = $modx->makeURL($id, $context, $params, ''full'');\n\n/* Check the URL type, if not front return the www domain link */\n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'' ) return $link;\n\n/* Substitute the www for the users website and lose the trailing path */\n$output = str_replace(''www'', $website, $link);\n$output = str_replace("/users/$website", '''', $output);\n\nreturn $output;', 0, NULL, ''),
(129, 'userEditBlogInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit Blog processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users blog page */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditBlogError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditBlogError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$context = $modx->context->get(''key'');\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Initialise the common form part */\n$modx->runSnippet(''userEditCommonInitialise'');\n\n/* Get the users blog list */\nif ( !isset($_REQUEST[''userPage''])) {\n    \n    $modx->setPlaceholder(''userEditBlogError'', "Error - No user page id found");\n    return;\n}\n$blogPageId = $_REQUEST[''userPage''];\n$modx->setPlaceholder(''userPageId'', $blogPageId);\n\n/* Get the resource and its children */\n$blogResource = $modx->getObject(''modResource'', array(''id'' => $blogPageId));\nif ( !$blogResource ) {\n    \n    $modx->setPlaceholder(''userEditBlogError'', "Error - No blog resource found");\n    return;\n}\n\n$children = $blogResource->getMany(''Children'');\n$output = "";\nforeach ( $children as $child ) {\n    \n    $id = $child->get(''id'');\n    $args = array(''id'' => $id);\n    $childLink = $modx->makeURL($id, $context, $args, "full");\n    $title = $child->get(''pagetitle'');\n    $editedOn = $child->get(''editedon'');\n    $editedOnTime = strtotime($editedOn);\n    $edited = strftime(''%d/%m/%y'', $editedOnTime);\n    $params = array(''userEditBlogDate'' => $edited,\n                    ''userEditBlogLink'' => $childLink,\n                    ''userEditBlogTitle'' => $title,\n                    ''userEditBlogAJAXLink'' => $childLink);\n    $output .= $modx->getChunk(''userEditBlogListItem'', $params);\n    \n}\n\n/* Set the placeholder */\n$modx->setPlaceholder(''userEditBlogList'', $output);', 0, 'a:0:{}', ''),
(130, 'userEditBlogJSInclude', '', 0, 45, 0, '/**\n * Wedding site User Edit Blog processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n\n/* Common Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) return;\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n$website = ''"'' . $website . ''"'';\n\n/* Blog */\n$blog = $javascriptPath . "js/pages/user/edit/blog/blog.js";\n$modx->regClientStartupScript($blog);\n\n$context = $modx->context->get(''key'');\n$blogAJAXProcessorLink = $modx->makeURL($blogAJAXProcessorId, $context, "", "full");\n$blogAJAXProcessorLink = ''"'' . $blogAJAXProcessorLink . ''"'';\n\n/* TinyMCE */\n$wsTinyMCEPath = $modx->getOption(''ws_tinyMCE.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_tinyMCE/'');\n$jqueryTinyMCE = $wsTinyMCEPath . "js/jscripts/tiny_mce/jquery.tinymce.js";\n$modx->regClientStartupScript($jqueryTinyMCE);\n\n/* Pajinate */\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\n$modx->regClientStartupScript($pajinate);\n\n$tinyMCEURL = $wsTinyMCEPath . "js/jscripts/tiny_mce/tiny_mce.js";\n$tinyMCEURL =  ''"'' . $tinyMCEURL . ''"'';\n$ready = ''<script>\n    \n        WS.tinymceURL = '' . $tinyMCEURL . '';\n        WS.website = '' . $website . '';\n        WS.blogAJAXProcessorLink = '' . $blogAJAXProcessorLink . '';       \n       \n</script>'';\n$modx->regClientStartupScript($ready, true);\n\n$tinyMCE = $javascriptPath . "js/pages/user/edit/blog/tinymce.js";\n$modx->regClientStartupScript($tinyMCE);', 0, 'a:1:{s:19:"blogAJAXProcessorId";a:6:{s:4:"name";s:19:"blogAJAXProcessorId";s:4:"desc";s:27:"Blog AJAX Processor page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"787";s:7:"lexicon";N;}}', ''),
(131, 'userEditBlogPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Blog processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditBlogError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditBlogError'', "Error - No user found please log in");\n    return;\n}\n\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check common form processing */\n$modx->runSnippet(''userEditCommonPostProcessor'');', 0, NULL, ''),
(132, 'userEditBlogAJAXProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Blog AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user) {\n    $userId = $user->get(''id'');\n    if ($userId == "")\n        return "Error! No logged in user";\n}\n\n\n/* Get the command type and switch */\nif (!isset($_REQUEST[''command''])) {\n\n    $response[''error''] = "No command specified";\n    $output = json_encode($response);\n    return $output;\n}\n\n$command = $_REQUEST[''command''];\n$response = array();\n\nswitch ($command) {\n\n    case ''edit'':\n\n        /* Get the blog details and return them */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $blog = $modx->getObject(''modResource'', array(''id'' => $id));\n        if (!$blog) {\n\n            $response[''error''] = "No blog resource found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n\n        $title = $blog->get(''pagetitle'');\n        $content = $blog->getContent();\n        $response[''error''] = "none";\n        $response[''title''] = $title;\n        $response[''content''] = $content;\n        $response[''id''] = $id;\n        $output = json_encode($response);\n\n        break;\n\n\n    case ''save'' :\n\n        /* Get the page id, If the id is -1, this is a create operation */\n        $userBlogPageId = $_REQUEST[''userBlogPage''];\n        $create = $userBlogPageId == -1 ? true : false;\n        \n        /* Set the page content and title */\n        if ( $create ) {\n            \n            $userBlogPage = $modx->newObject(''modResource'');\n        \n        } else {\n            \n            $userBlogPage = $modx->getObject(''modResource'', $userBlogPageId);\n        }\n        if (!$userBlogPage) {\n            $modx->setPlaceholder(''userEditBlogError'', "Error - No user blog page found");\n            return "Error - No user blog page found";\n        }\n\n        /* Add the HTML Purifier path and include the class */\n        $htmlPurifierPath = $modx->getOption(''ws_htmlpurifier.core_path'', null, $modx->getOption(''core_path'') . ''components/ws_htmlpurifier/'');\n        include_once $htmlPurifierPath . "library/HTMLPurifier.auto.php";\n        $purifier = new HTMLPurifier();\n\n        $content = $_REQUEST[''userEditBlogTinyMCEcontent''];\n        $content = $purifier->purify($content);\n        $userBlogPage->set(''content'', $content);\n        \n        /* Title and edited on */\n        $title = strip_tags($_REQUEST[''userEditBlogTitle'']);\n        $userBlogPage->set(''pagetitle'', $title);\n        $userBlogPage->set(''editedon'', time());\n        \n        /* Additionals for create */\n        if ( $create ) {\n            \n            /* Get the parent data */\n            $userPageId = $_REQUEST[''userPage''];\n            $blogParent = $modx->getObject(''modResource'', $userPageId);\n            if ( !$blogParent ) return "Create - no parent found";\n            $template = $blogParent->get(''template'');\n            \n            /* Set the parent data */\n            $userBlogPage->set(''createdon'', time());\n            $userBlogPage->set(''template'', $template);\n            $userBlogPage->set(''parent'', $userPageId);\n            $userBlogPage->set(''published'', 1);\n            $userBlogPage->set(''publishedon'', time());\n            $userBlogPage->set(''publishedby'', $userId);\n            $userBlogPage->set(''createdby'', $userId);\n            $userBlogPage->set(''richtext'', 1);\n            $alias = $userBlogPage->cleanAlias($title);\n            $userBlogPage->set(''alias'', $alias);\n            $context = $modx->context->get(''key'');\n            $userBlogPage->set(''context_key'', $context);\n            \n        }\n        \n        /* Save it */\n        $userBlogPage->save();\n\n        /* Add the id just to force the alias to be unique on create */\n        if ( $create ) {\n            \n            $id = $modx->lastInsertId();\n            $alias = $userBlogPage->get(''alias'');\n            $alias .= "-$id";\n            $userBlogPage->set(''alias'', $alias);\n            $userBlogPage->save();\n        }\n\n        /* Clear the cache */\n        $cacheManager = $modx->getCacheManager();\n        $cacheManager->clearCache(array(\n            "{$resource->context_key}/",\n                ), array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n                )\n        );\n\n        break;\n        \n    case ''delete'':\n        \n        /* Get the blog resource id */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $blog = $modx->getObject(''modResource'', array(''id'' => $id));\n        if (!$blog) {\n\n            $response[''error''] = "No blog resource found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        \n        /* Delete it */\n        $blog->remove();\n        \n        break;\n\n    default:\n        \n        return "Unknown command";\n}\n\n/* Return the response */\nreturn $output;', 0, NULL, ''),
(133, 'userFEBlogJSInclude', '', 0, 52, 0, '/**\n * Wedding site User Front End Blog processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'', null, $modx->getOption(''assets_url'') . ''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'', null, $modx->getOption(''assets_url'') . ''components/ws_javascript/'');\n\n/* Blog */\n$blog = $javascriptPath . "js/pages/user/frontend/blog/blog.js";\n$modx->regClientStartupScript($blog);\n\n$context = $modx->context->get(''key'');\n$blogFEAJAXProcessorLink = $modx->makeURL($blogFEAJAXProcessorId, $context, "", "full");\n$blogFEAJAXProcessorLink = ''"'' . $blogFEAJAXProcessorLink . ''"'';\n\n/* Pajinate */\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\n$modx->regClientStartupScript($pajinate);\n\n$ready = ''<script>\n    \n        WS.blogFEAJAXProcessorLink = '' . $blogFEAJAXProcessorLink . '';       \n       \n</script>'';\n$modx->regClientStartupScript($ready, true);', 0, 'a:1:{s:21:"blogFEAJAXProcessorId";a:6:{s:4:"name";s:21:"blogFEAJAXProcessorId";s:4:"desc";s:22:"FE Blog AJAX processor";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"822";s:7:"lexicon";N;}}', ''),
(134, 'userFEBlogAJAXProcessor', '', 0, 52, 0, '/**\n * Wedding site User Front End Blog processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Allow requests from our users front end origin */\n$origin = $_SERVER[''HTTP_ORIGIN''];\n\n/* Get the link and return the title and contents */\nif (!isset($_REQUEST[''blog''])) {\n\n    $response[''error''] = "No command specified";\n    $output = json_encode($response);\n    header("Access-Control-Allow-Origin: $origin");\n    return $output;\n}\n\n$blogLink = $_REQUEST[''blog''];\n\n/* Get the alias part and the resource id */\n$blogString = strstr($blogLink, ''blog/'');\n$blogArray = explode(''/'', $blogString);\n$resourceStringArray = explode(''-'', $blogArray[1]);\n$resourceArray = explode(''.'', end($resourceStringArray));\n$blogId = $resourceArray[0]; \n\n/* Get the blog itself */\n$blog = $modx->getObject(''modResource'', $blogId);\nif ( !$blog ) {\n    \n    $response[''error''] = "No blog for id $blogId";\n    $output = json_encode($response);\n    header("Access-Control-Allow-Origin: $origin");\n    return $output;\n}\n\n/* Get and return the data */\n$response = array();\n$response[''title''] = $blog->get(''pagetitle'');\n$response[''content''] = $blog->getContent();\n$response[''error''] = "none";\n$output = json_encode($response);\nheader("Access-Control-Allow-Origin: $origin");\nreturn $output;', 0, 'a:0:{}', ''),
(136, 'userInvitesPageInitialiseForms', '', 0, 53, 0, '/**\n * Wedding site User Invites processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/*  Initialise the personal and website user setting forms\n */\n\n$modx->setPlaceholder(''userInviteWebsiteError'', "");\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ($userId == "") {\n    $modx->setPlaceholder(''userInviteError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ($weddingUser == null) {\n    $modx->setPlaceholder(''userInviteError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Set the placeholders for the invites form */\n\n/* Get the events */\n$events = $weddingUser->getMany(''Events'');\n$eventsOutput = "";\nforeach ($events as $event) {\n\n    $active = $event->get(''active'');\n    if ($active) {\n        $eventId = $event->get(''id'');\n        $eventName = $event->get(''name'');\n        $eventsOutput .= $modx->getChunk(''userInvitesEvents'', array(\n            ''eventId'' => $eventId,\n            ''eventName'' => $eventName));\n    }\n}\n$modx->setPlaceholder(''userInvitesEventsEventSelection'', $eventsOutput);\n\n/* Set the placeholders for the themes gallery */\n\n/* Get the themes */\n$themeCategory = $modx->getObject(''modCategory'', array(''category'' => $themeCategoryName));\nif (!$themeCategory)\n    return;\n$catId = $themeCategory->get(''id'');\n$themes = $modx->getCollection(''modChunk'', array(''category'' => $catId));\n\n/* Get the theme files so we can get the filetypes */\n$assetsPath = $modx->getOption(''assets_path'');\n$inviteImageDirectory = $assetsPath . $invitesImagePath;\n$fileExtensions = array();\nif ($dir = opendir($inviteImageDirectory)) {\n    while (($file = readdir($dir)) !== false) {\n\n        if (($file == ''..'') || ($file == ''.''))\n            continue;\n        $fileInfo = pathinfo($file);\n        $fileExtensions[$fileInfo[''filename'']] = $fileInfo[''extension''];\n    }\n}\n\n/* Process into gallery placeholders */\n$themeOutput = "";\n$assetsURL = $modx->getOption(''assets_url'');\nforeach ($themes as $theme) {\n\n    $themeName = $theme->get(''name'');\n    $themeImage = $assetsURL . "templates/wedding/images/invites/" . $themeName . ".$fileExtensions[$themeName]";\n    $placeholders = array(''theme-name'' => $themeName,\n        ''userInvitesThemeImage'' => $themeImage,\n        ''userInvitesThemeImageTitle'' => $themeName);\n    $themeOutput .= $modx->getChunk(''userInvitesThemeItem'', $placeholders);\n}\n\n$modx->setPlaceholder(''userInvitesThemeItems'', $themeOutput);\n\n/* Set the placeholders for the theme filters */\n$themeOutput = "";\n$colourOutput = "";\n$nameArray = array();\n$colourArray = array();\n\nforeach ($themes as $theme) {\n\n    $name = $theme->get(''name'');\n    $themeArray = explode(''-'', $name);\n    $nameArray[] = $themeArray[0];\n    $colourArray[] = $themeArray[2];\n}\n\n/* Unique the arrays */\n$nameArray = array_unique($nameArray);\n$colourArray = array_unique($colourArray);\n\nforeach ($nameArray as $name) {\n\n    $themeOutput .= $modx->getChunk(''userInvitesTheme'', array(''theme'' => $name));\n}\n\nforeach ($colourArray as $colour) {\n\n    $colourOutput .= $modx->getChunk(''userInvitesColour'', array(''colour'' => $colour));\n}\n\n$modx->setPlaceholder(''userInvitesThemes'', $themeOutput);\n$modx->setPlaceholder(''userInvitesColours'', $colourOutput);', 0, 'a:2:{s:17:"themeCategoryName";a:6:{s:4:"name";s:17:"themeCategoryName";s:4:"desc";s:46:"Name of the category holding the invite themes";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:13:"Invite Themes";s:7:"lexicon";N;}s:16:"invitesImagePath";a:6:{s:4:"name";s:16:"invitesImagePath";s:4:"desc";s:48:"Path from assets to the invites images directory";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:33:"templates/wedding/images/invites/";s:7:"lexicon";N;}}', '');
INSERT INTO `modx_site_snippets` (`id`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`) VALUES
(137, 'userInvitesAJAXProcessor', '', 0, 53, 0, '/**\n * Wedding site User Invites processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user)\n    $userId = $user->get(''id'');\nif ($userId == "") {\n\n    echo "Error! No logged in user";\n    return;\n}\n\n/* Check for a command */\nif ($_REQUEST[''command''] == "getEventDetails") {\n\n    /* Get the wedding user */\n    $weddingUser = $modx->getObject(''weddingUser'', $userId);\n    if ($weddingUser == null)\n        return;\n    $userProfile = $user->getOne(''Profile'');\n    $profile = $userProfile->toArray();\n    $weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n    $attributes = $weddingUser->attributes->toArray();\n\n    /* Get the event and return the data */\n    $eventId = $_REQUEST[''event''];\n    $event = $modx->getObject(''weddingUserEvent'', $eventId);\n    if (!$event)\n        return;\n    $eventArray = $event->toArray();\n\n    /* Guest details */\n    $eventGuests = $event->getMany(''guestEvents'');\n    $guestDetails = "";\n    foreach ($eventGuests as $guestPtr) {\n\n        $guestEventId = $guestPtr->get(''id'');\n\n        $guest = $guestPtr->getOne(''weddingUserGuest'');\n        $active = $guest->get(''active'');\n        if (!$active)\n            continue;\n\n        /* Guest details */\n        $guestName = $guest->get(''name'');\n\n        /* RSVP status */\n        $RSVPdOnline = $guestPtr->get(''RSVPdOnline'');\n        $RSVPdManual = $guestPtr->get(''RSVPdManual'');\n        $RSVPd = $RSVPdManual || $RSVPdOnline;\n        $RSVPdMethodString = "";\n\n        /* Invite status */\n        $inviteSent = $guestPtr->get(''InviteSent'');\n        $invitedOn = $guestPtr->get(''lastReminderDate'');\n        $invitedOnString = "";\n        if ($invitedOn != 0)\n            $invitedOnString = strftime(''%d/%m/%y'', $invitedOn);\n        $RSVPdOn = $guestPtr->get(''RSVPDate'');\n        $RSVPdOnString = "";\n        if ($RSVPdOn != 0)\n            $RSVPdOnString = strftime(''%d/%m/%y'', $RSVPdOn);\n        $willAttend = $guestPtr->get(''willAttend'');\n        $willAttendString = "";\n        if ($inviteSent) {\n\n            if ($RSVPd) {\n\n                $RSVPdMethodString = "Online";\n                if ($RSVPdManual == 1)\n                    $RSVPdMethodString = "Manual";\n                $willAttendString = "No";\n                if ($willAttend == 1)\n                    $willAttendString = "Yes";\n                \n            }\n            \n            $sendInviteString = "No";\n            \n        } else {\n\n            $sendInviteString = "Yes";\n        }\n\n        $placeholders = array(''userGuestId'' => $guestEventId,\n            ''userGuestName'' => $guestName,\n            ''userInviteGuestSendInvite'' => $sendInviteString,\n            ''userInvitesGuestInvitedOn'' => $invitedOnString,\n            ''userInvitesGuestAttending'' => $willAttendString,\n            ''userInvitesGuestRSVPdOn'' => $RSVPdOnString,\n            ''userInvitesGuestRSVPMethod'' => $RSVPdMethodString);\n\n        $guestDetails .= $modx->getChunk(''userInvitesGuestList'', $placeholders);\n    }\n\n    $dateString = strftime(''%Y-%m-%d'', $eventArray[''date'']);\n    $eventData = array(''partner1'' => $attributes[''partnerName1''],\n        ''date'' => $dateString,\n        ''location'' => $eventArray[''location''],\n        ''address2'' => $eventArray[''address2''],\n        ''address3'' => $eventArray[''address3''],\n        ''address4'' => $eventArray[''address4''],\n        ''start'' => $eventArray[''startTime''],\n        ''end'' => $eventArray[''endTime''],\n        ''phone'' => $profile[''phone''],\n        ''email'' => $profile[''email''],\n        ''guestDetails'' => $guestDetails);\n\n    $eventDataString = json_encode($eventData);\n    return $eventDataString;\n}\n\n/* Check for a datatables update */\nif (isset($_REQUEST[''row_id''])) {\n\n    /* Assemble the parameters from the request data */\n    $guestEventId = $_REQUEST[''row_id''];\n    $newValue = strip_tags($_REQUEST[''value'']);\n    $columnName = $_REQUEST[''column''];\n    $updateType = $_REQUEST[''type''];\n    $echoValue = $newValue;\n\n    /* Get the guest event data */\n    $guestEventObject = $modx->getObject(''guestEvents'', array(''id'' => $guestEventId));\n    if (!$guestEventObject) {\n\n        echo "Error! No guest event map object found";\n        return;\n    }\n\n    /* Process the edit update */\n    switch ($updateType) {\n\n        case ''yesnoedit'' :\n\n            /* Guest updates first */\n            if ($columnName == "Send Invite") {\n\n                $updateValue = 1;\n                if ($newValue == "Yes")\n                    $updateValue = 0;\n                $guestEventObject->set(''InviteSent'', $updateValue);\n                if (!$guestEventObject->save())\n                    $echoValue = "Error! Cant save guest";\n                break;\n            }\n\n\n            break;\n\n        case ''normaledit'' :\n\n\n            break;\n\n\n\n        default:\n\n            /* Shouldn''t happen */\n            $echoValue = "Error! Invalid update type";\n            break;\n    }\n\n    /* Return the output */\n    echo $echoValue;\n}', 0, 'a:0:{}', ''),
(138, 'userInvitesPostProcessor', '', 0, 53, 0, '/**\n * Wedding site Invites processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for an event add/edit submit operation */\nif (isset($_REQUEST[''userInvitesEventsSubmit''])) {\n\n    /* Get the MODX user */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ($userId == "") {\n        $modx->setPlaceholder(''userInviteError'', "Error - No MODX user found please log in");\n        return;\n    }\n\n    $weddingUserObject = $modx->getObject(''weddingUser'', $userId);\n    if ($weddingUserObject == null) {\n        $modx->setPlaceholder(''userInviteError'', "Error - No Wedding user found please log in");\n        return;\n    }\n\n    $userProfile = $user->getOne(''Profile'');\n    $profile = $userProfile->toArray();\n    $weddingUser->attributes = $weddingUserObject->getOne(''Attributes'');\n    $attributes = $weddingUser->attributes->toArray();\n    $context = $modx->context->get(''key'');\n\n    /* Get the event */\n    $eventId = $_REQUEST[''userInvitesEventsEventSelection''];\n    $event = $modx->getObject(''weddingUserEvent'', $eventId);\n    if (!$event) {\n        $modx->setPlaceholder(''userInviteError'', "Error - No Event found with an id of $eventId");\n        return;\n    }\n    $eventName = $event->get(''name'');\n\n    /* Get the guests that are active for the selected event and \n      ones we have to send an invite to.\n     */\n    $eventGuests = $event->getMany(''guestEvents'');\n    foreach ($eventGuests as $guestPtr) {\n\n        $guest = $guestPtr->getOne(''weddingUserGuest'');\n        $active = $guest->get(''active'');\n        $inviteSent = $guestPtr->get(''InviteSent'');\n        if ($active && !$inviteSent)\n            $guests[] = $guest;\n    }\n\n    /* Get the users rsvp page id */\n    $c = $modx->newQuery(''weddingUserPage'');\n    $c->where(array(''editType:='' => ''rsvp''));\n\n    $pages = $weddingUserObject->getMany(''Pages'', $c);\n    if (!$pages) {\n        $modx->setPlaceholder(''userInviteError'', "Error - No user page data found");\n        return;\n    }\n    $pages = array_values($pages);\n    $pageId = $pages[0]->get(''pageId'');\n\n    /* Get the request data */\n    $partner1 = $_REQUEST[''userInvitesEventsPartner1''];\n    $address1 = $_REQUEST[''userInvitesEventsAddress1''];\n    $address2 = $_REQUEST[''userInvitesEventsAddress2''];\n    $address3 = $_REQUEST[''userInvitesEventsAddress3''];\n    $address4 = $_REQUEST[''userInvitesEventsAddress4''];\n    $start = $_REQUEST[''userInvitesEventsStartTime''];\n    $end = $_REQUEST[''userInvitesEventsEndTime''];\n    $tel = $_REQUEST[''userInvitesEventsPhone''];\n    $dateString = $_REQUEST[''userInvitesEventsDatepicker''];\n    $fromMail = $_REQUEST[''userInvitesEventsEmail''];\n    $themeName = $_REQUEST[''userInvitesEventsTheme''];\n    /* If empty pick the first theme */\n    if ($themeName == "") {\n\n        $themeCategory = $modx->getObject(''modCategory'', array(''category'' => $themeCategoryName));\n        if (!$themeCategory)\n            return;\n        $catId = $themeCategory->get(''id'');\n        $themes = $modx->getCollection(''modChunk'', array(''category'' => $catId));\n        foreach ($themes as $theme) {\n            $themeName = $theme->get(''name'');\n            break;\n        }\n    }\n\n    /* Wedding/profile data */\n    $partner2 = $attributes[''partnerName2''];\n    $weddingDate = $attributes[''date''];\n    $weddingDateString = strftime(''%d/%m/%y'', $weddingDate);\n\n    /* Process each guest */\n    foreach ($guests as $guest) {\n\n        /* Guest dependant data */\n        $guestArray = $guest->toArray();\n        $guestName = $guestArray[''name''];\n        $guestEmail = $guestArray[''email''];\n\n        /* Get the guest/event map entry */\n        $mapObject = $modx->getObject(''guestEvents'', array(''guestId'' => $guestArray[''id''],\n            ''eventId'' => $eventId));\n        if (!$mapObject)\n            continue;\n\n        /* Get the guid */\n        $guid = $modx->runSnippet(''getGuestsGUID'', array(''guestId'' => $guestArray[''id'']));\n\n        /* RSVP page link */\n        $params = array(''guid'' => $guid,\n            ''event'' => $eventId);\n        $pageLink = $modx->makeURL($pageId, $context, $params, ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        \n        /* Get the themed chunk */\n        $assets_url = $modx->getOption(''assets_url'');\n        $imageURL = $assets_url . ''templates/wedding/images/invites/'' . $themeName . ''.gif'';\n        \n        /* Placeholders */\n        $placeholders = array(''guestName'' => $guestName,\n            ''userPartner1'' => $partner1,\n            ''userPartner2'' => $partner2,\n            ''eventName'' => $eventName,\n            ''userWeddingDate'' => $weddingDateString,\n            ''eventStart'' => $start,\n            ''eventEnd'' => $end,\n            ''eventDate'' => $dateString,\n            ''eventAddress1'' => $address1,\n            ''eventAddress2'' => $address2,\n            ''eventAddress3'' => $address3,\n            ''eventAddress4'' => $address4,\n            ''eventPageLink'' => $pageLink,\n            ''guestIdentifier'' => $guid,\n            ''userTel'' => $tel,\n            ''inviteImageURL'' => $imageURL,\n            ''userEmail'' => $fromMail);\n        \n        $themedChunk = $modx->getChunk($themeName, $placeholders);\n\n        /* Send the mail */\n        $mailArray = array(''to'' => $guestEmail,\n            ''from'' => $fromMail,\n            ''fromName'' => $guestName,\n            ''subject'' => ''Wedding Event Invite from Wedding Websites UK'',\n            ''message'' => $themedChunk,\n            ''html'' => true);\n\n\n        $modx->runSnippet(''mailMan'', $mailArray);\n\n        /* Update the attendance data */\n        $mapObject->set(''InviteSent'', 1);\n        $mapObject->set(''lastReminderDate'', time());\n        $mapObject->set(''willAttend'', 0);\n        $mapObject->set(''RSVPdOnline'', 0);\n        $mapObject->set(''RSVPdManual'', 0);\n        $mapObject->set(''RSVPDate'',0);\n        $mapObject->save();\n    }\n}', 0, 'a:1:{s:17:"themeCategoryName";a:6:{s:4:"name";s:17:"themeCategoryName";s:4:"desc";s:27:"Invite themes category name";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:13:"Invite Themes";s:7:"lexicon";N;}}', ''),
(139, 'userInvitesFEPage', '', 0, 55, 0, '/**\n * Wedding site User FE Invites Page  processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Get the website name from the URL */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Get the user details */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$weddingUser = $modx->getObject(''weddingUser'', $weddingUserId);\nif (!$weddingUser) return;\n$attributes = $weddingUserAttributes->toArray();\n$modxUser = $modx->getObject(''modUser'', $weddingUserId);\nif (!$modxUser) return;\n$userProfile = $modxUser->getOne(''Profile'');\n$profile = $userProfile->toArray();\n\n/* Create a link back to here using the correct domain for the forms */\n$pageId = $modx->resource->get(''id'');\n$params = array();\n$formLink = $modx->runSnippet(''userCreateFELink'', array(''id'' => $pageId,\n                                                        ''website'' => $website,\n                                                        ''params'' => $params));\n/* Get the event list for this wedding user */\n$events = $weddingUser->getMany(''Events'');\n$eventsOutput = "";\nforeach ($events as $event) {\n\n    $active = $event->get(''active'');\n    if ($active) {\n        $eventId = $event->get(''id'');\n        $eventName = $event->get(''name'');\n        $eventsOutput .= $modx->getChunk(''userInvitesEvents'', array(\n            ''eventId'' => $eventId,\n            ''eventName'' => $eventName));\n    }\n}\n\n/* First check for acceptance of an invite */\nif (isset($_REQUEST[''userInvitesFEResponseFormSubmit''])) {\n\n    /* Get the map object from the event and guest id''s */\n    $guestId = $_REQUEST[''userInvitesFEResponseGuestId''];\n    $eventId = $_REQUEST[''userInvitesFEResponseEventId''];\n    $c = $modx->newQuery(''guestEvents'');\n    $c->where(array(''guestId:='' => $guestId));\n    $c->andCondition(array(''eventId:='' => $eventId));\n    $mapObject = $modx->getObject(''guestEvents'', $c);\n    if (!$mapObject) {\n\n        $modx->setPlaceholder(''userInvitesFEResponseError'', "Failed to get map Object");\n        return;\n    }\n\n    /* Get Acceptance */\n    $accepted = $_REQUEST[''userInvitesFEResponseAcceptance''] == 1 ? true : false;\n    if ($accepted) {\n\n        $mapObject->set(''willAttend'', 1);\n    } else {\n\n        $mapObject->set(''willAttend'', 0);\n    }\n    $mapObject->set(''RSVPDate'', time());\n    $mapObject->set(''RSVPdOnline'', 1);\n    $mapObject->set(''RSVPdManual'', 0);\n\n    /* Save the update */\n    $mapObject->save();\n\n    /* Message here */\n    $messageText = $_REQUEST[''userInvitesFEResponseMessage''];\n    $messageText = strip_tags($messageText);\n    if ($messageText != "") {\n\n        $guest = $modx->getObject(''weddingUserGuest'', $guestId);\n        if (!$guest)\n            return;\n        $event = $modx->getObject(''weddingUserEvent'', $eventId);\n        if (!$event)\n            return;\n        $guestName = $guest->get(''name'');\n        $guestId = $guest->get(''id'');\n        $eventName = $event->get(''name'');\n        $subject = $modx->getChunk(''userInvitesFEMessageSubject'', array(''userInvitesFEGuestName'' => $guestName,\n            ''userInvitesFEEventName'' => $eventName));\n\n        $message = $modx->newObject(''modUserMessage'');\n        $message->set(''subject'', $subject);\n        $message->set(''message'', $messageText);\n        $message->set(''sender'', $guestId);\n        $message->set(''recipient'', $weddingUserId);\n        $message->set(''private'', true);\n        $message->set(''date_sent'', time());\n        $message->set(''read'', false);\n\n        $message->save();\n    }\n\n}\n\n/* Check for a guid from ether a link or the response form */\n$guid = "";\nif (isset($_REQUEST[''guid''])) {\n\n    $guid = $_REQUEST[''guid''];\n}\n\nif (isset($_REQUEST[''userInvitesFEGUID''])) {\n\n    $guid = $_REQUEST[''userInvitesFEGUID''];\n}\n\n/* Process the page */\nif ($guid == "") {\n\n    $output = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n        ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n        ''formLink'' => $formLink));\n    return $output;\n    \n} else {\n\n    /* Decode the guid */\n    $guidArray = explode(''-'', $guid);\n    $guidGuestId = $guidArray[1];\n\n    /* Check the guest exists */\n    $guest = $modx->getObject(''weddingUserGuest'', $guidGuestId);\n    if (!$guest) {\n        $failedOutput = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n            ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n            ''invalid'' => $noSuchGuest,\n            ''formLink'' => $formLink));\n        return $failedOutput;\n    }\n    $guestArray = $guest->toArray();\n\n    /* Check guest validity */\n    if (!$guestArray[''active'']) {\n\n        $failedOutput = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n            ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n            ''invalid'' => $guestNotActive,\n            ''formLink'' => $formLink));\n        return $failedOutput;\n    }\n\n\n    /* Check the GUID */\n    $calculatedGUID = $modx->runSnippet(''getGuestsGUID'', array(''guestId'' => $guestArray[''id'']));\n    if ($calculatedGUID != $guid) {\n        $failedOutput = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n            ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n            ''invalid'' => $incorrectGuestCode,\n            ''formLink'' => $formLink));\n        return $failedOutput;\n    }\n\n    /* Ok, guid is valid, get the event */\n    $eventId = "";\n    if (isset($_REQUEST[''event''])) {\n\n        $eventId = $_REQUEST[''event''];\n    }\n\n    if (isset($_REQUEST[''userInvitesFEEventSelection''])) {\n\n        $eventId = $_REQUEST[''userInvitesFEEventSelection''];\n    }\n\n    if ($eventId == "") {\n        $failedOutput = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n            ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n            ''invalid'' => $noEventSelected,\n            ''formLink'' => $formLink));\n        return $failedOutput;\n    }\n\n    /* Check event details */\n    $event = $modx->getObject(''weddingUserEvent'', $eventId);\n    if (!$event) {\n        $failedOutput = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n            ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n            ''invalid'' => $eventNoLongerExists,\n            ''formLink'' => $formLink));\n        return $failedOutput;\n    }\n\n    $eventArray = $event->toArray();\n    if (!$eventArray[''active'']) {\n        $failedOutput = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n            ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n            ''invalid'' => $eventNoLongerActive,\n            ''formLink'' => $formLink));\n        return $failedOutput;\n    }\n\n    /* Check if the guest has been invited */\n    $guestEvent = $modx->getObject(''guestEvents'', array(\n        ''guestId'' => $guestArray[''id''],\n        ''eventId'' => $eventArray[''id'']));\n\n    if (!$guestEvent) {\n        $failedOutput = $modx->getChunk(''userInvitesFENormal'', array(''partner1'' => $attributes[''partnerName1''],\n            ''partner2'' => $attributes[''partnerName2''], ''userInvitesFEEventSelection'' => $eventsOutput,\n            ''invalid'' => $eventNotInvited,\n            ''formLink'' => $formLink));\n        return $failedOutput;\n    }\n\n    /* Ok, all checks passed */\n\n    /* Add other placeholders to the event array */\n    $eventArray[''date''] = strftime(''%d/%m/%y'', $eventArray[''date'']);\n    $eventArray[''partner1''] = $attributes[''partnerName1''];\n    $eventArray[''partner2''] = $attributes[''partnerName2''];\n    $eventArray[''guestName''] = $guestArray[''firstName''];\n    $eventArray[''userName''] = $attributes[''name''];\n    $eventArray[''userPhone''] = $profile[''phone''];\n    $eventArray[''userEmail''] = $profile[''email''];\n    $eventArray[''userInvitesFEResponseEventId''] = $eventArray[''id''];\n    $eventArray[''userInvitesFEResponseGuestId''] = $guestArray[''id''];\n    $eventArray[''formLink''] = $formLink;\n    \n    $output = $modx->getChunk(''userInvitesFEResponse'', $eventArray);\n    \n    /* Correctly form any links in the output */\n    \n    return $output;\n}', 0, 'a:7:{s:11:"noSuchGuest";a:6:{s:4:"name";s:11:"noSuchGuest";s:4:"desc";s:25:"The guest does not exist.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:70:"Sorry, this guest cannot be found in the guests list for this wedding.";s:7:"lexicon";N;}s:14:"guestNotActive";a:6:{s:4:"name";s:14:"guestNotActive";s:4:"desc";s:66:"The guest exists but is no longer active in the wedding guest list";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:70:"Sorry, you are not an active guest in the guest list for this wedding.";s:7:"lexicon";N;}s:18:"incorrectGuestCode";a:6:{s:4:"name";s:18:"incorrectGuestCode";s:4:"desc";s:48:"The guest code(GUID) for the guest is incorrect.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:97:"Sorry, the guest code entered does not identify a valid guest in the guest list for this wedding.";s:7:"lexicon";N;}s:15:"noEventSelected";a:6:{s:4:"name";s:15:"noEventSelected";s:4:"desc";s:42:"No event has been selected for this invite";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:57:"Sorry, a valid event has not been selected for this RSVP.";s:7:"lexicon";N;}s:19:"eventNoLongerExists";a:6:{s:4:"name";s:19:"eventNoLongerExists";s:4:"desc";s:50:"The selected event for the invite no longer exists";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:57:"Sorry, the event selected for this RSVP no longer exists.";s:7:"lexicon";N;}s:19:"eventNoLongerActive";a:6:{s:4:"name";s:19:"eventNoLongerActive";s:4:"desc";s:55:"The selected event is no longer active for this wedding";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:63:"Sorry, the selected event is no longer active for this wedding.";s:7:"lexicon";N;}s:15:"eventNotInvited";a:6:{s:4:"name";s:15:"eventNotInvited";s:4:"desc";s:46:"The guest is not invited to the selected event";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:93:"Sorry, the guest entered is not invited to this event at this wedding, please select another.";s:7:"lexicon";N;}}', ''),
(140, 'userEditRSVPInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit RSVP processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users RSVP page */\n \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditRSVPError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditRSVPError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$context = $modx->context->get(''key'');\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Initialise the common form part */\n$modx->runSnippet(''userEditCommonInitialise'');\n\n/* Get the users message list */\n$c = $modx->newQuery(''modUserMessage'');\n$c->where(array(\n    ''recipient'' => $modx->user->get(''id'')\n));\n$messages = $modx->getCollection(''modUserMessage'',$c);\n\n$output = "";\n$pageId = $modx->resource->get(''id'');\n\nforeach ( $messages as $message ) {\n    $messageArray = $message->toArray();\n    $id = $messageArray[''id''];\n    $args = array(''id'' => $id);\n    $messageLink = $modx->makeURL($pageId, $context, $args, "full");\n    $subject = $messageArray[''subject''];\n    $boldClass = "";\n    if ( !$messageArray[''read''] ) $boldClass = ''user-message-unread'';\n    $date = $messageArray[''date_sent''];\n    $dateTime = strtotime($date);\n    $dateString = strftime(''%d/%m/%y'', $dateTime);\n    $params = array(''userEditRSVPMessageDate'' => $dateString,\n                    ''userEditRSVPMessageLink'' => $messageLink,\n                    ''userEditRSVPMessageTitle'' => $subject,\n                    ''userEditRSVPMessageSubject'' => $subject,\n                    ''userEditRSVPMessageAJAXLink'' => $messageLink,\n                    ''userEditRSVPBoldClass'' => $boldClass);\n    $output .= $modx->getChunk(''userEditRSVPMessageListItem'', $params);\n    \n}\n\n/* Set the placeholder */\n$modx->setPlaceholder(''userEditRSVPMessageList'', $output);', 0, 'a:0:{}', ''),
(141, 'getGuestsGUID', '', 0, 61, 0, '/**\n * Wedding site getGuestsGUID\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a guests unique guid\n * \n * Parameters :- \n * \n * $guestId - The id of the guest\n *  \n */\n$guest = $modx->getObject(''weddingUserGuest'', $guestId);\nif (!$guest)\n    return "No guest";\n$guestArray = $guest->toArray();\n$guestName = $guestArray[''name''];\n$guestEmail = $guestArray[''email''];\n$checksum = 0;\n$length = strlen($guestEmail);\nfor ($count = 0; $count <= $length; $count++) {\n\n    $checksum += ord($guestEmail[$count]);\n}\n\n$checksum %= 256;\n$guestNameArray = explode('' '', $guestName);\n$guidArray = array();\n$guidArray[] = $guestNameArray[0];\n$guidArray[] = $guestArray[''id''];\n$guidArray[] = $checksum;\n$guid = implode(''-'', $guidArray);\n\nreturn $guid;', 0, NULL, ''),
(142, 'userEditRSVPJSInclude', '', 0, 45, 0, '/** \n * Wedding site User Edit RSVP processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n\n/* Common Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) return;\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n$website = ''"'' . $website . ''"'';\n\n/* RSVP */\n$rsvp = $javascriptPath . "js/pages/user/edit/rsvp/rsvp.js";\n$modx->regClientStartupScript($rsvp);\n\n/* Pajinate */\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\n$modx->regClientStartupScript($pajinate);\n\n$context = $modx->context->get(''key'');\n$RSVPAJAXProcessorLink = $modx->makeURL($RSVPAJAXProcessorId, $context, "", "full");\n$RSVPAJAXProcessorLink = ''"'' . $RSVPAJAXProcessorLink . ''"'';\n\n$ready = ''<script>\n    \n        WS.rsvpAJAXProcessorLink = '' . $RSVPAJAXProcessorLink . '';       \n       \n</script>'';\n$modx->regClientStartupScript($ready, true);', 0, 'a:1:{s:19:"RSVPAJAXProcessorId";a:6:{s:4:"name";s:19:"RSVPAJAXProcessorId";s:4:"desc";s:37:"User EDIT RSVP AJAX processor page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"1038";s:7:"lexicon";N;}}', ''),
(143, 'userEditRSVPAJAXProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit RSVP AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user) {\n    $userId = $user->get(''id'');\n    if ($userId == "")\n        return "Error! No logged in user";\n}\n\n\n/* Get the command type and switch */\nif (!isset($_REQUEST[''command''])) {\n\n    $response[''error''] = "No command specified";\n    $output = json_encode($response);\n    return $output;\n}\n\n$command = $_REQUEST[''command''];\n$response = array();\n\nswitch ($command) {\n\n    case ''read'':\n        \n        /* Get the message details and return them */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $message = $modx->getObject(''modUserMessage'', array(''id'' => $id));\n        if (!$message) {\n\n            $response[''error''] = "No message found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        $guestId = $message->get(''sender'');\n        $guest = $modx->getObject(''weddingUserGuest'', $guestId);\n        $guestName = "Unknown";\n        if ( $guest ) $guestName = $guest->get(''name'');\n        $title = $message->get(''subject'');\n        $content = $message->get(''message'');\n        $response[''error''] = "none";\n        $response[''title''] = $title;\n        $response[''content''] = $content;\n        $response[''id''] = $id;\n        $response[''guestName''] = $guestName;\n        \n        /* Mark the message as read */\n        $message->set(''read'', 1);\n        $message->save();\n        $output = json_encode($response);\n\n        break;\n        \n   case ''delete'':\n        \n        /* Get the blog resource id */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $message = $modx->getObject(''modUserMessage'', array(''id'' => $id));\n        if (!$message) {\n\n            $response[''error''] = "No message found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        \n        /* Delete it */\n        $message->remove();\n        \n        break;\n\n    default:\n        \n        return "Unknown command";\n}\n\n/* Return the response */\nreturn $output;', 0, NULL, ''),
(144, 'userEditRSVPPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit RSVP processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ($userId == "") {\n    $modx->setPlaceholder(''userEditRSVPError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ($weddingUser == null) {\n    $modx->setPlaceholder(''userEditRSVPError'', "Error - No user found please log in");\n    return;\n}\n\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check common form processing */\n$modx->runSnippet(''userEditCommonPostProcessor'');', 0, NULL, ''),
(145, 'getUserRSVPMessageLink', '', 0, 61, 0, '/**\n * Wedding site getRSVPMessageLink\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a users RSVP page link and message count\n * \n * Parameters :- \n * \n *  \n */\n\n/* Get the user details */\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return "Error no authenticated user";\n\n$user = $modx->user;\n$userId = $user->get(''id'');\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n\n/* Get the users messages */\n$c = $modx->newQuery(''modUserMessage'');\n$c->where(array(\n    ''recipient'' => $modx->user->get(''id'')\n));\n$c->andCondition(array(\n    ''read'' => 0\n));\n$messages = $modx->getCollection(''modUserMessage'', $c);\n$count = count($messages);\n\n/* Get the users rsvp page id */\n$c = $modx->newQuery(''weddingUserPage'');\n$c->where(array(''editType:='' => ''rsvp''));\n\n$pages = $weddingUser->getMany(''Pages'', $c);\nif (!$pages) {\n    $modx->setPlaceholder(''userInviteError'', "Error - No user page data found");\n    return;\n}\n$pages = array_values($pages);\n$pageId = $pages[0]->get(''pageId'');\n$params = array(''userPage'' => $pageId);\n\n/* Create the link to the RSVP edit page */\n$pageLink = $modx->makeURL($RSVPEditPageId, $context, $params, ''full'');\n$pageLink = htmlspecialchars_decode($pageLink);\n\n/* Return the output */\n$output = $modx->getChunk(''userRSVPMessageCount'', array(''userRSVPMessageLink'' => $pageLink,\n                                                        ''userRSVPMessageCount'' => $count));\nreturn $output;', 0, 'a:1:{s:14:"RSVPEditPageId";a:6:{s:4:"name";s:14:"RSVPEditPageId";s:4:"desc";s:24:"Id of the RSVP edit page";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:3:"513";s:7:"lexicon";N;}}', ''),
(146, 'getUserManagementPage', '', 0, 61, 0, '/**\n * Wedding site getUserManagementPage\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a users management page link\n * \n * Parameters :- \n * \n * $page - one of guest, event, invite, home, setting\n *  \n */\n\n/* Get the user details */\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return "Error no authenticated user";\n\nswitch ( $page ) {\n    \n    case ''event'' :\n        \n        $pageLink = $modx->makeURL($eventPageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    case ''guest'' :\n        \n        $pageLink = $modx->makeURL($guestPageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    case ''invite'' :\n        \n        $pageLink = $modx->makeURL($invitePageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    case ''setting'' :\n        \n        $pageLink = $modx->makeURL($settingPageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n    \n    default :\n    case ''home'' :\n    \n        $pageLink = $modx->makeURL($homePageId, $context, "", ''full'');\n        $pageLink = htmlspecialchars_decode($pageLink);\n        break;\n}\n\nreturn $pageId;', 0, 'a:5:{s:11:"eventPageId";a:6:{s:4:"name";s:11:"eventPageId";s:4:"desc";s:18:"User Event page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"48";s:7:"lexicon";N;}s:11:"guestPageId";a:6:{s:4:"name";s:11:"guestPageId";s:4:"desc";s:18:"User guest page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"32";s:7:"lexicon";N;}s:12:"invitePageId";a:6:{s:4:"name";s:12:"invitePageId";s:4:"desc";s:20:"User Iinvite page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"49";s:7:"lexicon";N;}s:13:"settingPageId";a:6:{s:4:"name";s:13:"settingPageId";s:4:"desc";s:21:"User Settings page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"30";s:7:"lexicon";N;}s:10:"homePageId";a:6:{s:4:"name";s:10:"homePageId";s:4:"desc";s:17:"User home page id";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"27";s:7:"lexicon";N;}}', ''),
(168, 'userCreateWebsite', '', 0, 62, 0, '/**\n * Wedding site userCreateWebsite\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates a users website\n * \n * Parameters :-\n *\n * website - the website name\n * \n * \n * Returns :-\n * \n * None\n *\n * \n */\n/* Sanity check the website */\nif ($website == "") {\n$modx->log(modX::LOG_LEVEL_ERROR, "Create User - No website set");\n    return;\n}\n$modx->log(modX::LOG_LEVEL_ERROR, "Create User - Creating website $website");\n\n/* Create the users pages */\n$modx->runSnippet(''createUserPageSet'', array(''name'' => $website));\n\n/* Create the sub domain */\n$modx->runSnippet(''cPanelAddSubDomain'', array(''subDomainName'' => $website));\n\n/* Create the user directories */\n$modx->runSnippet(''createUserDirectories'');', 0, NULL, ''),
(148, 'locator', 'A Simple IP address to country location snippet', 0, 57, 0, '/**\r\n * Locator - A Simple IP address to country location snippet\r\n *\r\n * @author S. Hamblett <steve.hamblett@linux.com> 2012\r\n *\r\n * @package  locator\r\n * \r\n * Parameters :-\r\n * \r\n * ipAddress            - IP address in standard xxx.xxx.xxx.xxx format, if the string\r\n *                               ''remote'' is specified then the incoming IP address as returned by\r\n *                               $_SERVER["REMOTE_ADDR"] is used.\r\n * tpl                        - Output template to use, if not set only placeholders are set\r\n * toJSON               - Return the dataset as a JSON string defaults to 0\r\n\r\n * \r\n * Placeholders, all prefixed with locator :-\r\n * \r\n * ipAddress                     - IP address supplied\r\n * countryCode2               - 2 letter country code based on ISO 3166\r\n * countryCode3               - 3 letter country code based on ISO 3166\r\n * countryName                - Country name based on ISO 3166\r\n * modxCountryCode        - Country code from the MODX countries file(en)\r\n * error                              - Set to ''None'' if no error or an error code string.\r\n * \r\n * Examples : \r\n * \r\n *           Place holders only - [[!locator? &ipAddress=`82.28.123.128` ]] \r\n *           Plus template and incoming IP address  - [[!locator? &ipAddress=`remote` &tpl=`locatorChunk` ]]\r\n *           To JSON - [[!locator? &ipAddress=`82.28.123.128` &toJSON=`1` ]]\r\n */\r\n\r\n/* Add the locator package, we need to do this because of our table prefix */\r\n$modelPath = $modx->getOption(''locator.core_path'', null, $modx->getOption(''core_path'') . ''components/locator/'') . ''model/'';\r\n$modx->addPackage(''locator'', $modelPath, ''lo_'');\r\n\r\n/* Initialise the parameter set */\r\n$ipAddress = $ipAddress == ''remote'' ? $_SERVER["REMOTE_ADDR"] : $ipAddress;\r\n$tpl = !empty($tpl) ? $tpl : '''';\r\n$toJSON = $toJSON == 1 ? true : false;\r\n\r\n/* Convert the IP address */\r\n$ipNumber = ip2long($ipAddress);\r\nif ($ipNumber === false) {\r\n\r\n    $modx->toPlaceholder(''error'', ''IP Address Conversion Error'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''error'' => ''IP Address Conversion Error''));\r\n        return $output;\r\n    }\r\n    return;\r\n}\r\n\r\n/* Do the look up */\r\n$c = $modx->newQuery(''locatorCountry'');\r\n$c->where(array(''ipFrom:<='' => $ipNumber));\r\n$c->andCondition(array(''ipTo:>='' => $ipNumber));\r\n$countryObject = $modx->getObject(''locatorCountry'', $c);\r\nif (!$countryObject) {\r\n\r\n    $modx->toPlaceholder(''error'', ''No Country Found'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''locator.error'' => ''No Country Found''));\r\n        return $output;\r\n    }\r\n    return;\r\n}\r\n\r\n$countryInfo = $countryObject->toArray();\r\n$countryInfo[''ipAddress''] = $ipAddress;\r\n\r\n/* Get the MODX  country number, use ''en'' for ISO 3166 */\r\ninclude $modx->getOption(''core_path'') . "lexicon/country/en.inc.php";\r\nif (!isset($_country_lang)) {\r\n\r\n    $modx->toPlaceholder(''error'', ''No MODX Country File'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''locator.error'' => ''No MODX Country File''));\r\n        return $output;\r\n    }\r\n    return;\r\n}\r\nforeach ($_country_lang as $countryKey => $countryName) {\r\n\r\n    if (trim(strtolower($countryName)) == trim(strtolower($countryInfo[''countryName''])))\r\n        $countryInfo[''modxCountryCode''] = $countryKey;\r\n}\r\n\r\nif ( !isset( $countryInfo[''modxCountryCode''] )) {\r\n    \r\n     $modx->toPlaceholder(''error'', ''No MODX Country Code'', ''locator'');\r\n    if ($tpl != '''') {\r\n        $output = $modx->getChunk($tpl, array(''locator.error'' => ''No MODX Country Code''));\r\n        return $output;\r\n    }\r\n    return;\r\n    \r\n}\r\n/* Return the country information */\r\n$countryInfo[''error''] = ''None'';\r\n$modx->toPlaceholders($countryInfo, ''locator'');\r\n\r\nif ($toJSON) {\r\n\r\n    $countryString = json_encode($countryInfo);\r\n    return $countryString;\r\n}\r\n\r\nif ($tpl != '''') {\r\n    $output = $modx->getChunk($tpl, $countryInfo);\r\n    return $output;\r\n}', 0, NULL, ''),
(149, 'userEditGuestbookInitialise', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/*  Initialise the users guestbook page */\n        \n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditGuestbookError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditGuestError'', "Error - No user found please log in");\n    return;\n}\n\n$userProfile = $user->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$context = $modx->context->get(''key'');\n$userArray = $user->toArray();\n\n/* Website links */\n$modx->runSnippet(''userAdminWebPageLinks'', array(''attributes'' => $attributes));\n\n/* Support */\n$modx->runSnippet(''userAdminSupportLinks'', array(''attributes'' => $attributes));\n\n/* Initialise the common form part */\n$modx->runSnippet(''userEditCommonInitialise'');\n\n/* Include Quip */\n$corePath = $modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'');\n$modx->addPackage(''quip'',$corePath . ''model/'');\n\n/* Get the users comment list */\n$c = $modx->newQuery(''quipComment'');\n$c->where(array(''quipComment.thread'' => $userArray[''username'']));\n$c->sortBy(''createdon'', ''DESC'');\n$comments = $modx->getCollection(''quipComment'',$c);\n\n$output = "";\n$pageId = $modx->resource->get(''id'');\n\nforeach ( $comments as $comment ) {\n    \n    $commentArray = $comment->toArray();\n    $id = $commentArray[''id''];\n    $args = array(''id'' => $id);\n    $commentLink = $modx->makeURL($pageId, $context, $args, "full");\n    $name = $commentArray[''name''];\n    $date = $commentArray[''createdon''];\n    $dateTime = strtotime($date);\n    $dateString = strftime(''%d/%m/%y'', $dateTime);\n     if ( $attributes[''moderateGuestbook''] == 1 ) {\n        \n        /* Moderate output required */\n        $approved = $commentArray[''approved''];\n        $onOff = "on";\n        if ( $approved == 0 ) $onOff = ''off'';\n        $params = array(''userEditGuestbookMessageDate'' => $dateString,\n                        ''userEditGuestbookMessageLink'' => $commentLink,\n                        ''userEditGuestbookMessageName'' => $name,\n                        ''userEditGuestbookMessageAJAXLink'' => $commentLink,\n                       ''userEditGuestbookModerate'' => $onOff);\n        $output .= $modx->getChunk(''userEditGuestbookListItemModerate'', $params);\n        \n    } else {\n        \n    \n        $params = array(''userEditGuestbookMessageDate'' => $dateString,\n                        ''userEditGuestbookMessageLink'' => $commentLink,\n                        ''userEditGuestbookMessageName'' => $name,\n                        ''userEditGuestbookMessageAJAXLink'' => $commentLink);\n        $output .= $modx->getChunk(''userEditGuestbookListItem'', $params);\n    \n    }\n    \n    \n}\n\n/* Set the placeholder */\n$modx->setPlaceholder(''userEditGuestbookMessageList'', $output);', 0, 'a:0:{}', ''),
(150, 'userEditGuestbookJSInclude', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n *\n */\n\n/* Paths */\n$assets_url = $modx->getOption(''assets_url'');\n$wsJQueryPath = $modx->getOption(''ws_jQuery.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_jQuery/'');\n$javascriptPath = $modx->getOption(''ws_javascript.assets_url'',null,$modx->getOption(''assets_url'').''components/ws_javascript/'');\n\n/* Common Scripts */\n$websiteLinks = $modx->runSnippet(''userAdminWebPageLinksCommonJS'');\n$modx->regClientStartupScript($websiteLinks, true);\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) return;\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) return;\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n$website = ''"'' . $website . ''"'';\n\n/* Guestbook */\n$guestbook = $javascriptPath . "js/pages/user/edit/guestbook/guestbook.js";\n$modx->regClientStartupScript($guestbook);\n\n/* Pajinate */\n$pajinate = $wsJQueryPath . "js/pajinate/jquery.pajinate.min.js";\n$modx->regClientStartupScript($pajinate);\n\n$context = $modx->context->get(''key'');\n$guestbookAJAXProcessorLink = $modx->makeURL($guestbookAJAXProcessorId, $context, "", "full");\n$guestbookAJAXProcessorLink = ''"'' . $guestbookAJAXProcessorLink . ''"'';\n\n$ready = ''<script>\n    \n        WS.guestbookAJAXProcessorLink = '' . $guestbookAJAXProcessorLink . '';       \n       \n</script>'';\n$modx->regClientStartupScript($ready, true);', 0, 'a:1:{s:24:"guestbookAJAXProcessorId";a:6:{s:4:"name";s:24:"guestbookAJAXProcessorId";s:4:"desc";s:40:"AJAX page processor id for the guestbook";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"1080";s:7:"lexicon";N;}}', ''),
(151, 'userEditGuestbookAJAXProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook AJAX processing\n *\n * Copyright 2011 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/* Check for a logged in user */\n$user = $modx->user;\n$userId = "";\nif ($user) {\n    $userId = $user->get(''id'');\n    if ($userId == "")\n        return "Error! No logged in user";\n}\n\n\n/* Get the command type and switch */\nif (!isset($_REQUEST[''command''])) {\n\n    $response[''error''] = "No command specified";\n    $output = json_encode($response);\n    return $output;\n}\n\n$command = $_REQUEST[''command''];\n$response = array();\n\n/* Include Quip */\n$corePath = $modx->getOption(''quip.core_path'',null,$modx->getOption(''core_path'').''components/quip/'');\n$modx->addPackage(''quip'',$corePath . ''model/'');\n\nswitch ($command) {\n\n    case ''read'':\n        \n        /* Get the comment details and return them */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $comment = $modx->getObject(''quipComment'', array(''id'' => $id));\n        if (!$comment) {\n\n            $response[''error''] = "No comment found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        $commentArray = $comment->toArray();\n        $response[''name''] = $commentArray[''name''];\n        $response[''email''] = $commentArray[''email''];\n        $response[''body''] = $commentArray[''body''];\n        $response[''error''] = "none";\n        $output = json_encode($response);\n\n        break;\n        \n    case ''delete'':\n        \n        /* Get the comment resource id */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $comment = $modx->getObject(''quipComment'', array(''id'' => $id));\n        if (!$comment) {\n\n            $response[''error''] = "No comment found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n        \n        /* Delete it */\n        $comment->remove();\n        \n        break;\n     case ''moderate'':\n\n        /* Get the comment resource id */\n        $error = "none";\n        $id = $_REQUEST[''id''];\n        $comment = $modx->getObject(''quipComment'', array(''id'' => $id));\n        if (!$comment) {\n\n            $response[''error''] = "No comment found with id $id";\n            $output = json_encode($response);\n            return $output;\n        }\n\n        /* Moderate it */\n        $approved = $comment->get(''approved'');\n        $newApproved = 0;\n        if ($approved == 0)\n            $newApproved = 1;\n        $comment->set(''approved'', $newApproved);\n        if ($newApproved == 1) {\n\n            $comment->set(''approvedby'', $userId);\n            $approvedOn = strftime(''%Y-%m-%d %T'');\n            $comment->set(''approvedon'', $approvedOn);\n        } else {\n\n            $comment->set(''approvedby'', 0);\n            $comment->set(''approvedon'', ''0000-00-00 00:00:00'');\n        }\n\n        /* Save the comment */\n        $comment->save();\n\n        break;\n    \n    default:\n        \n        return "Unknown command";\n}\n\n/* Return the response */\nreturn $output;', 0, 'a:0:{}', '');
INSERT INTO `modx_site_snippets` (`id`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`) VALUES
(152, 'userEditGuestbookPostProcessor', '', 0, 45, 0, '/**\n * Wedding site User Edit Guestbook processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the current logged in user details */\n$user = $modx->user;\n$userId = $user->get(''id'');\nif ( $userId == "" ) {\n    $modx->setPlaceholder(''userEditGuestbookError'', "Error - No user found please log in");\n    return;\n}\n$weddingUser = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUser == null ) {\n    $modx->setPlaceholder(''userEditGuestbookError'', "Error - No user found please log in");\n    return;\n}\n\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n\n/* Check common form processing */\n$modx->runSnippet(''userEditCommonPostProcessor'');', 0, NULL, ''),
(161, 'userGenerateFEWeblinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEWeblinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for user domains\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n\n/* Get the parent container */\n$parent = $modx->resource->get(''parent'');\n\n/* Call Wayfinder as we would normally but with a ''full'' url*/\n$output = $modx->runSnippet(''Wayfinder'', array(''startId'' => $parent,\n                                               ''outerTpl'' => ''wed1-Outer'',\n                                               ''rowTpl'' => ''wed1-Row'',\n                                               ''level'' => 1,\n                                               ''hereClass'' => ''active'',\n                                               ''textOfLinks'' => ''pagetitle'',\n                                               ''scheme'' => ''full''));\n        \n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */        \n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'') return $output;\n\n/* Get the website name */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Replace the Wayfinder generated ''www'' with the website domain */\n$output = str_replace(''www'', $website, $output);\n\n/* Remove the ''users'' path */\n$output = str_replace("/users/$website", '''', $output);\n\nreturn $output;', 0, NULL, ''),
(160, 'userPreviewNavigationLink', '', 0, 62, 0, '/**\n * Wedding site userPreviewNavigationLink\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Adds a back to Admin link if a logged in user is previewing\n * \n *  \n */\n\n/* If logged in add the admin link */\n$context = $modx->context->get(''key'');\nif ( !$modx->user->isAuthenticated($context) ) return;\n$output = $modx->getChunk(''userPreviewNavigation'');\nreturn $output;', 0, NULL, ''),
(162, 'userGetURLType', '', 0, 62, 0, '/**\n * Wedding site userGetURLType\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets the users type, this can be one of :-\n * \n * front - an access to the front end pages of a users web site\n * back  - an access to the www section of the site visible to non logged in users\n * user  - an access to the www section of the site whilst logged in\n * \n * Parameters :-\n * \n * None\n *  \n * Returns :-\n * \n * User type, either front, back or user\n */\n\n$pageURL = $_SERVER["SERVER_NAME"] . $_SERVER["REQUEST_URI"];\n\n/* Parse the url */\n$urlArray = parse_url($pageURL);\n$pathArray = explode(''/'', $urlArray[path] );\n\n\n/* Check for the admin(www) domain */\n$hostArray = explode(''.'', $pathArray[0]);\nif ( $hostArray[0] == ''www'') {\n    \n    $context = $modx->context->get(''key'');\n    if ( $modx->user->isAuthenticated($context) ) {\n        \n        /* Logged in user */\n        return ''user'';\n    \n    }\n    \n    /* Must be back end */\n    return ''back'';\n    \n}\n\n/* Must be a front end user */\nreturn ''front'';', 0, NULL, ''),
(163, 'userRedirectToHome', '', 0, 62, 0, '/**\n * Wedding site userRedirectToHOme\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Redirects refs to a user container to the home page depending\n * on the domain we have come from\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - None, issues a redirect\n * \n */\n/* Are we front or back end */\n$userType = $modx->runSnippet(''userGetURLType'');\nif ($userType != ''front'') {\n\n    /* Use first child redirect */\n    $modx->runSnippet(''FirstChildRedirect'');\n    \n} else {\n\n    /* Redirect using the users domain */\n    $website = $modx->runSnippet(''userGetWebsiteFromURL'');\n    $pageURL = $_SERVER["SERVER_NAME"];\n    $redirectURL = ''http://'' . $pageURL . ''/home.html'';\n    $modx->sendRedirect($redirectURL);\n}', 0, 'a:0:{}', ''),
(176, 'userGenerateFEGuestbooklinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEGuestbooklinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for the users guestbook\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n\n/* Get the users username */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Get the user details */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$modxUser = $modx->getObject(''modUser'', $weddingUserId);\nif (!$modxUser) return;\n$userProfile = $modxUser->getOne(''Profile'');\n$profile = $userProfile->toArray();\n$userArray = $modxUser->toArray();\n\n/* Run quip */\n$output = $modx->runSnippet(''Quip'',  array(''thread'' =>  $userArray[''username''],\n                                                                     ''tplComment'' => ''userGuestbookFEComment'',\n                                                                     ''tplComments'' => ''userGuestbookFEComments'',\n                                                                     ''limit'' => 7,\n                                                                     ''sortDir'' => ''DESC''));  \n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */        \n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'') return $output;\n\n/* Generate the FE URL link */\n$alias = $modx->resource->get(''alias'');  \n$domain = $modx->getOption(''domain'');  \n$protocol = $modx->getOption(''server_protocol'');\n$tld = $modx->getOption(''tld'');\n$contentType = $modx->resource->get(''contentType'');\n$contentTypeArray = explode(''/'', $contentType);\n$link = $protocol . ''://'' . $website . ''.'' . $domain . ''.'' . $tld . ''/'' . $alias . ''.'' . $contentTypeArray[1];  \n$search = "users/$website/sign-guestbook.html";\n$output = str_replace($search, $link, $output);\n  \nreturn $output;', 0, 'a:0:{}', ''),
(164, 'userFEDisplayCountdown', '', 0, 50, 0, '/**\n * Wedding site userFEDisplayCountdown\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Displays the countdown if selected by the user\n * \n * Parameters :-\n * \n * none\n *  \n * Returns :-\n * \n * Countdown html\n */\n/* Get the user details */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$displayCountdown = $weddingUserAttributes->get(''displayCountdown'');\nif ( $displayCountdown == 1 ) {\n    \n    $output = $modx->getChunk(''userCountdown'');\n    return $output;\n}', 0, 'a:0:{}', ''),
(165, 'userFEGenerateSocialLinks', '', 0, 50, 0, '/**\n * Wedding site userFEGenerateSocialLinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates a users social links\n * \n * Parameters :-\n * \n * none\n *  \n * Returns :-\n * \n * Social link html\n */\n\n/* Get the user details */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$attributes = $weddingUserAttributes->toArray();\n\n$output = "";\n\n/* Generate each link if not empty */\nif ( $attributes[''socialGoogle''] != "" ) {\n    \n    $socialLink = "https://" . $attributes[''socialGoogle''];\n    $socialClass = "google";\n    $socialTitle = "Google";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialFacebook''] != "" ) {\n    \n    $socialLink = "http://" . $attributes[''socialFacebook''];\n    $socialClass = "face";\n    $socialTitle = "Facebook";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialTwitter''] != "" ) {\n    \n    $socialLink = "http://" . $attributes[''socialTwitter''];\n    $socialClass = "twit";\n    $socialTitle = "Twitter";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialOther1''] != "" ) {\n    \n    $socialLink = "http://" . $attributes[''socialOther1''];\n    $socialClass = "other1";\n    $socialTitle = "Other1";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\nif ( $attributes[''socialOther2''] != "" ) {\n    \n    $socialLink = "http://" . $attributes[''socialOther2''];\n    $socialClass = "other2";\n    $socialTitle = "Other2";\n    $output .= $modx->getChunk(''userFESocialLink'', array(''socialLink'' => $socialLink,\n                                                         ''socialClass'' => $socialClass,\n                                                         ''socialTitle'' => $socialTitle));\n}\n\n/* Return the links */\nreturn $output;', 0, NULL, ''),
(166, 'registrationFormInitialise', '', 0, 20, 0, '/**\n * Wedding site Registration processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n\n/* Get the recaptcah library */\n$recaptchaPath = $modx->getOption(''ws_recaptcha.core_path'',null,$modx->getOption(''core_path'').''components/ws_recaptcha/'');\ninclude_once $recaptchaPath . "recaptchalib.php";\n\n/* Set the recaptcha placeholder */\n$output = recaptcha_get_html($publickey, $_SESSION[''recaptcha_error'']);\n$modx->setPlaceholder(''registration.recaptcha'', $output);\n\n/* Country processing */\n$country = strlen($profile[''country'']) != 0  ? $profile[''country''] : ''None'';\ninclude_once $modx->getOption(''core_path'').''lexicon/country/en.inc.php'';\n$countriesOutput = "";\n\nif ( $country == "None" ) {\n    \n    /* Nothing set, autodetect */\n    $modxCountryKey =  array_search(''United Kingdom'', $_country_lang );\n    $countryInfoString = $modx->runSnippet(''locator'', array(''ipAddress''=> ''remote'', ''toJSON'' => 1));\n    $countryInfo = json_decode( $countryInfoString, true);\n    if ( $countryInfo[''error''] == ''None'') {\n        \n        $modxCountryKey = $countryInfo[''modxCountryCode''];\n      \n        \n    }\n    \n} else {\n    \n    $modxCountryKey = array_search($country, $_country_lang );\n}\n\n\n\nforeach ( $_country_lang as $countryKey => $countryName ) {\n    \n    $countrySelected = "";\n    if (   $modxCountryKey  == $countryKey ) $countrySelected = '' selected="yes" '';\n    $countriesOutput .= $modx->getChunk(''userSettingsCountry'', array( \n                                          ''countrySelected'' => $countrySelected,\n                                          ''countryValue'' => $countryKey,\n                                          ''countryName'' => $countryName));\n}\n$modx->setPlaceholder(''userSettingsCountries'', $countriesOutput);', 0, 'a:1:{s:9:"publickey";a:6:{s:4:"name";s:9:"publickey";s:4:"desc";s:24:"Public key for recaptcha";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:40:"6LcMd88SAAAAACah58iR7rlrFwOtNqywmrcZsQbE";s:7:"lexicon";N;}}', ''),
(167, 'userRenameWebsite', '', 0, 62, 0, '/**\n * Wedding site userRenameWebsite\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Renames s a users website\n * \n * Parameters :-\n *\n * website - the new website name\n * existingWebsite - the existing website name\n * \n * \n * Returns :-\n * \n * None\n *\n * \n */\n/* Sanity check the parameters */\nif (($website == "") || ($existingWebsite == ""))\n    return;\n\n/* Change the sub domain name */\n$modx->runSnippet(''cPanelChangeSubDomain'', array(''from'' => $existingWebsite,\n    ''to'' => $website));\n/* Rename the user directory */\n$modx->runSnippet(''renameUserDirectory'', array(''from'' => $existingWebsite,\n    ''to'' => $website));\n\n/* Adjust the existing user alias */\n$c = $modx->newQuery(''modResource'');\n$c->where(array(''alias:='' => $existingWebsite));\n$c->andCondition(array(''parent:='' => $userWebpageContainerId));\n$userContainer = $modx->getObject(''modResource'', $c);\n\nif ($userContainer) {\n\n    $userContainer->set(''alias'', $website);\n    $userContainer->set(''pagetitle'', $website);\n    $userContainer->save();\n    $cacheManager = $modx->getCacheManager();\n    $cacheManager->clearCache(array(\n        "{$resource->context_key}/",\n            ), array(\n        ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n        ''publishing'' => true\n            )\n    );\n}', 0, 'a:1:{s:22:"userWebpageContainerId";a:6:{s:4:"name";s:22:"userWebpageContainerId";s:4:"desc";s:28:"User are web pages container";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
(175, 'userGenerateFEBloglinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEBloglinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for the users blogs\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n\n/* Get the parents, which is where we are */\n$parent = $modx->resource->get(''id'');\n\n/* Call getArchive as we would normally but with a ''full'' url*/\n$output = $modx->runSnippet(''getArchives'', array(''parents'' => $parent,\n                                               ''tpl'' => ''userFEBlogArchiveTemplate'',\n                                               ''limit'' => ''100'',\n                                               ''sortby'' => ''editedon''));\n        \n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */        \n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'') return $output;\n\n/* Get the website name */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Replace the Wayfinder generated ''www'' with the website domain */\n$output = str_replace(''www'', $website, $output);\n\n/* Remove the ''users'' path */\n$output = str_replace("/users/$website", '''', $output);\n\nreturn $output;', 0, NULL, ''),
(169, 'createUserDirectoriesManager', '', 0, 62, 0, '/**\n * Wedding site creatUserDirectoriesManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the users directory set for use by the backend management component\n * \n * Parameters :- \n * \n * id - the wedding user id\n *  \n*/\n\n/* Get the directories */\n$uploadDirectory = $modx->runSnippet(''userGetDirectoryManager'', array(''directory'' => "uploads",\n                                                                      ''id'' => $id));\n$galleryDirectory = $modx->runSnippet(''userGetDirectoryManager'', array(''directory'' => "gallery",\n                                                                      ''id'' => $id)); \n$imagesDirectory = $modx->runSnippet(''userGetDirectoryManager'', array(''directory'' => "images",\n                                                                      ''id'' => $id)); \n\n/* Create them */\n@mkdir($uploadDirectory, 0755, true);\n@mkdir($galleryDirectory, 0755, true);\n@mkdir($imagesDirectory, 0755, true);', 0, NULL, ''),
(170, 'userCreateWebsiteManager', '', 0, 62, 0, '/**\n * Wedding site userCreateWebsiteManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates a users website when called from the backend management component\n * \n * Parameters :-\n *\n * website - the website name\n * id - the wedding user id\n * \n * \n * Returns :-\n * \n * None\n *\n * \n */\n/* Sanity check the website */\nif ($website == "")\n    return;\n\n/* Create the users pages */\n$modx->runSnippet(''createUserPageSetManager'', array(''name'' => $website, ''id'' => $id ));\n\n/* Create the sub domain */\n$modx->runSnippet(''cPanelAddSubDomain'', array(''subDomainName'' => $website));\n\n/* Create the user directories */\n$modx->runSnippet(''createUserDirectoriesManager'', array(''id'' => $id));', 0, NULL, ''),
(171, 'userGetDirectoryManager', '', 0, 62, 0, '/**\n * Wedding site userGetDirectoryManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Gets a users directory path snippet for use by the backend management component\n * \n * Parameters :-\n * \n *  directory : The directory needed, one of uploads, \n *  gallery, images\n *  id - the wedding user id\n *  \n * Returns :-\n * \n * path - the directory path for the user, ''error'' means no user logged in\n */\n\n/* Get the user details */\n$weddingUser = $modx->getObject(''weddingUser'', $id);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n$attributes = $weddingUser->attributes->toArray();\n$website = $attributes[''website''];\n\n/* Construct the path */\n$assetsPath = $modx->getOption(''assets_path'');\n$userDirectory = $assetsPath . "users" .  DIRECTORY_SEPARATOR  .  $website .  DIRECTORY_SEPARATOR  . $directory;\nreturn $userDirectory;', 0, NULL, ''),
(172, 'createUserPageSetManager', '', 0, 62, 0, '/**\n * Wedding site creatUserPageSetManager\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the user page set under the users area from the default set.\n * For use by the backend maangement component\n * \n * Parameters :- \n * \n * $name - The name of the users desired subdomain e.g. janiceswedding\n * $id - the id of the wedding user\n *  \n */\n\n/* Sanity check the name */\nif ( $name == "" ) $name = "nouser";\n\n$weddingUserObject = $modx->getObject(''weddingUser'', $id);\nif ( $weddingUserObject == null ) return;\n\n    \n/* Duplicate the users page set from the default set */\n$defaultUserPage = $modx->getObject(''modResource'', $defaultUserPageSetContainerId);\n$newResource = $defaultUserPage->duplicate(array(\n    ''newName'' => $name,\n    ''duplicateChildren'' => true,\n    ''prefixDuplicate'' => false,\n));\n\n/* Reparent the new resource to the user page area */\n$newResource->set(''parent'', $userPagesParentContainerId);\n\n/* Publish this resource and its children */\n$newResource->set(''published'', true);\n$newResource->save();\n\n$newResourceId = $newResource->get(''id'');\n$children = $modx->getcollection(''modResource'', array(''parent'' => $newResourceId));\n\nforeach ( $children as $child ) {\n    \n    $child->set(''published'', true);\n    $child->save();\n    \n    /* Add details to the user page table */\n    $pageObject = $modx->newObject(''weddingUserPage'');\n    $childId = $child->get(''id'');\n    $editType = $child->get(''longtitle'');\n    $pageObject->set(''pageId'', $childId);\n    $pageObject->set(''editType'', $editType);\n    $weddingUserObject->addMany($pageObject, ''Pages'');\n    $weddingUserObject->save();\n}\n\n/* We need to clear the cache to regen the alias map */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n            "{$resource->context_key}/",\n        ),\n        array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n        )\n    );\n\n/* Done */', 0, 'a:2:{s:29:"defaultUserPageSetContainerId";a:6:{s:4:"name";s:29:"defaultUserPageSetContainerId";s:4:"desc";s:16:"Default page set";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"60";s:7:"lexicon";N;}s:26:"userPagesParentContainerId";a:6:{s:4:"name";s:26:"userPagesParentContainerId";s:4:"desc";s:32:"User web pages parent container ";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
(173, 'getPackageTypes', '', 0, 61, 0, '/**\n * Wedding site getPackageTypes\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * \n * Returns :- an array of available package types\n *\n */\n\n$packageArray = explode('','', $packageTypes);\nreturn json_encode($packageArray);', 0, 'a:1:{s:12:"packageTypes";a:6:{s:4:"name";s:12:"packageTypes";s:4:"desc";s:37:"Comma seperated list of package types";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:24:"Trial,Bronze,Silver,Gold";s:7:"lexicon";N;}}', ''),
(177, 'userGenerateFEGuestbookSignlinks', '', 0, 50, 0, '/**\n * Wedding site userGenerateFEGuestbookSignlinks\n *\n * Copyright 2011 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Generates the links in the FE for the users guestbook signing form\n * \n *  \n *  Parameters :- None\n * \n * \n * Returns - html for the generated links\n * \n */\n\n/* Get the users username */\n$website = $modx->runSnippet(''userGetWebsiteFromURL'');\n\n/* Get the user details */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''website:='' => $website));\n$weddingUserAttributes = $modx->getObject(''weddingUserAttribute'', $c);\nif (!$weddingUserAttributes) return;\n$weddingUserArray = $weddingUserAttributes->toArray();\n$weddingUserId = $weddingUserAttributes->get(''user'');\n$modxUser = $modx->getObject(''modUser'', $weddingUserId);\nif (!$modxUser) return;\n$userArray = $modxUser->toArray();\n$moderate = $weddingUserArray[''moderateGuestbook''];\n\n/* Run quip reply */\n$output = $modx->runSnippet(''QuipReply'',  array(''thread'' =>  $userArray[''username''],\n                                                                     ''tplAddComment'' => ''userGuestbookFEMessage'',\n                                                                     ''moderate'' => $moderate,\n                                                                     ''requirePreview'' => 0));\n\n/* If we are previewing leave the URLS alone, otherwise make them \n * user domain relative.\n */        \n$urlType = $modx->runSnippet(''userGetURLType'');\nif ( $urlType != ''front'') return $output;\n\n/* Generate the FE URL link */\n$alias = $modx->resource->get(''alias'');  \n$domain = $modx->getOption(''domain'');  \n$protocol = $modx->getOption(''server_protocol'');\n$tld = $modx->getOption(''tld'');\n$contentType = $modx->resource->get(''contentType'');\n$contentTypeArray = explode(''/'', $contentType);\n$link = $protocol . ''://'' . $website . ''.'' . $domain . ''.'' . $tld . ''/'' . $alias . ''.'' . $contentTypeArray[1];  \n$search = "users/$website/sign-guestbook.html";\n$output = str_replace($search, $link, $output);\n\nreturn $output;', 0, 'a:0:{}', ''),
(178, 'ditsnewsconfirm', 'Confirm (opt-in) newsletter subscription', 0, 63, 0, 'require_once MODX_CORE_PATH.''/components/ditsnews/model/ditsnews/ditsnews.class.php'';\n$ditsnews = new Ditsnews($modx);\n\nif( $ditsnews->confirmSignup() )\n{\n return $modx->lexicon(''ditsnews.subscribers.confirm.success'');\n}\nelse\n{\n return $modx->lexicon(''ditsnews.subscribers.confirm.err'');\n}', 0, NULL, ''),
(179, 'ditsnewsPlaceholders', 'Use this snippet in your newsletter template to convert placeholders', 0, 63, 0, 'if (!isset($modx->ditsnews)) {\n    $modx->addPackage(''ditsnews'', $modx->getOption(''core_path'').''components/ditsnews/model/'');\n    $modx->ditsnews = $modx->getService(''ditsnews'', ''DitsNews'', $modx->getOption(''core_path'').''components/ditsnews/model/ditsnews/'');\n}\n \n$dn =& $modx->ditsnews;\nreturn $dn->setPlaceholders($scriptProperties);', 0, NULL, ''),
(180, 'ditsnewssignup', 'Add newsletter subscriber (or return error)', 0, 63, 0, 'require_once MODX_CORE_PATH.''/components/ditsnews/model/ditsnews/ditsnews.class.php'';\n$ditsnews = new Ditsnews($modx);\n\nif( $ditsnews->signup($scriptProperties[''fields''], $scriptProperties[''confirmPage'']) ) {\n  return true;\n}\nelse {\n  $scriptProperties[''hook'']->errors[''signup''] = $ditsnews->errormsg;\n  return false;\n}', 0, NULL, ''),
(181, 'ditsnewsunsubscribe', 'Removes subscriber', 0, 63, 0, 'require_once MODX_CORE_PATH.''/components/ditsnews/model/ditsnews/ditsnews.class.php'';\n$ditsnews = new Ditsnews($modx);\n\nif( $ditsnews->unsubscribe() )\n{\n return $modx->lexicon(''ditsnews.subscribers.unsubscribe.success'');\n}\nelse\n{\n return $modx->lexicon(''ditsnews.subscribers.unsubscribe.err'');\n}', 0, NULL, ''),
(182, 'packageExpiryCheck', '', 0, 33, 0, '/**\n * Wedding site packageExpiryCheck\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Site package expiry check snippet\n * \n * Called on a regular(cron) basis from the Dits Newsletter cron interface\n * \n * Parameters :-\n * \n *  None:\n */\n\n/* Check for test mode */\nif ( $testMode == 1 ) return;\n\n/* Get all the ''trial users'' who haven''t been processed */\n$c = $modx->newQuery(''weddingUserAttribute'');\n$c->where(array(''packageType:='' => ''Trial''));\n$c->andCondition(array(''expiryCount:<''  => 3));\n$weddingUsers = $modx->getCollection(''weddingUserAttribute'', $c);\n\n/* Process each user */\n$now = time();\nforeach ($weddingUsers as $weddingUser  ) {\n    \n    $registrationDate = $weddingUser->get(''registrationDate'');\n    $userId = $weddingUser->get(''user'');\n    \n    switch ( $expiryCount ) {\n        \n       /* 2 days before */\n        case 0: \n            \n            $fiveDays = strtotime("+5 day",  $registrationDate);\n            if ( $now >= $fiveDays ) {\n                \n                $weddingUser->set(''expiryCount'', 1);\n                $weddingUser->save();\n                $modxUser = $modx->getObject(''modUser'', $userId);\n                if (!$modxUser) continue;\n                $userName = $modxUser->get(''username'');\n                $firstName = $weddingUser->get(''firstName'');\n                $message = $modx->getChunk(''userPackageDaysLeft'', array(''daysLeft'' => 2,\n                                                                                                                   ''firstName'' => $firstName));\n                $modx->runSnippet(''maiMan'', array(''to'' => $userName,\n                                                                         ''subject'' => $subject,\n                                                                         ''message'' => $message));\n                \n            }\n            \n            break;\n        \n        /* 1 day before */\n        case 1:\n            \n             $sixDays = strtotime("+6 day",  $registrationDate);\n             if ( $now >= $sixDays ) {\n                \n                $weddingUser->set(''expiryCount'', 2);\n                $weddingUser->save();\n                $modxUser = $modx->getObject(''modUser'', $userId);\n                if (!$modxUser) continue;\n                $userName = $modxUser->get(''username'');\n                $firstName = $weddingUser->get(''firstName'');\n                $message = $modx->getChunk(''userPackageDaysLeft'', array(''daysLeft'' => 1,\n                                                                                                                   ''firstName'' => $firstName));\n                $modx->runSnippet(''maiMan'', array(''to'' => $userName,\n                                                                         ''subject'' => $subject,\n                                                                         ''message'' => $message));\n                \n            }\n            \n            break;\n        \n       /* Block check */\n        case 2:\n            \n             $sevenDays = strtotime("+7 day",  $registrationDate);\n             if ( $now >= $sevenDays ) {\n                 \n                 $weddingUser->set(''expiryCount'', 3);\n                 $weddingUser->save();\n                 $userProfile = $modxUser->getOne(''Profile'');\n                 $userProfile->set(''blocked'', 1);\n                 $userProfile->save();\n                 \n             }\n             \n            break;\n        \n        default:\n            \n    }\n    \n}\n\nreturn;', 0, 'a:2:{s:7:"subject";a:6:{s:4:"name";s:7:"subject";s:4:"desc";s:37:"The subject of package reminder mails";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:61:"The Wedding Website Group - Trial package expiration reminder";s:7:"lexicon";N;}s:8:"testMode";a:6:{s:4:"name";s:8:"testMode";s:4:"desc";s:67:"If set to 1 the expiry check is not performed, used in development.";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:1:"0";s:7:"lexicon";N;}}', ''),
(183, 'userValidateRegistration', '', 0, 62, 0, '/**\n * Wedding site userValidateRegistration\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Validates a users registration from the email validation link.\n * \n *  \n *  Parameters :- \n * uid - User id \n * \n * Returns - None, issues a redirect or outputs  a chunk\n * \n */\n\n/* Check for a UID */\nif ( !isset($_REQUEST[''uid''] )) {\n    \n    $output = $modx->getChunk(''registrationValidateNormal'');\n    return $output;\n}\n\n/* Process the registration */\n$uid = $_REQUEST[''uid''];\nif ( $uid == "" ) {\n    \n    $output = $modx->getChunk(''registrationValidateNoUser'');\n    return $output;\n    \n}\n$user = $modx->getObject(''modUser'', $uid);\nif (!$user) {\n    \n    $output = $modx->getChunk(''registrationValidateNoUser'');\n    return $output;\n}\n\n$user->profile = $user->getOne(''Profile'');\n$weddingUser = $modx->getObject(''weddingUser'', $uid);\n$weddingUser->attributes = $weddingUser->getOne(''Attributes'');\n\n/* Check for a registration date of 0 */\nif ( $weddingUser->attributes->get(''registrationDate'') != 0 ) {\n    \n    $output = $modx->getChunk(''registrationValidateAlreadyRegistered'');\n    return $output;\n    \n}\n\n/* Create the users website and associated data */\n$website = $weddingUser->attributes->get(''website'');\n$modx->runSnippet(''userCreateWebsiteRegistration'', array(''website'' => $website));\n\n/* Ok, complete the registration */\n$weddingUser->attributes->set(''registrationDate'', time());\n$weddingUser->save();\n$user->profile->set(''blocked'', 0);\n$user->save();\n\n  \n/* Redirect to login */\n$context = $modx->context->get(''key'');\n$redirectURL = $modx->makeURL($loginPage, $context, "", ''full'');\n$modx->sendRedirect($redirectURL);', 0, 'a:1:{s:9:"loginPage";a:6:{s:4:"name";s:9:"loginPage";s:4:"desc";s:20:"ID of the login page";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"34";s:7:"lexicon";N;}}', ''),
(184, 'userGuestsExportPostProcessor', '', 0, 26, 0, '/**\n * Wedding site Guests export  processing\n *\n * Copyright 2012 by S. Hamblett<steve.hamblett@linux.com>\n * \n */\n/*  Export the guest list and guest data if requested\n */\n\n/* Check for a submit */\nif ($_REQUEST[''userGuestsExportSubmit''] == ''Export'') {\n\n    /* Get the user details */\n    $user = $modx->user;\n    $userId = $user->get(''id'');\n    if ($userId == "") {\n        $modx->setPlaceholder(''userGuestError'', "Error - No MODX user found please log in");\n        return;\n    }\n\n    $weddingUser = $modx->getObject(''weddingUser'', $userId);\n    if (!$weddingUser) {\n        $modx->setPlaceholder(''userGuestError'', "Error - No Wedding user found please log in");\n        return;\n    }\n\n    /* Get a file and open it */\n    $filename = tempnam(sys_get_temp_dir(), "csv");\n    $file = fopen($filename, "w");\n\n    /* Get the guests and events */\n    $guests = $weddingUser->getMany(''Guests'');\n    $events = $weddingUser->getMany(''Events'');\n\n    /* Build the column headers */\n    $eventIdArray = array();\n    $columnHeader = array();\n    $coulumnHeader[] = ''ID'';\n    $coulumnHeader[] = ''NAME'';\n    foreach ($events as $event) {\n\n        if ($event->get(''active'') == 1) {\n\n            $name = $event->get(''name'');\n            $coulumnHeader[] = strtoupper($name);\n            $eventId = $event->get(''id'');\n            $eventIdArray[] = $eventId;\n        }\n    }\n\n    $coulumnHeader[] = ''GUEST OF'';\n    $coulumnHeader[] = ''GUID'';\n    $coulumnHeader[] = ''ACTIVE'';\n\n    /* Write them out */\n    fputcsv($file, $coulumnHeader);\n\n    /* Construct the row data */\n    foreach ($guests as $guest) {\n\n        $guestArray = $guest->toArray();\n        \n        $rowData = array();\n        $rowData[] = $guestArray[''id''];\n        $rowData[] = $guestArray[''name''];\n        \n\n        /* Get the event array into a string we can use in the table body for this guest */\n        $attendOutput = "";\n        foreach ($eventIdArray as $id) {\n\n            /* See if we are invited and what attendance there has been */\n            $guestEvent = $modx->getObject(''guestEvents'', array(\n                ''guestId'' => $guestArray[''id''],\n                ''eventId'' => $id));\n\n            if (!$guestEvent) {\n\n                $attend = "Not Invited";\n            } else {\n\n                /* Invited , check status */\n                $attend = "Invited";\n                $attendance = $guestEvent->get(''willAttend'');\n                if ($attendance == 1) {\n\n                    $attend = "Yes";\n                } else {\n\n                    $rsvpOnline = $guestEvent->get(''RSVPdOnline'');\n                    $rsvpManual = $guestEvent->get(''RSVPdManual'');\n                    if ($rsvpOnline || $rsvpManual) {\n\n                        $attend = "No";\n                    }\n                }\n            }\n            $rowData[] = $attend;\n        }\n        \n        $rowData[] = $guestArray[''guestOf''];\n        $rowData[] = $modx->runSnippet(''getGuestsGUID'', array(''guestId'' => $guestArray[''id''] ));\n        $rowData[] = $guestArray[''active''] ? "Yes" : "No";\n        \n        /* Write the row out*/\n        fputcsv($file, $rowData);\n    }\n    \n    /* Output guest details if requested */\n    if ($_REQUEST[''userGuestsExportGuestDetails''] == ''1'') {\n        \n        fputs($file, PHP_EOL);\n        fputs($file, PHP_EOL);\n        \n        /* Column headers */\n        $guestDetailsHeader = array();\n        $guestDetailsHeader[] = ''ID'';\n        $guestDetailsHeader[] = ''NAME'';\n        $guestDetailsHeader[] = ''EMAIL'';\n        $guestDetailsHeader[] = ''ADDRESS 1'';\n        $guestDetailsHeader[] = ''ADDRESS 2'';\n        $guestDetailsHeader[] = ''CITY'';\n        $guestDetailsHeader[] = ''POSTCODE'';\n        $guestDetailsHeader[] = ''TELEPHONE'';\n        $guestDetailsHeader[] = ''GUEST OF'';\n        $guestDetailsHeader[] = ''PARTY'';\n        $guestDetailsHeader[] = ''ACTIVE'';\n        \n        /* Write them out */\n        fputcsv($file, $guestDetailsHeader);\n        \n        /* Construct the row data */\n        foreach ($guests as $guest) {\n\n            $guestDetailsArray = $guest->toArray();\n        \n            $rowDetailsData = array();\n            $rowDetailsData[] = $guestDetailsArray[''id''];\n            $rowDetailsData[] = $guestDetailsArray[''name''];\n            $rowDetailsData[] = $guestDetailsArray[''email''];\n            $rowDetailsData[] = $guestDetailsArray[''address1''];\n            $rowDetailsData[] = $guestDetailsArray[''address2''];\n            $rowDetailsData[] = $guestDetailsArray[''city''];\n            $rowDetailsData[] = $guestDetailsArray[''postCode''];\n            $rowDetailsData[] = $guestDetailsArray[''telephone''];\n            $rowDetailsData[] = $guestDetailsArray[''guestOf''];\n            $rowDetailsData[] = $guestDetailsArray[''party''];\n            $rowDetailsData[] = $guestDetailsArray[''active''] ? "Yes" : "No";\n            \n            /* Write the row out*/\n            fputcsv($file, $rowDetailsData);\n            \n        }\n    }\n\n    /* Close the file */\n    fclose($file);\n\n    /* Set the content of the output page */\n    $output = file_get_contents($filename);\n    unlink($filename);\n\n    $resource = $modx->getObject(''modResource'', array(''id'' => $userGuestCSVPage));\n    if ($resource) {\n\n        $resource->set(''content'', $output);\n        $resource->save();\n    }\n\n    /* Redirect to the CSV generation page */\n    $context = $modx->context->get(''key'');\n    $pageURL = $modx->makeURL($userGuestCSVPage, $context, "", "full");\n    header("Location: {$pageURL}");\n}', 0, 'a:1:{s:16:"userGuestCSVPage";a:6:{s:4:"name";s:16:"userGuestCSVPage";s:4:"desc";s:34:"Page id of the CSV generation page";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"1587";s:7:"lexicon";N;}}', ''),
(187, 'createUserPageSetRegistration', '', 0, 62, 0, '/**\n * Wedding site creatUserPageSetRegistration\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the user page set under the users area from the default set.\n * \n * Parameters :- \n * \n * $name - The name of the users desired subdomain e.g. janiceswedding\n *  \n */\n\n/* Sanity check the name */\nif ( $name == "" ) $name = "nouser";\n\n$weddingUserAttribute = $modx->getObject(''weddingUserAttribute'', array(''website'' => $name));\nif ( !$weddingUserAttribute ) return;\n\n$userId = $weddingUserAttribute->get(''user'');\n$weddingUserObject = $modx->getObject(''weddingUser'', $userId);\nif ( $weddingUserObject == null ) return;\n\n    \n/* Duplicate the users page set from the default set */\n$defaultUserPage = $modx->getObject(''modResource'', $defaultUserPageSetContainerId);\n$newResource = $defaultUserPage->duplicate(array(\n    ''newName'' => $name,\n    ''duplicateChildren'' => true,\n    ''prefixDuplicate'' => false,\n));\n\n/* Reparent the new resource to the user page area */\n$newResource->set(''parent'', $userPagesParentContainerId);\n\n/* Publish this resource and its children */\n$newResource->set(''published'', true);\n$newResource->save();\n\n$newResourceId = $newResource->get(''id'');\n$children = $modx->getcollection(''modResource'', array(''parent'' => $newResourceId));\n\nforeach ( $children as $child ) {\n    \n    $child->set(''published'', true);\n    $child->save();\n    \n    /* Add details to the user page table */\n    $pageObject = $modx->newObject(''weddingUserPage'');\n    $childId = $child->get(''id'');\n    $editType = $child->get(''longtitle'');\n    $pageObject->set(''pageId'', $childId);\n    $pageObject->set(''editType'', $editType);\n    $weddingUserObject->addMany($pageObject, ''Pages'');\n    $weddingUserObject->save();\n}\n\n/* We need to clear the cache to regen the alias map */\n$cacheManager= $modx->getCacheManager();\n$cacheManager->clearCache(array (\n            "{$resource->context_key}/",\n        ),\n        array(\n            ''objects'' => array(''modResource'', ''modContext'', ''modTemplateVarResource''),\n            ''publishing'' => true\n        )\n    );\n\n/* Done */', 0, 'a:2:{s:29:"defaultUserPageSetContainerId";a:6:{s:4:"name";s:29:"defaultUserPageSetContainerId";s:4:"desc";s:34:"Container id for the user page set";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"60";s:7:"lexicon";N;}s:26:"userPagesParentContainerId";a:6:{s:4:"name";s:26:"userPagesParentContainerId";s:4:"desc";s:24:"User pages container id ";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:2:"33";s:7:"lexicon";N;}}', ''),
(188, 'createUserDirectoriesRegistration', '', 0, 62, 0, '/**\n * Wedding site creatUserDirectoriesRegistration\n *\n * Copyright 2012 by S. Hamblett <steve.hamblett@linux.com>\n *\n * Creates the users directory set\n * \n * Parameters :- \n * \n * website : The website name of the user\n * \n * None\n *  \n*/\n\n/* Get the directories */\n$uploadDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => ''uploads'', ''website'' => $website));\n$galleryDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => ''gallery'', ''website'' => $website)); \n$imagesDirectory = $modx->runSnippet(''userGetDirectory'', array(''directory'' => ''images'', ''website'' => $website)); \n\n/* Create them */\n@mkdir($uploadDirectory, 0755, true);\n@mkdir($galleryDirectory, 0755, true);\n@mkdir($imagesDirectory, 0755, true);', 0, NULL, '');
