(3, 'blog-post', '', 0, 0, '', 0, '[[$head]]\n\n[[$headerWithMenu]]\n  \n  <!-- page title -->\n\n\n   <section class="page-title">\n            <div class="wrapper">\n              <h2>Blog</h2>\n            </div><!--.wrapper-->\n        </section>\n        \n \n\n  <!-- content -->\n     <section class="content-main">  \n            <div class="wrapper">\n           \n                  <article class="article-main-500left">\n    \n<div class="entry">\n       <div class="post">\n     <h2 class="title"> <a href="[[~[[*id]]]]">[[*pagetitle]]</a></h2>\n     <p class="postmeta"><span class="date">[[*publishedon:strtotime:date=`%d.%b.%g`]]</span>\n <a href="[[~[[+id]]]]#comments" class="comments">\n    Comments ([[!QuipCount? &thread=`blog-post-[[+id]]`]])\n  </a>\n [[*tags:notempty=`<span class="tags">Tags:\n[[!tolinks? &items=`[[*tags]]` &key=`tag` &target=`1`]]\n</span>`]]\n  </p>\n \n         <div class="entry"><p>[[*introtext]]</p>\n    [[*content]]\n   </div>\n \n\n  <div class="divider-blog">&nbsp;</div>\n  <div class="post-comments" id="comments">\n[[!Quip?\n      &thread=`blog-post-[[*id]]`\n      &replyResourceId=`123`\n      &closeAfter=`200`\n      &tpl=`quip-comments-tpl`\n    ]]\n    <br /><br />\n    [[!QuipReply?\n       &thread=`blog-post-[[*id]]`\n       &notifyEmails=`my@email.com`\n       &moderate=`1`\n       &moderatorGroup=`Moderators`\n       &recaptcha=`1`\n       &disableRecaptchaWhenLoggedIn=`1`\n       &autoConvertLinks=`1`\n    ]]\n</div>\n</div>\n</div>\n<div class="divider">&nbsp;</div>\n\n\n\n                    </article>\n  \n     <article class="side-180">\n        <h3>Archives</h3>\n            <ul>\n           [[!Archivist? &target=`10` &parents=`6`]]\n            </ul>\n<h3>Categories</h3>    \n<ul> [[!tagLister? &tv=`tags` &target=`1`]]</ul>\n <h3>Latest Posts</h3>\n<ul>\n[[!getResources?\n  &parents=`6`\n  &hideContainers=`1`\n  &tpl=`latestPostsTpl`\n  &limit=`5`\n  &sortby=`publishedon`\n   &showHidden=`1`\n]]\n</ul>\n\n   </ar